public class GooglePlaces implements Places {

    private String request = "https://maps.googleapis.com/maps/api/place/textsearch/json?";

    private String apiKey;

    public static final String PARAM_LOCATION = "location";

    public static final String PARAM_RADIUS = "radius";

    public static final String PARAM_NAME = "query";

    // JSON Node names
    private static final String TAG_RESULTS = "results";

    private static final String TAG_GEOMETRY = "geometry";

    private static final String TAG_LOCATION = "location";

    private static final String TAG_LATITUDE = "lat";

    private static final String TAG_LONGITUDE = "lng";

    private static final String TAG_NAME = "name";

    private static final String TAG_FORMATTED_ADDRESS = "formatted_address";

    JSONArray results = null;

    public GooglePlaces(String apiKey) {
        this.setApiKey(apiKey);
    }

    public JSONObject requestPlaces(String paramLocation, String paramRadius, String paramName) {
        StringBuilder builder = new StringBuilder();

        String encodedParamLocation = "";
        String encodedParamRadius = "";
        String encodedParamName;
        try {
            if ((paramLocation != null) && (paramRadius != null)) {
                encodedParamLocation = URLEncoder.encode(paramLocation, OTPApp.URL_ENCODING);
                encodedParamRadius = URLEncoder.encode(paramRadius, OTPApp.URL_ENCODING);
            }
            encodedParamName = URLEncoder.encode(paramName, OTPApp.URL_ENCODING);
        } catch (UnsupportedEncodingException e1) {
            Log.e(OTPApp.TAG, "Error encoding Google Places request");
            e1.printStackTrace();
            return null;
        }

        if ((paramLocation != null) && (paramRadius != null)) {
            request += "location=" + encodedParamLocation;
            request += "&radius=" + encodedParamRadius;
            request += "&query=" + encodedParamName;
        } else {
            request += "query=" + encodedParamName;
        }
        request += "&sensor=false";
        request += "&key=" + getApiKey();

        Log.d(OTPApp.TAG, request);

        HttpURLConnection urlConnection = null;

        try {
            URL url = new URL(request);
            urlConnection = (HttpURLConnection) url.openConnection();
            urlConnection.setConnectTimeout(OTPApp.HTTP_CONNECTION_TIMEOUT);
            urlConnection.setReadTimeout(OTPApp.HTTP_SOCKET_TIMEOUT);
            urlConnection.connect();
            int status = urlConnection.getResponseCode();

            if (status == HttpURLConnection.HTTP_OK) {
                BufferedReader reader = new BufferedReader(
                        new InputStreamReader(urlConnection.getInputStream()));
                String line;
                while ((line = reader.readLine()) != null) {
                    builder.append(line);
                }
            } else {
                Log.e(OTPApp.TAG,
                        "Error obtaining Google Places response, status code: \" + status");
            }
        } catch (IOException e) {
            Log.e(OTPApp.TAG, "Error obtaining Google Places response" + e.toString());
            e.printStackTrace();
            return null;
        } finally {
            if (urlConnection != null) {
                urlConnection.disconnect();
            }
        }
        Log.d(OTPApp.TAG, builder.toString());

        JSONObject json = null;
        try {
            json = new JSONObject(builder.toString());
        } catch (JSONException e) {
            Log.e(OTPApp.TAG, "Error parsing Google Places data " + e.toString());
        }

        return json;
    }

    public ArrayList<POI> getPlaces(HashMap<String, String> params) {
        ArrayList<POI> pois = new ArrayList<POI>();

        String paramLocation = params.get(PARAM_LOCATION);
        String paramRadius = params.get(PARAM_RADIUS);
        String paramName = params.get(PARAM_NAME);

        // Get JSON
        JSONObject json = this.requestPlaces(paramLocation, paramRadius, paramName);

        if (json != null) {
            // Decrypt JSON
            try {
                results = json.getJSONArray(TAG_RESULTS);

                for (int i = 0; i < results.length(); i++) {
                    JSONObject r = results.getJSONObject(i);

                    String name = r.getString(TAG_NAME);
                    String address = r.getString(TAG_FORMATTED_ADDRESS);

                    JSONObject geometry = r.getJSONObject(TAG_GEOMETRY);
                    JSONObject location = geometry.getJSONObject(TAG_LOCATION);
                    double lat = location.getDouble(TAG_LATITUDE);
                    double lon = location.getDouble(TAG_LONGITUDE);

                    POI point = new POI(name, address, lat, lon);
                    pois.add(point);
                }
            } catch (JSONException e) {
                Log.e(OTPApp.TAG, "Error parsing Google Places data " + e.toString());
                e.printStackTrace();
            }
        }

        return pois;
    }

    /**
     * @return the apiKey
     */
    public String getApiKey() {
        return apiKey;
    }

    /**
     * @param apiKey the apiKey to set
     */
    public void setApiKey(String apiKey) {
        this.apiKey = apiKey;
    }
}

public class Nominatim implements Places {

    private String request = "http://open.mapquestapi.com/nominatim/v1/search?format=json";

    public static final String PARAM_NAME = "q";

    public static final String PARAM_LEFT = "left";

    public static final String PARAM_TOP = "top";

    public static final String PARAM_RIGHT = "right";

    public static final String PARAM_BOTTOM = "bottom";


    // JSON Node names
    private static final String TAG_LATITUDE = "lat";

    private static final String TAG_LONGITUDE = "lon";

    private static final String TAG_NAME = "display_name";

    private String mApiKey;

    public Nominatim(String apiKey) {
        mApiKey = apiKey;
    }

    //	http://open.mapquestapi.com/nominatim/v1/search?format=json&q=Walmart&viewbox=-82.8511308,27.6236434,-82.0559399,28.3251809&bounded=1

    public JSONArray requestPlaces(String paramName, String left, String top, String right,
                                   String bottom) {
        StringBuilder builder = new StringBuilder();

        String encodedParamName;
        String encodedParamLeft = "";
        String encodedParamTop = "";
        String encodedParamRight = "";
        String encodedParamBottom = "";
        try {
            encodedParamName = URLEncoder.encode(paramName, OTPApp.URL_ENCODING);
            if ((left != null) && (top != null) && (right != null) && (bottom != null)) {
                encodedParamLeft = URLEncoder.encode(left, OTPApp.URL_ENCODING);
                encodedParamTop = URLEncoder.encode(top, OTPApp.URL_ENCODING);
                encodedParamRight = URLEncoder.encode(right, OTPApp.URL_ENCODING);
                encodedParamBottom = URLEncoder.encode(bottom, OTPApp.URL_ENCODING);
            }
        } catch (UnsupportedEncodingException e1) {
            Log.e(OTPApp.TAG, "Error encoding Nominatim request");
            e1.printStackTrace();
            return null;
        }

        request += "&q=" + encodedParamName;
        if ((left != null) && (top != null) && (right != null) && (bottom != null)) {
            request += "&viewbox=" + encodedParamLeft
                    + "," + encodedParamTop
                    + "," + encodedParamRight
                    + "," + encodedParamBottom;
            request += "&bounded=1";
        }

        request += "&key=" + mApiKey;

        Log.d(OTPApp.TAG, request);

        HttpURLConnection urlConnection = null;

        try {
            URL url = new URL(request);
            urlConnection = (HttpURLConnection) url.openConnection();
            urlConnection.setConnectTimeout(OTPApp.HTTP_CONNECTION_TIMEOUT);
            urlConnection.setReadTimeout(OTPApp.HTTP_SOCKET_TIMEOUT);
            urlConnection.connect();
            int status = urlConnection.getResponseCode();

            if (status == HttpURLConnection.HTTP_OK) {
                BufferedReader reader = new BufferedReader(
                        new InputStreamReader(urlConnection.getInputStream()));
                String line;
                while ((line = reader.readLine()) != null) {
                    builder.append(line);
                }
            } else {
                Log.e(OTPApp.TAG, "Error obtaining Nominatim response, status code: " + status);
            }
        } catch (IOException e) {
            e.printStackTrace();
            Log.e(OTPApp.TAG, "Error obtaining Nominatim response" + e.toString());
            return null;
        } finally {
            if (urlConnection != null) {
                urlConnection.disconnect();
            }
        }
        Log.d(OTPApp.TAG, builder.toString());

        JSONArray json = null;
        try {
            json = new JSONArray(builder.toString());
        } catch (JSONException e) {
            Log.e(OTPApp.TAG, "Error parsing Nominatim data " + e.toString());
        }

        return json;
    }

    public ArrayList<POI> getPlaces(HashMap<String, String> params) {
        ArrayList<POI> pois = new ArrayList<POI>();

        String paramName = params.get(PARAM_NAME);
        String paramLeft = params.get(PARAM_LEFT);
        String paramTop = params.get(PARAM_TOP);
        String paramRight = params.get(PARAM_RIGHT);
        String paramBottom = params.get(PARAM_BOTTOM);

        // Get JSON
        JSONArray json = this
                .requestPlaces(paramName, paramLeft, paramTop, paramRight, paramBottom);

        if (json != null) {
            // Decrypt JSON
            try {
                for (int i = 0; i < json.length(); i++) {
                    JSONObject r = json.getJSONObject(i);

                    String name = r.getString(TAG_NAME);
                    double lat = Double.parseDouble(r.getString(TAG_LATITUDE));
                    double lon = Double.parseDouble(r.getString(TAG_LONGITUDE));

                    POI point = new POI(name, lat, lon);
                    pois.add(point);
                }
            } catch (JSONException e) {
                Log.e(OTPApp.TAG, "Error parsing Google Places data " + e.toString());
                e.printStackTrace();
            }
        }

        return pois;
    }
}

public class POI {

    private String name, address;

    private double latitude, longitude;

    public POI(String name) {
        this.name = name;
    }

    public POI(String name, String address, double latitude, double longitude) {
        this.name = name;
        this.address = address;
        this.latitude = latitude;
        this.longitude = longitude;
    }

    public POI(String name, double latitude, double longitude) {
        this.name = name;
        this.latitude = latitude;
        this.longitude = longitude;
    }

    public POI(String name, String address) {
        this.name = name;
        this.address = address;
    }

    public POI(double latitude, double longitude) {
        this.latitude = latitude;
        this.longitude = longitude;
    }

    /**
     * @return the name
     */
    public String getName() {
        return name;
    }

    /**
     * @param name the name to set
     */
    public void setName(String name) {
        this.name = name;
    }

    /**
     * @return the address
     */
    public String getAddress() {
        return address;
    }

    /**
     * @param address the address to set
     */
    public void setAddress(String address) {
        this.address = address;
    }

    /**
     * @return the latitude
     */
    public double getLatitude() {
        return latitude;
    }

    /**
     * @param latitude the latitude to set
     */
    public void setLatitude(double latitude) {
        this.latitude = latitude;
    }

    /**
     * @return the longitude
     */
    public double getLongitude() {
        return longitude;
    }

    /**
     * @param longitude the longitude to set
     */
    public void setLongitude(double longitude) {
        this.longitude = longitude;
    }
}

