public class RequestTimesForTrips extends AsyncTask<String, Integer, HashMap<String, List<TripTimeShort>>> {

    private WeakReference<Activity> activity;

    private Context context;

    private RequestTimesForTripsCompleteListener callback;

    private static ObjectMapper mapper = null;

    public RequestTimesForTrips(Context context, RequestTimesForTripsCompleteListener callback) {
        this.context = context;
        this.callback = callback;
    }

    protected void onPreExecute() {
        // Do nothing
    }

    protected HashMap<String, List<TripTimeShort>> doInBackground(String... reqs) {
        String prefix = PreferenceManager.getDefaultSharedPreferences(context)
                .getString(OTPApp.PREFERENCE_KEY_FOLDER_STRUCTURE_PREFIX
                        , OTPApp.FOLDER_STRUCTURE_PREFIX_NEW);
        if (reqs.length <= 1){
            return null;
        }
        HashMap<String, List<TripTimeShort>> timesUpdatesForTrips = new HashMap<String,
                List<TripTimeShort>>(reqs.length - 1);

        HttpURLConnection urlConnection = null;
        List<TripTimeShort> updatedTripTimesList = null;

        try {
            if (mapper == null) {
                mapper = new ObjectMapper();
            }
            mapper.configure(DeserializationFeature.FAIL_ON_UNKNOWN_PROPERTIES, false);
            JavaType bikeRentalStationListType = mapper.getTypeFactory().constructCollectionType(List.class, TripTimeShort.class);
            for (String tripId : reqs) {
                if (tripId.equals(reqs[0])) {
                    continue;
                }
                String encodedTripId = URLEncoder.encode(tripId, Charset.defaultCharset().name());
                String u = reqs[0] + prefix + OTPApp.TRIP_TIMES_UPDATES_LOCATION_BEFORE_ID
                        + encodedTripId + OTPApp.TRIP_TIMES_UPDATES_LOCATION_AFTER_ID;
                URL url = new URL(u);
                Log.d(OTPApp.TAG, "URL: " + u);
                urlConnection = (HttpURLConnection) url.openConnection();
                urlConnection.setRequestProperty("Accept", "application/json");
                urlConnection.setConnectTimeout(OTPApp.HTTP_CONNECTION_TIMEOUT);
                urlConnection.setReadTimeout(OTPApp.HTTP_SOCKET_TIMEOUT);
                updatedTripTimesList = mapper.readValue(urlConnection.getInputStream(), bikeRentalStationListType);
                timesUpdatesForTrips.put(tripId, updatedTripTimesList);
            }
        } catch (IOException e) {
            Log.e(OTPApp.TAG, "Error fetching JSON or XML: " + e);
            e.printStackTrace();
        } finally {
            if (urlConnection != null) {
                urlConnection.disconnect();
            }
        }

        return timesUpdatesForTrips;
    }

    protected void onPostExecute(HashMap<String, List<TripTimeShort>> timesUpdatesForTrips) {
        if (timesUpdatesForTrips != null) {
            callback.onUpdateTripTimesComplete(timesUpdatesForTrips);
        } else {
            Toast.makeText(context, context.getResources().getString(R.string.toast_realtime_updates_fail),
                    Toast.LENGTH_SHORT).show();
            Log.e(OTPApp.TAG, "No bike rental stations!");
            callback.onUpdateTripTimesFail();
        }
    }
}

public class ServerChecker extends AsyncTask<Server, Long, String> {

    private ProgressDialog progressDialog;

    private WeakReference<Activity> activity;

    private Context context;

    private ServerCheckerCompleteListener callback = null;

    private boolean showMessage = false;

    private boolean isWorking = false;

    private static ObjectMapper mapper = null;

    private boolean isCustomServer = false;

    private boolean isAutoDetected = false;

    private boolean showToast = false;

    /**
     * Constructs a new ServerChecker
     */
    public ServerChecker(WeakReference<Activity> activity, Context context, boolean showMessage) {
        this.activity = activity;
        this.context = context;
        this.showMessage = showMessage;
        Activity activityRetrieved = activity.get();
        if (activityRetrieved != null) {
            progressDialog = new ProgressDialog(activityRetrieved);
        }
    }

    /**
     * Constructs a new ServerChecker
     */
    public ServerChecker(WeakReference<Activity> activity, Context context,
            ServerCheckerCompleteListener callback, boolean isCustomServer, boolean showToast,
            boolean isAutoDetected) {
        this.activity = activity;
        this.context = context;
        this.callback = callback;
        this.isCustomServer = isCustomServer;
        this.isAutoDetected = isAutoDetected;
        this.showToast = showToast;
        Activity activityRetrieved = activity.get();
        if (activityRetrieved != null) {
            progressDialog = new ProgressDialog(activityRetrieved);
        }
    }

    @Override
    protected void onPreExecute() {
        if (activity.get() != null) {
            progressDialog.setIndeterminate(true);
            progressDialog.setCancelable(true);
            Activity activityRetrieved = activity.get();
            if (activityRetrieved != null) {
                progressDialog = ProgressDialog
                        .show(activityRetrieved, "",
                                context.getString(R.string.task_progress_server_checker_progress),
                                true);
            }
        }
    }

    @Override
    protected String doInBackground(Server... params) {
        Server server = params[0];
        ServerInfo serverInfo;

        if (server == null) {
            Log.w(OTPApp.TAG,
                    "Tried to get server info when no server was selected");
            cancel(true);
            return null;
        } else {
            String message =
                    context.getResources().getString(R.string.server_checker_info_region)
                            + " " + server.getRegion() + "\n" + context.getResources()
                            .getString(R.string.server_checker_info_language) + " " + server
                            .getLanguage() + "\n" + context.getResources()
                            .getString(R.string.server_checker_info_contact) + " " + server
                            .getContactName() + " ("
                            + server.getContactEmail() + ")" + "\n" + context.getResources()
                            .getString(R.string.server_checker_info_url) + " " + server
                            .getBaseURL()
                            + "\n" + context.getResources()
                            .getString(R.string.server_checker_info_bounds) + " " + server
                            .getBounds() + "\n" + context.getResources()
                            .getString(R.string.server_checker_info_bike_rental) + " ";
            if (server.getOffersBikeRental()){
                message +=  context.getResources().getString(android.R.string.yes);
            }
            else{
                message += context.getResources().getString(android.R.string.no);
            }

            message += "\n" + context.getResources()
                            .getString(R.string.server_checker_info_reachable) + " ";

            if (mapper == null) {
                mapper = new ObjectMapper();
            }

            int status = 0;
            HttpURLConnection urlConnection = null;
            mapper.configure(DeserializationFeature.FAIL_ON_UNKNOWN_PROPERTIES, false);

            SharedPreferences.Editor prefsEditor = PreferenceManager.getDefaultSharedPreferences(
                    context).edit();

            try {
                URL url = new URL(server.getBaseURL() + OTPApp.SERVER_INFO_LOCATION_NEW);
                urlConnection = (HttpURLConnection) url.openConnection();
                urlConnection.setConnectTimeout(OTPApp.HTTP_CONNECTION_TIMEOUT);
                urlConnection.setReadTimeout(OTPApp.HTTP_SOCKET_TIMEOUT);
                urlConnection.connect();
                status = urlConnection.getResponseCode();
                serverInfo = mapper.readValue(urlConnection.getInputStream(), ServerInfo.class);
                prefsEditor.putString(OTPApp.PREFERENCE_KEY_FOLDER_STRUCTURE_PREFIX, OTPApp.FOLDER_STRUCTURE_PREFIX_NEW);
            } catch (IOException e1) {
                Log.e(OTPApp.TAG, "Server not working with API V1, trying again this time with" +
                        " old version: "
                        + e1.getMessage());
                try {
                    URL url = new URL(server.getBaseURL() + OTPApp.SERVER_INFO_LOCATION_OLD);
                    urlConnection = (HttpURLConnection) url.openConnection();
                    urlConnection.setConnectTimeout(OTPApp.HTTP_CONNECTION_TIMEOUT);
                    urlConnection.setReadTimeout(OTPApp.HTTP_SOCKET_TIMEOUT);
                    urlConnection.connect();
                    status = urlConnection.getResponseCode();
                    serverInfo = mapper.readValue(urlConnection.getInputStream(), ServerInfo.class);
                    prefsEditor.putString(OTPApp.PREFERENCE_KEY_FOLDER_STRUCTURE_PREFIX, OTPApp.FOLDER_STRUCTURE_PREFIX_OLD);
                } catch (IOException e2) {
                    Log.e(OTPApp.TAG, "Unable to reach server: " + e2.getMessage());
                    message = context.getResources().getString(R.string.toast_server_checker_error_unreachable)
                            + " "
                            + e2.getMessage();
                    return message;
                } finally {
                    if (urlConnection != null) {
                        urlConnection.disconnect();
                    }
                }
            } finally {
                if (urlConnection != null) {
                    urlConnection.disconnect();
                }
            }

            if (serverInfo != null){
                int api_version = OTPApp.API_VERSION_V3;
                if (serverInfo.serverVersion.major == 0){
                    if (serverInfo.serverVersion.minor >= OTPApp.API_VERSION_MINOR_019){
                        api_version = OTPApp.API_VERSION_V2;
                    }else if (serverInfo.serverVersion.minor >= OTPApp.API_VERSION_MINOR_011){
                        api_version = OTPApp.API_VERSION_V1;
                    }else {
                        api_version = OTPApp.API_VERSION_PRE_V1;
                    }
                }
                prefsEditor.putInt(OTPApp.PREFERENCE_KEY_API_VERSION,
                        api_version);
                prefsEditor.commit();
            }


            if (status == HttpURLConnection.HTTP_OK) {
                message += context.getResources().getString(android.R.string.yes);
                isWorking = true;
            } else {
                message += context.getResources().getString(android.R.string.no);
            }

            return message;
        }
    }

    @Override
    protected void onCancelled() {
        super.onCancelled();

        Toast.makeText(context, context.getResources().getString(R.string.toast_server_checker_info_error),
                Toast.LENGTH_SHORT).show();
    }

    @Override
    protected void onPostExecute(String result) {
        if (activity.get() != null) {
            try {
                if (progressDialog != null && progressDialog.isShowing()) {
                    progressDialog.dismiss();
                }
            } catch (Exception e) {
                Log.e(OTPApp.TAG, "Error in Server Checker PostExecute dismissing dialog: " + e);
            }
        }

        if (showMessage) {
            Activity activityRetrieved = activity.get();
            if (activityRetrieved != null) {
                AlertDialog.Builder dialog = new AlertDialog.Builder(activityRetrieved);
                dialog.setTitle(context.getResources()
                        .getString(R.string.server_checker_info_title));
                dialog.setMessage(result);
                dialog.setNeutralButton(context.getResources().getString(android.R.string.ok),
                        null);
                dialog.create().show();
            }
        } else{
            if (isCustomServer && showToast) {
                if (isWorking) {
                    Toast.makeText(context,
                            context.getResources().getString(R.string.toast_server_checker_successful),
                            Toast.LENGTH_SHORT).show();
                } else {
                    Toast.makeText(context,
                            context.getResources().getString(R.string.settings_menu_custom_server_url_description_error_unreachable),
                            Toast.LENGTH_SHORT).show();
                }
            }
            if (callback != null) {
                callback.onServerCheckerComplete(result, isCustomServer, isAutoDetected, isWorking);
            }
        }
    }

}

public class ServerSelector extends AsyncTask<LatLng, Integer, Integer>
        implements ServerCheckerCompleteListener {

    private Server selectedServer;

    private ProgressDialog progressDialog;

    private WeakReference<Activity> activity;

    private Context context;

    private static List<Server> knownServers = new ArrayList<Server>();

    private boolean mustRefreshList = false;

    private boolean isAutoDetectEnabled = true;

    private ServerSelectorCompleteListener callback;

    private boolean selectedCustomServer;

    private boolean showDialog;

    public ServersDataSource dataSource = null;

    /**
     * Constructs a new ServerSelector
     *
     * @param mustRefreshList true if we should download a new list of servers from the Google
     *                        Doc, false if we should use cached list of servers
     * @param showDialog      true if a progress dialog is requested
     */
    public ServerSelector(WeakReference<Activity> activity, Context context,
            ServersDataSource dataSource, ServerSelectorCompleteListener callback,
            boolean mustRefreshList, boolean showDialog) {
        this.activity = activity;
        this.context = context;
        this.dataSource = dataSource;
        this.callback = callback;
        this.mustRefreshList = mustRefreshList;
        this.showDialog = showDialog;
        Activity activityRetrieved = activity.get();
        if ((activityRetrieved != null) && showDialog) {
            progressDialog = new ProgressDialog(activityRetrieved);
        }
    }

    protected void onPreExecute() {
        Activity activityRetrieved = activity.get();

        if ((activityRetrieved != null) && showDialog) {
            progressDialog.setIndeterminate(true);
            progressDialog.setCancelable(true);
            progressDialog = ProgressDialog.show(activityRetrieved, "",
                    context.getResources().getString(R.string.task_progress_server_selector_progress), true);
        }

        SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(context);

        isAutoDetectEnabled = prefs.getBoolean(OTPApp.PREFERENCE_KEY_AUTO_DETECT_SERVER,
                true);
    }


    protected Integer doInBackground(LatLng... latLng) {
        LatLng currentLocation = latLng[0];

        List<Server> serverList = null;

        // If not forced to refresh list
        if (!mustRefreshList) {
            // Check if servers are stored in SQLite?
            Log.d(OTPApp.TAG, "Attempt retrieving servers from sqlite");
            serverList = getServersFromSQLite();
        }

        // If forced to refresh list OR
        // If severs are not stored, download list from the Google Spreadsheet and Insert to database
        if (serverList == null || serverList.isEmpty() || mustRefreshList) {
            Log.d(OTPApp.TAG,
                    "No data from sqlite. Attempt retrieving servers from google spreadsheet");
            serverList = downloadServerList(
                    context.getResources().getString(R.string.servers_spreadsheet_url));

            // Insert new list to database
            if (serverList != null && !serverList.isEmpty()) {
                insertServerListToDatabase(serverList);
                serverList = getServersFromSQLite();
            }

            // If still null
            if (serverList == null || serverList.isEmpty()) {
                return null;
            }
        }

        knownServers.clear();
        knownServers.addAll(serverList);

        //If we're autodetecting a server, get the location find the optimal server
        if (isAutoDetectEnabled && (currentLocation != null)) {
            selectedServer = findOptimalSever(currentLocation);
        }

        return serverList.size();
    }

    private List<Server> getServersFromSQLite() {
        List<Server> servers = new ArrayList<Server>();

        dataSource.open();
        List<Server> values = dataSource.getMostRecentServers();
        String shown = "";
        for (Server s : values) {
            shown += s.getRegion() + s.getDate().toString() + "\n";
            servers.add(new Server(s));
        }
        Log.d(OTPApp.TAG, shown);
        dataSource.close();

        dataSource.open();
        Calendar someDaysBefore = Calendar.getInstance();
        someDaysBefore.add(Calendar.DAY_OF_MONTH, -OTPApp.EXPIRATION_DAYS_FOR_SERVER_LIST);
        Long serversUpdateDate = dataSource.getMostRecentDate();
        if ((serversUpdateDate != null) && (someDaysBefore.getTime().getTime()
                > serversUpdateDate)) {
            servers = null;
        }
        dataSource.close();
//		Toast.makeText(activity.getApplicationContext(), shown, Toast.LENGTH_SHORT).show();

        return servers;
    }

    /**
     * Downloads the list of OTP servers from the Google Doc directory
     *
     * @param urlString URL address of the Google Doc
     * @return Server a list of OTP servers contained in the Google Doc
     */
    private List<Server> downloadServerList(String urlString) {
        HttpURLConnection urlConnection = null;

        try {
            List<Server> serverList = new ArrayList<Server>();
            URL url = new URL(urlString);
            urlConnection = (HttpURLConnection) url.openConnection();
            urlConnection.setConnectTimeout(OTPApp.HTTP_CONNECTION_TIMEOUT);
            urlConnection.setReadTimeout(OTPApp.HTTP_SOCKET_TIMEOUT);
            urlConnection.connect();

            BufferedReader bufferedReader = new BufferedReader
                    (new InputStreamReader(urlConnection.getInputStream()));
            CSVReader reader = new CSVReader(bufferedReader);
            try {
                Long currentTime = Calendar.getInstance().getTime().getTime();

                List<String[]> serversStrings = reader.readAll();
                for (String[] serverString : serversStrings) {
                    if (serverString[0].equalsIgnoreCase("Region")) {
                        continue; //Ignore the first line of the file
                    }
                    for (String stringEntry : serverString) {
                        stringEntry = stringEntry.trim();
                    }
                    boolean allFieldsNotNull = true;
                    for (String serverField : serverString) {
                        serverField = serverField.trim();
                        if (serverField.equals("")) {
                            Log.e(OTPApp.TAG, "Some necessary fields are null, server not added");
                            allFieldsNotNull = false;
                            break;
                        }
                    }
                    if (allFieldsNotNull && (serverString.length >= 9)) {
                        try {
                            Server s = new Server(currentTime, serverString[0], serverString[1],
                                    serverString[2], serverString[3], serverString[4],
                                    serverString[5], serverString[6], serverString[7],
                                    serverString[8]);
                            serverList.add(s);
                        } catch (ServerListParsingException e) {
                            Log.e(OTPApp.TAG,
                                    "Error parsing necessary fields, server not added: " + e);
                        }
                    } else {
                        Log.e(OTPApp.TAG,
                                "Server does not provide necessary fields, server not added");
                    }
                }
            } catch (IOException e) {
                Log.e(OTPApp.TAG, "Problem reading CSV server file: " + e.getMessage());

                try {
                    reader.close();
                } catch (IOException e2) {
                    Log.e(OTPApp.TAG, "Error closing CSVReader file: " + e2);
                }
                return null;
            } finally {
                try {
                    reader.close();
                } catch (IOException e) {
                    Log.e(OTPApp.TAG, "Error closing CSVReader file: " + e);
                }
            }
            Log.d(OTPApp.TAG, "Servers: " + serverList.size());
            return serverList;

        } catch (IOException e) {
            Log.e(OTPApp.TAG, "Unable to download spreadsheet with server list: " + e.getMessage());
            return null;
        } finally {
            if (urlConnection != null) {
                urlConnection.disconnect();
            }
        }


    }

    private void insertServerListToDatabase(List<Server> servers) {
        dataSource.open();
        for (Server server : servers) {
            if (dataSource.createServer(server) == null) {
                Log.e(OTPApp.TAG, "Some server fields are incorrect, server not added");
            }
        }
        dataSource.close();
    }

    /**
     * Automatically detects the correct OTP server based on the location of the device
     *
     * @param currentLocation location of the device
     * @return Server the OTP server that the location is within
     */
    private Server findOptimalSever(LatLng currentLocation) {
        if (currentLocation == null) {
            return null;
        }

        //If we've already selected a server, just return the one we selected
        if (selectedServer != null) {
            return selectedServer;
        }

        boolean isInBoundingBox;
        Server server = null;
        for (Server knownServer : knownServers) {
            isInBoundingBox = LocationUtil.checkPointInBoundingBox(currentLocation, knownServer);

            if (isInBoundingBox) {
                server = knownServer;
                break;
            }
        }

        return server;
    }

    protected void onPostExecute(Integer result) {
        if ((activity.get() != null) && showDialog) {
            try {
                if (progressDialog != null && progressDialog.isShowing()) {
                    progressDialog.dismiss();
                }
            } catch (Exception e) {
                Log.e(OTPApp.TAG, "Error in Server Selector PostExecute dismissing dialog: " + e);
            }
        }

        if (selectedServer != null) {
            //We've already auto-selected a server
            ServerChecker serverChecker = new ServerChecker(activity,
                    context, ServerSelector.this, false, false, true);
            serverChecker.execute(selectedServer);
        } else if (knownServers != null && !knownServers.isEmpty()) {
            Log.d(OTPApp.TAG,
                    "No server automatically selected.  User will need to choose the OTP server.");

            // Create dialog for user to choose
            List<String> serverNames = new ArrayList<String>();
            for (Server server : knownServers) {
                serverNames.add(server.getRegion());
            }

            Collections.sort(serverNames);

            serverNames.add(0, context.getResources().getString(R.string.server_checker_info_custom_server_name));

            final CharSequence[] items = serverNames.toArray(new CharSequence[serverNames.size()]);

            Activity activityRetrieved = activity.get();

            if (activityRetrieved != null) {
                AlertDialog.Builder builder = new AlertDialog.Builder(activityRetrieved);
                builder.setTitle(context.getResources()
                        .getString(R.string.server_checker_info_title));
                        builder.setCancelable(false);
                builder.setItems(items, new DialogInterface.OnClickListener() {

                    public void onClick(DialogInterface dialog, int item) {

                        //If the user selected to enter a custom URL, they are shown this EditText box to enter it
                        if (items[item].equals(context.getResources()
                                .getString(R.string.server_checker_info_custom_server_name))) {
                            SharedPreferences prefs = PreferenceManager
                                    .getDefaultSharedPreferences(context);

                            Activity activityRetrieved = activity.get();

                            if (activityRetrieved != null) {
                                final EditText tbBaseURL = new EditText(activityRetrieved);
                                String actualCustomServer = prefs
                                        .getString(PREFERENCE_KEY_CUSTOM_SERVER_URL, "");
                                tbBaseURL.setText(actualCustomServer);

                                AlertDialog.Builder urlAlert = new AlertDialog.Builder(
                                        activityRetrieved);
                                urlAlert.setTitle(context.getResources()
                                        .getString(
                                                R.string.server_selector_custom_server_alert_title));
                                urlAlert.setView(tbBaseURL);
                                urlAlert.setPositiveButton(
                                        context.getResources().getString(android.R.string.ok),
                                        new DialogInterface.OnClickListener() {
                                            public void onClick(DialogInterface dialog,
                                                    int whichButton) {
                                                Editable tbEditable = tbBaseURL.getText();
                                                if (tbEditable != null) {
                                                    String value = tbEditable.toString().trim();
                                                    if (URLUtil.isValidUrl(value)) {
                                                        SharedPreferences.Editor prefsEditor
                                                                = PreferenceManager
                                                                .getDefaultSharedPreferences(
                                                                        context)
                                                                .edit();
                                                        prefsEditor
                                                                .putString(
                                                                        PREFERENCE_KEY_CUSTOM_SERVER_URL,
                                                                        value);

                                                        ServerChecker serverChecker
                                                                = new ServerChecker(activity,
                                                                context, ServerSelector.this, true,
                                                                true, false);
                                                        serverChecker.execute(
                                                                new Server(value, context));
                                                        prefsEditor.commit();
                                                    } else {
                                                        Toast.makeText(context,
                                                                context.getResources()
                                                                        .getString(
                                                                                R.string.settings_menu_custom_server_url_description_error_url),
                                                                Toast.LENGTH_SHORT).show();
                                                    }
                                                }

                                            }
                                        });
                                selectedCustomServer = true;
                                urlAlert.create().show();
                            }
                        } else {
                            //User picked server from the list
                            for (Server server : knownServers) {
                                //If this server region matches what the user picked, then set the server as the selected server
                                if (server.getRegion().equals(items[item])) {
                                    selectedServer = server;
                                    ServerChecker serverChecker = new ServerChecker(activity,
                                            context, ServerSelector.this, false, false, false);
                                    serverChecker.execute(selectedServer);
                                    break;
                                }
                            }
                        }
                        Log.d(OTPApp.TAG, "Chosen: " + items[item]);
                    }
                });
                builder.show();
            }
        } else {
            Log.e(OTPApp.TAG, "Server list could not be downloaded!!");
            Toast.makeText(context,
                    context.getResources().getString(R.string.toast_server_selector_refresh_server_list_error),
                    Toast.LENGTH_SHORT).show();
        }
    }

    @Override
    public void onServerCheckerComplete(String result, boolean isCustomServer,
            boolean isAutoDetected, boolean isWorking) {
        SharedPreferences prefs = PreferenceManager
                .getDefaultSharedPreferences(context);
        SharedPreferences.Editor prefsEditor = prefs.edit();
        if (isCustomServer){
            if (isWorking) {
                prefsEditor.putBoolean(PREFERENCE_KEY_AUTO_DETECT_SERVER, false);
                prefsEditor.putBoolean(PREFERENCE_KEY_SELECTED_CUSTOM_SERVER, true);
                prefsEditor.putBoolean(PREFERENCE_KEY_CUSTOM_SERVER_URL_IS_VALID, true);
                prefsEditor.commit();
                if (selectedCustomServer) {
                    String baseURL = prefs.getString(PREFERENCE_KEY_CUSTOM_SERVER_URL, "");
                    selectedServer = new Server(baseURL, context);
                    callback.onServerSelectorComplete(selectedServer);
                }
            } else {
                prefsEditor.putBoolean(PREFERENCE_KEY_CUSTOM_SERVER_URL_IS_VALID, false);
                prefsEditor.putBoolean(PREFERENCE_KEY_SELECTED_CUSTOM_SERVER, false);
                prefsEditor.commit();
                Toast.makeText(context,
                        context.getResources().getString(R.string.toast_server_checker_error_bad_url),
                        Toast.LENGTH_SHORT).show();
            }
        }
        else{
            if (isWorking){
                if (isAutoDetected){
                    long serverId = prefs.getLong(OTPApp.PREFERENCE_KEY_SELECTED_SERVER, 0);
                    Server s = null;
                    boolean serverIsChanged = true;
                    if (serverId != 0) {
                        dataSource.open();
                        s = dataSource.getServer(prefs.getLong(OTPApp.PREFERENCE_KEY_SELECTED_SERVER, 0));
                        dataSource.close();
                    }
                    if (s != null) {
                        serverIsChanged = !(s.getRegion().equals(selectedServer.getRegion()));
                    }
                    if (showDialog || serverIsChanged) {
                        Toast.makeText(context,
                                context.getResources()
                                        .getString(R.string.toast_server_selector_detected) + " "
                                        + selectedServer.getRegion() + ". " + context.getResources()
                                        .getString(R.string.toast_server_selector_server_change_info),
                                Toast.LENGTH_SHORT).show();
                    }
                }
                prefsEditor.putLong(PREFERENCE_KEY_SELECTED_SERVER,
                        selectedServer.getId());
                prefsEditor.putBoolean(PREFERENCE_KEY_SELECTED_CUSTOM_SERVER, false);
                prefsEditor.commit();
                callback.onServerSelectorComplete(selectedServer);
            }
            else{
                Toast.makeText(context,
                        context.getResources()
                                .getString(R.string.
                                        toast_server_checker_error_unreachable_detected_server),
                        Toast.LENGTH_SHORT).show();
            }
        }
    }
}

public class TripRequest extends AsyncTask<Request, Integer, Long> {

    private Response response;

    private ProgressDialog progressDialog;

    private WeakReference<Activity> activity;

    private Context context;

    private Resources resources;

    private String currentRequestString = "";

    private Server selectedServer;

    private TripRequestCompleteListener callback;

    public TripRequest(WeakReference<Activity> activity, Context context, Resources resources,
                       Server selectedServer, TripRequestCompleteListener callback) {
        this.activity = activity;
        this.context = context;
        this.selectedServer = selectedServer;
        this.callback = callback;
        this.resources = resources;
        if (activity != null) {
            Activity activityRetrieved = activity.get();
            progressDialog = new ProgressDialog(activityRetrieved);
        }
    }

    protected void onPreExecute() {
        if (activity.get() != null) {
            progressDialog.setIndeterminate(true);
            progressDialog.setCancelable(true);
            Activity activityRetrieved = activity.get();
            if (activityRetrieved != null) {
                progressDialog = ProgressDialog.show(activityRetrieved, "",
                        resources.getText(R.string.task_progress_tripplanner_progress), true);
            }
        }
    }

    protected Long doInBackground(Request... reqs) {
        long totalSize = 0;
        if (selectedServer == null) {
            Toast.makeText(context,
                    resources.getString(R.string.toast_no_server_selected_error),
                    Toast.LENGTH_SHORT).show();
            return null;
        }
        else{
            String prefix = PreferenceManager.getDefaultSharedPreferences(context)
                    .getString(OTPApp.PREFERENCE_KEY_FOLDER_STRUCTURE_PREFIX
                            , OTPApp.FOLDER_STRUCTURE_PREFIX_NEW);
            String baseURL = selectedServer.getBaseURL();
            for (Request req : reqs) {
                response = requestPlan(req, prefix, baseURL);
            }
        }
        return totalSize;
    }

    protected void onCancelled(Long result) {

        try {
            if (progressDialog != null && progressDialog.isShowing()) {
                progressDialog.dismiss();
            }
        } catch (Exception e) {
            Log.e(OTPApp.TAG, "Error in TripRequest Cancelled dismissing dialog: " + e);
        }

        Activity activityRetrieved = activity.get();
        if (activityRetrieved != null) {
            AlertDialog.Builder geocoderAlert = new AlertDialog.Builder(activityRetrieved);
            geocoderAlert.setTitle(R.string.tripplanner_results_title)
                    .setMessage(R.string.tripplanner_error_request_timeout)
                    .setCancelable(false)
                    .setPositiveButton(android.R.string.ok, new DialogInterface.OnClickListener() {
                        public void onClick(DialogInterface dialog, int id) {
                        }
                    });

            AlertDialog alert = geocoderAlert.create();
            alert.show();
        }

        Log.e(OTPApp.TAG, "No route to display!");
    }

    protected void onPostExecute(Long result) {
        if (activity.get() != null) {
            try {
                if (progressDialog != null && progressDialog.isShowing()) {
                    progressDialog.dismiss();
                }
            } catch (Exception e) {
                Log.e(OTPApp.TAG, "Error in TripRequest PostExecute dismissing dialog: " + e);
            }
        }

        if (response != null && response.getPlan() != null
                && response.getPlan().getItinerary().get(0) != null) {

            List<Itinerary> itineraries = response.getPlan().getItinerary();

            callback.onTripRequestComplete(itineraries, currentRequestString);
        } else {
            Activity activityRetrieved = activity.get();
            if (activityRetrieved != null) {
                AlertDialog.Builder feedback = new AlertDialog.Builder(activityRetrieved);
                feedback.setTitle(resources
                        .getString(R.string.tripplanner_error_dialog_title));
                feedback.setNeutralButton(resources.getString(android.R.string.ok),
                        null);
                String msg = resources
                        .getString(R.string.tripplanner_error_not_defined);

                PlannerError error = response.getError();
                if (error != null) {
                    int errorCode = error.getId();

                    if (response != null && response.getError() != null
                            && errorCode != Message.PLAN_OK
                            .getId()) {

                        msg = getErrorMessage(response.getError().getId());
                        if (msg == null) {
                            msg = response.getError().getMsg();
                        }
                    }
                }
                feedback.setMessage(msg);
                feedback.create().show();
            }

            Log.e(OTPApp.TAG, "No route to display!");
        }
    }

    protected String getErrorMessage(int errorCode) {
        if (errorCode == Message.SYSTEM_ERROR.getId()) {
            return (resources.getString(R.string.tripplanner_error_system));
        } else if (errorCode == Message.OUTSIDE_BOUNDS.getId()) {
            return (resources.getString(R.string.tripplanner_error_outside_bounds));
        } else if (errorCode == Message.PATH_NOT_FOUND.getId()) {
            return (resources.getString(R.string.tripplanner_error_path_not_found));
        } else if (errorCode == Message.NO_TRANSIT_TIMES.getId()) {
            return (resources.getString(R.string.tripplanner_error_no_transit_times));
        } else if (errorCode == Message.REQUEST_TIMEOUT.getId()) {
            return (resources.getString(R.string.tripplanner_error_request_timeout));
        } else if (errorCode == Message.BOGUS_PARAMETER.getId()) {
            return (resources.getString(R.string.tripplanner_error_bogus_parameter));
        } else if (errorCode == Message.GEOCODE_FROM_NOT_FOUND.getId()) {
            return (resources
                    .getString(R.string.tripplanner_error_geocode_from_not_found));
        } else if (errorCode == Message.GEOCODE_TO_NOT_FOUND.getId()) {
            return (resources
                    .getString(R.string.tripplanner_error_geocode_to_not_found));
        } else if (errorCode == Message.GEOCODE_FROM_TO_NOT_FOUND.getId()) {
            return (resources
                    .getString(R.string.tripplanner_error_geocode_from_to_not_found));
        } else if (errorCode == Message.TOO_CLOSE.getId()) {
            return (resources.getString(R.string.tripplanner_error_too_close));
        } else if (errorCode == Message.LOCATION_NOT_ACCESSIBLE.getId()) {
            return (resources
                    .getString(R.string.tripplanner_error_location_not_accessible));
        } else if (errorCode == Message.GEOCODE_FROM_AMBIGUOUS.getId()) {
            return (resources
                    .getString(R.string.tripplanner_error_geocode_from_ambiguous));
        } else if (errorCode == Message.GEOCODE_TO_AMBIGUOUS.getId()) {
            return (resources
                    .getString(R.string.tripplanner_error_geocode_to_ambiguous));
        } else if (errorCode == Message.GEOCODE_FROM_TO_AMBIGUOUS.getId()) {
            return (resources
                    .getString(R.string.tripplanner_error_geocode_from_to_ambiguous));
        } else if (errorCode == Message.UNDERSPECIFIED_TRIANGLE.getId()
                || errorCode == Message.TRIANGLE_NOT_AFFINE.getId()
                || errorCode == Message.TRIANGLE_OPTIMIZE_TYPE_NOT_SET.getId()
                || errorCode == Message.TRIANGLE_VALUES_NOT_SET.getId()) {
            return (resources.getString(R.string.tripplanner_error_triangle));
        } else {
            return null;
        }
    }

    protected Response requestPlan(Request requestParams, String prefix, String baseURL) {
        HashMap<String, String> tmp = requestParams.getParameters();

        Collection c = tmp.entrySet();
        Iterator itr = c.iterator();

        String params = "";
        boolean first = true;
        while (itr.hasNext()) {
            if (first) {
                params += "?" + itr.next();
                first = false;
            } else {
                params += "&" + itr.next();
            }
        }

        if (requestParams.getBikeRental()) {
            String updatedString;
            if (prefix.equals(OTPApp.FOLDER_STRUCTURE_PREFIX_NEW)){
                updatedString = params.replace(TraverseMode.BICYCLE.toString(),
                        TraverseMode.BICYCLE.toString() + OTPApp.OTP_RENTAL_QUALIFIER);
            }
            else{
                updatedString = params.replace(TraverseMode.BICYCLE.toString(),
                        TraverseMode.BICYCLE.toString() + ", " + TraverseMode.WALK.toString());
            }

            params = updatedString;
        }


        if (requestParams.getModes().getTrainish()) {
            // TraverseModeSet.toString() enumerates activated modes, which might not be supported by server
            // so we filter them out. Should be solved differently, e.g. by  decoupling and introducing
            // an interface with version dependent implementations
            SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(context);

            if (prefs.getInt(OTPApp.PREFERENCE_KEY_API_VERSION, OTPApp.API_VERSION_V3)
                    >= OTPApp.API_VERSION_V3) {
                String updatedString;
                updatedString = params.replace(TraverseMode.TRAINISH.toString(), "");
                updatedString = updatedString.replace(TraverseMode.BUSISH.toString(), "");

                params = updatedString;
            }
        }

        String u = baseURL + prefix + OTPApp.PLAN_LOCATION + params;

        Log.d(OTPApp.TAG, "URL: " + u);

        currentRequestString = u;

        HttpURLConnection urlConnection = null;
        URL url;
        Response plan = null;

        try {
            url = new URL(u);

            disableConnectionReuseIfNecessary(); // For bugs in HttpURLConnection pre-Froyo

            urlConnection = (HttpURLConnection) url.openConnection();
            urlConnection.setRequestProperty("Accept", "application/json");
            urlConnection.setConnectTimeout(OTPApp.HTTP_CONNECTION_TIMEOUT);
            urlConnection.setReadTimeout(OTPApp.HTTP_SOCKET_TIMEOUT);
            plan = JacksonConfig.getObjectReaderInstance()
                    .readValue(urlConnection.getInputStream());
        } catch (java.net.SocketTimeoutException e) {
            Log.e(OTPApp.TAG, "Timeout fetching JSON or XML: " + e);
            e.printStackTrace();
            cancel(true);
        } catch (IOException e) {
            Log.e(OTPApp.TAG, "Error fetching JSON or XML: " + e);
            e.printStackTrace();
            cancel(true);
            // Reset timestamps to show there was an error
            // requestStartTime = 0;
            // requestEndTime = 0;
        } finally {
            if (urlConnection != null) {
                urlConnection.disconnect();
            }
        }
        return plan;
    }

    /**
     * Disable HTTP connection reuse which was buggy pre-froyo
     */
    private void disableConnectionReuseIfNecessary() {
        if (Build.VERSION.SDK_INT < Build.VERSION_CODES.FROYO) {
            System.setProperty("http.keepAlive", "false");
        }
    }
}

