public class CustomAddress extends Address {

    public CustomAddress(Locale locale) {
        super(locale);
    }

    public CustomAddress(Address address) {
        super(address.getLocale());
        for (int i = 0; i <= address.getMaxAddressLineIndex(); i++){
            super.setAddressLine(i, address.getAddressLine(i));
        }
		super.setFeatureName(address.getFeatureName());
		super.setAdminArea(address.getAdminArea());
		super.setSubAdminArea(address.getSubAdminArea());
		super.setLocality(address.getLocality());
		super.setSubLocality(address.getSubLocality());
		super.setThoroughfare(address.getThoroughfare());
		super.setSubThoroughfare(address.getSubThoroughfare());
		super.setPostalCode(address.getPostalCode());
		super.setCountryCode(address.getCountryCode());
		super.setCountryName(address.getCountryName());
		super.setLatitude(address.getLatitude());
		super.setLongitude(address.getLongitude());
		super.setPhone(address.getPhone());
		super.setUrl(address.getUrl());
		super.setExtras(address.getExtras());
    }

    @Override
    public String toString(){
        StringBuilder sb = new StringBuilder();
        if (getFeatureName() != null){
            sb.append(getFeatureName());
        }
        if (getThoroughfare() != null && !getThoroughfare().equals(getFeatureName())){
            sb.append(", " + getThoroughfare());
        }
        if (getSubThoroughfare() != null && !getSubThoroughfare().equals(getFeatureName())){
            sb.append(", " + getSubThoroughfare());
        }
        if (getAdminArea() != null && !getAdminArea().equals(getFeatureName())){
            sb.append(", " + getAdminArea());
        }
        if (getSubAdminArea() != null && !getSubAdminArea().equals(getFeatureName())){
            sb.append(", " + getSubAdminArea());
        }
        if (getLocality() != null && !getLocality().equals(getFeatureName())){
            sb.append(", " + getLocality());
        }
        if (TextUtils.isEmpty(sb.toString())){
            int maxLines = (OTPApp.ADDRESS_MAX_LINES_TO_SHOW > getMaxAddressLineIndex())
                    ? getMaxAddressLineIndex() : OTPApp.ADDRESS_MAX_LINES_TO_SHOW;
            sb.append(getAddressLine(0));
            for (int i = 1; i < maxLines; i++) {
                if (getAddressLine(i) != null) {
                    sb.append(", " + getAddressLine(i));
                }
            }
        }
        return sb.toString();
    }

    public String getStringAddress(boolean multiline) {
        int maxLines = (OTPApp.ADDRESS_MAX_LINES_TO_SHOW > getMaxAddressLineIndex())
                ? getMaxAddressLineIndex() : OTPApp.ADDRESS_MAX_LINES_TO_SHOW;

        if (maxLines >= 0) {

            String result = getAddressLine(0);
            this.getAdminArea();
            if (multiline) {
                for (int i = 1; i < maxLines; i++) {
                    if (i == 1) {
                        result += "\n";
                        if (getAddressLine(i) != null) {
                            result += getAddressLine(i);
                        }
                    } else if (i == 2) {
                        result += "\n";
                        if (getAddressLine(i) != null) {
                            result += getAddressLine(i);
                        }
                    } else {
                        if (getAddressLine(i) != null) {
                            result += ", " + getAddressLine(i);
                        }
                    }
                }
            } else {
                for (int i = 1; i < maxLines; i++) {
                    if (getAddressLine(i) != null) {
                        result += ", " + getAddressLine(i);
                    }
                }
            }

            return result;
        } else {
            return null;
        }
    }
}

public class CustomInfoWindowAdapter implements GoogleMap.InfoWindowAdapter{

    private LayoutInflater layoutInflater;

    private Map<Marker, TripInfo> markers;

    private Context context;

    public CustomInfoWindowAdapter(LayoutInflater layoutInflater, Context context){
        this.layoutInflater = layoutInflater;
        this.context = context;
    }

    @Override
    public View getInfoWindow(Marker marker) {
        return null;
    }

    @Override
    public View getInfoContents(Marker marker) {
        View view  = layoutInflater.inflate(R.layout.custom_info_window, null);
        TextView title = (TextView) view.findViewById(R.id.customInfoWindowTitle);
        TextView snippet = (TextView)view.findViewById(R.id.customInfoWindowSnippet);

        title.setText(marker.getTitle());

        TripInfo infoMarker = markers.get(marker);
        if ((infoMarker == null) || (infoMarker.getSnippet() == null)){
            snippet.setText(marker.getSnippet());
        }
        else {
            snippet.setText(infoMarker.getSnippet());
            if (Boolean.TRUE.equals(infoMarker.realtime)){
                TextView liveUpdatesText = (TextView) view
                        .findViewById(R.id.customInfoWindowLiveUpdatesText);
                liveUpdatesText.setVisibility(View.VISIBLE);
                int delayInSeconds = infoMarker.getDelayInSeconds();
                String delay = ConversionUtils
                        .getFormattedDurationTextNoSeconds(delayInSeconds,
                                true, context);
                int color = ConversionUtils.getDelayColor(delayInSeconds, context);
                String delayText = "\u25cf";
                if (delayInSeconds == 0) {
                    delayText += context.getString(R.string.map_markers_warning_live_upates_on_time);
                }
                else if (delayInSeconds > 0) {
                    delayText += " " + delay + " "
                            + context
                                .getResources()
                                .getString(R.string.map_markers_warning_live_upates_late_arrival);
                }
                else {
                    String positiveDelay = delay.replace("-","");
                    delayText += " " + positiveDelay + " "
                            + context
                                .getResources()
                                .getString(R.string.map_markers_warning_live_upates_early_arrival);
                }
                SpannableString delayFullText = new SpannableString(delayText);
                delayFullText.setSpan(new ForegroundColorSpan(color), 0, delayText.length(), 0);
                liveUpdatesText.setText(delayFullText);
            }
        }

        return view;
    }

    public void setMarkers(Map<Marker, TripInfo> markers) {
        this.markers = markers;
    }
}

public class DateTimeDialog extends
        DialogFragment /* implements DatePickerCompleteListener, TimePickerCompleteListener*/ {

    private Spinner spinScheduleType;

    private TimePicker pickerTime;

    private DatePicker pickerDate;

    private Button btnOk;

    private Button btnCancel;

    @TargetApi(Build.VERSION_CODES.HONEYCOMB)
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
            Bundle savedInstanceState) {

        View view = inflater.inflate(R.layout.date_time_dialog, container);

        spinScheduleType = (Spinner) view.findViewById(R.id.spinScheduleType);
        pickerTime = (TimePicker) view.findViewById(R.id.timePicker1);
        pickerDate = (DatePicker) view.findViewById(R.id.datePicker1);
        btnOk = (Button) view.findViewById(R.id.btnOk);
        btnCancel = (Button) view.findViewById(R.id.btnCancel);

        getDialog().setTitle(getResources().getString(R.string.date_time_selector_title));
        getDialog().setCanceledOnTouchOutside(true);

        pickerTime.setIs24HourView(DateFormat.is24HourFormat(getActivity()));

        //TimePicker state needs to be saved manually because of this bug in Android that affects at least ICS: http://code.google.com/p/android/issues/detail?id=22754
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.HONEYCOMB) {
            pickerTime.setSaveFromParentEnabled(false);
        }

        OnClickListener oclOk = new OnClickListener() {

            @Override
            public void onClick(View v) {
                Calendar cal = Calendar.getInstance();

                cal.set(pickerDate.getYear(), pickerDate.getMonth(), pickerDate.getDayOfMonth(),
                        pickerTime.getCurrentHour(), pickerTime.getCurrentMinute());
                ArriveBySpinnerItem selectedSscheduleType = (ArriveBySpinnerItem) spinScheduleType
                        .getSelectedItem();
                ((MyActivity) getActivity())
                        .onDateComplete(cal.getTime(), selectedSscheduleType.getValue());
                dismiss();
            }
        };
        btnOk.setOnClickListener(oclOk);

        OnClickListener oclCancel = new OnClickListener() {

            @Override
            public void onClick(View v) {
                dismiss();
            }
        };
        btnCancel.setOnClickListener(oclCancel);

        ArrayAdapter<ArriveBySpinnerItem> arriveByTypeAdapter
                = new ArrayAdapter<ArriveBySpinnerItem>(
                getActivity(),
                android.R.layout.simple_spinner_dropdown_item,
                new ArriveBySpinnerItem[]{new ArriveBySpinnerItem(
                        getResources().getString(R.string.date_time_selector_spinner_depart), false),
                        new ArriveBySpinnerItem(
                                getResources().getString(R.string.date_time_selector_spinner_arrive),
                                true)});
        spinScheduleType.setAdapter(arriveByTypeAdapter);

        return view;
    }


    @TargetApi(Build.VERSION_CODES.HONEYCOMB)
    @Override
    public void onActivityCreated(Bundle savedInstanceState) {
        super.onActivityCreated(savedInstanceState);

        Bundle bundle;

        if (savedInstanceState == null) {
            bundle = this.getArguments();
            Date tripDate = (Date) bundle.getSerializable(OTPApp.BUNDLE_KEY_TRIP_DATE);
            boolean arriveBy = bundle.getBoolean(OTPApp.BUNDLE_KEY_ARRIVE_BY);

            Calendar cal = Calendar.getInstance();
            if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.HONEYCOMB) {
                if (cal.getTimeInMillis() < tripDate.getTime()) {
                    //min time should preceede setted time and setted time will be set with 0 seconds, so we aloud one minute less
                    pickerDate.setMinDate(cal.getTimeInMillis() - 60000);
                } else {
                    //min time should preceede setted time and setted time will be set with 0 seconds, so we aloud one minute less
                    pickerDate.setMinDate(tripDate.getTime() - 60000);
                }
            }

            cal.setTime(tripDate);

            if (!arriveBy) {
                spinScheduleType.setSelection(0);
            } else {
                spinScheduleType.setSelection(1);
            }
            pickerTime.setCurrentHour(cal.get(Calendar.HOUR_OF_DAY));
            pickerTime.setCurrentMinute(cal.get(Calendar.MINUTE));
            pickerDate.updateDate(cal.get(Calendar.YEAR), cal.get(Calendar.MONTH),
                    cal.get(Calendar.DAY_OF_MONTH));
        } else {
            //TimePicker state needs to be saved manually because of this bug in Android that affects at least ICS: http://code.google.com/p/android/issues/detail?id=22754
            bundle = savedInstanceState;
            pickerTime.setCurrentHour(bundle.getInt(OTPApp.BUNDLE_KEY_TIMEPICKER_SAVED_HOUR));
            pickerTime.setCurrentMinute(bundle.getInt(OTPApp.BUNDLE_KEY_TIMEPICKER_SAVED_MINUTE));
        }


    }


    @Override
    public void onSaveInstanceState(Bundle savedInstanceState) {
        super.onSaveInstanceState(savedInstanceState);
        //TimePicker state needs to be saved manually because of this bug in Android that affects at least ICS: http://code.google.com/p/android/issues/detail?id=22754
        savedInstanceState
                .putInt(OTPApp.BUNDLE_KEY_TIMEPICKER_SAVED_HOUR, pickerTime.getCurrentHour());
        savedInstanceState
                .putInt(OTPApp.BUNDLE_KEY_TIMEPICKER_SAVED_MINUTE, pickerTime.getCurrentMinute());
    }


}

