public class DirectionExpandableListAdapter extends BaseExpandableListAdapter {

    Context context;

    int directionLayoutResourceId;

    int subDirectionLayoutResourceId;

    Direction data[] = null;

    public DirectionExpandableListAdapter(Context context, int directionLayoutResourceId,
            int subDirectionLayoutResourceId, Direction[] data) {
        this.directionLayoutResourceId = directionLayoutResourceId;
        this.subDirectionLayoutResourceId = subDirectionLayoutResourceId;
        this.context = context;
        this.data = data;
    }

    @Override
    public Object getChild(int groupPosition, int childPosition) {
        ArrayList<Direction> subDirections = data[groupPosition].getSubDirections();

        if (subDirections != null && !subDirections.isEmpty()) {
            return subDirections.get(childPosition);
        }

        return null;
    }

    @Override
    public long getChildId(int groupPosition, int childPosition) {
        return childPosition;
    }

    @Override
    public int getChildrenCount(int groupPosition) {
        ArrayList<Direction> subDirections = data[groupPosition].getSubDirections();

        if (subDirections != null) {
            return subDirections.size();
        }

        return 0;
    }

    @Override
    public View getChildView(int groupPosition, int childPosition, boolean isLastChild,
            View convertView, ViewGroup parent) {

        View row = convertView;
        DirectionHolder holder = null;

        if (row == null) {
            LayoutInflater inflater = ((Activity) context).getLayoutInflater();
            row = inflater.inflate(subDirectionLayoutResourceId, parent, false);

            holder = new DirectionHolder();
            holder.imgIcon = (ImageView) row.findViewById(R.id.imgIcon);
            holder.txtDirection = (TextView) row.findViewById(R.id.directionText);

            row.setTag(holder);
        } else {
            holder = (DirectionHolder) row.getTag();
        }

        Direction dir = data[groupPosition];
        Direction subDirection = (Direction) getChild(groupPosition, childPosition);
        CharSequence text = subDirection == null ? "null here" : subDirection.getDirectionText();
        holder.txtDirection.setText(text);
        holder.imgIcon.setImageResource(subDirection.getIcon());

        return row;

//        return textView;
    }

    @Override
    public Object getGroup(int groupPosition) {
        return data[groupPosition];
    }

    @Override
    public int getGroupCount() {
        return data.length;
    }

    @Override
    public long getGroupId(int groupPosition) {
        return groupPosition;
    }

    @Override
    public View getGroupView(int groupPosition, boolean isExpanded, View convertView,
            ViewGroup parent) {

        View row = convertView;
        DirectionHolder holder = null;

        if (row == null) {
            LayoutInflater inflater = ((Activity) context).getLayoutInflater();
            row = inflater.inflate(directionLayoutResourceId, parent, false);

            holder = new DirectionHolder();
            holder.imgIcon = (ImageView) row.findViewById(R.id.imgIcon);
            holder.noIconText = (TextView) row.findViewById(R.id.noIconText);
            holder.txtDirection = (TextView) row.findViewById(R.id.directionText);

            row.setTag(holder);
        } else {
            holder = (DirectionHolder) row.getTag();
        }

        Direction dir = data[groupPosition];

        if (!dir.isTransit()){
            holder.txtDirection.setText(dir.getDirectionIndex() + ". " + dir.getDirectionText());
            holder.imgIcon.setVisibility(View.VISIBLE);
            holder.imgIcon.setImageResource(dir.getIcon());
        }
        else {
            CharSequence textBeforeTime = dir.getDirectionIndex() + ". " +  dir.getService();
            CharSequence text;
            CharSequence time = dir.getOldTime();
            text = new SpannableString(textBeforeTime);
            if (dir.isRealTimeInfo()){
                if (dir.getNewTime() != null){
                    time = dir.getNewTime();
                }
            }
            text = TextUtils.concat(text, " ", time, "\n", dir.getPlaceAndHeadsign());

            if (!TextUtils.isEmpty(dir.getAgency())){
                text = TextUtils.concat(text, "\n", dir.getAgency());
            }
            if (!TextUtils.isEmpty(dir.getExtra())){
                SpannableString extraSpannableString = new SpannableString(dir.getExtra());
                extraSpannableString.setSpan(new StyleSpan(Typeface.ITALIC), 0,
                        extraSpannableString.length(), 0);
                text = TextUtils.concat(text, "\n", extraSpannableString);
            }

            holder.txtDirection.setText(text);
            if (dir.getIcon() == -1){
                holder.imgIcon.setVisibility(View.INVISIBLE);
                holder.noIconText.setVisibility(View.VISIBLE);
            }
            else{
                holder.imgIcon.setVisibility(View.VISIBLE);
                holder.imgIcon.setImageResource(dir.getIcon());
                holder.noIconText.setVisibility(View.INVISIBLE);
            }
        }

        return row;

//        return textView;
    }

    @Override
    public boolean isChildSelectable(int groupPosition, int childPosition) {
        return true;
    }

    @Override
    public boolean hasStableIds() {
        return true;
    }

    static class DirectionHolder {

        ImageView imgIcon;

        TextView noIconText;

        TextView txtDirection;
    }
}

public class DirectionsGenerator {

    private List<Leg> legs = new ArrayList<Leg>();

    private ArrayList<Direction> directions = new ArrayList<Direction>();

    private double totalDistance = 0;

    private double totalTimeTraveled = 0;

    private Context applicationContext;

    public DirectionsGenerator(List<Leg> legs, Context applicationContext) {
        this.legs.addAll(legs);
        this.applicationContext = applicationContext;

        convertToDirectionList();
    }

    /**
     * @return the directions
     */
    public ArrayList<Direction> getDirections() {
        return directions;
    }

    /**
     * @param directions the directions to set
     */
    public void setDirections(ArrayList<Direction> directions) {
        this.directions = directions;
    }

    public void addDirection(Direction dir) {
        if (directions == null) {
            directions = new ArrayList<Direction>();
        }

        directions.add(dir);
    }

    private void convertToDirectionList() {
        int index = 0;
        for (Leg leg : legs) {
            index++;
            setTotalDistance(getTotalDistance() + leg.distance);

            TraverseMode traverseMode = TraverseMode.valueOf((String) leg.mode);
            if (traverseMode.isOnStreetNonTransit()) {
                Direction dir = generateNonTransitDirections(leg);
                if (dir == null) {
                    continue;
                }
                dir.setDirectionIndex(index);
                addDirection(dir);
            } else {
                ArrayList<Direction> directions = generateTransitDirections(leg);
                if (directions == null) {
                    continue;
                }

                if (directions.get(0) != null) {
                    directions.get(0).setDirectionIndex(index);
                    addDirection(directions.get(0));
                }

                if (directions.get(1) != null) {
                    if (directions.get(0) != null) {
                        index++;
                    }
                    directions.get(1).setDirectionIndex(index);
                    addDirection(directions.get(1));
                }
            }

            //			directionText+=leg.mode+"\n";
        }
    }

    private Direction generateNonTransitDirections(Leg leg) {
        Direction direction = new Direction();

        //http://opentripplanner.usf.edu/opentripplanner-api-webapp/ws/plan?optimize=QUICK&time=09:24pm&arriveBy=false&wheelchair=false&maxWalkDistance=7600.0&fromPlace=28.033389%2C+-82.521034&toPlace=28.064709%2C+-82.471618&date=03/07/12&mode=WALK,TRAM,SUBWAY,RAIL,BUS,FERRY,CABLE_CAR,GONDOLA,FUNICULAR,TRANSIT,TRAINISH,BUSISH

        // Get appropriate action and icon
        String action = applicationContext.getResources().getString(R.string.step_by_step_non_transit_mode_walk_action);
        TraverseMode mode = TraverseMode.valueOf((String) leg.mode);
        int icon = getModeIcon(new TraverseModeSet(mode));
        if (mode.compareTo(TraverseMode.BICYCLE) == 0) {
            action = applicationContext.getResources().getString(R.string.step_by_step_non_transit_mode_bicycle_action);
        } else if (mode.compareTo(TraverseMode.CAR) == 0) {
            action = applicationContext.getResources().getString(R.string.step_by_step_non_transit_mode_car_action);
        }

        direction.setIcon(icon);

        //		Main direction
        Place fromPlace = leg.from;
        Place toPlace = leg.to;
        String mainDirectionText = action;
        mainDirectionText += fromPlace.name == null ? ""
                : " " + applicationContext.getResources().getString(R.string.step_by_step_non_transit_from)
                        + " " + getLocalizedStreetName(fromPlace.name,
                        applicationContext.getResources());
        mainDirectionText += toPlace.name == null ? ""
                : " " + applicationContext.getResources().getString(R.string.step_by_step_non_transit_to) + " "
                        + getLocalizedStreetName(toPlace.name, applicationContext.getResources());
        String extraStopInformation = toPlace.stopCode;
        long legDuration;
        legDuration = ConversionUtils.normalizeDuration(leg.duration, applicationContext);
        if (!TextUtils.isEmpty(extraStopInformation)) {
            mainDirectionText += " (" + extraStopInformation + ")";
        }
        mainDirectionText += "\n[" + ConversionUtils
                .getFormattedDistance(leg.distance, applicationContext) + " - " +
                ConversionUtils.getFormattedDurationTextNoSeconds(legDuration, false, applicationContext)
                + " ]";
        direction.setDirectionText(mainDirectionText);

        //Sub-direction
        List<WalkStep> walkSteps = leg.getSteps();

        if (walkSteps == null) {
            return direction;
        }

        ArrayList<Direction> subDirections = new ArrayList<Direction>(walkSteps.size());

        for (WalkStep step : walkSteps) {
            int subdirection_icon = R.drawable.clear;
            Direction dir = new Direction();
            String subDirectionText = "";

            double distance = step.distance;

            RelativeDirection relativeDir = step.relativeDirection;
            String relativeDirString = getLocalizedRelativeDir(relativeDir,
                    applicationContext.getResources());
            String streetName = step.streetName;
            AbsoluteDirection absoluteDir = step.absoluteDirection;
            String absoluteDirString = getLocalizedAbsoluteDir(absoluteDir,
                    applicationContext.getResources());
            String exit = step.exit;
            boolean isStayOn = (step.stayOn == null ? false : step.stayOn);
            boolean isBogusName = (step.bogusName == null ? false : step.bogusName);
            double lon = step.lon;
            double lat = step.lat;
            String streetConnector = applicationContext.getResources()
                    .getString(R.string.step_by_step_non_transit_connector_street_name);
            //Elevation[] elevation = step.getElevation();  //Removed elevation for now, since we're not doing anything with it and it causes version issues between OTP server APIs v0.9.1-SNAPSHOT and v0.9.2-SNAPSHOT
            List<Alerts> alert = step.alerts;

            // Walk East
            if (relativeDir == null) {
                subDirectionText += action + " " + applicationContext.getResources()
                        .getString(R.string.step_by_step_non_transit_heading) + " ";
                subDirectionText += absoluteDirString + " ";
            }
            // (Turn left)/(Continue)
            else {
                RelativeDirection rDir = RelativeDirection.valueOf(relativeDir.name());

                subdirection_icon = getRelativeDirectionIcon(rDir,
                        applicationContext.getResources());

                // Do not need TURN Continue
                if (rDir.compareTo(RelativeDirection.RIGHT) == 0 ||
                        rDir.compareTo(RelativeDirection.LEFT) == 0) {
                    subDirectionText += applicationContext.getResources()
                            .getString(R.string.step_by_step_non_transit_turn) + " ";
                }

                subDirectionText += relativeDirString + " ";

                if (rDir.compareTo(RelativeDirection.CIRCLE_CLOCKWISE) == 0
                        || rDir.compareTo(RelativeDirection.CIRCLE_COUNTERCLOCKWISE) == 0) {
                    if (step.exit != null) {
                        try {
                            String ordinal = getOrdinal(Integer.parseInt(step.exit),
                                    applicationContext.getResources());
                            if (ordinal != null) {
                                subDirectionText += ordinal + " ";
                            } else {
                                subDirectionText += applicationContext.getResources()
                                        .getString(R.string.step_by_step_non_transit_roundabout_number) + " " + ordinal
                                        + " ";
                            }
                        } catch (NumberFormatException e) {
                            //If is not a step_by_step_non_transit_roundabout_number and is not null is better to try to display it
                            subDirectionText += step.exit + " ";
                        }
                        subDirectionText += applicationContext.getResources()
                                .getString(R.string.step_by_step_non_transit_roundabout_exit) + " ";
                        streetConnector = applicationContext.getResources()
                                .getString(R.string.step_by_step_non_transit_connector_street_name_roundabout);
                    }
                }
            }

            subDirectionText += streetConnector + " "
                    + getLocalizedStreetName(streetName, applicationContext.getResources()) + " ";

            subDirectionText += "\n[" + ConversionUtils
                    .getFormattedDistance(distance, applicationContext) + " ]";

            dir.setDirectionText(subDirectionText);

            dir.setIcon(subdirection_icon);

            // Add new sub-direction
            subDirections.add(dir);
        }

        direction.setSubDirections(subDirections);

        return direction;
    }

    private static String getOrdinal(int number, Resources resources) {
        switch (number) {
            case 1:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_first);
            case 2:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_second);
            case 3:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_third);
            case 4:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_fourth);
            case 5:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_fifth);
            case 6:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_sixth);
            case 7:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_seventh);
            case 8:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_eighth);
            case 9:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_ninth);
            case 10:
                return resources.getString(R.string.step_by_step_non_transit_roundabout_ordinal_tenth);
            default:
                return null;
        }

    }

    // Dirty fix to avoid the presence of names for unnamed streets (as road, track, etc.) for other languages than English
    public static String getLocalizedStreetName(String streetName, Resources resources) {
        if (streetName != null) {
            if (streetName.equals("bike path")) {
                return resources.getString(R.string.street_type_bike_path);
            } else if (streetName.equals("open area")) {
                return resources.getString(R.string.street_type_open_area);
            } else if (streetName.equals("path")) {
                return resources.getString(R.string.street_type_path);
            } else if (streetName.equals("bridleway")) {
                return resources.getString(R.string.street_type_bridleway);
            } else if (streetName.equals("footpath")) {
                return resources.getString(R.string.street_type_footpath);
            } else if (streetName.equals("platform")) {
                return resources.getString(R.string.street_type_platform);
            } else if (streetName.equals("footbridge")) {
                return resources.getString(R.string.street_type_footbridge);
            } else if (streetName.equals("underpass")) {
                return resources.getString(R.string.street_type_underpass);
            } else if (streetName.equals("road")) {
                return resources.getString(R.string.street_type_road);
            } else if (streetName.equals("ramp")) {
                return resources.getString(R.string.street_type_ramp);
            } else if (streetName.equals("link")) {
                return resources.getString(R.string.street_type_link);
            } else if (streetName.equals("service road")) {
                return resources.getString(R.string.street_type_service_road);
            } else if (streetName.equals("alley")) {
                return resources.getString(R.string.street_type_alley);
            } else if (streetName.equals("parking aisle")) {
                return resources.getString(R.string.street_type_parking_aisle);
            } else if (streetName.equals("byway")) {
                return resources.getString(R.string.street_type_byway);
            } else if (streetName.equals("track")) {
                return resources.getString(R.string.street_type_track);
            } else if (streetName.equals("sidewalk")) {
                return resources.getString(R.string.street_type_sidewalk);
            } else if (streetName.startsWith("osm:node:")) {
                return resources.getString(R.string.street_type_sidewalk);
            } else if (streetName.equals("steps")) {
                return resources.getString(R.string.street_type_steps);
            } else {
                return streetName;
            }
        }
        else {
            return resources.getString(R.string.street_type_sidewalk);
        }
    }

    public static String getLocalizedRelativeDir(RelativeDirection relDir, Resources resources) {
        if (relDir != null) {
            if (relDir.equals(RelativeDirection.CIRCLE_CLOCKWISE)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_circle_clockwise);
            } else if (relDir.equals(RelativeDirection.CIRCLE_COUNTERCLOCKWISE)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_circle_counterclockwise);
            } else if (relDir.equals(RelativeDirection.CONTINUE)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_continue);
            } else if (relDir.equals(RelativeDirection.DEPART)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_depart);
            } else if (relDir.equals(RelativeDirection.ELEVATOR)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_elevator);
            } else if (relDir.equals(RelativeDirection.HARD_LEFT)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_hard_left);
            } else if (relDir.equals(RelativeDirection.HARD_RIGHT)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_hard_right);
            } else if (relDir.equals(RelativeDirection.LEFT)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_left);
            } else if (relDir.equals(RelativeDirection.RIGHT)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_right);
            } else if (relDir.equals(RelativeDirection.SLIGHTLY_LEFT)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_slightly_left);
            } else if (relDir.equals(RelativeDirection.SLIGHTLY_RIGHT)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_slightly_right);
            } else if (relDir.equals(RelativeDirection.UTURN_LEFT)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_uturn_left);
            } else if (relDir.equals(RelativeDirection.UTURN_RIGHT)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_relative_uturn_right);
            }
        }
        return null;
    }

    public static String getLocalizedAbsoluteDir(AbsoluteDirection absDir, Resources resources) {
        if (absDir != null) {
            if (absDir.equals(AbsoluteDirection.EAST)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_absolute_east);
            } else if (absDir.equals(AbsoluteDirection.NORTH)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_absolute_north);
            } else if (absDir.equals(AbsoluteDirection.NORTHEAST)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_absolute_northeast);
            } else if (absDir.equals(AbsoluteDirection.NORTHWEST)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_absolute_northwest);
            } else if (absDir.equals(AbsoluteDirection.SOUTH)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_absolute_south);
            } else if (absDir.equals(AbsoluteDirection.SOUTHEAST)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_absolute_southeast);
            } else if (absDir.equals(AbsoluteDirection.SOUTHWEST)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_absolute_southwest);
            } else if (absDir.equals(AbsoluteDirection.WEST)) {
                return resources.getString(R.string.step_by_step_non_transit_dir_absolute_west);
            }
        }
        return null;
    }

    public static String getLocalizedMode(TraverseMode mode, Resources resources) {
        if (mode != null) {
            if (mode.equals(TraverseMode.TRAM)) {
                return resources.getString(R.string.step_by_step_transit_mode_tram);
            } else if (mode.equals(TraverseMode.SUBWAY)) {
                return resources.getString(R.string.step_by_step_transit_mode_subway);
            } else if (mode.equals(TraverseMode.RAIL)) {
                return resources.getString(R.string.step_by_step_transit_mode_rail);
            } else if (mode.equals(TraverseMode.BUS)) {
                return resources.getString(R.string.step_by_step_transit_mode_bus);
            } else if (mode.equals(TraverseMode.FERRY)) {
                return resources.getString(R.string.step_by_step_transit_mode_ferry);
            } else if (mode.equals(TraverseMode.CABLE_CAR)) {
                return resources.getString(R.string.step_by_step_transit_mode_cable_car);
            } else if (mode.equals(TraverseMode.GONDOLA)) {
                return resources.getString(R.string.step_by_step_transit_mode_gondola);
            } else if (mode.equals(TraverseMode.FUNICULAR)) {
                return resources.getString(R.string.step_by_step_transit_mode_funicular);
            }
        }
        return null;
    }

    private ArrayList<Direction> generateTransitDirections(Leg leg) {
        ArrayList<Direction> directions = new ArrayList<Direction>(2);

        directions.add(generateTransitSubdirection(leg, true));
        directions.add(generateTransitSubdirection(leg, false));

        return directions;
    }

    public Direction generateTransitSubdirection(Leg leg, boolean isOnDirection){
        Direction direction = new Direction();
        direction.setRealTimeInfo(leg.realTime);

        //		set icon
        String mode = getLocalizedMode(TraverseMode.valueOf(leg.mode),
                applicationContext.getResources());
        int icon;
        String route;
        String agencyName = leg.agencyName;
        Place from = leg.from;
        Place to = leg.to;
        Calendar newTime = Calendar.getInstance();
        Calendar oldTime = Calendar.getInstance();

        String shortName;
        if (!TextUtils.isEmpty(leg.tripShortName)){
            shortName = leg.tripShortName ;
        }
        else{
            shortName = leg.routeShortName ;
        }

        route = ConversionUtils.getRouteLongNameSafe(leg.routeLongName, shortName, true);

        direction.setTransit(true);

        String action, placeAndHeadsign, extra = "";

        if (isOnDirection){
            action = applicationContext.getResources()
                    .getString(R.string.step_by_step_transit_get_on);
            placeAndHeadsign = from.name;
            icon = getModeIcon(new TraverseModeSet(leg.mode));
            newTime.setTime(new Date(Long.parseLong(leg.startTime)));
            oldTime.setTime(new Date(newTime.getTimeInMillis()));
            oldTime.add(Calendar.SECOND, - leg.departureDelay);

            // Only onDirection has subdirection (list of stops in between)
            ArrayList<Place> stopsInBetween = new ArrayList<Place>();
            if ((leg.getIntermediateStops() != null) && !leg.getIntermediateStops().isEmpty()) {
                stopsInBetween.addAll(leg.getIntermediateStops());
            }
            else if ((leg.stop != null) && !leg.stop.isEmpty()){
                stopsInBetween.addAll(leg.stop);
            }
            // sub-direction
            ArrayList<Direction> subDirections = new ArrayList<Direction>();
            for (int i = 0; i < stopsInBetween.size(); i++) {
                Direction subDirection = new Direction();

                Place stop = stopsInBetween.get(i);
                String extraStopInformation = stop.stopCode;
                String subDirectionText = Integer.toString(i + 1) + ". " + stop.name;
                if (!TextUtils.isEmpty(extraStopInformation)) {
                    subDirectionText += " (" + extraStopInformation + ")";
                }
                subDirection.setDirectionText(subDirectionText);
                subDirection.setIcon(icon);

                subDirections.add(subDirection);
            }
            direction.setSubDirections(subDirections);

            if (stopsInBetween.size() > 0){
                String connector = applicationContext.getResources()
                        .getString(R.string.step_by_step_transit_stops_in_between);
                if (stopsInBetween.size() == 1){
                    connector = applicationContext.getResources()
                            .getString(R.string.step_by_step_transit_stops_in_between_singular);
                }
                extra = stopsInBetween.size() + " " + connector;
            }

            if (!TextUtils.isEmpty(leg.headsign)){
                placeAndHeadsign += " "
                        + applicationContext
                        .getResources().getString(R.string.step_by_step_transit_connector_headsign)
                        + " " + leg.headsign;
            }
        }
        else{
            action = applicationContext.getResources()
                    .getString(R.string.step_by_step_transit_get_off);
            placeAndHeadsign = to.name;
            icon = -1;
            newTime.setTime(new Date(Long.parseLong(leg.endTime)));
            oldTime.setTime(new Date(newTime.getTimeInMillis()));
            oldTime.add(Calendar.SECOND, - leg.arrivalDelay);
        }

        direction.setIcon(icon);
        direction.setPlaceAndHeadsign(applicationContext.getResources()
                .getString(R.string.step_by_step_transit_connector_stop_name) + " "
                + placeAndHeadsign);
        direction.setService(action + " " + mode + " " + route);
        direction.setAgency(agencyName);
        direction.setExtra(extra);

        SpannableString oldTimeString;

        if (leg.realTime) {
            CharSequence newTimeString;
            newTimeString = ConversionUtils
                    .getTimeUpdated(applicationContext, leg.agencyTimeZoneOffset,
                            oldTime.getTimeInMillis(), newTime.getTimeInMillis());
            direction.setNewTime(newTimeString);
        }

        oldTimeString = new SpannableString(ConversionUtils
                    .getTimeWithContext(applicationContext, leg.agencyTimeZoneOffset,
                            oldTime.getTimeInMillis(), true));
        direction.setOldTime(oldTimeString);

        return direction;
    }

    public static int getModeIcon(TraverseModeSet mode) {
        if (/*mode.contains(TraverseMode.FERRY) && TODO */
                mode.contains(TraverseMode.BUSISH) &&
                mode.contains(TraverseMode.TRAINISH)) {
            return R.drawable.mode_transit;
        } else if (mode.contains(TraverseMode.BUSISH)) {
            return R.drawable.mode_bus;
        } else if (mode.contains(TraverseMode.TRAINISH)) {
            return R.drawable.mode_train;
        } else if (mode.contains(TraverseMode.FERRY)) {
            return R.drawable.mode_ferry;
        } else if (mode.contains(TraverseMode.GONDOLA)) {
            return R.drawable.mode_ferry;
        } else if (mode.contains(TraverseMode.SUBWAY)) {
            return R.drawable.mode_metro;
        } else if (mode.contains(TraverseMode.TRAM)) {
            return R.drawable.mode_train;
        } else if (mode.contains(TraverseMode.WALK)) {
            return R.drawable.mode_walk;
        } else if (mode.contains(TraverseMode.BICYCLE)) {
            return R.drawable.mode_bike;
        } else {
            return R.drawable.icon;
        }
    }

    public static int getNotificationIcon(TraverseModeSet mode) {
        if (/*mode.contains(TraverseMode.FERRY) && TODO */
                mode.contains(TraverseMode.BUSISH) &&
                        mode.contains(TraverseMode.TRAINISH)) {
            return R.drawable.transit_notification;
        } else if (mode.contains(TraverseMode.BUSISH)) {
            return R.drawable.bus_notification;
        } else if (mode.contains(TraverseMode.TRAINISH)) {
            return R.drawable.train_notification;
        } else if (mode.contains(TraverseMode.FERRY)) {
            return R.drawable.ferry_notification;
        } else if (mode.contains(TraverseMode.SUBWAY)) {
            return R.drawable.metro_notification;
        } else {
            return R.drawable.icon;
        }
    }

    public static int getRelativeDirectionIcon(RelativeDirection relDir, Resources resources) {
        if (relDir.equals(RelativeDirection.CIRCLE_CLOCKWISE)) {
            return R.drawable.circle_clockwise;
        } else if (relDir.equals(RelativeDirection.CIRCLE_COUNTERCLOCKWISE)) {
            return R.drawable.circle_counterclockwise;
        } else if (relDir.equals(RelativeDirection.CONTINUE)) {
            return R.drawable.reldir_continue;
        } else if (relDir.equals(RelativeDirection.DEPART)) {
            return R.drawable.clear;
        } else if (relDir.equals(RelativeDirection.ELEVATOR)) {
            return R.drawable.elevator;
        } else if (relDir.equals(RelativeDirection.HARD_LEFT)) {
            return R.drawable.hard_left;
        } else if (relDir.equals(RelativeDirection.HARD_RIGHT)) {
            return R.drawable.hard_right;
        } else if (relDir.equals(RelativeDirection.LEFT)) {
            return R.drawable.left;
        } else if (relDir.equals(RelativeDirection.RIGHT)) {
            return R.drawable.right;
        } else if (relDir.equals(RelativeDirection.SLIGHTLY_LEFT)) {
            return R.drawable.slightly_left;
        } else if (relDir.equals(RelativeDirection.SLIGHTLY_RIGHT)) {
            return R.drawable.slightly_right;
        } else if (relDir.equals(RelativeDirection.UTURN_LEFT)) {
            return R.drawable.uturn_left;
        } else if (relDir.equals(RelativeDirection.UTURN_RIGHT)) {
            return R.drawable.uturn_right;
        } else {
            return R.drawable.clear;
        }
    }

    /**
     * @return the totalDistance
     */
    public double getTotalDistance() {
        return totalDistance;
    }

    /**
     * @param totalDistance the totalDistance to set
     */
    public void setTotalDistance(double totalDistance) {
        this.totalDistance = totalDistance;
    }

    /**
     * @return the totalTimeTraveled
     */
    public double getTotalTimeTraveled(Context context) {
        if (legs.isEmpty()) {
            return 0;
        }

        Leg legStart = legs.get(0);
        String startTimeText = legStart.startTime;
        Leg legEnd = legs.get(legs.size() - 1);
        String endTimeText = legEnd.endTime;

        totalTimeTraveled = ConversionUtils.getDuration(startTimeText, endTimeText, context);

        return totalTimeTraveled;
    }

}

public class ExpandableListFragment extends Fragment
        implements OnCreateContextMenuListener,
        ExpandableListView.OnChildClickListener, ExpandableListView.OnGroupCollapseListener,
        ExpandableListView.OnGroupExpandListener {

    static final int INTERNAL_EMPTY_ID = 0x00ff0001;

    static final int INTERNAL_PROGRESS_CONTAINER_ID = 0x00ff0002;

    static final int INTERNAL_LIST_CONTAINER_ID = 0x00ff0003;

    final private Handler mHandler = new Handler();

    final private Runnable mRequestFocus = new Runnable() {
        public void run() {
            mExpandableList.focusableViewAvailable(mExpandableList);
        }
    };

    final private AdapterView.OnItemClickListener mOnClickListener
            = new AdapterView.OnItemClickListener() {
        public void onItemClick(AdapterView<?> parent, View v, int position, long id) {
            onListItemClick((ExpandableListView) parent, v, position, id);
        }
    };

    ExpandableListAdapter mAdapter;

    ExpandableListView mExpandableList;

    boolean mFinishedStart = false;

    View mEmptyView;

    TextView mStandardEmptyView;

    View mProgressContainer;

    View mExpandableListContainer;

    CharSequence mEmptyText;

    boolean mExpandableListShown;

    public ExpandableListFragment() {
    }

    /**
     * Provide default implementation to return a simple list view.  Subclasses
     * can override to replace with their own layout.  If doing so, the
     * returned view hierarchy <em>must</em> have a ListView whose id
     * is {@link android.R.id#list android.R.id.list} and can optionally
     * have a sibling view id {@link android.R.id#empty android.R.id.empty}
     * that is to be shown when the list is empty.
     *
     * <p>If you are overriding this method with your own custom content,
     * consider including the standard layout {@link android.R.layout#list_content}
     * in your layout file, so that you continue to retain all of the standard
     * behavior of ListFragment.  In particular, this is currently the only
     * way to have the built-in indeterminant progress state be shown.
     */
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
            Bundle savedInstanceState) {
        final Context context = getActivity();

        FrameLayout root = new FrameLayout(context);

        // ------------------------------------------------------------------

        LinearLayout pframe = new LinearLayout(context);
        pframe.setId(INTERNAL_PROGRESS_CONTAINER_ID);
        pframe.setOrientation(LinearLayout.VERTICAL);
        pframe.setVisibility(View.GONE);
        pframe.setGravity(Gravity.CENTER);

        ProgressBar progress = new ProgressBar(context, null,
                android.R.attr.progressBarStyleLarge);
        pframe.addView(progress, new FrameLayout.LayoutParams(
                ViewGroup.LayoutParams.WRAP_CONTENT, ViewGroup.LayoutParams.WRAP_CONTENT));

        root.addView(pframe, new FrameLayout.LayoutParams(
                ViewGroup.LayoutParams.FILL_PARENT, ViewGroup.LayoutParams.FILL_PARENT));

        // ------------------------------------------------------------------

        FrameLayout lframe = new FrameLayout(context);
        lframe.setId(INTERNAL_LIST_CONTAINER_ID);

        TextView tv = new TextView(getActivity());
        tv.setId(INTERNAL_EMPTY_ID);
        tv.setGravity(Gravity.CENTER);
        lframe.addView(tv, new FrameLayout.LayoutParams(
                ViewGroup.LayoutParams.FILL_PARENT, ViewGroup.LayoutParams.FILL_PARENT));

        ExpandableListView lv = new ExpandableListView(getActivity());
        lv.setId(android.R.id.list);
        lv.setDrawSelectorOnTop(false);
        lframe.addView(lv, new FrameLayout.LayoutParams(
                ViewGroup.LayoutParams.FILL_PARENT, ViewGroup.LayoutParams.FILL_PARENT));

        root.addView(lframe, new FrameLayout.LayoutParams(
                ViewGroup.LayoutParams.FILL_PARENT, ViewGroup.LayoutParams.FILL_PARENT));

        // ------------------------------------------------------------------

        root.setLayoutParams(new FrameLayout.LayoutParams(
                ViewGroup.LayoutParams.FILL_PARENT, ViewGroup.LayoutParams.FILL_PARENT));

        return root;
    }

    /**
     * Attach to list view once the view hierarchy has been created.
     */
    @Override
    public void onViewCreated(View view, Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        ensureList();
    }

    /**
     * Detach from list view.
     */
    @Override
    public void onDestroyView() {
        mHandler.removeCallbacks(mRequestFocus);
        mExpandableList = null;
        mExpandableListShown = false;
        mEmptyView = mProgressContainer = mExpandableListContainer = null;
        mStandardEmptyView = null;
        super.onDestroyView();
    }

    /**
     * This method will be called when an item in the list is selected.
     * Subclasses should override. Subclasses can call
     * getListView().getItemAtPosition(position) if they need to access the
     * data associated with the selected item.
     *
     * @param l        The ListView where the click happened
     * @param v        The view that was clicked within the ListView
     * @param position The position of the view in the list
     * @param id       The row id of the item that was clicked
     */
    public void onListItemClick(ExpandableListView l, View v, int position, long id) {
    }

    /**
     * Provide the cursor for the list view.
     */
    public void setListAdapter(ExpandableListAdapter adapter) {
        boolean hadAdapter = mAdapter != null;
        mAdapter = adapter;
        if (mExpandableList != null) {
            mExpandableList.setAdapter(adapter);
            if (!mExpandableListShown && !hadAdapter) {
                // The list was hidden, and previously didn't have an
                // adapter.  It is now time to show it.
                setListShown(true, getView().getWindowToken() != null);
            }
        }
    }

    /**
     * Set the currently selected list item to the specified
     * position with the adapter's data
     */
    public void setSelection(int position) {
        ensureList();
        mExpandableList.setSelection(position);
    }

    /**
     * Get the position of the currently selected list item.
     */
    public int getSelectedItemPosition() {
        ensureList();
        return mExpandableList.getSelectedItemPosition();
    }

    /**
     * Get the cursor row ID of the currently selected list item.
     */
    public long getSelectedItemId() {
        ensureList();
        return mExpandableList.getSelectedItemId();
    }

    /**
     * Get the activity's list view widget.
     */
    public ExpandableListView getListView() {
        ensureList();
        return mExpandableList;
    }

    /**
     * The default content for a ListFragment has a TextView that can
     * be shown when the list is empty.  If you would like to have it
     * shown, call this method to supply the text it should use.
     */
    public void setEmptyText(CharSequence text) {
        ensureList();
        if (mStandardEmptyView == null) {
            throw new IllegalStateException("Can't be used with a custom content view");
        }
        mStandardEmptyView.setText(text);
        if (mEmptyText == null) {
            mExpandableList.setEmptyView(mStandardEmptyView);
        }
        mEmptyText = text;
    }

    /**
     * Control whether the list is being displayed.  You can make it not
     * displayed if you are waiting for the initial data to show in it.  During
     * this time an indeterminant progress indicator will be shown instead.
     *
     * <p>Applications do not normally need to use this themselves.  The default
     * behavior of ListFragment is to start with the list not being shown, only
     * showing it once an adapter is given with setListAdapter(ListAdapter).
     * If the list at that point had not been shown, when it does get shown
     * it will be do without the user ever seeing the hidden state.
     *
     * @param shown If true, the list view is shown; if false, the progress
     *              indicator.  The initial value is true.
     */
    public void setListShown(boolean shown) {
        setListShown(shown, true);
    }

    /**
     * Like {@link #setListShown(boolean)}, but no animation is used when
     * transitioning from the previous state.
     */
    public void setListShownNoAnimation(boolean shown) {
        setListShown(shown, false);
    }

    /**
     * Control whether the list is being displayed.  You can make it not
     * displayed if you are waiting for the initial data to show in it.  During
     * this time an indeterminant progress indicator will be shown instead.
     *
     * @param shown   If true, the list view is shown; if false, the progress
     *                indicator.  The initial value is true.
     * @param animate If true, an animation will be used to transition to the
     *                new state.
     */
    private void setListShown(boolean shown, boolean animate) {
        ensureList();
        if (mProgressContainer == null) {
            throw new IllegalStateException("Can't be used with a custom content view");
        }
        if (mExpandableListShown == shown) {
            return;
        }
        mExpandableListShown = shown;
        if (shown) {
            if (animate) {
                mProgressContainer.startAnimation(AnimationUtils.loadAnimation(
                        getActivity(), android.R.anim.fade_out));
                mExpandableListContainer.startAnimation(AnimationUtils.loadAnimation(
                        getActivity(), android.R.anim.fade_in));
            } else {
                mProgressContainer.clearAnimation();
                mExpandableListContainer.clearAnimation();
            }
            mProgressContainer.setVisibility(View.GONE);
            mExpandableListContainer.setVisibility(View.VISIBLE);
        } else {
            if (animate) {
                mProgressContainer.startAnimation(AnimationUtils.loadAnimation(
                        getActivity(), android.R.anim.fade_in));
                mExpandableListContainer.startAnimation(AnimationUtils.loadAnimation(
                        getActivity(), android.R.anim.fade_out));
            } else {
                mProgressContainer.clearAnimation();
                mExpandableListContainer.clearAnimation();
            }
            mProgressContainer.setVisibility(View.VISIBLE);
            mExpandableListContainer.setVisibility(View.GONE);
        }
    }

    /**
     * Get the ListAdapter associated with this activity's ListView.
     */
    public ExpandableListAdapter getListAdapter() {
        return mAdapter;
    }

    private void ensureList() {
        if (mExpandableList != null) {
            return;
        }
        View root = getView();
        if (root == null) {
            throw new IllegalStateException("Content view not yet created");
        }
        if (root instanceof ExpandableListView) {
            mExpandableList = (ExpandableListView) root;
        } else {
            mStandardEmptyView = (TextView) root.findViewById(INTERNAL_EMPTY_ID);
            if (mStandardEmptyView == null) {
                mEmptyView = root.findViewById(android.R.id.empty);
            } else {
                mStandardEmptyView.setVisibility(View.GONE);
            }
            mProgressContainer = root.findViewById(INTERNAL_PROGRESS_CONTAINER_ID);
            mExpandableListContainer = root.findViewById(INTERNAL_LIST_CONTAINER_ID);
            View rawExpandableListView = root.findViewById(android.R.id.list);
            if (!(rawExpandableListView instanceof ExpandableListView)) {
                if (rawExpandableListView == null) {
                    throw new RuntimeException(
                            "Your content must have a ListView whose id attribute is " +
                                    "'android.R.id.list'");
                }
                throw new RuntimeException(
                        "Content has view with id attribute 'android.R.id.list' "
                                + "that is not a ListView class");
            }
            mExpandableList = (ExpandableListView) rawExpandableListView;
            if (mEmptyView != null) {
                mExpandableList.setEmptyView(mEmptyView);
            } else if (mEmptyText != null) {
                mStandardEmptyView.setText(mEmptyText);
                mExpandableList.setEmptyView(mStandardEmptyView);
            }
        }
        mExpandableListShown = true;
        mExpandableList.setOnItemClickListener(mOnClickListener);
        if (mAdapter != null) {
            ExpandableListAdapter adapter = mAdapter;
            mAdapter = null;
            setListAdapter(adapter);
        } else {
            // We are starting without an adapter, so assume we won't
            // have our data right away and start with the progress indicator.
            if (mProgressContainer != null) {
                setListShown(false, false);
            }
        }
        mHandler.post(mRequestFocus);
    }

    /**
     * Override this to populate the context menu when an item is long pressed. menuInfo
     * will contain an {@link android.widget.ExpandableListView.ExpandableListContextMenuInfo}
     * whose packedPosition is a packed position
     * that should be used with {@link ExpandableListView#getPackedPositionType(long)} and
     * the other similar methods.
     * <p>
     * {@inheritDoc}
     */
    @Override
    public void onCreateContextMenu(ContextMenu menu, View v, ContextMenuInfo menuInfo) {
    }

    /**
     * Override this for receiving callbacks when a child has been clicked.
     * <p>
     * {@inheritDoc}
     */
    public boolean onChildClick(ExpandableListView parent, View v, int groupPosition,
            int childPosition, long id) {
        return false;
    }

    /**
     * Override this for receiving callbacks when a group has been collapsed.
     */
    public void onGroupCollapse(int groupPosition) {
    }

    /**
     * Override this for receiving callbacks when a group has been expanded.
     */
    public void onGroupExpand(int groupPosition) {
    }

//    /**
//     * Ensures the expandable list view has been created before Activity restores all
//     * of the view states.
//     * 
//     *@see Activity#onRestoreInstanceState(Bundle)
//     */
//    @Override
//    protected void onRestoreInstanceState(Bundle state) {
//        ensureList();
//        super.onRestoreInstanceState(state);
//    }

    /**
     * Updates the screen state (current list and other views) when the
     * content changes.
     *
     * @see Activity.onContentChanged()
     */

    public void onContentChanged() {
//        super.onContentChanged();
        View emptyView = getView().findViewById(android.R.id.empty);
        mExpandableList = (ExpandableListView) getView().findViewById(android.R.id.list);
        if (mExpandableList == null) {
            throw new RuntimeException(
                    "Your content must have a ExpandableListView whose id attribute is " +
                            "'android.R.id.list'");
        }
        if (emptyView != null) {
            mExpandableList.setEmptyView(emptyView);
        }
        mExpandableList.setOnChildClickListener(this);
        mExpandableList.setOnGroupExpandListener(this);
        mExpandableList.setOnGroupCollapseListener(this);

        if (mFinishedStart) {
            setListAdapter(mAdapter);
        }
        mFinishedStart = true;
    }

    /**
     * Get the activity's expandable list view widget.  This can be used to get the selection,
     * set the selection, and many other useful functions.
     *
     * @see ExpandableListView
     */
    public ExpandableListView getExpandableListView() {
        ensureList();
        return mExpandableList;
    }

    /**
     * Get the ExpandableListAdapter associated with this activity's
     * ExpandableListView.
     */
    public ExpandableListAdapter getExpandableListAdapter() {
        return mAdapter;
    }

    /**
     * Gets the ID of the currently selected group or child.
     *
     * @return The ID of the currently selected group or child.
     */
    public long getSelectedId() {
        return mExpandableList.getSelectedId();
    }

    /**
     * Gets the position (in packed position representation) of the currently
     * selected group or child. Use
     * {@link ExpandableListView#getPackedPositionType},
     * {@link ExpandableListView#getPackedPositionGroup}, and
     * {@link ExpandableListView#getPackedPositionChild} to unpack the returned
     * packed position.
     *
     * @return A packed position representation containing the currently
     * selected group or child's position and type.
     */
    public long getSelectedPosition() {
        return mExpandableList.getSelectedPosition();
    }

    /**
     * Sets the selection to the specified child. If the child is in a collapsed
     * group, the group will only be expanded and child subsequently selected if
     * shouldExpandGroup is set to true, otherwise the method will return false.
     *
     * @param groupPosition     The position of the group that contains the child.
     * @param childPosition     The position of the child within the group.
     * @param shouldExpandGroup Whether the child's group should be expanded if
     *                          it is collapsed.
     * @return Whether the selection was successfully set on the child.
     */
    public boolean setSelectedChild(int groupPosition, int childPosition,
            boolean shouldExpandGroup) {
        return mExpandableList.setSelectedChild(groupPosition, childPosition, shouldExpandGroup);
    }

    /**
     * Sets the selection to the specified group.
     *
     * @param groupPosition The position of the group that should be selected.
     */
    public void setSelectedGroup(int groupPosition) {
        mExpandableList.setSelectedGroup(groupPosition);
    }
}

public class JacksonConfig {

    // For JSON
    private static ObjectMapper mapper = null;

    private static ObjectReader reader = null;

    // For caching objects (ObjectMapper, ObjectReader, and XmlMapper) if
    // desired
    private static Context context = null;

    // Used to time cache read and write
    private static long cacheReadStartTime = 0;

    private static long cacheReadEndTime = 0;

    private static long cacheWriteStartTime = 0;

    private static long cacheWriteEndTime = 0;

    private static boolean usingCache = false;

    // Constants for defining which object type to read/write from/to cache
    private static final String OBJECT_READER = "ObjectReader";

    private static final String OBJECT_MAPPER = "ObjectMapper";

    private static final String CACHE_FILE_EXTENSION = ".cache";

    // Used to format decimals to 3 places
    static DecimalFormat df = new DecimalFormat("#,###.###");

    // Private empty constructor since this object shouldn't be instantiated
    private JacksonConfig() {
    }

    /**
     * Returns true if the application is using a cache to read/write serialized
     * Jackson ObjectMapper/ObjectReader/XmlMapper to reduce cold-start latency,
     * false if it is not
     *
     * @return true if the application is using a cache to read/write serialized
     * Jackson ObjectMapper/ObjectReader/XmlMapper to reduce cold-start
     * latency, false if it is not
     */
    public static boolean isUsingCache() {
        // Check to see if the context is null. If it is, we can't cache data.
        if (usingCache && context != null) {
            return true;
        } else {
            if (!usingCache || context == null) {
                return false;
            }
        }

        // Should never reach here
        return usingCache;
    }

    /**
     * True if the application should use a cache to read/write serialized
     * Jackson ObjectMapper/ObjectReader/XmlMapper to reduce cold-start latency,
     * false if it should not
     *
     * @param usingCache True if the application should use a cache to read/write
     *                   serialized Jackson ObjectMapper/ObjectReader/XmlMapper to
     *                   reduce cold-start latency, false if it should not
     * @param context    Context that should be used to access the cache location.
     *                   getApplicationContext() is suggested, since the Jackson
     *                   Objects are thread-safe and static
     */
    public static void setUsingCache(boolean usingCache, Context context) {
        JacksonConfig.usingCache = usingCache;
        JacksonConfig.context = context;
    }

    /**
     * Returns a benchmark of the amount of time the last cache read took for
     * the ObjectMapper or ObjectReader or XmlReader (in nanoseconds)
     *
     * @return a benchmark of the amount of time the last cache read took for
     * the ObjectMapper or ObjectReader or XmlReader (in nanoseconds)
     */
    public static long getLastCacheReadTime() {
        return cacheReadEndTime - cacheReadStartTime;
    }

    /**
     * Returns a benchmark of the amount of time the last cache write took for
     * the ObjectMapper or ObjectReader or XmlReader (in nanoseconds)
     *
     * @return a benchmark of the amount of time the last cache write took for
     * the ObjectMapper or ObjectReader or XmlReader (in nanoseconds)
     */
    public static long getLastCacheWriteTime() {
        return cacheWriteEndTime - cacheWriteStartTime;
    }

    /**
     * Constructs a thread-safe instance of a Jackson ObjectMapper configured to
     * parse JSON responses from a OTP REST API.
     *
     * According to Jackson Best Practices
     * (http://wiki.fasterxml.com/JacksonBestPracticesPerformance), for
     * efficiency reasons you should use the ObjectReader (via
     * getObjectReaderInstance()) instead of the ObjectMapper.
     *
     * @return thread-safe ObjectMapper configured for OTP JSON responses
     * @deprecated
     */
    public synchronized static ObjectMapper getObjectMapperInstance() {
        return initObjectMapper();
    }

    /**
     * Constructs a thread-safe instance of a Jackson ObjectReader configured to
     * parse JSON responses from a Mobile OTP API
     *
     * According to Jackson Best Practices
     * (http://wiki.fasterxml.com/JacksonBestPracticesPerformance), this should
     * be more efficient than the ObjectMapper.
     *
     * @return thread-safe ObjectMapper configured for OTP JSON responses
     */
    public synchronized static ObjectReader getObjectReaderInstance() {
        if (reader == null) {
            /**
             * We don't have a reference to an ObjectReader, so we need to read
             * from cache or instantiate a new one
             */
            if (usingCache) {
                reader = (ObjectReader) readFromCache(OBJECT_READER);

                if (reader != null) {
                    // Successful read from the cache
                    return reader;
                }
            }

            /**
             * If we reach this point then we're either not reading from the
             * cache, there was nothing in the cache to retrieve, or there was
             * an error reading from the cache.
             *
             * Instantiate the object like normal.
             */
            reader = initObjectMapper().reader(Response.class);
        }
        return reader;
    }

    /**
     * Internal method used to init main ObjectMapper for JSON parsing
     *
     * @return initialized ObjectMapper ready for JSON parsing
     */
    private static ObjectMapper initObjectMapper() {
        if (mapper == null) {
            /**
             * We don't have a reference to an ObjectMapper, so we need to read
             * from cache or instantiate a new one
             */
            if (usingCache) {
                mapper = (ObjectMapper) readFromCache(OBJECT_MAPPER);

                if (mapper != null) {
                    // Successful read from the cache
                    return mapper;
                }
            }

            /**
             * If we reach this point then we're either not reading from the
             * cache, there was nothing in the cache to retrieve, or there was
             * an error reading from the cache.
             *
             * Instantiate the object like normal.
             */
            // Jackson configuration
            mapper = new ObjectMapper();

            mapper.configure(DeserializationFeature.ACCEPT_SINGLE_VALUE_AS_ARRAY, true);
            mapper.configure(DeserializationFeature.ACCEPT_EMPTY_STRING_AS_NULL_OBJECT, true);
            mapper.configure(DeserializationFeature.USE_JAVA_ARRAY_FOR_JSON_ARRAY, true);
            mapper.configure(DeserializationFeature.READ_ENUMS_USING_TO_STRING, true);
            mapper.configure(DeserializationFeature.FAIL_ON_UNKNOWN_PROPERTIES, false);
        }
        return mapper;
    }


    /**
     * Forces the write of a ObjectMapper or ObjectReader to the app
     * cache. The cache is used to reduce the cold-start delay for Jackson
     * parsing on future runs, after this VM instance is destroyed.
     *
     * Applications may call this after a JSON or XML call to the server to
     * attempt to hide the cache write latency from the user, instead of having
     * the cache write occur as part of the first request to use the
     * ObjectMapper or ObjectReader.
     *
     *
     * This method is non-blocking.
     *
     * @param object object to be written to the cache
     */
    public static void forceCacheWrite(final Serializable object) {
        if (isUsingCache()) {
            new Thread() {
                public void run() {
                    writeToCache(object);
                }

                ;
            }.start();
        } else {
            Log.w(TAG,
                    "App tried to force a cache write but caching is not activated.  If you want to use the cache, call JacksonConfig.setUsingCache(true, context) with a reference to your context.");
        }
    }

    /**
     * Forces the read of a ObjectMapper or ObjectReader from the
     * app cache to be stored as a static instance in this object. The cache is
     * used to reduce the cold-start delay for Jackson parsing on future runs,
     * after this VM instance is destroyed.
     *
     * Applications should call this on startup to attempt to hide the cache
     * read latency from the user, instead of having the cache read occur on the
     * first request to use the ObjectMapper or ObjectReader.
     *
     * This method is non-blocking.
     */
    public static void forceCacheRead() {
        if (isUsingCache()) {
            new Thread() {
                public void run() {
                    readFromCache(OBJECT_MAPPER);
                    readFromCache(OBJECT_READER);
                }

                ;
            }.start();
        } else {
            Log.w(TAG,
                    "App tried to force a cache write but caching is not activated.  If you want to use the cache, call JacksonConfig.setUsingCache(true, context) with a reference to your context.");
        }
    }

    /**
     * Write the given object to Android internal storage for this app
     *
     * @param object serializable object to be written to cache (ObjectReader,
     *               ObjectMapper, or XmlReader)
     * @return true if object was successfully written to cache, false if it was
     * not
     */
    private synchronized static boolean writeToCache(Serializable object) {

        FileOutputStream fileStream = null;
        ObjectOutputStream objectStream = null;
        String fileName = "";
        boolean success = false;

        if (context != null) {
            try {
                if (object instanceof ObjectMapper) {
                    fileName = OBJECT_MAPPER + CACHE_FILE_EXTENSION;
                }
                if (object instanceof ObjectReader) {
                    fileName = OBJECT_READER + CACHE_FILE_EXTENSION;
                }

                cacheWriteStartTime = System.nanoTime();
                fileStream = context.openFileOutput(fileName,
                        Context.MODE_PRIVATE);
                objectStream = new ObjectOutputStream(fileStream);
                objectStream.writeObject(object);
                objectStream.flush();
                fileStream.getFD().sync();
                cacheWriteEndTime = System.nanoTime();
                success = true;

                // Get size of serialized object
                long fileSize = context.getFileStreamPath(fileName).length();

                Log.d("TAG", "Wrote " + fileName + " to cache (" + fileSize
                        + " bytes) in " + df.format(getLastCacheWriteTime())
                        + " ms.");
            } catch (IOException e) {
                // Reset timestamps to show there was an error
                cacheWriteStartTime = 0;
                cacheWriteEndTime = 0;
                Log.e(TAG, "Couldn't write Jackson object '" + fileName + "' to cache: " + e);
            } finally {
                try {
                    if (objectStream != null) {
                        objectStream.close();
                    }
                    if (fileStream != null) {
                        fileStream.close();
                    }
                } catch (Exception e) {
                    Log.e(TAG, "Error closing file connections: " + e);
                }
            }
        } else {
            Log.w(TAG,
                    "Can't write to cache - no context provided.  If you want to use the cache, call JacksonConfig.setUsingCache(true, context) with a reference to your context.");
        }

        return success;
    }

    /**
     * Read the given object from Android internal storage for this app
     *
     * @param objectType object type, defined by class constant Strings, to retrieve
     *                   from cache (ObjectReader, ObjectMapper, or XmlReader)
     * @return deserialized Object, or null if object couldn't be deserialized
     */
    private static synchronized Serializable readFromCache(String objectType) {

        FileInputStream fileStream = null;
        ObjectInputStream objectStream = null;

        // Holds object to be read from cache
        Serializable object = null;

        // Before reading from cache, check to make sure that we don't already
        // have the requested object in memory
        if (objectType.equalsIgnoreCase(OBJECT_MAPPER) && mapper != null) {
            return mapper;
        }
        if (objectType.equalsIgnoreCase(OBJECT_READER) && reader != null) {
            return reader;
        }

        if (context != null) {
            try {
                String fileName = objectType + CACHE_FILE_EXTENSION;

                cacheReadStartTime = System.nanoTime();
                fileStream = context.openFileInput(fileName);
                objectStream = new ObjectInputStream(fileStream);
                object = (Serializable) objectStream.readObject();
                cacheReadEndTime = System.nanoTime();

                // Get size of serialized object
                long fileSize = context.getFileStreamPath(fileName).length();

                Log.d("TAG", "Read " + fileName + " from cache (" + fileSize
                        + " bytes) in " + df.format(getLastCacheReadTime())
                        + " ms.");
            } catch (FileNotFoundException e) {
                Log.w(TAG, "Cache miss - Jackson object '" + objectType
                        + "' does not exist in app cache: " + e);
                return null;
            } catch (Exception e) {
                // Reset timestamps to show there was an error
                cacheReadStartTime = 0;
                cacheReadEndTime = 0;
                Log.e(TAG, "Couldn't read Jackson object '" + objectType + "' from cache: " + e);
            } finally {
                try {
                    if (objectStream != null) {
                        objectStream.close();
                    }
                    if (fileStream != null) {
                        fileStream.close();
                    }
                } catch (Exception e) {
                    Log.e(TAG, "Error closing cache file connections: " + e);
                }
            }

            if (object instanceof ObjectMapper) {
                mapper = (ObjectMapper) object;
            }
            if (object instanceof ObjectReader) {
                reader = (ObjectReader) object;
            }

            return object;
        } else {
            Log.w(TAG,
                    "Couldn't read from cache - no context provided.  If you want to use the cache, call JacksonConfig.setUsingCache(true, context) with a reference to your context.");
            return null;
        }
    }
}

public class LocationUtil {

    // Borrowed from
    // http://jeffreysambells.com/posts/2010/05/27/decoding-polylines-from-google-maps-direction-api-with-java/

    /**
     * Decode a set of GeoPoints from an EncodedPolylineBean object from the OTP
     * server project
     *
     * @param encoded string from EncodedPolylineBean
     * @return set of GeoPoints represented by the EncodedPolylineBean string
     */
    public static List<LatLng> decodePoly(String encoded) {

        List<LatLng> poly = new ArrayList<LatLng>();
        int index = 0, len = encoded.length();
        int lat = 0, lng = 0;

        while (index < len) {
            int b, shift = 0, result = 0;
            do {
                b = encoded.charAt(index++) - 63;
                result |= (b & 0x1f) << shift;
                shift += 5;
            } while (b >= 0x20);
            int dlat = ((result & 1) != 0 ? ~(result >> 1) : (result >> 1));
            lat += dlat;

            shift = 0;
            result = 0;
            do {
                b = encoded.charAt(index++) - 63;
                result |= (b & 0x1f) << shift;
                shift += 5;
            } while (b >= 0x20);
            int dlng = ((result & 1) != 0 ? ~(result >> 1) : (result >> 1));
            lng += dlng;

            LatLng ll = new LatLng(((double) lat / 1E5),
                    (((double) lng / 1E5)));
            poly.add(ll);
        }

        return poly;
    }

    /**
     * Compares the current location of the user against a bounding box for a OTP server
     *
     * @param location        current location of the user
     * @param selectedServer  OTP server being compared to the current location
     * @return true if the location of the user is within the bounding box of the selectedServer,
     * false if it is not
     */
    public static boolean checkPointInBoundingBox(LatLng location, Server selectedServer) {
        LatLng lowerLeft = new LatLng(selectedServer.getLowerLeftLatitude(),
                selectedServer.getLowerLeftLongitude());
        LatLng upperLeft = new LatLng(selectedServer.getUpperRightLatitude(),
                selectedServer.getLowerLeftLongitude());
        LatLng upperRight = new LatLng(selectedServer.getUpperRightLatitude(),
                selectedServer.getUpperRightLongitude());
        LatLng lowerRight = new LatLng(selectedServer.getLowerLeftLatitude(),
                selectedServer.getUpperRightLongitude());

        List<LatLng> rectangle = new ArrayList<LatLng>(2);
        rectangle.add(lowerLeft);
        rectangle.add(upperLeft);
        rectangle.add(upperRight);
        rectangle.add(lowerRight);

        return PolyUtil.containsLocation(location, rectangle, true);
    }

    public static ArrayList<CustomAddress> processGeocoding(Context context, Server selectedServer,
                                                            String... reqs) {
        return processGeocoding(context, selectedServer, false, reqs);
    }

    public static ArrayList<CustomAddress> processGeocoding(Context context, Server selectedServer, boolean geocodingForMarker, String... reqs) {
        ArrayList<CustomAddress> addressesReturn = new ArrayList<CustomAddress>();

        String address = reqs[0];
        SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(context);

        if (address == null || address.equalsIgnoreCase("")) {
            return null;
        }

        double latitude = 0, longitude = 0;
        boolean latLngSet = false;

        try{
            if (reqs.length >= 3) {
                latitude = Double.parseDouble(reqs[1]);
                longitude = Double.parseDouble(reqs[2]);
                latLngSet = true;
            }
        }
        catch(Exception e){
            Log.d(OTPApp.TAG, "Geocoding without reference latitude/longitude");
        }

        if (address.equalsIgnoreCase(context.getString(R.string.text_box_my_location))) {
            if (latLngSet){
                CustomAddress addressReturn = new CustomAddress(context.getResources().getConfiguration().locale);
                addressReturn.setLatitude(latitude);
                addressReturn.setLongitude(longitude);
                addressReturn.setAddressLine(addressReturn.getMaxAddressLineIndex() + 1,
                        context.getString(R.string.text_box_my_location));

                addressesReturn.add(addressReturn);

                return addressesReturn;
            }
            return null;
        }

        List<CustomAddress> addresses = new ArrayList<CustomAddress>();

        if (prefs.getBoolean(OTPApp.PREFERENCE_KEY_USE_ANDROID_GEOCODER, true)) {
            Geocoder gc = new Geocoder(context);
            try {
                List<Address> androidTypeAddresses;
                if (selectedServer != null) {
                    androidTypeAddresses = gc.getFromLocationName(address,
                            context.getResources().getInteger(R.integer.geocoder_max_results),
                            selectedServer.getLowerLeftLatitude(),
                            selectedServer.getLowerLeftLongitude(),
                            selectedServer.getUpperRightLatitude(),
                            selectedServer.getUpperRightLongitude());
                } else {
                    androidTypeAddresses = gc.getFromLocationName(address,
                            context.getResources().getInteger(R.integer.geocoder_max_results));
                }
                for (Address androidTypeAddress : androidTypeAddresses){
                    addresses.add(new CustomAddress(androidTypeAddress));
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        addresses = filterAddressesBBox(selectedServer, addresses);

        boolean resultsCloseEnough = true;

        if (geocodingForMarker && latLngSet){
            float results[] = new float[1];
            resultsCloseEnough = false;

            for (CustomAddress addressToCheck : addresses){
                Location.distanceBetween(latitude, longitude,
                        addressToCheck.getLatitude(), addressToCheck.getLongitude(), results);
                if (results[0] < OTPApp.GEOCODING_MAX_ERROR) {
                    resultsCloseEnough = true;
                    break;
                }
            }
        }

        if ((addresses == null) || addresses.isEmpty() || !resultsCloseEnough) {
            if (addresses == null){
                addresses = new ArrayList<CustomAddress>();
            }
            addresses.addAll(searchPlaces(context, selectedServer, address));

            for (CustomAddress addressRetrieved : addresses) {
                String str = addressRetrieved.getAddressLine(0);
                List<String> addressLines = Arrays.asList(str.split(", "));
                for (int j = 0; j < addressLines.size(); j++) {
                    addressRetrieved.setAddressLine(j, addressLines.get(j));
                }
            }
        }

        addresses = filterAddressesBBox(selectedServer, addresses);

        if (geocodingForMarker && latLngSet && addresses != null && !addresses.isEmpty()){
            float results[] = new float[1];
            float minDistanceToOriginalLatLon = Float.MAX_VALUE;
            CustomAddress closestAddress = addresses.get(0);

            for (CustomAddress addressToCheck : addresses){
                Location.distanceBetween(latitude, longitude,
                        addressToCheck.getLatitude(), addressToCheck.getLongitude(), results);
                if (results[0] < minDistanceToOriginalLatLon){
                    closestAddress = addressToCheck;
                    minDistanceToOriginalLatLon = results[0];
                }
            }
            addressesReturn.add(closestAddress);
        }
        else{
            addressesReturn.addAll(addresses);
        }

        return addressesReturn;
    }

    /**
     * Filters the addresses obtained in geocoding process, removing the
     * results outside server limits.
     *
     * @param addresses list of addresses to filter
     * @return a new list filtered
     */
    private static List<CustomAddress> filterAddressesBBox(Server selectedServer, List<CustomAddress> addresses) {
        if ((!(addresses == null || addresses.isEmpty())) && selectedServer != null) {
            for (Iterator<CustomAddress> it=addresses.iterator(); it.hasNext();) {
                CustomAddress address = it.next();
                if (!LocationUtil.checkPointInBoundingBox(
                        new LatLng(address.getLatitude(), address.getLongitude()),
                        selectedServer)) {
                    it.remove();
                }
            }
        }
        return addresses;
    }

    /**
     * Try to grab the developer key from an unversioned resource file, if it exists
     *
     * @param context
     * @param apiKeyResourceId Resource ID of the raw file containing the API key
     * @return the developer key from a resource file, or empty string if it doesn't
     * exist
     */
    private static String getKeyFromResource(Context context, int apiKeyResourceId) {
        String strKey = "";

        try {
            InputStream in = context.getResources().openRawResource(apiKeyResourceId);
            BufferedReader r = new BufferedReader(new InputStreamReader(in));
            StringBuilder total = new StringBuilder();

            while ((strKey = r.readLine()) != null) {
                total.append(strKey);
            }

            strKey = total.toString();
            strKey = strKey.trim(); //Remove any whitespace
        } catch (Resources.NotFoundException e) {
            Log.w(OTPApp.TAG, "Warning - didn't find the google places key file:" + e);
        } catch (IOException e) {
            Log.w(OTPApp.TAG, "Error reading the developer key file:" + e);
        }

        return strKey;
    }

    private static List<CustomAddress> searchPlaces(Context context, Server selectedServer, String name) {
        HashMap<String, String> params = new HashMap<String, String>();
        Places p;
        SharedPreferences mPrefs = PreferenceManager.getDefaultSharedPreferences(
                context);
        String placesService = mPrefs.getString(
                OTPApp.PREFERENCE_KEY_GEOCODER_PROVIDER,
                context.getResources().getString(R.string.geocoder_nominatim));

        if (placesService
                .equals(context.getResources().getString(R.string.geocoder_google_places))) {
            params.put(GooglePlaces.PARAM_NAME, name);
            if (selectedServer != null) {
                params.put(GooglePlaces.PARAM_LOCATION,
                        Double.toString(selectedServer.getGeometricalCenterLatitude()) + ","
                                + Double.toString(selectedServer.getGeometricalCenterLongitude())
                );
                params.put(GooglePlaces.PARAM_RADIUS, Double.toString(selectedServer.getRadius()));
            }
            p = new GooglePlaces(getKeyFromResource(context, R.raw.googleplaceskey));

            Log.d(OTPApp.TAG, "Using Google Places!");
        } else {
            params.put(Nominatim.PARAM_NAME, name);
            if (selectedServer != null) {
                params.put(Nominatim.PARAM_LEFT,
                        Double.toString(selectedServer.getLowerLeftLongitude()));
                params.put(Nominatim.PARAM_TOP,
                        Double.toString(selectedServer.getLowerLeftLatitude()));
                params.put(Nominatim.PARAM_RIGHT,
                        Double.toString(selectedServer.getUpperRightLongitude()));
                params.put(Nominatim.PARAM_BOTTOM,
                        Double.toString(selectedServer.getUpperRightLatitude()));
            }

            p = new Nominatim(getKeyFromResource(context, R.raw.mapquestgeocoderkey));

            Log.d(OTPApp.TAG, "Using Nominatim!");
        }

        ArrayList<POI> pois = new ArrayList<POI>();
        pois.addAll(p.getPlaces(params));

        List<CustomAddress> addresses = new ArrayList<CustomAddress>();

        for (POI poi : pois) {
            Log.d(OTPApp.TAG, poi.getName() + " " + poi.getLatitude() + "," + poi.getLongitude());
            CustomAddress address = new CustomAddress(context.getResources().getConfiguration().locale);
            address.setLatitude(poi.getLatitude());
            address.setLongitude(poi.getLongitude());
            String addressLine;

            if (poi.getAddress() != null) {
                if (!poi.getAddress().contains(poi.getName())) {
                    addressLine = (poi.getName() + ", " + poi.getAddress());
                } else {
                    addressLine = poi.getAddress();
                }
            } else {
                addressLine = poi.getName();
            }
            address.setAddressLine(address.getMaxAddressLineIndex() + 1, addressLine);
            addresses.add(address);
        }

        return addresses;
    }


}

public class PlacesAutoCompleteAdapter extends ArrayAdapter<CustomAddress> implements Filterable {

    private Context context;
    private Server selectedServer;

    private ArrayList<CustomAddress> resultList = new ArrayList<CustomAddress>();
    public PlacesAutoCompleteAdapter(Context context, int textViewResourceId,
                                     Server selectedServer) {
        super(context, textViewResourceId);
        this.context = context;
        this.selectedServer = selectedServer;
    }

    @Override
    public int getCount() {
        if (resultList != null){
            return resultList.size();
        }
        else{
            return 0;
        }
    }

    @Override
    public CustomAddress getItem(int index) {
        return resultList.get(index);
    }

    @Override
    public Filter getFilter() {
        Filter filter = new Filter() {
            @Override
            protected Filter.FilterResults performFiltering(CharSequence constraint) {
                FilterResults filterResults = new FilterResults();
                if (constraint != null) {
                    // Retrieve the autocomplete results.
                        resultList = LocationUtil.processGeocoding(context, selectedServer,
                                constraint.toString());
                    if (resultList != null){
                        // Assign the data to the FilterResults
                        filterResults.values = resultList;
                        filterResults.count = resultList.size();
                    }
                }
                return filterResults;
            }

            @Override
            protected void publishResults(CharSequence constraint, FilterResults results) {
                if (results != null && results.count > 0) {
                    notifyDataSetChanged();
                }
                else {
                    notifyDataSetInvalidated();
                }
            }};
        return filter;
    }

    public Server getSelectedServer() {
        return selectedServer;
    }

    public void setSelectedServer(Server selectedServer) {
        this.selectedServer = selectedServer;
    }

}

public class RangeSeekBar<T extends Number> extends ImageView {

    private final Paint paint = new Paint(Paint.ANTI_ALIAS_FLAG);

    // private final Bitmap thumbImage = BitmapFactory.decodeResource(
    // getResources(), R.drawable.seek_thumb_normal);
    private final Bitmap thumbImage;

    // private final Bitmap thumbPressedImage = BitmapFactory.decodeResource(
    // getResources(), R.drawable.seek_thumb_pressed);
    private final Bitmap thumbPressedImage;

    private final float thumbWidth;

    private final float thumbHalfWidth;

    private final float thumbHalfHeight;

    private final float lineHeight;

    private final float padding;

    private final T absoluteMinValue, absoluteMaxValue;

    private final NumberType numberType;

    private final double absoluteMinValuePrim, absoluteMaxValuePrim;

    private double normalizedMinValue = 0d;

    private double normalizedMaxValue = 1d;

    private Thumb pressedThumb = null;

    private boolean notifyWhileDragging = true;

    public final boolean IS_MULTI_COLORED;

    public final int SINGLE_COLOR;

    public int LEFT_COLOR;

    public int MIDDLE_COLOR;

    public int RIGHT_COLOR;

    public static final int BACKGROUND_COLOR = Color.GRAY;

    private OnRangeSeekBarChangeListener<T> listener;

    /**
     * Default color of a {@link RangeSeekBar}, #FF33B5E5. This is also known as
     * "Ice Cream Sandwich" blue.
     */
    public static final int DEFAULT_COLOR = Color.argb(0xFF, 0x33, 0xB5, 0xE5);

    /**
     * Callback listener interface to notify about changed range values.
     *
     * @param <T> The Number type the RangeSeekBar has been declared with.
     * @author Stephan Tittel (stephan.tittel@kom.tu-darmstadt.de)
     */
    public interface OnRangeSeekBarChangeListener<T> {

        public void onRangeSeekBarValuesChanged(RangeSeekBar<?> bar,
                T minValue, T maxValue);
    }

    /**
     * Registers given listener callback to notify about changed selected
     * values.
     *
     * @param listener The listener to notify about changed selected values.
     */
    public void setOnRangeSeekBarChangeListener(
            OnRangeSeekBarChangeListener<T> listener) {
        this.listener = listener;
    }

    /**
     * An invalid pointer id.
     */
    public static final int INVALID_POINTER_ID = 255;

    // Localized constants from MotionEvent for compatibility
    // with API < 8 "Froyo".
    public static final int ACTION_POINTER_UP = 0x6,
            ACTION_POINTER_INDEX_MASK = 0x0000ff00,
            ACTION_POINTER_INDEX_SHIFT = 8;

    private float mDownMotionX;

    private int mActivePointerId = INVALID_POINTER_ID;

    /**
     * On touch, this offset plus the scaled value from the position of the
     * touch will form the progress value. Usually 0.
     */
    float mTouchProgressOffset;

    private int mScaledTouchSlop;

    private boolean mIsDragging;

    /**
     * Creates a new RangeSeekBar.
     *
     * @param absoluteMinValue The minimum value of the selectable range.
     * @param absoluteMaxValue The maximum value of the selectable range.
     * @throws IllegalArgumentException Will be thrown if min/max value type is not one of Long,
     *                                  Double, Integer, Float, Short, Byte or BigDecimal.
     */
    public RangeSeekBar(T absoluteMinValue, T absoluteMaxValue, Context context)
            throws IllegalArgumentException {
        super(context);
        this.absoluteMinValue = absoluteMinValue;
        this.absoluteMaxValue = absoluteMaxValue;
        absoluteMinValuePrim = absoluteMinValue.doubleValue();
        absoluteMaxValuePrim = absoluteMaxValue.doubleValue();
        numberType = NumberType.fromNumber(absoluteMinValue);
        IS_MULTI_COLORED = false;
        SINGLE_COLOR = Color.argb(0xFF, 0x33, 0xB5, 0xE5);
        LEFT_COLOR = 0;
        MIDDLE_COLOR = 0;
        RIGHT_COLOR = 0;
        thumbImage = BitmapFactory.decodeResource(getResources(),
                R.drawable.seek_thumb_normal);
        thumbPressedImage = BitmapFactory.decodeResource(getResources(),
                R.drawable.seek_thumb_pressed);
        thumbWidth = thumbImage.getWidth();
        thumbHalfWidth = 0.5f * thumbWidth;
        thumbHalfHeight = 0.5f * thumbImage.getHeight();
        lineHeight = 0.3f * thumbHalfHeight;
        padding = thumbHalfWidth;

        // make RangeSeekBar focusable. This solves focus handling issues in
        // case EditText widgets are being used along with the RangeSeekBar
        // within ScollViews.
        setFocusable(true);
        setFocusableInTouchMode(true);
        init();
    }

    /**
     * Creates a new RangeSeekBar.
     *
     * @param absoluteMinValue   The minimum value of the selectable range.
     * @param absoluteMaxValue   The maximum value of the selectable range.
     * @param singleColor        The <tt>int</tt> of the color to go between the two thumb
     *                           points will Default to #FF33B5E5 (ICS) if 0 is sent.
     * @param thumbNormal_ResID  The ResourceID of the image for the normal thumb icon.
     * @param thumbPressed_ResID The ResourceID of the image for the pressed thumb icon.
     * @throws IllegalArgumentException Will be thrown if min/max value type is not one of Long,
     *                                  Double, Integer, Float, Short, Byte or BigDecimal.
     */
    public RangeSeekBar(T absoluteMinValue, T absoluteMaxValue,
            Context context, int singleColor, int thumbNormal_ResID,
            int thumbPressed_ResID) throws IllegalArgumentException {
        super(context);
        this.absoluteMinValue = absoluteMinValue;
        this.absoluteMaxValue = absoluteMaxValue;
        absoluteMinValuePrim = absoluteMinValue.doubleValue();
        absoluteMaxValuePrim = absoluteMaxValue.doubleValue();
        numberType = NumberType.fromNumber(absoluteMinValue);
        IS_MULTI_COLORED = false;
        SINGLE_COLOR = singleColor < 0 ? singleColor : Color.argb(0xFF, 0x33,
                0xB5, 0xE5);
        LEFT_COLOR = 0;
        MIDDLE_COLOR = 0;
        RIGHT_COLOR = 0;
        thumbImage = BitmapFactory.decodeResource(getResources(),
                thumbNormal_ResID);
        thumbPressedImage = BitmapFactory.decodeResource(getResources(),
                thumbPressed_ResID);
        thumbWidth = thumbImage.getWidth();
        thumbHalfWidth = 0.5f * thumbWidth;
        thumbHalfHeight = 0.5f * thumbImage.getHeight();
        lineHeight = 0.3f * thumbHalfHeight;
        padding = thumbHalfWidth;

        // make RangeSeekBar focusable. This solves focus handling issues in
        // case EditText widgets are being used along with the RangeSeekBar
        // within ScollViews.
        setFocusable(true);
        setFocusableInTouchMode(true);
        init();
    }

    /**
     * Creates a new RangeSeekBar.
     *
     * @param absoluteMinValue   The minimum value of the selectable range.
     * @param absoluteMaxValue   The maximum value of the selectable range.
     * @param leftColor          The <tt>int</tt> of the color to go between the left most
     *                           point and the left (min) thumb point. will Default to
     *                           #FFFF0000 (RED) if 0 is sent.
     * @param middleColor        The <tt>int</tt> of the color to go between the two thumb
     *                           points will Default to #FF00FF00 (GREEN) if 0 is sent.
     * @param rightColor         The <tt>int</tt> of the color to go between the right most
     *                           point and the right (max) thumb point. will Default to
     *                           #FF0000FF (BLUE) if 0 is sent.
     * @param thumbNormal_ResID  The ResourceID of the image for the normal thumb icon.
     * @param thumbPressed_ResID The ResourceID of the image for the pressed thumb icon.
     * @throws IllegalArgumentException Will be thrown if min/max value type is not one of Long,
     *                                  Double, Integer, Float, Short, Byte or BigDecimal.
     */
    public RangeSeekBar(T absoluteMinValue, T absoluteMaxValue,
            Context context, int leftColor, int middleColor, int rightColor,
            int thumbNormal_ResID, int thumbPressed_ResID)
            throws IllegalArgumentException {
        super(context);
        this.absoluteMinValue = absoluteMinValue;
        this.absoluteMaxValue = absoluteMaxValue;
        absoluteMinValuePrim = absoluteMinValue.doubleValue();
        absoluteMaxValuePrim = absoluteMaxValue.doubleValue();
        numberType = NumberType.fromNumber(absoluteMinValue);

        // Added so we can draw right colors
        IS_MULTI_COLORED = true;
        SINGLE_COLOR = 0;
        LEFT_COLOR = leftColor < 0 ? leftColor : Color.argb(0xFF, 0xFF, 0x00,
                0x00);
        MIDDLE_COLOR = middleColor < 0 ? middleColor : Color.argb(0xFF, 0x00,
                0xFF, 0x00);
        RIGHT_COLOR = rightColor < 0 ? rightColor : Color.argb(0xFF, 0x00,
                0x00, 0xFF);
        thumbImage = BitmapFactory.decodeResource(getResources(),
                thumbNormal_ResID);
        thumbPressedImage = BitmapFactory.decodeResource(getResources(),
                thumbPressed_ResID);
        thumbWidth = thumbImage.getWidth();
        thumbHalfWidth = 0.5f * thumbWidth;
        thumbHalfHeight = 0.5f * thumbImage.getHeight();
        lineHeight = 0.3f * thumbHalfHeight;
        padding = thumbHalfWidth;

        // make RangeSeekBar focusable. This solves focus handling issues in
        // case EditText widgets are being used along with the RangeSeekBar
        // within ScollViews.
        setFocusable(true);
        setFocusableInTouchMode(true);
        init();
    }

    private final void init() {
        mScaledTouchSlop = ViewConfiguration.get(getContext())
                .getScaledTouchSlop();
    }

    public boolean isNotifyWhileDragging() {
        return notifyWhileDragging;
    }

    /**
     * Should the widget notify the listener callback while the user is still
     * dragging a thumb? Default is false.
     */
    public void setNotifyWhileDragging(boolean flag) {
        this.notifyWhileDragging = flag;
    }

    /**
     * Returns the absolute minimum value of the range that has been set at
     * construction time.
     *
     * @return The absolute minimum value of the range.
     */
    public T getAbsoluteMinValue() {
        return absoluteMinValue;
    }

    /**
     * Returns the absolute maximum value of the range that has been set at
     * construction time.
     *
     * @return The absolute maximum value of the range.
     */
    public T getAbsoluteMaxValue() {
        return absoluteMaxValue;
    }

    /**
     * Returns the currently selected min value.
     *
     * @return The currently selected min value.
     */
    public T getSelectedMinValue() {
        return normalizedToValue(normalizedMinValue);
    }

    /**
     * Sets the currently selected minimum value. The widget will be invalidated
     * and redrawn.
     *
     * @param value The Number value to set the minimum value to. Will be clamped
     *              to given absolute minimum/maximum range.
     */
    public void setSelectedMinValue(T value) {
        // in case absoluteMinValue == absoluteMaxValue, avoid division by zero
        // when normalizing.
        if (0 == (absoluteMaxValuePrim - absoluteMinValuePrim)) {
            setNormalizedMinValue(0d);
        } else {
            setNormalizedMinValue(valueToNormalized(value));
        }
    }

    /**
     * Returns the currently selected max value.
     *
     * @return The currently selected max value.
     */
    public T getSelectedMaxValue() {
        return normalizedToValue(normalizedMaxValue);
    }

    /**
     * Sets the currently selected maximum value. The widget will be invalidated
     * and redrawn.
     *
     * @param value The Number value to set the maximum value to. Will be clamped
     *              to given absolute minimum/maximum range.
     */
    public void setSelectedMaxValue(T value) {
        // in case absoluteMinValue == absoluteMaxValue, avoid division by zero
        // when normalizing.
        if (0 == (absoluteMaxValuePrim - absoluteMinValuePrim)) {
            setNormalizedMaxValue(1d);
        } else {
            setNormalizedMaxValue(valueToNormalized(value));
        }
    }

    /**
     * Handles thumb selection and movement. Notifies listener callback on
     * certain events.
     */
    @Override
    public boolean onTouchEvent(MotionEvent event) {

        if (!isEnabled()) {
            return false;
        }

        int pointerIndex;

        final int action = event.getAction();
        switch (action & MotionEvent.ACTION_MASK) {

            case MotionEvent.ACTION_DOWN:
                // Remember where the motion event started
                mActivePointerId = event.getPointerId(event.getPointerCount() - 1);
                pointerIndex = event.findPointerIndex(mActivePointerId);
                mDownMotionX = event.getX(pointerIndex);

                pressedThumb = evalPressedThumb(mDownMotionX);

                // Only handle thumb presses.
                if (pressedThumb == null) {
                    return super.onTouchEvent(event);
                }

                setPressed(true);
                invalidate();
                onStartTrackingTouch();
                trackTouchEvent(event);
                attemptClaimDrag();

                break;
            case MotionEvent.ACTION_MOVE:
                if (pressedThumb != null) {

                    if (mIsDragging) {
                        trackTouchEvent(event);
                    } else {
                        // Scroll to follow the motion event
                        pointerIndex = event.findPointerIndex(mActivePointerId);
                        final float x = event.getX(pointerIndex);

                        if (Math.abs(x - mDownMotionX) > mScaledTouchSlop) {
                            setPressed(true);
                            invalidate();
                            onStartTrackingTouch();
                            trackTouchEvent(event);
                            attemptClaimDrag();
                        }
                    }

                    if (notifyWhileDragging && listener != null) {
                        listener.onRangeSeekBarValuesChanged(this,
                                getSelectedMinValue(), getSelectedMaxValue());
                    }
                }
                break;
            case MotionEvent.ACTION_UP:
                if (mIsDragging) {
                    trackTouchEvent(event);
                    onStopTrackingTouch();
                    setPressed(false);
                } else {
                    // Touch up when we never crossed the touch slop threshold
                    // should be interpreted as a tap-seek to that location.
                    onStartTrackingTouch();
                    trackTouchEvent(event);
                    onStopTrackingTouch();
                }

                pressedThumb = null;
                invalidate();
                if (listener != null) {
                    listener.onRangeSeekBarValuesChanged(this,
                            getSelectedMinValue(), getSelectedMaxValue());
                }
                break;
            case MotionEvent.ACTION_POINTER_DOWN: {
                final int index = event.getPointerCount() - 1;
                // final int index = ev.getActionIndex();
                mDownMotionX = event.getX(index);
                mActivePointerId = event.getPointerId(index);
                invalidate();
                break;
            }
            case MotionEvent.ACTION_POINTER_UP:
                onSecondaryPointerUp(event);
                invalidate();
                break;
            case MotionEvent.ACTION_CANCEL:
                if (mIsDragging) {
                    onStopTrackingTouch();
                    setPressed(false);
                }
                invalidate(); // see above explanation
                break;
        }
        return true;
    }

    private final void onSecondaryPointerUp(MotionEvent ev) {
        final int pointerIndex = (ev.getAction() & ACTION_POINTER_INDEX_MASK)
                >> ACTION_POINTER_INDEX_SHIFT;

        final int pointerId = ev.getPointerId(pointerIndex);
        if (pointerId == mActivePointerId) {
            // This was our active pointer going up. Choose
            // a new active pointer and adjust accordingly.
            // TODO: Make this decision more intelligent.
            final int newPointerIndex = pointerIndex == 0 ? 1 : 0;
            mDownMotionX = ev.getX(newPointerIndex);
            mActivePointerId = ev.getPointerId(newPointerIndex);
        }
    }

    private final void trackTouchEvent(MotionEvent event) {
        final int pointerIndex = event.findPointerIndex(mActivePointerId);
        final float x = event.getX(pointerIndex);

        if (Thumb.MIN.equals(pressedThumb)) {
            setNormalizedMinValue(screenToNormalized(x));
        } else if (Thumb.MAX.equals(pressedThumb)) {
            setNormalizedMaxValue(screenToNormalized(x));
        }
    }

    /**
     * Tries to claim the user's drag motion, and requests disallowing any
     * ancestors from stealing events in the drag.
     */
    private void attemptClaimDrag() {
        if (getParent() != null) {
            getParent().requestDisallowInterceptTouchEvent(true);
        }
    }

    /**
     * This is called when the user has started touching this widget.
     */
    void onStartTrackingTouch() {
        mIsDragging = true;
    }

    /**
     * This is called when the user either releases his touch or the touch is
     * canceled.
     */
    void onStopTrackingTouch() {
        mIsDragging = false;
    }

    /**
     * Ensures correct size of the widget.
     */
    @Override
    protected synchronized void onMeasure(int widthMeasureSpec,
            int heightMeasureSpec) {
        int width = 200;
        if (MeasureSpec.UNSPECIFIED != MeasureSpec.getMode(widthMeasureSpec)) {
            width = MeasureSpec.getSize(widthMeasureSpec);
        }
        int height = thumbImage.getHeight();
        if (MeasureSpec.UNSPECIFIED != MeasureSpec.getMode(heightMeasureSpec)) {
            height = Math.min(height, MeasureSpec.getSize(heightMeasureSpec));
        }
        setMeasuredDimension(width, height);
    }

    /**
     * Draws the widget on the given canvas.
     */
    @Override
    protected synchronized void onDraw(Canvas canvas) {
        super.onDraw(canvas);

        paint.setStyle(Style.FILL);
        paint.setAntiAlias(true);

        if (!IS_MULTI_COLORED) {
            // draw seek bar background line
            final RectF rect = new RectF(padding,
                    0.5f * (getHeight() - lineHeight), getWidth() - padding,
                    0.5f * (getHeight() + lineHeight));
            paint.setColor(BACKGROUND_COLOR);
            canvas.drawRect(rect, paint);

            // draw seek bar active range line
            rect.left = normalizedToScreen(normalizedMinValue);
            rect.right = normalizedToScreen(normalizedMaxValue);

            // orange color
            paint.setColor(SINGLE_COLOR);
            canvas.drawRect(rect, paint);
        } else {
            final RectF rectR = new RectF(padding,
                    0.5f * (getHeight() - lineHeight),
                    normalizedToScreen(normalizedMinValue),
                    0.5f * (getHeight() + lineHeight));
            paint.setColor(LEFT_COLOR);
            canvas.drawRect(rectR, paint);

            // draw seek bar background line
            final RectF rectY = new RectF(padding,
                    0.5f * (getHeight() - lineHeight), getWidth() - padding,
                    0.5f * (getHeight() + lineHeight));

            // draw seek bar active range line
            rectY.left = normalizedToScreen(normalizedMinValue);
            rectY.right = normalizedToScreen(normalizedMaxValue);

            paint.setColor(MIDDLE_COLOR);
            canvas.drawRect(rectY, paint);

            final RectF rectG = new RectF(
                    normalizedToScreen(normalizedMaxValue),
                    0.5f * (getHeight() - lineHeight), getWidth() - padding,
                    0.5f * (getHeight() + lineHeight));
            paint.setColor(RIGHT_COLOR);
            canvas.drawRect(rectG, paint);
        }

        // draw minimum thumb
        drawThumb(normalizedToScreen(normalizedMinValue),
                Thumb.MIN.equals(pressedThumb), canvas);

        // draw maximum thumb
        drawThumb(normalizedToScreen(normalizedMaxValue),
                Thumb.MAX.equals(pressedThumb), canvas);
    }

    /**
     * Overridden to save instance state when device orientation changes. This
     * method is called automatically if you assign an id to the RangeSeekBar
     * widget using the {@link #setId(int)} method. Other members of this class
     * than the normalized min and max values don't need to be saved.
     */
    @Override
    protected Parcelable onSaveInstanceState() {
        final Bundle bundle = new Bundle();
        bundle.putParcelable("SUPER", super.onSaveInstanceState());
        bundle.putDouble("MIN", normalizedMinValue);
        bundle.putDouble("MAX", normalizedMaxValue);
        return bundle;
    }

    /**
     * Overridden to restore instance state when device orientation changes.
     * This method is called automatically if you assign an id to the
     * RangeSeekBar widget using the {@link #setId(int)} method.
     */
    @Override
    protected void onRestoreInstanceState(Parcelable parcel) {
        final Bundle bundle = (Bundle) parcel;
        super.onRestoreInstanceState(bundle.getParcelable("SUPER"));
        normalizedMinValue = bundle.getDouble("MIN");
        normalizedMaxValue = bundle.getDouble("MAX");
    }

    /**
     * Draws the "normal" resp. "pressed" thumb image on specified x-coordinate.
     *
     * @param screenCoord The x-coordinate in screen space where to draw the image.
     * @param pressed     Is the thumb currently in "pressed" state?
     * @param canvas      The canvas to draw upon.
     */
    private void drawThumb(float screenCoord, boolean pressed, Canvas canvas) {
        canvas.drawBitmap(pressed ? thumbPressedImage : thumbImage, screenCoord
                - thumbHalfWidth,
                (float) ((0.5f * getHeight()) - thumbHalfHeight), paint);
    }

    /**
     * Decides which (if any) thumb is touched by the given x-coordinate.
     *
     * @param touchX The x-coordinate of a touch event in screen space.
     * @return The pressed thumb or null if none has been touched.
     */
    private Thumb evalPressedThumb(float touchX) {
        Thumb result = null;
        boolean minThumbPressed = isInThumbRange(touchX, normalizedMinValue);
        boolean maxThumbPressed = isInThumbRange(touchX, normalizedMaxValue);
        if (minThumbPressed && maxThumbPressed) {
            // if both thumbs are pressed (they lie on top of each other),
            // choose the one with more room to drag. this avoids "stalling" the
            // thumbs in a corner, not being able to drag them apart anymore.
            result = (touchX / getWidth() > 0.5f) ? Thumb.MIN : Thumb.MAX;
        } else if (minThumbPressed) {
            result = Thumb.MIN;
        } else if (maxThumbPressed) {
            result = Thumb.MAX;
        }
        return result;
    }

    /**
     * Decides if given x-coordinate in screen space needs to be interpreted as
     * "within" the normalized thumb x-coordinate.
     *
     * @param touchX               The x-coordinate in screen space to check.
     * @param normalizedThumbValue The normalized x-coordinate of the thumb to check.
     * @return true if x-coordinate is in thumb range, false otherwise.
     */
    private boolean isInThumbRange(float touchX, double normalizedThumbValue) {
        return Math.abs(touchX - normalizedToScreen(normalizedThumbValue)) <= thumbHalfWidth;
    }

    /**
     * Sets normalized min value to value so that 0 <= value <= normalized max
     * value <= 1. The View will get invalidated when calling this method.
     *
     * @param value The new normalized min value to set.
     */
    public void setNormalizedMinValue(double value) {
        normalizedMinValue = Math.max(0d,
                Math.min(1d, Math.min(value, normalizedMaxValue)));
        invalidate();
    }

    /**
     * Sets normalized max value to value so that 0 <= normalized min value <=
     * value <= 1. The View will get invalidated when calling this method.
     *
     * @param value The new normalized max value to set.
     */
    public void setNormalizedMaxValue(double value) {
        normalizedMaxValue = Math.max(0d,
                Math.min(1d, Math.max(value, normalizedMinValue)));
        invalidate();
    }

    /**
     * Converts a normalized value to a Number object in the value space between
     * absolute minimum and maximum.
     */
    @SuppressWarnings("unchecked")
    private T normalizedToValue(double normalized) {
        return (T) numberType.toNumber(absoluteMinValuePrim + normalized
                * (absoluteMaxValuePrim - absoluteMinValuePrim));
    }

    /**
     * Converts the given Number value to a normalized double.
     *
     * @param value The Number value to normalize.
     * @return The normalized double.
     */
    private double valueToNormalized(T value) {
        if (0 == absoluteMaxValuePrim - absoluteMinValuePrim) {
            // prevent division by zero, simply return 0.
            return 0d;
        }
        return (value.doubleValue() - absoluteMinValuePrim)
                / (absoluteMaxValuePrim - absoluteMinValuePrim);
    }

    /**
     * Converts a normalized value into screen space.
     *
     * @param normalizedCoord The normalized value to convert.
     * @return The converted value in screen space.
     */
    private float normalizedToScreen(double normalizedCoord) {
        return (float) (padding + normalizedCoord * (getWidth() - 2 * padding));
    }

    /**
     * Converts screen space x-coordinates into normalized values.
     *
     * @param screenCoord The x-coordinate in screen space to convert.
     * @return The normalized value.
     */
    private double screenToNormalized(float screenCoord) {
        int width = getWidth();
        if (width <= 2 * padding) {
            // prevent division by zero, simply return 0.
            return 0d;
        } else {
            double result = (screenCoord - padding) / (width - 2 * padding);
            return Math.min(1d, Math.max(0d, result));
        }
    }

    /**
     * Thumb constants (min and max).
     */
    private static enum Thumb {
        MIN, MAX
    }

    ;

    /**
     * Utility enumaration used to convert between Numbers and doubles.
     *
     * @author Stephan Tittel (stephan.tittel@kom.tu-darmstadt.de)
     */
    private static enum NumberType {
        LONG, DOUBLE, INTEGER, FLOAT, SHORT, BYTE, BIG_DECIMAL;

        public static <E extends Number> NumberType fromNumber(E value)
                throws IllegalArgumentException {
            if (value instanceof Long) {
                return LONG;
            }
            if (value instanceof Double) {
                return DOUBLE;
            }
            if (value instanceof Integer) {
                return INTEGER;
            }
            if (value instanceof Float) {
                return FLOAT;
            }
            if (value instanceof Short) {
                return SHORT;
            }
            if (value instanceof Byte) {
                return BYTE;
            }
            if (value instanceof BigDecimal) {
                return BIG_DECIMAL;
            }
            throw new IllegalArgumentException("Number class '"
                    + value.getClass().getName() + "' is not supported");
        }

        public Number toNumber(double value) {
            switch (this) {
                case LONG:
                    return new Long((long) value);
                case DOUBLE:
                    return value;
                case INTEGER:
                    return new Integer((int) value);
                case FLOAT:
                    return new Float(value);
                case SHORT:
                    return new Short((short) value);
                case BYTE:
                    return new Byte((byte) value);
                case BIG_DECIMAL:
                    return new BigDecimal(value);
            }
            throw new InstantiationError("can't convert " + this
                    + " to a Number object");
        }
    }

    public int getLeftColor() {
        return LEFT_COLOR;
    }

    public void setLeftColor(int leftColor) {
        this.LEFT_COLOR = leftColor;
    }

    public int getMiddleColor() {
        return MIDDLE_COLOR;
    }

    public void setMiddleColor(int middleColor) {
        this.MIDDLE_COLOR = middleColor;
    }

    public int getRightColor() {
        return RIGHT_COLOR;
    }

    public void setRightColor(int rightColor) {
        this.RIGHT_COLOR = rightColor;
    }
}

public abstract class RightDrawableOnTouchListener implements OnTouchListener {

    Drawable drawable;

    private int fuzz = 10;

    /**
     * @param view
     */
    public RightDrawableOnTouchListener(TextView view) {
        super();
        final Drawable[] drawables = view.getCompoundDrawables();
        if (drawables != null && drawables.length == 4) {
            this.drawable = drawables[2];
        }
    }

    /*
     * (non-Javadoc)
     * 
     * @see android.view.View.OnTouchListener#onTouch(android.view.View, android.view.MotionEvent)
     */
    @Override
    public boolean onTouch(final View v, final MotionEvent event) {
        if (event.getAction() == MotionEvent.ACTION_DOWN && drawable != null) {
            final int x = (int) event.getX();
            final int y = (int) event.getY();
            final Rect bounds = drawable.getBounds();
            if (x >= (v.getRight() - bounds.width() - fuzz) && x <= (
                    v.getRight() - v.getPaddingRight() + fuzz)
                    && y >= (v.getPaddingTop() - fuzz)
                    && y <= (v.getHeight() - v.getPaddingBottom()) + fuzz) {
                return onDrawableTouch(event);
            }
        }
        return false;
    }

    public abstract boolean onDrawableTouch(final MotionEvent event);

}

