public class KeePass extends Activity {

	public static final int EXIT_NORMAL = 0;
	public static final int EXIT_LOCK = 1;
	public static final int EXIT_REFRESH = 2;
	public static final int EXIT_REFRESH_TITLE = 3;

	@Override
	protected void onCreate(Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);
	}

	@Override
	protected void onStart() {
		super.onStart();
		startFileSelect();
	}

	private void startFileSelect() {
		Intent intent = new Intent(this, FileSelectActivity.class);
		startActivityForResult(intent, 0);
	}

	@Override
	protected void onDestroy() {
		super.onDestroy();
	}

	@Override
	protected void onActivityResult(int requestCode, int resultCode, Intent data) {
		super.onActivityResult(requestCode, resultCode, data);

		if (resultCode == EXIT_NORMAL) {
			finish();
		}
	}
}

public class AboutDialog extends AppCompatDialog {

	public AboutDialog(Context context) {
		super(context);
	}

	@Override
	protected void onCreate(Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);
		setContentView(R.layout.about);
		setTitle(R.string.app_name);

		setVersion();

		Button okButton = (Button) findViewById(R.id.about_button);
		okButton.setOnClickListener(new View.OnClickListener() {
			public void onClick(View v) {
				dismiss();
			}
		});
	}

	private void setVersion() {
		Context ctx = getContext();
		String version;
		try {
			PackageInfo packageInfo = ctx.getPackageManager().getPackageInfo(ctx.getPackageName(), 0);
			version = packageInfo.versionName;
		} catch (NameNotFoundException e) {
			e.printStackTrace();
			version = "";
		}
		TextView tv = (TextView) findViewById(R.id.version);
		tv.setText(version);
	}
}

public class CancelDialog extends AppCompatDialog {

	private boolean mCanceled = false; 

	public CancelDialog(Context context) {
		super(context);
	}

	public boolean canceled() {
		return mCanceled;
	}

	@Override
	public void cancel() {
		super.cancel();
		mCanceled = true;
	}
}

