		super.onResume();

		TimeoutHelper.checkShutdown(this);
	}
}

public abstract class LockingPreferenceActivity extends PreferenceActivity {

	@Override
	protected void onPause() {
		super.onPause();

		TimeoutHelper.pause(this);
	}

	@Override
	protected void onResume() {
		super.onResume();

		TimeoutHelper.resume(this);
	}
}

public class PasswordActivity extends LockingActivity {

    public static final String KEY_DEFAULT_FILENAME = "defaultFileName";
    public static final String KEY_FILENAME = "fileName";
    public static final String KEY_KEYFILE = "keyFile";


    private static final String[] READ_WRITE_PERMISSIONS =
            {Manifest.permission.READ_EXTERNAL_STORAGE, Manifest.permission.WRITE_EXTERNAL_STORAGE};



    public static void Launch(
            Activity act,
            String fileName) throws FileNotFoundException {
        Launch(act, fileName, "");
    }

    public static void Launch(
            Activity act,
            String fileName,
            String keyFile) throws FileNotFoundException {
        if (EmptyUtils.isNullOrEmpty(fileName)) {
            throw new FileNotFoundException();
        }

        Uri uri = UriUtil.parseDefaultFile(fileName);
        String scheme = uri.getScheme();

        if (!EmptyUtils.isNullOrEmpty(scheme) && scheme.equalsIgnoreCase("file")) {
            File dbFile = new File(uri.getPath());
            if (!dbFile.exists()) {
                throw new FileNotFoundException();
            }
        }

        Intent i = new Intent(act, PasswordActivity.class);
        i.putExtra(KEY_FILENAME, fileName);
        i.putExtra(KEY_KEYFILE, keyFile);

        act.startActivityForResult(i, 0);

    }

    @Override
    protected void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.password_activity);

        Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);
    }
}

public class ProgressTask implements Runnable {
	private Activity mAct;
	private Handler mHandler;
	private RunnableOnFinish mTask;
	private ProgressDialog mPd;

	public ProgressTask(Activity act, RunnableOnFinish task, int messageId) {
		mAct = act;
		mTask = task;
		mHandler = new Handler();

		// Show process dialog
		mPd = new ProgressDialog(mAct);
		mPd.setCanceledOnTouchOutside(false);
		mPd.setTitle(act.getText(R.string.progress_title));
		mPd.setMessage(act.getText(messageId));

		// Set code to run when this is finished
		mTask.setStatus(new UpdateStatus(act, mHandler, mPd));
		mTask.mFinish = new AfterTask(task.mFinish, mHandler);

	}

	public void run() {
		// Show process dialog
		mPd.show();

		// Start Thread to Run task
		Thread t = new Thread(mTask);
		t.start();
	}

	private class AfterTask extends OnFinish {

		public AfterTask(OnFinish finish, Handler handler) {
			super(finish, handler);
		}

		@Override
		public void run() {
			super.run();
			// Remove the progress dialog
			mHandler.post(new CloseProcessDialog());
		}

	}

	private class CloseProcessDialog implements Runnable {

		public void run() {
			Activity act = mPd.getOwnerActivity();
			if (act != null && act.isFinishing()) {
				return;
			}

			boolean isDestroyed = false;
			if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.JELLY_BEAN_MR1) {
			    isDestroyed = mAct.isDestroyed();
            }

			if ( (mPd != null) && mPd.isShowing() && !isDestroyed) {
				mPd.dismiss();
			}
		}

	}

}

public class PwGroupListAdapter extends BaseAdapter {

	private GroupBaseActivity mAct;
	private PwGroup mGroup;
	private List<PwGroup> groupsForViewing;
	private List<PwEntry> entriesForViewing;
	private Comparator<PwEntry> entryComp = new PwEntry.EntryNameComparator();
	private Comparator<PwGroup> groupComp = new PwGroup.GroupNameComparator();
	private SharedPreferences prefs;

	public PwGroupListAdapter(GroupBaseActivity act, PwGroup group) {
		mAct = act;
		mGroup = group;
		prefs = PreferenceManager.getDefaultSharedPreferences(act);

		filterAndSort();

	}

	@Override
	public void notifyDataSetChanged() {
		super.notifyDataSetChanged();

		filterAndSort();
	}

	@Override
	public void notifyDataSetInvalidated() {
		super.notifyDataSetInvalidated();

		filterAndSort();
	}

	private void filterAndSort() {
		entriesForViewing = new ArrayList<PwEntry>();

		for (int i = 0; i < mGroup.childEntries.size(); i++) {
			PwEntry entry = mGroup.childEntries.get(i);
			if ( ! entry.isMetaStream() ) {
				entriesForViewing.add(entry);
			}
		}

		boolean sortLists = prefs.getBoolean(mAct.getString(R.string.sort_key),	mAct.getResources().getBoolean(R.bool.sort_default)); 
		if ( sortLists ) {
			groupsForViewing = new ArrayList<PwGroup>(mGroup.childGroups);

			Collections.sort(entriesForViewing, entryComp);
			Collections.sort(groupsForViewing, groupComp);
		} else {
			groupsForViewing = mGroup.childGroups;
		}
	}

	public int getCount() {

		return groupsForViewing.size() + entriesForViewing.size();
	}

	public Object getItem(int position) {
		return position;
	}

	public long getItemId(int position) {
		return position;
	}

	public View getView(int position, View convertView, ViewGroup parent) {
		int size = groupsForViewing.size();

		if ( position < size ) { 
			return createGroupView(position, convertView);
		} else {
			return createEntryView(position - size, convertView);
		}
	}

	private View createGroupView(int position, View convertView) {
		PwGroup group = groupsForViewing.get(position);
		PwGroupView gv;

		if (convertView == null || !(convertView instanceof PwGroupView)) {

			gv = PwGroupView.getInstance(mAct, group);
		} 
		else {
			gv = (PwGroupView) convertView;
			gv.convertView(group);

		}

		return gv;
	}

	private PwEntryView createEntryView(int position, View convertView) {
		PwEntry entry = entriesForViewing.get(position);
		PwEntryView ev;

		if (convertView == null || !(convertView instanceof PwEntryView)) {
			ev = PwEntryView.getInstance(mAct, entry, position);
		}
		else {
			ev = (PwEntryView) convertView;
			ev.convertView(entry, position);
		}

		return ev;
	}

}

public class SetPasswordDialog extends CancelDialog {

	private byte[] masterKey;
	private Uri mKeyfile;
	private FileOnFinish mFinish;

	public SetPasswordDialog(Context context) {
		super(context);
	}

	public SetPasswordDialog(Context context, FileOnFinish finish) {
		super(context);
		mFinish = finish;
	}

	public byte[] getKey() {
		return masterKey;
	}

	public Uri keyfile() {
		return mKeyfile;
	}

	@Override
	protected void onCreate(Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);
		setContentView(R.layout.set_password);

		setTitle(R.string.password_title);

		// Ok button
		Button okButton = (Button) findViewById(R.id.ok);
		okButton.setOnClickListener(new View.OnClickListener() {

			public void onClick(View v) {
				TextView passView = (TextView) findViewById(R.id.pass_password);
				String pass = passView.getText().toString();
				TextView passConfView = (TextView) findViewById(R.id.pass_conf_password);
				String confpass = passConfView.getText().toString();

				// Verify that passwords match
				if ( ! pass.equals(confpass) ) {
					// Passwords do not match
					Toast.makeText(getContext(), R.string.error_pass_match, Toast.LENGTH_LONG).show();
					return;
				}

				TextView keyfileView = (TextView) findViewById(R.id.pass_keyfile);
				Uri keyfile = UriUtil.parseDefaultFile(keyfileView.getText().toString());
				mKeyfile = keyfile;

				// Verify that a password or keyfile is set
				if ( pass.length() == 0 && EmptyUtils.isNullOrEmpty(keyfile)) {
					Toast.makeText(getContext(), R.string.error_nopass, Toast.LENGTH_LONG).show();
					return;

				}

				SetPassword sp = new SetPassword(getContext(), App.getDB(), pass, keyfile, new AfterSave(mFinish, new Handler()));
				final ProgressTask pt = new ProgressTask(getOwnerActivity(), sp, R.string.saving_database);
				boolean valid = sp.validatePassword(getContext(), new OnClickListener() {

					@Override
					public void onClick(DialogInterface dialog, int which) {
						pt.run();
					}
				});

				if (valid) {
				    pt.run();
				}
			}

		});

		// Cancel button
		Button cancel = (Button) findViewById(R.id.cancel);
		cancel.setOnClickListener(new View.OnClickListener() {

			public void onClick(View v) {
				cancel();
				if ( mFinish != null ) {
					mFinish.run();
				}
			}
		});
	}

	private class AfterSave extends OnFinish {
		private FileOnFinish mFinish;

		public AfterSave(FileOnFinish finish, Handler handler) {
			super(finish, handler);
			mFinish = finish;
		}

		@Override
		public void run() {
			if ( mSuccess ) {
				if ( mFinish != null ) {
					mFinish.setFilename(mKeyfile);
				}
				dismiss();
			} else {
				displayMessage(getContext());
			}
			super.run();
		}
	}
}

public class UIToastTask implements Runnable {

	private String mText;
	private Context mCtx;

	public UIToastTask(Context ctx, int resId) {
		mCtx = ctx;
		mText = ctx.getString(resId);
	}

	public UIToastTask(Context ctx, String text) {
		mCtx = ctx;
		mText = text;
	}

	public void run() {
		Toast.makeText(mCtx, mText, Toast.LENGTH_LONG).show();
	}
}

public class UpdateStatus {
	private ProgressDialog mPD;
	private Context mCtx;
	private Handler mHandler;

	public UpdateStatus() {

	}

	public UpdateStatus(Context ctx, Handler handler, ProgressDialog pd) {
		mCtx = ctx;
		mPD = pd;
		mHandler = handler;
	}

	public void updateMessage(int resId) {
		if ( mCtx != null && mPD != null && mHandler != null ) {
			mHandler.post(new UpdateMessage(resId));
		}
	}

	private class UpdateMessage implements Runnable {
		private int mResId;

		public UpdateMessage(int resId) {
			mResId = resId;
		}

		public void run() {
			mPD.setMessage(mCtx.getString(mResId));
		}

	}
}

public class App extends Application {
	private static Database db = null;
	private static boolean shutdown = false;
	private static Calendar calendar = null;
	private static RecentFileHistory fileHistory = null;
	private static final String TAG = "KeePassDroid Timer";

	private BroadcastReceiver mIntentReceiver;

	public static Database getDB() {
		if ( db == null ) {
			db = new Database();
		}

		return db;
	}

	public static RecentFileHistory getFileHistory() {
		return fileHistory;
	}

	public static void setDB(Database d) {
		db = d;
	}

	public static boolean isShutdown() {
		return shutdown;
	}

	public static void setShutdown() {
		shutdown = true;
	}

	public static void clearShutdown() {
		shutdown = false;
	}

	public static Calendar getCalendar() {
		if ( calendar == null ) {
			calendar = Calendar.getInstance();
		}

		return calendar;
	}

	@Override
	public void onCreate() {
		super.onCreate();

		fileHistory = new RecentFileHistory(this);

		PRNGFixes.apply();

		mIntentReceiver = new BroadcastReceiver() {
			@Override
			public void onReceive(Context context, Intent intent) {
				String action = intent.getAction();

				if ( action.equals(Intents.TIMEOUT) ) {
					timeout(context);
				}
			}
		};

		IntentFilter filter = new IntentFilter();
		filter.addAction(Intents.TIMEOUT);
		registerReceiver(mIntentReceiver, filter);
	}

	private void timeout(Context context) {
		Log.d(TAG, "Timeout");
		App.setShutdown();

		NotificationManager nm = (NotificationManager) getSystemService(NOTIFICATION_SERVICE);
		nm.cancelAll();
	}

	@Override
	public void onTerminate() {
		if ( db != null ) {
			db.clear(getApplicationContext());
		}

		unregisterReceiver(mIntentReceiver);
		super.onTerminate();
	}
}

public class TypefaceFactory {
	private static ReferenceMap typefaceMap = new ReferenceMap(AbstractReferenceMap.HARD, AbstractReferenceMap.WEAK);

	public static Typeface getTypeface(Context ctx, String fontPath) {
		Typeface tf;

		tf = (Typeface) typefaceMap.get(fontPath);
		if (tf != null) {
			return tf;
		}

		try {
		    return Typeface.createFromAsset(ctx.getAssets(), fontPath);
		} catch (Exception e) {
			// Return null if we can't create it
			return null;
		}
	}
}

@SuppressLint("NewApi")
public class SettingsBackupAgent extends BackupAgentHelper {

	private static final String PREFS_BACKUP_KEY = "prefs";

	@Override
	public void onCreate() {
		String defaultPrefs = this.getPackageName() + "_preferences";

		SharedPreferencesBackupHelper prefHelper = new SharedPreferencesBackupHelper(this, defaultPrefs);
		addHelper(PREFS_BACKUP_KEY, prefHelper);
	}

}

public class BiometricHelper {

    private static final String ALIAS_KEY = "example-key";

    private BiometricManager biometricManager;
    private KeyStore keyStore = null;
    private KeyGenerator keyGenerator = null;
    private Cipher cipher = null;
    private KeyguardManager keyguardManager = null;
    private BiometricPrompt.CryptoObject cryptoObject = null;

    private boolean initOk = false;
    private boolean cryptoInitOk = false;
    private BiometricCallback biometricCallback;
    private CancellationSignal cancellationSignal;

    public interface BiometricCallback {

        void handleEncryptedResult(String value, String ivSpec);

        void handleDecryptedResult(String value);

        void onInvalidKeyException();

        void onException();

        void onException(boolean showWarningMessage);

        void onException(CharSequence message);

        void onException(int resId);

        void onKeyInvalidated();
    }

    public BiometricHelper(
            final Context context,
            final BiometricCallback biometricCallback) {

        this.biometricManager = BiometricManager.from(context);
        this.keyguardManager = (KeyguardManager)context.getSystemService(Context.KEYGUARD_SERVICE);

        if (!isBiometricSupported()) {
            // really not much to do when no fingerprint support found
            setInitOk(false);
            return;
        }
        this.biometricCallback = biometricCallback;

        try {
            this.keyStore = KeyStore.getInstance("AndroidKeyStore");
            this.keyGenerator = KeyGenerator.getInstance(
                    KeyProperties.KEY_ALGORITHM_AES,
                    "AndroidKeyStore");
            this.cipher = Cipher.getInstance(
                    KeyProperties.KEY_ALGORITHM_AES + "/"
                            + KeyProperties.BLOCK_MODE_CBC + "/"
                            + KeyProperties.ENCRYPTION_PADDING_PKCS7);
            this.cryptoObject = new BiometricPrompt.CryptoObject(cipher);
            setInitOk(true);
        } catch (final Exception e) {
            setInitOk(false);
            biometricCallback.onException();
        }
    }

    private boolean isBiometricSupported() {
        int auth = biometricManager.canAuthenticate();
        return (auth == BiometricManager.BIOMETRIC_SUCCESS || auth == BiometricManager.BIOMETRIC_ERROR_NONE_ENROLLED)
                && KeyguardManagerCompat.isKeyguardSecure(keyguardManager);
    }

    public boolean isFingerprintInitialized() {
        return initOk;
    }

    public boolean initEncryptData() {
        cryptoInitOk = false;

        if (!isFingerprintInitialized()) {
            if (biometricCallback != null) {
                biometricCallback.onException();
            }
            return false;
        }
        try {
            initEncryptKey(false);
            return true;
        } catch (final InvalidKeyException invalidKeyException) {
            try {
                biometricCallback.onKeyInvalidated();
                initEncryptKey(true);
            } catch (InvalidKeyException e) {
                biometricCallback.onInvalidKeyException();
            } catch (Exception e) {
                biometricCallback.onException();;
            }
        } catch (final Exception e) {
            biometricCallback.onException();
        }

        return false;
    }

    private void initEncryptKey(
            final boolean deleteExistingKey) throws Exception {

        createNewKeyIfNeeded(deleteExistingKey);
        keyStore.load(null);
        final SecretKey key = (SecretKey) keyStore.getKey(ALIAS_KEY, null);
        cipher.init(Cipher.ENCRYPT_MODE, key);
        cryptoInitOk = true;

    }

    public void encryptData(final String value) {

        if (!isFingerprintInitialized()) {
            if (biometricCallback != null) {
                biometricCallback.onException();
            }
            return;
        }
        try {
            // actual do encryption here
            byte[] encrypted = cipher.doFinal(value.getBytes());
            final String encryptedValue = new String(Base64.encodeToString(encrypted, Base64.NO_WRAP));

            // passes updated iv spec on to callback so this can be stored for decryption
            final IvParameterSpec spec = cipher.getParameters().getParameterSpec(IvParameterSpec.class);
            final String ivSpecValue = new String(Base64.encode(spec.getIV(), Base64.NO_WRAP));
            biometricCallback.handleEncryptedResult(encryptedValue, ivSpecValue);

        } catch (final Exception e) {
            biometricCallback.onException();
        }

    }

    public Cipher getCipher() {
        return cipher;
    }

    public boolean initDecryptData(
            final String ivSpecValue) {

        cryptoInitOk = false;
        try {
            initDecryptKey(ivSpecValue,false);
            return true;
        } catch (final InvalidKeyException invalidKeyException) {
            // Key was invalidated (maybe all registered fingerprints were changed)
            // Retry with new key
            try {
                biometricCallback.onKeyInvalidated();
                initDecryptKey(ivSpecValue, true);
            } catch (InvalidKeyException e) {
                biometricCallback.onInvalidKeyException();
            } catch (Exception e) {
                biometricCallback.onException();
            }
        } catch (final Exception e) {
            biometricCallback.onException();
        }

        return false;
    }

    private void initDecryptKey(
            final String ivSpecValue,
            final boolean deleteExistingKey) throws  Exception {

        createNewKeyIfNeeded(deleteExistingKey);
        keyStore.load(null);
        final SecretKey key = (SecretKey) keyStore.getKey(ALIAS_KEY, null);

        // important to restore spec here that was used for decryption
        final byte[] iv = Base64.decode(ivSpecValue, Base64.NO_WRAP);
        final IvParameterSpec spec = new IvParameterSpec(iv);
        cipher.init(Cipher.DECRYPT_MODE, key, spec);
        cryptoInitOk = true;

    }

    public void decryptData(final String encryptedValue) {

        if (!isFingerprintInitialized()) {
            if (biometricCallback != null) {
                biometricCallback.onException();
            }
            return;
        }
        try {
            // actual decryption here
            final byte[] encrypted = Base64.decode(encryptedValue, Base64.NO_WRAP);
            byte[] decrypted = cipher.doFinal(encrypted);
            final String decryptedString = new String(decrypted);

            //final String encryptedString = Base64.encodeToString(encrypted, 0 /* flags */);
            biometricCallback.handleDecryptedResult(decryptedString);

        } catch (BadPaddingException | IllegalBlockSizeException e) {
            biometricCallback.onKeyInvalidated();
        } catch (final Exception e) {
            biometricCallback.onException();
        }
    }

    private void createNewKeyIfNeeded(final boolean allowDeleteExisting) {
        try {
            keyStore.load(null);
            if (allowDeleteExisting
                    && keyStore.containsAlias(ALIAS_KEY)) {

                keyStore.deleteEntry(ALIAS_KEY);
            }

            // Create new key if needed
            if (!keyStore.containsAlias(ALIAS_KEY)) {
                // Set the alias of the entry in Android KeyStore where the key will appear
                // and the constrains (purposes) in the constructor of the Builder
                AlgorithmParameterSpec algSpec = KeyGenParameterSpecCompat.build(ALIAS_KEY,
                        KeyProperties.PURPOSE_ENCRYPT | KeyProperties.PURPOSE_DECRYPT,
                        KeyProperties.BLOCK_MODE_CBC, true,
                        KeyProperties.ENCRYPTION_PADDING_PKCS7);


                keyGenerator.init(algSpec);
                keyGenerator.generateKey();
            }
        } catch (final Exception e) {
            biometricCallback.onException();
        }
    }

    void setInitOk(final boolean initOk) {
        this.initOk = initOk;
    }

}

public class VariantDictionary {
    private static final int VdVersion = 0x0100;
    private static final int VdmCritical = 0xFF00;
    private static final int VdmInfo = 0x00FF;

    private Map<String, VdType> dict = new HashMap<String, VdType>();

    private class VdType {
        public static final byte None = 0x00;
        public static final byte UInt32 = 0x04;
        public static final byte UInt64 =0x05;
        public static final byte Bool =0x08;
        public static final byte Int32 =0x0C;
        public static final byte Int64 =0x0D;
        public static final byte String =0x18;
        public static final byte ByteArray =0x42;

        public final byte type;
        public final Object value;

        VdType(byte type, Object value) {
            this.type = type;
            this.value = value;
        }

    }

    private Object getValue(String name) {
        VdType val = dict.get(name);
        if (val == null) {
            return null;
        }

        return val.value;
    }
    private void putType(byte type, String name, Object value) {
        dict.put(name, new VdType(type, value));
    }

    public void setUInt32(String name, long value) { putType(VdType.UInt32, name, value); }
    public long getUInt32(String name) { return (long)dict.get(name).value; }

    public void setUInt64(String name, long value) { putType(VdType.UInt64, name, value); }
    public long getUInt64(String name) { return (long)dict.get(name).value; }

    public void setBool(String name, boolean value) { putType(VdType.Bool, name, value); }
    public boolean getBool(String name) { return (boolean)dict.get(name).value; }

    public void setInt32(String name, int value) { putType(VdType.Int32 ,name, value); }
    public int getInt32(String name) { return (int)dict.get(name).value; }

    public void setInt64(String name, long value) { putType(VdType.Int64 ,name, value); }
    public long getInt64(String name) { return (long)dict.get(name).value; }

    public void setString(String name, String value) { putType(VdType.String ,name, value); }
    public String getString(String name) { return (String)getValue(name); }

    public void setByteArray(String name, byte[] value) { putType(VdType.ByteArray, name, value); }
    public byte[] getByteArray(String name) { return (byte[])getValue(name); }

    public static VariantDictionary deserialize(LEDataInputStream lis) throws IOException {
        VariantDictionary d = new VariantDictionary();

        int version = lis.readUShort();
        if ((version & VdmCritical) > (VdVersion & VdmCritical)) {
            throw new IOException("Invalid format");
        }

        while(true) {
            int type = lis.read();
            if (type < 0) {
                throw new IOException(("Invalid format"));
            }

            byte bType = (byte)type;
            if (bType == VdType.None) {
                break;
            }

            int nameLen = lis.readInt();
            byte[] nameBuf = lis.readBytes(nameLen);
            if (nameLen != nameBuf.length) {
                throw new IOException("Invalid format");
            }
            String name = new String(nameBuf, "UTF-8");

            int valueLen = lis.readInt();
            byte[] valueBuf = lis.readBytes(valueLen);
            if (valueLen != valueBuf.length) {
                throw new IOException("Invalid format");
            }

            switch (bType) {
                case VdType.UInt32:
                    if (valueLen == 4) {
                        d.setUInt32(name, LEDataInputStream.readUInt(valueBuf, 0));
                    }
                    break;
                case VdType.UInt64:
                    if (valueLen == 8) {
                        d.setUInt64(name, LEDataInputStream.readLong(valueBuf, 0));
                    }
                    break;
                case VdType.Bool:
                    if (valueLen == 1) {
                        d.setBool(name, valueBuf[0] != 0);
                    }
                    break;
                case VdType.Int32:
                    if (valueLen == 4) {
                        d.setInt32(name, LEDataInputStream.readInt(valueBuf, 0));
                    }
                    break;
                case VdType.Int64:
                    if (valueLen == 8) {
                        d.setInt64(name, LEDataInputStream.readLong(valueBuf, 0));
                    }
                    break;
                case VdType.String:
                    d.setString(name, new String(valueBuf, "UTF-8"));
                    break;
                case VdType.ByteArray:
                    d.setByteArray(name, valueBuf);
                    break;
                default:
                    assert (false);
                    break;
            }
        }

        return d;
    }

    public static void serialize(VariantDictionary d, LEDataOutputStream los) throws IOException{
        if (los == null) {
            assert(false);
            return;
        }

        los.writeUShort(VdVersion);

        for (Map.Entry<String, VdType> entry: d.dict.entrySet()) {
            String name = entry.getKey();
            byte[] nameBuf = null;
            try {
                nameBuf = name.getBytes("UTF-8");
            } catch (UnsupportedEncodingException e) {
                assert(false);
                throw new IOException("Couldn't encode parameter name.");
            }

            VdType vd = entry.getValue();

            los.write(vd.type);
            los.writeInt(nameBuf.length);
            los.write(nameBuf);

            byte[] buf;
            switch (vd.type) {
                case VdType.UInt32:
                    los.writeInt(4);
                    los.writeUInt((long)vd.value);
                    break;
                case VdType.UInt64:
                    los.writeInt(8);
                    los.writeLong((long)vd.value);
                    break;
                case VdType.Bool:
                    los.writeInt(1);
                    byte bool = (boolean)vd.value ? (byte)1 : (byte)0;
                    los.write(bool);
                    break;
                case VdType.Int32:
                    los.writeInt(4);
                    los.writeInt((int)vd.value);
                    break;
                case VdType.Int64:
                    los.writeInt(8);
                    los.writeLong((long)vd.value);
                    break;
                case VdType.String:
                    String value = (String)vd.value;
                    buf = value.getBytes("UTF-8");
                    los.writeInt(buf.length);
                    los.write(buf);
                    break;
                case VdType.ByteArray:
                    buf = (byte[])vd.value;
                    los.writeInt(buf.length);
                    los.write(buf);
                    break;
                default:
                    assert(false);
                    break;
            }
        }

        los.write(VdType.None);

    }

    public void copyTo(VariantDictionary d) {
       for (Map.Entry<String, VdType> entry : d.dict.entrySet()) {
           String key = entry.getKey();
           VdType value = entry.getValue();

           dict.put(key, value);
       }
    }
;
    public int size() {
        return dict.size();
    }
}

public class ClipDataCompat {
    private static Class clipData;
    private static Class clipDataItem;
    private static Class clipDescription;
    private static Method getClipDataFromIntent;
    private static Method getDescription;
    private static Method getItemCount;
    private static Method getLabel;
    private static Method getItemAt;
    private static Method getUri;

    private static boolean initSucceded;

    static {
        try {
            /*
            clipData = ClipData.class;
            getDescription = clipData.getMethod("getDescription", (Class[])null);
            getItemCount = clipData.getMethod("getItemCount", (Class[])null);
            getItemAt = clipData.getMethod("getItemAt", new Class[]{int.class});
            clipDescription = Class.forName("android.content.ClipDescription");
            getLabel = clipDescription.getMethod("getLabel", (Class[])null);

            clipDataItem = Class.forName("android.content.ClipData$Item");
            getUri = clipDataItem.getMethod("getUri", (Class[])null);
            */

            getClipDataFromIntent = Intent.class.getMethod("getClipData", (Class[])null);

            initSucceded = true;
        } catch (Exception e) {
            initSucceded = false;
        }
    }

    public static Uri getUriFromIntent(Intent i, String key) {
        boolean clipDataSucceeded = false;
        if (initSucceded) {
            try {
                ClipData clip = (ClipData) getClipDataFromIntent.invoke(i, null);

                if (clip != null) {
                    ClipDescription clipDescription = clip.getDescription();
                    CharSequence label = clipDescription.getLabel();
                    if (label.equals(key)) {
                        int itemCount = clip.getItemCount();
                        if (itemCount == 1) {
                            ClipData.Item clipItem = clip.getItemAt(0);
                            if (clipItem != null) {
                                return clipItem.getUri();
                            }
                        }
                    }
                }
                return null;

            } catch (Exception e) {
                // Fall through below to backup method if reflection fails
            }
        }

        return i.getParcelableExtra(key);
    }
}

public class ContentResolverCompat {
    public static boolean available;
    private static Class contentResolver;
    private static Method takePersistableUriPermission;

    static {
        try {
            contentResolver = ContentResolver.class;
            takePersistableUriPermission = contentResolver.getMethod("takePersistableUriPermission", new Class[]{Uri.class, int.class});

            available = true;
        } catch (Exception e) {
            available = false;
        }
    }

    public static void takePersistableUriPermission(ContentResolver resolver, Uri uri, int modeFlags) {
        if (available) {
            try {
                takePersistableUriPermission.invoke(resolver, new Object[]{uri, modeFlags});
            } catch (Exception e) {
                // Fail silently
            }
        }
    }
}

public class KeyGenParameterSpecCompat {
    private static Class builder;
    private static Constructor buildConst;
    private static Method builderBuild;
    private static Method setBlockModes;
    private static Method setUserAuthReq;
    private static Method setEncPad;

    private static boolean available;

    static {
        try {
            builder = Class.forName("android.security.keystore.KeyGenParameterSpec$Builder");
            buildConst = builder.getConstructor(String.class, int.class);
            builderBuild = builder.getMethod("build", (Class [])null);
            setBlockModes = builder.getMethod("setBlockModes", String[].class);
            setUserAuthReq = builder.getMethod("setUserAuthenticationRequired", new Class []{boolean.class});
            setEncPad = builder.getMethod("setEncryptionPaddings", String[].class);


            available = true;
        } catch (Exception e) {
            available = false;
        }
    }

    public static AlgorithmParameterSpec build(String keystoreAlias, int purpose, String blockMode,
                                        boolean userAuthReq, String encPadding) {

        if (!available) {
            return null;
        }

        try {
            Object inst = buildConst.newInstance(keystoreAlias, purpose);
            inst = setBlockModes.invoke(inst, new Object[] {new String[] {blockMode}});
            inst = setUserAuthReq.invoke(inst, userAuthReq);
            inst = setEncPad.invoke(inst, new Object[] {new String[] {encPadding}});

            return (AlgorithmParameterSpec) builderBuild.invoke(inst, null);

        } catch (Exception e) {
            return null;
        }
    }
}

public class KeyguardManagerCompat {
    private static Method isKeyguardSecure;

    private static boolean available;

    static {
        try {
            isKeyguardSecure = KeyguardManager.class.getMethod("isKeyguardSecure", (Class[]) null);

            available = true;
        } catch (Exception e) {
            available = false;
        }
    }

    public static boolean isKeyguardSecure(KeyguardManager inst) {
        if (!available) {
            return false;
        }

        try {
            return (boolean) isKeyguardSecure.invoke(inst, null);

        } catch (Exception e) {
            return false;
        }
    }
}

public final class PRNGFixes {

    private static final byte[] BUILD_FINGERPRINT_AND_DEVICE_SERIAL =
        getBuildFingerprintAndDeviceSerial();
    private static int sdkVersion = Build.VERSION.SDK_INT;

    /** Hidden constructor to prevent instantiation. */
    private PRNGFixes() {}

    /**
     * Applies all fixes.
     *
     * @throws SecurityException if a fix is needed but could not be applied.
     */
    public static void apply() {
    	try {
	    	if (supportedOnThisDevice()) {
		        applyOpenSSLFix();
		        installLinuxPRNGSecureRandom();
	    	}
    	} catch (Exception e) {
    		// Do nothing, do the best we can to implement the workaround
    	}
    }

    private static boolean supportedOnThisDevice() {
    	// Blacklist on samsung devices
    	if (StrUtil.indexOfIgnoreCase(Build.MANUFACTURER, "samsung", Locale.ENGLISH) >= 0) {
    		return false;
    	}

        if (sdkVersion > Build.VERSION_CODES.JELLY_BEAN_MR2) {
            return false;
        }

        if (onSELinuxEnforce()) {
        	return false;
        }

    	File urandom = new File("/dev/urandom");

    	// Test permissions
    	if ( !(urandom.canRead() && urandom.canWrite()) ) {
    		return false;
    	}


    	// Test actually writing to urandom
    	try {
	    	FileOutputStream fos = new FileOutputStream(urandom);
	    	fos.write(0);
    	} catch (Exception e) {
    		return false;
    	}

    	return true;

    }

    private static boolean onSELinuxEnforce() {
    	try {
	    	ProcessBuilder builder = new ProcessBuilder("getenforce");
	    	builder.redirectErrorStream(true);
	    	java.lang.Process process = builder.start();
	    	BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
	    	process.waitFor();

	    	String output = reader.readLine();

	    	if (output == null) {
	    		return false;
	    	}

	    	return output.toLowerCase(Locale.US).startsWith("enforcing");
    	} catch (Exception e) {
    		return false;
    	}
    }

    /**
     * Applies the fix for OpenSSL PRNG having low entropy. Does nothing if the
     * fix is not needed.
     *
     * @throws SecurityException if the fix is needed but could not be applied.
     */
    private static void applyOpenSSLFix() throws SecurityException {
        if ((sdkVersion < Build.VERSION_CODES.JELLY_BEAN)
                || (sdkVersion > Build.VERSION_CODES.JELLY_BEAN_MR2)) {
            // No need to apply the fix
            return;
        }

        try {
            // Mix in the device- and invocation-specific seed.
            Class.forName("org.apache.harmony.xnet.provider.jsse.NativeCrypto")
                    .getMethod("RAND_seed", byte[].class)
                    .invoke(null, generateSeed());

            // Mix output of Linux PRNG into OpenSSL's PRNG
            int bytesRead = (Integer) Class.forName(
                    "org.apache.harmony.xnet.provider.jsse.NativeCrypto")
                    .getMethod("RAND_load_file", String.class, long.class)
                    .invoke(null, "/dev/urandom", 1024);
            if (bytesRead != 1024) {
                throw new IOException(
                        "Unexpected number of bytes read from Linux PRNG: "
                                + bytesRead);
            }
        } catch (Exception e) {
            throw new SecurityException("Failed to seed OpenSSL PRNG", e);
        }
    }

    /**
     * Installs a Linux PRNG-backed {@code SecureRandom} implementation as the
     * default. Does nothing if the implementation is already the default or if
     * there is not need to install the implementation.
     *
     * @throws SecurityException if the fix is needed but could not be applied.
     */
    private static void installLinuxPRNGSecureRandom()
            throws SecurityException {
        if (sdkVersion > Build.VERSION_CODES.JELLY_BEAN_MR2) {
            // No need to apply the fix
            return;
        }

        // Install a Linux PRNG-based SecureRandom implementation as the
        // default, if not yet installed.
        Provider[] secureRandomProviders =
                Security.getProviders("SecureRandom.SHA1PRNG");
        if ((secureRandomProviders == null)
                || (secureRandomProviders.length < 1)
                || (!LinuxPRNGSecureRandomProvider.class.equals(
                        secureRandomProviders[0].getClass()))) {
            Security.insertProviderAt(new LinuxPRNGSecureRandomProvider(), 1);
        }

        // Assert that new SecureRandom() and
        // SecureRandom.getInstance("SHA1PRNG") return a SecureRandom backed
        // by the Linux PRNG-based SecureRandom implementation.
        SecureRandom rng1 = new SecureRandom();
        if (!LinuxPRNGSecureRandomProvider.class.equals(
                rng1.getProvider().getClass())) {
            throw new SecurityException(
                    "new SecureRandom() backed by wrong Provider: "
                            + rng1.getProvider().getClass());
        }

        SecureRandom rng2;
        try {
            rng2 = SecureRandom.getInstance("SHA1PRNG");
        } catch (NoSuchAlgorithmException e) {
            throw new SecurityException("SHA1PRNG not available", e);
        }
        if (!LinuxPRNGSecureRandomProvider.class.equals(
                rng2.getProvider().getClass())) {
            throw new SecurityException(
                    "SecureRandom.getInstance(\"SHA1PRNG\") backed by wrong"
                    + " Provider: " + rng2.getProvider().getClass());
        }
    }

    /**
     * {@code Provider} of {@code SecureRandom} engines which pass through
     * all requests to the Linux PRNG.
     */
    private static class LinuxPRNGSecureRandomProvider extends Provider {

        public LinuxPRNGSecureRandomProvider() {
            super("LinuxPRNG",
                    1.0,
                    "A Linux-specific random number provider that uses"
                        + " /dev/urandom");
            // Although /dev/urandom is not a SHA-1 PRNG, some apps
            // explicitly request a SHA1PRNG SecureRandom and we thus need to
            // prevent them from getting the default implementation whose output
            // may have low entropy.
            put("SecureRandom.SHA1PRNG", LinuxPRNGSecureRandom.class.getName());
            put("SecureRandom.SHA1PRNG ImplementedIn", "Software");
        }
    }

    /**
     * {@link SecureRandomSpi} which passes all requests to the Linux PRNG
     * ({@code /dev/urandom}).
     */
    public static class LinuxPRNGSecureRandom extends SecureRandomSpi {

        /*
         * IMPLEMENTATION NOTE: Requests to generate bytes and to mix in a seed
         * are passed through to the Linux PRNG (/dev/urandom). Instances of
         * this class seed themselves by mixing in the current time, PID, UID,
         * build fingerprint, and hardware serial number (where available) into
         * Linux PRNG.
         *
         * Concurrency: Read requests to the underlying Linux PRNG are
         * serialized (on sLock) to ensure that multiple threads do not get
         * duplicated PRNG output.
         */

        private static final File URANDOM_FILE = new File("/dev/urandom");


        private static final Object sLock = new Object();

        /**
         * Input stream for reading from Linux PRNG or {@code null} if not yet
         * opened.
         *
         * @GuardedBy("sLock")
         */
        private static DataInputStream sUrandomIn;

        /**
         * Output stream for writing to Linux PRNG or {@code null} if not yet
         * opened.
         *
         * @GuardedBy("sLock")
         */
        private static OutputStream sUrandomOut;

        /**
         * Whether this engine instance has been seeded. This is needed because
         * each instance needs to seed itself if the client does not explicitly
         * seed it.
         */
        private boolean mSeeded;

        @Override
        protected void engineSetSeed(byte[] bytes) {
            try {
                OutputStream out;
                synchronized (sLock) {
                    out = getUrandomOutputStream();
                }
                out.write(bytes);
                out.flush();
                mSeeded = true;
            } catch (IOException e) {
                throw new SecurityException(
                        "Failed to mix seed into " + URANDOM_FILE, e);
            }
        }

        @Override
        protected void engineNextBytes(byte[] bytes) {
            if (!mSeeded) {
                // Mix in the device- and invocation-specific seed.
                engineSetSeed(generateSeed());
            }

            try {
                DataInputStream in;
                synchronized (sLock) {
                    in = getUrandomInputStream();
                }
                synchronized (in) {
                    in.readFully(bytes);
                }
            } catch (IOException e) {
                throw new SecurityException(
                        "Failed to read from " + URANDOM_FILE, e);
            }
        }

        @Override
        protected byte[] engineGenerateSeed(int size) {
            byte[] seed = new byte[size];
            engineNextBytes(seed);
            return seed;
        }

        private DataInputStream getUrandomInputStream() {
            synchronized (sLock) {
                if (sUrandomIn == null) {
                    // NOTE: Consider inserting a BufferedInputStream between
                    // DataInputStream and FileInputStream if you need higher
                    // PRNG output performance and can live with future PRNG
                    // output being pulled into this process prematurely.
                    try {
                        sUrandomIn = new DataInputStream(
                                new FileInputStream(URANDOM_FILE));
                    } catch (IOException e) {
                        throw new SecurityException("Failed to open "
                                + URANDOM_FILE + " for reading", e);
                    }
                }
                return sUrandomIn;
            }
        }

        private OutputStream getUrandomOutputStream() {
            synchronized (sLock) {
                if (sUrandomOut == null) {
                    try {
                        sUrandomOut = new FileOutputStream(URANDOM_FILE);
                    } catch (IOException e) {
                        throw new SecurityException("Failed to open "
                                + URANDOM_FILE + " for writing", e);
                    }
                }
                return sUrandomOut;
            }
        }
    }

    /**
     * Generates a device- and invocation-specific seed to be mixed into the
     * Linux PRNG.
     */
    private static byte[] generateSeed() {
        try {
            ByteArrayOutputStream seedBuffer = new ByteArrayOutputStream();
            DataOutputStream seedBufferOut =
                    new DataOutputStream(seedBuffer);
            seedBufferOut.writeLong(System.currentTimeMillis());
            seedBufferOut.writeLong(System.nanoTime());
            seedBufferOut.writeInt(Process.myPid());
            seedBufferOut.writeInt(Process.myUid());
            seedBufferOut.write(BUILD_FINGERPRINT_AND_DEVICE_SERIAL);
            seedBufferOut.close();
            return seedBuffer.toByteArray();
        } catch (IOException e) {
            throw new SecurityException("Failed to generate seed", e);
        }
    }

    /**
     * Gets the hardware serial number of this device.
     *
     * @return serial number or {@code null} if not available.
     */
    private static String getDeviceSerialNumber() {
        // We're using the Reflection API because Build.SERIAL is only available
        // since API Level 9 (Gingerbread, Android 2.3).
        try {
            return (String) Build.class.getField("SERIAL").get(null);
        } catch (Exception ignored) {
            return null;
        }
    }

    private static byte[] getBuildFingerprintAndDeviceSerial() {
        StringBuilder result = new StringBuilder();
        String fingerprint = Build.FINGERPRINT;
        if (fingerprint != null) {
            result.append(fingerprint);
        }
        String serial = getDeviceSerialNumber();
        if (serial != null) {
            result.append(serial);
        }
        try {
            return result.toString().getBytes("UTF-8");
        } catch (UnsupportedEncodingException e) {
            throw new RuntimeException("UTF-8 encoding not supported");
        }
    }
}

