public class PwDate implements Cloneable {

	private static final int DATE_SIZE = 5; 

	private boolean cDateBuilt = false;
	private boolean jDateBuilt = false;

	private Date jDate;
	private byte[] cDate;

	public PwDate(byte[] buf, int offset) {
		cDate = new byte[DATE_SIZE];
		System.arraycopy(buf, offset, cDate, 0, DATE_SIZE);
		cDateBuilt = true;
	}

	public PwDate(Date date) {
		jDate = date;
		jDateBuilt = true;
	}

	public PwDate(long millis) {
		jDate = new Date(millis);
		jDateBuilt = true;
	}

	private PwDate() {

	}

	@Override
	public Object clone() {
		PwDate copy = new PwDate();

		if ( cDateBuilt ) {
			byte[] newC = new byte[DATE_SIZE];
			System.arraycopy(cDate, 0, newC, 0, DATE_SIZE);
			copy.cDate = newC;
			copy.cDateBuilt = true;
		}

		if ( jDateBuilt ) {
			copy.jDate = (Date) jDate.clone();
			copy.jDateBuilt = true;
		}

		return copy;
	}



	public Date getJDate() {
		if ( ! jDateBuilt ) {
			jDate = readTime(cDate, 0, App.getCalendar());
			jDateBuilt = true;
		}

		return jDate;
	}

	public byte[] getCDate() {
		if ( ! cDateBuilt ) {
			cDate = writeTime(jDate, App.getCalendar());
			cDateBuilt = true;
		}

		return cDate;
	}


	/**
	 * Unpack date from 5 byte format. The five bytes at 'offset' are unpacked
	 * to a java.util.Date instance.
	 */
	public static Date readTime(byte[] buf, int offset, Calendar time) {
		int dw1 = Types.readUByte(buf, offset);
		int dw2 = Types.readUByte(buf, offset + 1);
		int dw3 = Types.readUByte(buf, offset + 2);
		int dw4 = Types.readUByte(buf, offset + 3);
		int dw5 = Types.readUByte(buf, offset + 4);

		// Unpack 5 byte structure to date and time
		int year = (dw1 << 6) | (dw2 >> 2);
		int month = ((dw2 & 0x00000003) << 2) | (dw3 >> 6);

		int day = (dw3 >> 1) & 0x0000001F;
		int hour = ((dw3 & 0x00000001) << 4) | (dw4 >> 4);
		int minute = ((dw4 & 0x0000000F) << 2) | (dw5 >> 6);
		int second = dw5 & 0x0000003F;

		if (time == null) {
			time = Calendar.getInstance();
		}
		// File format is a 1 based month, java Calendar uses a zero based month
		// File format is a 1 based day, java Calendar uses a 1 based day
		time.set(year, month - 1, day, hour, minute, second);

		return time.getTime();

	}

	public static byte[] writeTime(Date date) {
		return writeTime(date, null);
	}

	public static byte[] writeTime(Date date, Calendar cal) {
		if (date == null) {
			return null;
		}

		byte[] buf = new byte[5];
		if (cal == null) {
			cal = Calendar.getInstance();
		}
		cal.setTime(date);

		int year = cal.get(Calendar.YEAR);
		// File format is a 1 based month, java Calendar uses a zero based month
		int month = cal.get(Calendar.MONTH) + 1;
		// File format is a 0 based day, java Calendar uses a 1 based day
		int day = cal.get(Calendar.DAY_OF_MONTH) - 1;
		int hour = cal.get(Calendar.HOUR_OF_DAY);
		int minute = cal.get(Calendar.MINUTE);
		int second = cal.get(Calendar.SECOND);

		buf[0] = Types.writeUByte(((year >> 6) & 0x0000003F));
		buf[1] = Types.writeUByte(((year & 0x0000003F) << 2)
				| ((month >> 2) & 0x00000003));
		buf[2] = (byte) (((month & 0x00000003) << 6)
				| ((day & 0x0000001F) << 1) | ((hour >> 4) & 0x00000001));
		buf[3] = (byte) (((hour & 0x0000000F) << 4) | ((minute >> 2) & 0x0000000F));
		buf[4] = (byte) (((minute & 0x00000003) << 6) | (second & 0x0000003F));

		return buf;
	}

	@Override
	public boolean equals(Object o) {
		if ( this == o ) {
			return true;
		}
		if ( o == null ) {
			return false;
		}
		if ( getClass() != o.getClass() ) {
			return false;
		}

		PwDate date = (PwDate) o;
		if ( cDateBuilt && date.cDateBuilt ) {
			return Arrays.equals(cDate, date.cDate);
		} else if ( jDateBuilt && date.jDateBuilt ) {
			return IsSameDate(jDate, date.jDate);
		} else if ( cDateBuilt && date.jDateBuilt ) {
			return Arrays.equals(date.getCDate(), cDate);
		} else {
			return IsSameDate(date.getJDate(), jDate);
		}
	}

	public static boolean IsSameDate(Date d1, Date d2) {
		Calendar cal1 = Calendar.getInstance();
		cal1.setTime(d1);
		cal1.set(Calendar.MILLISECOND, 0);

		Calendar cal2 = Calendar.getInstance();
		cal2.setTime(d2);
		cal2.set(Calendar.MILLISECOND, 0);

		return (cal1.get(Calendar.YEAR) == cal2.get(Calendar.YEAR)) && 
		(cal1.get(Calendar.MONTH) == cal2.get(Calendar.MONTH)) &&
		(cal1.get(Calendar.DAY_OF_MONTH) == cal2.get(Calendar.DAY_OF_MONTH)) &&
		(cal1.get(Calendar.HOUR) == cal2.get(Calendar.HOUR)) &&
		(cal1.get(Calendar.MINUTE) == cal2.get(Calendar.MINUTE)) &&
		(cal1.get(Calendar.SECOND) == cal2.get(Calendar.SECOND));

	}

}

public abstract class PwDbHeader {

	public static final int PWM_DBSIG_1 = 0x9AA2D903;

	/** Seed that gets hashed with the userkey to form the final key */
	public byte masterSeed[];

	/** IV used for content encryption */
	public byte encryptionIV[] = new byte[16];

}

public class PwDbHeaderFactory {
	public static PwDbHeader getInstance(PwDatabase db) {
		if (db instanceof PwDatabaseV3) {
			return new PwDbHeaderV3();
		} else if (db instanceof PwDatabaseV4) {
			return new PwDbHeaderV4((PwDatabaseV4)db);
		} else {
			throw new RuntimeException("Not implemented.");

		}
	}
}

public class PwDbHeaderV3 extends PwDbHeader {

	// DB sig from KeePass 1.03
	public static final int DBSIG_2               = 0xB54BFB65;
	// DB sig from KeePass 1.03
	public static final int DBVER_DW              = 0x00030003;

	public static final int FLAG_SHA2             = 1;
	public static final int FLAG_RIJNDAEL         = 2;
	public static final int FLAG_ARCFOUR          = 4;
	public static final int FLAG_TWOFISH          = 8;

	/** Size of byte buffer needed to hold this struct. */
	public static final int BUF_SIZE        = 124;

	/** Used for the dwKeyEncRounds AES transformations */
	public byte transformSeed[] = new byte[32];

	public int              signature1;                  // = PWM_DBSIG_1
	public int              signature2;                  // = DBSIG_2
	public int              flags;
	public int              version;

	/** Number of groups in the database */
	public int              numGroups;
	/** Number of entries in the database */
	public int              numEntries;

	/** SHA-256 hash of the database, used for integrity check */
	public byte             contentsHash[] = new byte[32];

	public int              numKeyEncRounds;

	/**
	 * Parse given buf, as read from file.
	 * @param buf
	 * @throws IOException 
	 */
	public void loadFromFile( byte buf[], int offset ) throws IOException {
		signature1 = LEDataInputStream.readInt( buf, offset + 0 );
		signature2 = LEDataInputStream.readInt( buf, offset + 4 );
		flags = LEDataInputStream.readInt( buf, offset + 8 );
		version = LEDataInputStream.readInt( buf, offset + 12 );

		System.arraycopy( buf, offset + 16, masterSeed, 0, 16 );
		System.arraycopy( buf, offset + 32, encryptionIV, 0, 16 );

		numGroups = LEDataInputStream.readInt( buf, offset + 48 );
		numEntries = LEDataInputStream.readInt( buf, offset + 52 );

		System.arraycopy( buf, offset + 56, contentsHash, 0, 32 );

		System.arraycopy( buf, offset + 88, transformSeed, 0, 32 );
		numKeyEncRounds = LEDataInputStream.readInt( buf, offset + 120 );
		if ( numKeyEncRounds < 0 ) {
			// TODO: Really treat this like an unsigned integer
			throw new IOException("Does not support more than " + Integer.MAX_VALUE + " rounds.");
		}
	}

	public PwDbHeaderV3() {
		masterSeed = new byte[16];
	}

	public static boolean matchesHeader(int sig1, int sig2) {
		return (sig1 == PWM_DBSIG_1) && (sig2 == DBSIG_2);
	}


	/** Determine if the database version is compatible with this application
	 * @return true, if it is compatible
	 */
	public boolean matchesVersion() {
		return compatibleHeaders(version, DBVER_DW);
	}

	public static boolean compatibleHeaders(int one, int two) {
		return (one & 0xFFFFFF00) == (two & 0xFFFFFF00);
	}


}

public class PwDbHeaderV4 extends PwDbHeader {
	public static final int DBSIG_PRE2            = 0xB54BFB66;
    public static final int DBSIG_2               = 0xB54BFB67;

    private static final int FILE_VERSION_CRITICAL_MASK = 0xFFFF0000;
    public static final int FILE_VERSION_32_3 =           0x00030001;
	public static final int FILE_VERSION_32_4 =           0x00040000;
	public static final int FILE_VERSION_32 =             FILE_VERSION_32_4;

    public class PwDbHeaderV4Fields {
        public static final byte EndOfHeader = 0;
		public static final byte Comment = 1;
        public static final byte CipherID = 2;
        public static final byte CompressionFlags = 3;
        public static final byte MasterSeed = 4;
        public static final byte TransformSeed = 5;
        public static final byte TransformRounds = 6;
        public static final byte EncryptionIV = 7;
        public static final byte InnerRandomstreamKey = 8;
        public static final byte StreamStartBytes = 9;
        public static final byte InnerRandomStreamID = 10;
		public static final byte KdfParameters = 11;
		public static final byte PublicCustomData = 12;

    }

	public class PwDbInnerHeaderV4Fields {
		public static final byte EndOfHeader = 0;
		public static final byte InnerRandomStreamID = 1;
		public static final byte InnerRandomstreamKey = 2;
		public static final byte Binary = 3;
	}

	public class KdbxBinaryFlags {
		public static final byte None = 0;
		public static final byte Protected = 1;
	}

	public class HeaderAndHash {
		public byte[] header;
		public byte[] hash;

		public HeaderAndHash (byte[] header, byte[] hash) {
			this.header = header;
			this.hash = hash;
		}
	}

    private PwDatabaseV4 db;
    public byte[] innerRandomStreamKey = new byte[32];
    public byte[] streamStartBytes = new byte[32];
    public CrsAlgorithm innerRandomStream;
	public long version;

    public PwDbHeaderV4(PwDatabaseV4 d) {
    	db = d;
		version = d.getMinKdbxVersion();
    	masterSeed = new byte[32];
    }

	/** Assumes the input stream is at the beginning of the .kdbx file
	 * @param is
	 * @throws IOException 
	 * @throws InvalidDBVersionException 
	 */
	public HeaderAndHash loadFromFile(InputStream is) throws IOException, InvalidDBVersionException {
		MessageDigest md;
		try {
			md = MessageDigest.getInstance("SHA-256");
		} catch (NoSuchAlgorithmException e) {
			throw new IOException("No SHA-256 implementation");
		}

		ByteArrayOutputStream headerBOS = new ByteArrayOutputStream();
		CopyInputStream cis = new CopyInputStream(is, headerBOS);
		DigestInputStream dis = new DigestInputStream(cis, md);
		LEDataInputStream lis = new LEDataInputStream(dis);

		int sig1 = lis.readInt();
		int sig2 = lis.readInt();

		if ( ! matchesHeader(sig1, sig2) ) {
			throw new InvalidDBVersionException();
		}

		version = lis.readUInt();
		if ( ! validVersion(version) ) {
			throw new InvalidDBVersionException();
		}

		boolean done = false;
		while ( ! done ) {
			done = readHeaderField(lis);
		}

		byte[] hash = md.digest();
		return new HeaderAndHash(headerBOS.toByteArray(), hash);
	}

	private boolean readHeaderField(LEDataInputStream dis) throws IOException {
		byte fieldID = (byte) dis.read();

		int fieldSize;
		if (version < FILE_VERSION_32_4) {
			fieldSize = dis.readUShort();
		} else {
			fieldSize = dis.readInt();
		}

		byte[] fieldData = null;
		if ( fieldSize > 0 ) {
			fieldData = new byte[fieldSize];

			int readSize = dis.read(fieldData);
			if ( readSize != fieldSize ) {
				throw new IOException("Header ended early.");
			}
		}

		switch ( fieldID ) {
			case PwDbHeaderV4Fields.EndOfHeader:
				return true;

			case PwDbHeaderV4Fields.CipherID:
				setCipher(fieldData);
				break;

			case PwDbHeaderV4Fields.CompressionFlags:
				setCompressionFlags(fieldData);
				break;

			case PwDbHeaderV4Fields.MasterSeed:
				masterSeed = fieldData;
				break;

			case PwDbHeaderV4Fields.TransformSeed:
				assert(version < PwDbHeaderV4.FILE_VERSION_32_4);
				AesKdf kdfS = new AesKdf();
				if (!db.kdfParameters.kdfUUID.equals(kdfS.uuid)) {
					db.kdfParameters = kdfS.getDefaultParameters();
				}

				db.kdfParameters.setByteArray(AesKdf.ParamSeed, fieldData);
				break;

			case PwDbHeaderV4Fields.TransformRounds:
				assert(version < PwDbHeaderV4.FILE_VERSION_32_4);
				AesKdf kdfR = new AesKdf();
				if (!db.kdfParameters.kdfUUID.equals(kdfR.uuid)) {
					db.kdfParameters = kdfR.getDefaultParameters();
				}
				long rounds = LEDataInputStream.readLong(fieldData, 0);
				db.kdfParameters.setUInt64(AesKdf.ParamRounds, rounds);
				db.numKeyEncRounds = rounds;
				break;

			case PwDbHeaderV4Fields.EncryptionIV:
				encryptionIV = fieldData;
				break;

			case PwDbHeaderV4Fields.InnerRandomstreamKey:
			    assert(version < PwDbHeaderV4.FILE_VERSION_32_4);
				innerRandomStreamKey = fieldData;
				break;

			case PwDbHeaderV4Fields.StreamStartBytes:
				streamStartBytes = fieldData;
				break;

			case PwDbHeaderV4Fields.InnerRandomStreamID:
				assert(version < PwDbHeaderV4.FILE_VERSION_32_4);
				setRandomStreamID(fieldData);
				break;

			case PwDbHeaderV4Fields.KdfParameters:
				db.kdfParameters = KdfParameters.deserialize(fieldData);
				break;

			case PwDbHeaderV4Fields.PublicCustomData:
				db.publicCustomData =  KdfParameters.deserialize(fieldData);
				break;

			default:
				throw new IOException("Invalid header type: " + fieldID);

		}

		return false;
	}

	private void setCipher(byte[] pbId) throws IOException {
		if ( pbId == null || pbId.length != 16 ) {
			throw new IOException("Invalid cipher ID.");
		}

		db.dataCipher = Types.bytestoUUID(pbId);
	}

	private void setCompressionFlags(byte[] pbFlags) throws IOException {
		if ( pbFlags == null || pbFlags.length != 4 ) {
			throw new IOException("Invalid compression flags.");
		}

		int flag = LEDataInputStream.readInt(pbFlags, 0);
		if ( flag < 0 || flag >= PwCompressionAlgorithm.count ) {
			throw new IOException("Unrecognized compression flag.");
		}

		db.compressionAlgorithm = PwCompressionAlgorithm.fromId(flag);

	}

	private void setTransformRounds(byte[] rounds) throws IOException {
		if ( rounds == null || rounds.length != 8 ) {
			throw new IOException("Invalid rounds.");
		}

		long rnd = LEDataInputStream.readLong(rounds, 0);

		if ( rnd < 0 || rnd > Integer.MAX_VALUE ) {
			//TODO: Actually support really large numbers
			throw new IOException("Rounds higher than " + Integer.MAX_VALUE + " are not currently supported.");
		}

		db.numKeyEncRounds = rnd;

	}

	public void setRandomStreamID(byte[] streamID) throws IOException {
		if ( streamID == null || streamID.length != 4 ) {
			throw new IOException("Invalid stream id.");
		}

		int id = LEDataInputStream.readInt(streamID, 0);
		if ( id < 0 || id >= CrsAlgorithm.count ) {
			throw new IOException("Invalid stream id.");
		}

		innerRandomStream = CrsAlgorithm.fromId(id);
	}

	/** Determines if this is a supported version.
	 * 
	 *  A long is needed here to represent the unsigned int since we perform
	 *  arithmetic on it.
	 * @param version
	 * @return
	 */
	private boolean validVersion(long version) {

		return ! ((version & FILE_VERSION_CRITICAL_MASK) > (FILE_VERSION_32 & FILE_VERSION_CRITICAL_MASK));

	}

	public static boolean matchesHeader(int sig1, int sig2) {
		return (sig1 == PWM_DBSIG_1) && ( (sig2 == DBSIG_PRE2) || (sig2 == DBSIG_2) );
	}

	public static byte[] computeHeaderHmac(byte[] header, byte[] key) throws IOException{
		byte[] headerHmac;
		byte[] blockKey = HmacBlockStream.GetHmacKey64(key, Types.ULONG_MAX_VALUE);

		Mac hmac;
		try {
			hmac = Mac.getInstance("HmacSHA256");
			SecretKeySpec signingKey = new SecretKeySpec(blockKey, "HmacSHA256");
			hmac.init(signingKey);
		} catch (NoSuchAlgorithmException e) {
			throw new IOException("No HmacAlogirthm");
		} catch (InvalidKeyException e) {
			throw new IOException("Invalid Hmac Key");
		}

		return hmac.doFinal(header);
	}

	public byte[] getTransformSeed() {
		assert(version < FILE_VERSION_32_4);

		return db.kdfParameters.getByteArray(AesKdf.ParamSeed);
	}
}

public class PwDefsV4 {

	public static final String TITLE_FIELD = "Title";

    public static final String USERNAME_FIELD = "UserName";

    public static final String PASSWORD_FIELD = "Password";

    public static final String URL_FIELD = "URL";

    public static final String NOTES_FIELD = "Notes";

}

public class PwDeletedObject {
	public UUID uuid;
	private Date deletionTime;

	public PwDeletedObject() {

	}

	public PwDeletedObject(UUID u) {
		this(u, new Date());
	}

	public PwDeletedObject(UUID u, Date d) {
		uuid = u;
		deletionTime = d;
	}

	public Date getDeletionTime() {
		if ( deletionTime == null ) {
			return new Date(System.currentTimeMillis());
		}

		return deletionTime;
	}

	public void setDeletionTime(Date date) {
		deletionTime = date;
	}

	@Override
	public boolean equals(Object o) {
		if (this == o) {
			return true;
		}
		else if (o == null) {
			return false;
		}
		else if (!(o instanceof PwDeletedObject)) {
			return false;
		}

		PwDeletedObject rhs = (PwDeletedObject) o;

		return uuid.equals(rhs.uuid);
	}
}

public abstract class PwEntry implements Cloneable {

	protected static final String PMS_TAN_ENTRY = "<TAN>";

	public static class EntryNameComparator implements Comparator<PwEntry> {

		public int compare(PwEntry object1, PwEntry object2) {
			return object1.getTitle().compareToIgnoreCase(object2.getTitle());
		}

	}

	public PwIconStandard icon = PwIconStandard.FIRST;

	public PwEntry() {

	}

	public static PwEntry getInstance(PwGroup parent) {
		return PwEntry.getInstance(parent, true, true);
	}

	public static PwEntry getInstance(PwGroup parent, boolean initId, boolean initDates) {
		if (parent instanceof PwGroupV3) {
			return new PwEntryV3((PwGroupV3)parent);
		}
		else if (parent instanceof PwGroupV4) {
			return new PwEntryV4((PwGroupV4)parent);
		}
		else {
			throw new RuntimeException("Unknown PwGroup instance.");
		}
	}

	@Override
	public Object clone() {
		PwEntry newEntry;
		try {
			newEntry = (PwEntry) super.clone();
		} catch (CloneNotSupportedException e) {
			assert(false);
			throw new RuntimeException("Clone should be supported");
		}

		return newEntry;
	}

	public PwEntry clone(boolean deepStrings) {
		return (PwEntry) clone();
	}

	public void assign(PwEntry source) {
		icon = source.icon;
	}

	public abstract UUID getUUID();
	public abstract void setUUID(UUID u);

	public String getTitle() {
		return getTitle(false, null);
	}

	public String getUsername() {
		return getUsername(false, null);
	}

	public String getPassword() {
		return getPassword(false, null);
	}

	public String getUrl() {
		return getUrl(false, null);
	}

	public String getNotes() {
		return getNotes(false, null);
	}

	public abstract String getTitle(boolean decodeRef, PwDatabase db);
	public abstract String getUsername(boolean decodeRef, PwDatabase db);
	public abstract String getPassword(boolean decodeRef, PwDatabase db);
	public abstract String getUrl(boolean decodeRef, PwDatabase db);
	public abstract String getNotes(boolean decodeRef, PwDatabase db);
	public abstract Date getCreationTime();
	public abstract Date getLastModificationTime();
	public abstract Date getLastAccessTime();
	public abstract Date getExpiryTime();
	public abstract boolean expires();
	public abstract PwGroup getParent();

	public abstract void setTitle(String title, PwDatabase db);
	public abstract void setUsername(String user, PwDatabase db);
	public abstract void setPassword(String pass, PwDatabase db);
	public abstract void setUrl(String url, PwDatabase db);
	public abstract void setNotes(String notes, PwDatabase db);
	public abstract void setCreationTime(Date create);
	public abstract void setLastModificationTime(Date mod);
	public abstract void setLastAccessTime(Date access);
	public abstract void setExpires(boolean exp);
	public abstract void setExpiryTime(Date expires);


	public PwIcon getIcon() {
		return icon;
	}

	public boolean isTan() {
		return getTitle().equals(PMS_TAN_ENTRY) && (getUsername().length() > 0);
	}

	public String getDisplayTitle() {
		if ( isTan() ) {
			return PMS_TAN_ENTRY + " " + getUsername();
		} else {
			return getTitle();
		}
	}


	public boolean isMetaStream() {
		return false;
	}

	public EntrySearchStringIterator stringIterator() {
		return EntrySearchStringIterator.getInstance(this);
	}

	public void touch(boolean modified, boolean touchParents) {
		Date now = new Date();

		setLastAccessTime(now);

		if (modified) {
			setLastModificationTime(now);
		}

		PwGroup parent = getParent();
		if (touchParents && parent != null) {
			parent.touch(modified, true);
		}
	}

	public void touchLocation() { }

	public abstract void setParent(PwGroup parent);

	public boolean isSearchingEnabled() {
		return false;
	}

}

public class PwEntryV3 extends PwEntry {

	public static final Date NEVER_EXPIRE = getNeverExpire();
	public static final Date NEVER_EXPIRE_BUG = getNeverExpireBug();
	public static final Date DEFAULT_DATE = getDefaultDate();
	public static final PwDate PW_NEVER_EXPIRE = new PwDate(NEVER_EXPIRE);
	public static final PwDate PW_NEVER_EXPIRE_BUG = new PwDate(NEVER_EXPIRE_BUG);
	public static final PwDate DEFAULT_PWDATE = new PwDate(DEFAULT_DATE);


	/** Size of byte buffer needed to hold this struct. */
	public static final String PMS_ID_BINDESC = "bin-stream";
	public static final String PMS_ID_TITLE   = "Meta-Info";
	public static final String PMS_ID_USER    = "SYSTEM";
	public static final String PMS_ID_URL     = "$";



	public int              groupId;
	public 	String 			username;
	private byte[]          password;
	private byte[]          uuid;
	public String title;
	public String url;
	public String additional;


	public PwDate             tCreation;
	public PwDate             tLastMod;
	public PwDate             tLastAccess;
	public PwDate             tExpire;

	/** A string describing what is in pBinaryData */
	public String           binaryDesc;
	private byte[]          binaryData;

	private static Date getDefaultDate() {
		Calendar cal = Calendar.getInstance();
		cal.set(Calendar.YEAR, 2004);
		cal.set(Calendar.MONTH, Calendar.JANUARY);
		cal.set(Calendar.DAY_OF_MONTH, 1);
		cal.set(Calendar.HOUR, 0);
		cal.set(Calendar.MINUTE, 0);
		cal.set(Calendar.SECOND, 0);

		return cal.getTime();
	}

	private static Date getNeverExpire() {
		Calendar cal = Calendar.getInstance();
		cal.set(Calendar.YEAR, 2999);
		cal.set(Calendar.MONTH, 11);
		cal.set(Calendar.DAY_OF_MONTH, 28);
		cal.set(Calendar.HOUR, 23);
		cal.set(Calendar.MINUTE, 59);
		cal.set(Calendar.SECOND, 59);

		return cal.getTime();
	}

	/** This date was was accidentally being written
	 *  out when an entry was supposed to be marked as
	 *  expired. We'll use this to silently correct those
	 *  entries.
	 * @return
	 */
	private static Date getNeverExpireBug() {
		Calendar cal = Calendar.getInstance();
		cal.set(Calendar.YEAR, 2999);
		cal.set(Calendar.MONTH, 11);
		cal.set(Calendar.DAY_OF_MONTH, 30);
		cal.set(Calendar.HOUR, 23);
		cal.set(Calendar.MINUTE, 59);
		cal.set(Calendar.SECOND, 59);

		return cal.getTime();
	}

	public static boolean IsNever(Date date) {
		return PwDate.IsSameDate(NEVER_EXPIRE, date);
	}

	// for tree traversing
	public PwGroupV3 parent = null;


	public PwEntryV3() {
		super();
	}

	/*
	public PwEntryV3(PwEntryV3 source) {
		assign(source);
	}
	*/

	public PwEntryV3(PwGroupV3 p) {
		this(p, true, true);
	}

	public PwEntryV3(PwGroupV3 p, boolean initId, boolean initDates) {

		parent = p;
		groupId = ((PwGroupIdV3)parent.getId()).getId();

		if (initId) {
			Random random = new Random();
			uuid = new byte[16];
			random.nextBytes(uuid);
		}

		if (initDates) {
			Calendar cal = Calendar.getInstance();
			Date now = cal.getTime();
			tCreation = new PwDate(now);
			tLastAccess = new PwDate(now);
			tLastMod = new PwDate(now);
			tExpire = new PwDate(NEVER_EXPIRE);
		}

	}

	/**
	 * @return the actual password byte array.
	 */
	@Override
	public String getPassword(boolean decodeRef, PwDatabase db) {
		if (password == null) {
			return "";
		}

		return new String(password);
	}

	public byte[] getPasswordBytes() {
		return password;
	}


	/**
	 * fill byte array
	 */
	private static void fill(byte[] array, byte value)
	{
		for (int i=0; i<array.length; i++)
			array[i] = value;
		return;
	}

	/** Securely erase old password before copying new. */
	public void setPassword( byte[] buf, int offset, int len ) {
		if( password != null ) {
			fill( password, (byte)0 );
			password = null;
		}
		password = new byte[len];
		System.arraycopy( buf, offset, password, 0, len );
	}



	@Override
	public void setPassword(String pass, PwDatabase db) {
		byte[] password;
		try {
			password = pass.getBytes("UTF-8");
			setPassword(password, 0, password.length);
		} catch (UnsupportedEncodingException e) {
			assert false;
			password = pass.getBytes();
			setPassword(password, 0, password.length);
		}
	}

	/**
	 * @return the actual binaryData byte array.
	 */
	public byte[] getBinaryData() {
		return binaryData;
	}



	/** Securely erase old data before copying new. */
	public void setBinaryData( byte[] buf, int offset, int len ) {
		if( binaryData != null ) {
			fill( binaryData, (byte)0 );
			binaryData = null;
		}
		binaryData = new byte[len];
		System.arraycopy( buf, offset, binaryData, 0, len );
	}

	// Determine if this is a MetaStream entry
	@Override
	public boolean isMetaStream() {
		if ( binaryData == null ) return false;
		if ( additional == null || additional.length() == 0 ) return false;
		if ( ! binaryDesc.equals(PMS_ID_BINDESC) ) return false;
		if ( title == null ) return false;
		if ( ! title.equals(PMS_ID_TITLE) ) return false;
		if ( username == null ) return false;
		if ( ! username.equals(PMS_ID_USER) ) return false;
		if ( url == null ) return false;
		if ( ! url.equals(PMS_ID_URL)) return false;
		if ( !icon.isMetaStreamIcon() ) return false;

		return true;
	}


	@Override
	public void assign(PwEntry source) {

		if ( ! (source instanceof PwEntryV3) ) {
			throw new RuntimeException("DB version mix");
		}

		super.assign(source);

		PwEntryV3 src = (PwEntryV3) source;
		assign(src);

	}

	private void assign(PwEntryV3 source) {
		title = source.title;
		url = source.url;
		groupId = source.groupId;
		username = source.username;
		additional = source.additional;
		uuid = source.uuid;

		int passLen = source.password.length;
		password = new byte[passLen]; 
		System.arraycopy(source.password, 0, password, 0, passLen);

		tCreation = (PwDate) source.tCreation.clone();
		tLastMod = (PwDate) source.tLastMod.clone();
		tLastAccess = (PwDate) source.tLastAccess.clone();
		tExpire = (PwDate) source.tExpire.clone();

		binaryDesc = source.binaryDesc;

		if ( source.binaryData != null ) {
			int descLen = source.binaryData.length;
			binaryData = new byte[descLen]; 
			System.arraycopy(source.binaryData, 0, binaryData, 0, descLen);
		}

		parent = source.parent;

	}

	@Override
	public Object clone() {
		PwEntryV3 newEntry = (PwEntryV3) super.clone();

		if (password != null) {
			int passLen = password.length;
			password = new byte[passLen]; 
			System.arraycopy(password, 0, newEntry.password, 0, passLen);
		}

		newEntry.tCreation = (PwDate) tCreation.clone();
		newEntry.tLastMod = (PwDate) tLastMod.clone();
		newEntry.tLastAccess = (PwDate) tLastAccess.clone();
		newEntry.tExpire = (PwDate) tExpire.clone();

		newEntry.binaryDesc = binaryDesc;

		if ( binaryData != null ) {
			int descLen = binaryData.length;
			newEntry.binaryData = new byte[descLen]; 
			System.arraycopy(binaryData, 0, newEntry.binaryData, 0, descLen);
		}

		newEntry.parent = parent;


		return newEntry;
	}

	@Override
	public Date getLastAccessTime() {
		return tLastAccess.getJDate();
	}

	@Override
	public Date getCreationTime() {
		return tCreation.getJDate();
	}

	@Override
	public Date getExpiryTime() {
		return tExpire.getJDate();
	}

	@Override
	public Date getLastModificationTime() {
		return tLastMod.getJDate();
	}

	@Override
	public void setCreationTime(Date create) {
		tCreation = new PwDate(create);

	}

	@Override
	public void setLastModificationTime(Date mod) {
		tLastMod = new PwDate(mod);

	}

	@Override
	public void setLastAccessTime(Date access) {
		tLastAccess = new PwDate(access);

	}

	@Override
	public void setExpires(boolean expires) {
		if (!expires) {
			tExpire = PW_NEVER_EXPIRE;
		}
	}

	@Override
	public void setExpiryTime(Date expires) {
		tExpire = new PwDate(expires);
	}

	@Override
	public PwGroupV3 getParent() {
		return parent;
	}

	@Override
	public UUID getUUID() {
		return Types.bytestoUUID(uuid);
	}

	@Override
	public void setUUID(UUID u) {
		uuid = Types.UUIDtoBytes(u);
	}

	@Override
	public String getUsername(boolean decodeRef, PwDatabase db) {
		if (username == null) {
			return "";
		}

		return username;
	}

	@Override
	public void setUsername(String user, PwDatabase db) {
		username = user;
	}

	@Override
	public String getTitle(boolean decodeRef, PwDatabase db) {
        return title;
	}

	@Override
	public void setTitle(String title, PwDatabase db) {
		this.title = title;
	}

	@Override
	public String getNotes(boolean decodeRef, PwDatabase db) {
		return additional;
	}

	@Override
	public void setNotes(String notes, PwDatabase db) {
		additional = notes;
	}

	@Override
	public String getUrl(boolean decodeRef, PwDatabase db) {
		return url;
	}

	@Override
	public void setUrl(String url, PwDatabase db) {
		this.url = url;
	}

	@Override
	public boolean expires() {
		return ! IsNever(tExpire.getJDate());
	}

	public void populateBlankFields(PwDatabaseV3 db) {
		if (icon == null) {
			icon = db.iconFactory.getIcon(1);
		}

		if (username == null) {
			username = "";
		}

		if (password == null) {
			password = new byte[0];
		}

		if (uuid == null) {
			uuid = Types.UUIDtoBytes(UUID.randomUUID());
		}

		if (title == null) {
			title = "";
		}

		if (url == null) {
			url = "";
		}

		if (additional == null) {
			additional = "";
		}

		if (tCreation == null) {
			tCreation = DEFAULT_PWDATE;
		}

		if (tLastMod == null) {
			tLastMod = DEFAULT_PWDATE;
		}

		if (tLastAccess == null) {
			tLastAccess = DEFAULT_PWDATE;
		}

		if (tExpire == null) {
			tExpire = PW_NEVER_EXPIRE;
		}

		if (binaryDesc == null) {
			binaryDesc = "";
		}

		if (binaryData == null) {
			binaryData = new byte[0];
		}
	}

	@Override
	public void setParent(PwGroup parent) {
		this.parent = (PwGroupV3) parent;
	}
}

public class PwEntryV4 extends PwEntry implements ITimeLogger {
	public static final String STR_TITLE = "Title";
	public static final String STR_USERNAME = "UserName";
	public static final String STR_PASSWORD = "Password";
	public static final String STR_URL = "URL";
	public static final String STR_NOTES = "Notes";

	public PwGroupV4 parent;
	public UUID uuid = PwDatabaseV4.UUID_ZERO;
	public HashMap<String, ProtectedString> strings = new HashMap<String, ProtectedString>();
	public HashMap<String, ProtectedBinary> binaries = new HashMap<String, ProtectedBinary>();
	public PwIconCustom customIcon = PwIconCustom.ZERO;
	public String foregroundColor = "";
	public String backgroupColor = "";
	public String overrideURL = "";
	public AutoType autoType = new AutoType();
	public ArrayList<PwEntryV4> history = new ArrayList<PwEntryV4>();

	private Date parentGroupLastMod = PwDatabaseV4.DEFAULT_NOW;
	private Date creation = PwDatabaseV4.DEFAULT_NOW;
	private Date lastMod = PwDatabaseV4.DEFAULT_NOW;
	private Date lastAccess = PwDatabaseV4.DEFAULT_NOW;
	private Date expireDate = PwDatabaseV4.DEFAULT_NOW;
	private boolean expires = false;
	private long usageCount = 0;
	public String url = "";
	public String additional = "";
	public String tags = "";
	public Map<String, String> customData = new HashMap<String, String>();

	public class AutoType implements Cloneable {
		private static final long OBF_OPT_NONE = 0;

		public boolean enabled = true;
		public long obfuscationOptions = OBF_OPT_NONE;
		public String defaultSequence = "";

		private HashMap<String, String> windowSeqPairs = new HashMap<String, String>();

		@SuppressWarnings("unchecked")
		public Object clone() {
			AutoType auto;
			try {
				auto = (AutoType) super.clone();
			} 
			catch (CloneNotSupportedException e) {
				assert(false);
				throw new RuntimeException(e);
			}

			auto.windowSeqPairs = (HashMap<String, String>) windowSeqPairs.clone();

			return auto;

		}

		public void put(String key, String value) {
			windowSeqPairs.put(key, value);
		}

		public Set<Entry<String, String>> entrySet() {
			return windowSeqPairs.entrySet();
		}

	}

	public PwEntryV4() {

	}

	public PwEntryV4(PwGroupV4 p) {
		this(p, true, true);
	}

	public PwEntryV4(PwGroupV4 p, boolean initId, boolean initDates) {
		parent = p;

		if (initId) {
			uuid = UUID.randomUUID();
		}

		if (initDates) {
			Calendar cal = Calendar.getInstance();
			Date now = cal.getTime();
			creation = now;
			lastAccess = now;
			lastMod = now;
			expires = false;
		}
	}

	@SuppressWarnings("unchecked")
	@Override
	public PwEntry clone(boolean deepStrings) {
		PwEntryV4 entry = (PwEntryV4) super.clone(deepStrings);

		if (deepStrings) {
			entry.strings = (HashMap<String, ProtectedString>) strings.clone();
		}

		return entry;
	}

	@SuppressWarnings("unchecked")
	public PwEntryV4 cloneDeep() {
		PwEntryV4 entry = (PwEntryV4) clone(true);

		entry.binaries = (HashMap<String, ProtectedBinary>) binaries.clone();
		entry.history = (ArrayList<PwEntryV4>) history.clone();
		entry.autoType = (AutoType) autoType.clone();

		return entry;
	}

	@Override
	public void assign(PwEntry source) {

		if ( ! (source instanceof PwEntryV4) ) {
			throw new RuntimeException("DB version mix.");
		}

		super.assign(source);

		PwEntryV4 src = (PwEntryV4) source;
		assign(src);
	}

	private void assign(PwEntryV4 source) {
		parent = source.parent;
		uuid = source.uuid;
		strings = source.strings;
		binaries = source.binaries;
		customIcon = source.customIcon;
		foregroundColor = source.foregroundColor;
		backgroupColor = source.backgroupColor;
		overrideURL = source.overrideURL;
		autoType = source.autoType;
		history = source.history;
		parentGroupLastMod = source.parentGroupLastMod;
		creation = source.creation;
		lastMod = source.lastMod;
		lastAccess = source.lastAccess;
		expireDate = source.expireDate;
		expires = source.expires;
		usageCount = source.usageCount;
		url = source.url;
		additional = source.additional;

	}

	@Override
	public Object clone() {
		PwEntryV4 newEntry = (PwEntryV4) super.clone();

		return newEntry;
	}

	private String decodeRefKey(boolean decodeRef, String key, PwDatabase db) {
		String text = getString(key);
		if (decodeRef) {
			text = decodeRef(text, db);
		}

		return text;
	}

	private String decodeRef(String text, PwDatabase db) {
		if (db == null) { return text; }

		SprEngine spr = SprEngine.getInstance(db);
		return spr.compile(text, this, db);
	}

	@Override
	public String getUsername(boolean decodeRef, PwDatabase db) {
		return decodeRefKey(decodeRef, STR_USERNAME, db);
	}

	@Override
	public String getTitle(boolean decodeRef, PwDatabase db) {
		return decodeRefKey(decodeRef, STR_TITLE, db);
	}

	@Override
	public String getPassword(boolean decodeRef, PwDatabase db) {
		return decodeRefKey(decodeRef, STR_PASSWORD, db);
	}

	@Override
	public Date getLastAccessTime() {
		return lastAccess;
	}

	@Override
	public Date getCreationTime() {
		return creation;
	}

	@Override
	public Date getExpiryTime() {
		return expireDate;
	}

	@Override
	public Date getLastModificationTime() {
		return lastMod;
	}

	@Override
	public void setTitle(String title, PwDatabase d) {
		PwDatabaseV4 db = (PwDatabaseV4) d;
		boolean protect = db.memoryProtection.protectTitle;

		setString(STR_TITLE, title, protect);
	}

	@Override
	public void setUsername(String user, PwDatabase d) {
		PwDatabaseV4 db = (PwDatabaseV4) d;
		boolean protect = db.memoryProtection.protectUserName;

		setString(STR_USERNAME, user, protect);
	}

	@Override
	public void setPassword(String pass, PwDatabase d) {
		PwDatabaseV4 db = (PwDatabaseV4) d;
		boolean protect = db.memoryProtection.protectPassword;

		setString(STR_PASSWORD, pass, protect);
	}

	@Override
	public void setUrl(String url, PwDatabase d) {
		PwDatabaseV4 db = (PwDatabaseV4) d;
		boolean protect = db.memoryProtection.protectUrl;

		setString(STR_URL, url, protect);
	}

	@Override
	public void setNotes(String notes, PwDatabase d) {
		PwDatabaseV4 db = (PwDatabaseV4) d;
		boolean protect = db.memoryProtection.protectNotes;

		setString(STR_NOTES, notes, protect);
	}

	public void setCreationTime(Date date) {
		creation = date;
	}

	public void setExpiryTime(Date date) {
		expireDate = date;
	}

	public void setLastAccessTime(Date date) {
		lastAccess = date;
	}

	public void setLastModificationTime(Date date) {
		lastMod = date;
	}

	@Override
	public PwGroupV4 getParent() {
		return parent;
	}

	@Override
	public UUID getUUID() {
		return uuid;
	}


	@Override
	public void setUUID(UUID u) {
		uuid = u;
	}

	public String getString(String key) {
		ProtectedString value = strings.get(key);

		if ( value == null ) return new String("");

		return value.toString();
	}

	public void setString(String key, String value, boolean protect) {
		ProtectedString ps = new ProtectedString(protect, value);
		strings.put(key, ps);
	}

	public Date getLocationChanged() {
		return parentGroupLastMod;
	}

	public long getUsageCount() {
		return usageCount;
	}

	public void setLocationChanged(Date date) {
		parentGroupLastMod = date;
	}

	public void setUsageCount(long count) {
		usageCount = count;
	}

	@Override
	public boolean expires() {
		return expires;
	}

	public void setExpires(boolean exp) {
		expires = exp;
	}

	@Override
	public String getNotes(boolean decodeRef, PwDatabase db) {
		return decodeRefKey(decodeRef, STR_NOTES, db);
	}

	@Override
	public String getUrl(boolean decodeRef, PwDatabase db) {
		return decodeRefKey(decodeRef, STR_URL, db);
	}

	@Override
	public PwIcon getIcon() {
		if (customIcon == null || customIcon.uuid.equals(PwDatabaseV4.UUID_ZERO)) {
			return super.getIcon();
		} else {
			return customIcon;
		}

	}

	public static boolean IsStandardString(String key) {
		return key.equals(STR_TITLE) || key.equals(STR_USERNAME) 
		  || key.equals(STR_PASSWORD) || key.equals(STR_URL)
		  || key.equals(STR_NOTES);
	}

	public void createBackup(PwDatabaseV4 db) {
		PwEntryV4 copy = cloneDeep();
		copy.history = new ArrayList<PwEntryV4>();
		history.add(copy);

		if (db != null) maintainBackups(db);
	}

	private boolean maintainBackups(PwDatabaseV4 db) {
		boolean deleted = false;

		int maxItems = db.historyMaxItems;
		if (maxItems >= 0) {
			while (history.size() > maxItems) {
				removeOldestBackup();
				deleted = true;
			}
		}

		long maxSize = db.historyMaxSize;
		if (maxSize >= 0) {
			while(true) {
				long histSize = 0;
				for (PwEntryV4 entry : history) {
					histSize += entry.getSize();
				}

				if (histSize > maxSize) {
					removeOldestBackup();
					deleted = true;
				} else {
					break;
				}
			}
		}

		return deleted;
	}

	private void removeOldestBackup() {
		Date min = null;
		int index = -1;

		for (int i = 0; i < history.size(); i++) {
			PwEntry entry = history.get(i);
			Date lastMod = entry.getLastModificationTime();
			if ((min == null) || lastMod.before(min)) {
				index = i;
				min = lastMod;
			}
		}

		if (index != -1) {
			history.remove(index);
		}
	}


	private static final long FIXED_LENGTH_SIZE = 128; // Approximate fixed length size
	public long getSize() {
		long size = FIXED_LENGTH_SIZE;

		for (Entry<String, ProtectedString> pair : strings.entrySet()) {
			size += pair.getKey().length();
			size += pair.getValue().length();
		}

		for (Entry<String, ProtectedBinary> pair : binaries.entrySet()) {
			size += pair.getKey().length();
			size += pair.getValue().length();
		}

		size += autoType.defaultSequence.length();
		for (Entry<String, String> pair : autoType.entrySet()) {
			size += pair.getKey().length();
			size += pair.getValue().length();
		}

		for (PwEntryV4 entry : history) {
			size += entry.getSize();
		}

		size += overrideURL.length();
		size += tags.length();

		return size;
	}

	@Override
	public void touch(boolean modified, boolean touchParents) {
		super.touch(modified, touchParents);

		++usageCount;
	}

	@Override
	public void touchLocation() {
		parentGroupLastMod = new Date();
	}

	@Override
	public void setParent(PwGroup parent) {
		this.parent = (PwGroupV4) parent;
	}

	public boolean isSearchingEnabled() {
		if (parent != null) {
			return parent.isSearchEnabled();
		}

		return PwGroupV4.DEFAULT_SEARCHING_ENABLED;
	}
}

public abstract class PwGroup {
	public List<PwGroup> childGroups = new ArrayList<PwGroup>();
	public List<PwEntry> childEntries = new ArrayList<PwEntry>();
	public String name = "";
	public PwIconStandard icon;

	public abstract PwGroup getParent();
	public abstract void setParent(PwGroup parent);

	public abstract PwGroupId getId();
	public abstract void setId(PwGroupId id);

	public abstract String getName();

	public abstract Date getLastMod();

	public PwIcon getIcon() {
		return icon;
	}

	public void sortGroupsByName() {
		Collections.sort(childGroups, new GroupNameComparator());
	}

	public static class GroupNameComparator implements Comparator<PwGroup> {

		public int compare(PwGroup object1, PwGroup object2) {
			return object1.getName().compareToIgnoreCase(object2.getName());
		}

	}

	public abstract void setLastAccessTime(Date date);

	public abstract void setLastModificationTime(Date date);

	public void sortEntriesByName() {
		Collections.sort(childEntries, new PwEntry.EntryNameComparator());
	}

	public void initNewGroup(String nm, PwGroupId newId) {
		setId(newId);
		name = nm;
	}

	public boolean isContainedIn(PwGroup container) {
		PwGroup cur = this;
		while (cur != null) {
			if (cur == container) {
				return true;
			}

			cur = cur.getParent();
		}

		return false;
	}

	public void touch(boolean modified, boolean touchParents) {
		Date now = new Date();

		setLastAccessTime(now);

		if (modified) {
			setLastModificationTime(now);
		}

		PwGroup parent = getParent();
		if (touchParents && parent != null) {
			parent.touch(modified, true);
		}
	}


	public void searchEntries(SearchParameters sp, List<PwEntry> listStorage) {
		if (sp == null)  { return; }
		if (listStorage == null) { return; }

		List<String> terms = StrUtil.splitSearchTerms(sp.searchString);
		if (terms.size() <= 1 || sp.regularExpression) {
			searchEntriesSingle(sp, listStorage);
			return;
		}

		// Search longest term first
		Comparator<String> stringLengthComparator = new Comparator<String>() {

			@Override
			public int compare(String lhs, String rhs) {
				return lhs.length() - rhs.length();
			}

		};
		Collections.sort(terms, stringLengthComparator);

		String fullSearch = sp.searchString;
		List<PwEntry> pg = this.childEntries;
		for (int i = 0; i < terms.size(); i ++) {
			List<PwEntry> pgNew = new ArrayList<PwEntry>();

			sp.searchString = terms.get(i);

			boolean negate = false;
			if (sp.searchString.startsWith("-")) {
				sp.searchString = sp.searchString.substring(1);
				negate = sp.searchString.length() > 0;
			}

			if (!searchEntriesSingle(sp, pgNew)) {
				pg = null;
				break;
			}

			List<PwEntry> complement = new ArrayList<PwEntry>();
			if (negate) {
				for (PwEntry entry: pg) {
					if (!pgNew.contains(entry)) {
						complement.add(entry);
					}
				}

				pg = complement;
			}
			else {
				pg = pgNew;
			}
		}

		if (pg != null) {
			listStorage.addAll(pg);
		}
		sp.searchString = fullSearch;

	}

	private boolean searchEntriesSingle(SearchParameters spIn, List<PwEntry> listStorage) {
		SearchParameters sp = (SearchParameters) spIn.clone();

		EntryHandler<PwEntry> eh;
		if (sp.searchString.length() <= 0) {
			eh = new EntrySearchHandlerAll(sp, listStorage);
		} else {
			eh = EntrySearchHandler.getInstance(this, sp, listStorage);
		}

		if (!preOrderTraverseTree(null, eh)) { return false; }

		return true;
	}

	public boolean preOrderTraverseTree(GroupHandler<PwGroup> groupHandler, EntryHandler<PwEntry> entryHandler) {
		if (entryHandler != null) {
			for (PwEntry entry : childEntries) {
				if (!entryHandler.operate(entry)) return false;

			}
		}

		for (PwGroup group : childGroups) {

			if ((groupHandler != null) && !groupHandler.operate(group)) return false;

			group.preOrderTraverseTree(groupHandler, entryHandler);
		}


		return true;

	}
}

public class PwGroupIdV3 extends PwGroupId {

	private int id;

	public PwGroupIdV3(int i) {
		id = i;
	}

	@Override
	public boolean equals(Object compare) {
		if ( ! (compare instanceof PwGroupIdV3) ) {
			return false;
		}

		PwGroupIdV3 cmp = (PwGroupIdV3) compare;
		return id == cmp.id;
	}

	@Override
	public int hashCode() {
		Integer i = Integer.valueOf(id);
		return i.hashCode();
	}

	public int getId() {
		return id;
	}


}

public class PwGroupIdV4 extends PwGroupId {
	private UUID uuid;

	public PwGroupIdV4(UUID u) {
		uuid = u;
	}

	@Override
	public boolean equals(Object id) {
		if ( ! (id instanceof PwGroupIdV4) ) {
			return false;
		}

		PwGroupIdV4 v4 = (PwGroupIdV4) id;

		return uuid.equals(v4.uuid);
	}

	@Override
	public int hashCode() {
		return uuid.hashCode();
	}

	public UUID getId() {
		return uuid;
	}

}

public class PwGroupV3 extends PwGroup {
  public PwGroupV3() {
  }

	public String toString() {
		return name;
	}

	public static final Date NEVER_EXPIRE = PwEntryV3.NEVER_EXPIRE;

	/** Size of byte buffer needed to hold this struct. */
	public static final int BUF_SIZE = 124;

	// for tree traversing
	public PwGroupV3 parent = null;

	public int groupId;

	public PwDate tCreation;
	public PwDate tLastMod;
	public PwDate tLastAccess;
	public PwDate tExpire;

	public int level; // short

	/** Used by KeePass internally, don't use */
	public int flags;

	public void setGroups(List<PwGroup> groups) {
		childGroups = groups;
	}

	@Override
	public PwGroup getParent() {
		return parent;
	}

	@Override
	public PwGroupId getId() {
		return new PwGroupIdV3(groupId);
	}

	@Override
	public void setId(PwGroupId id) {
		PwGroupIdV3 id3 = (PwGroupIdV3) id;
		groupId = id3.getId();
	}

	@Override
	public String getName() {
		return name;
	}

	@Override
	public Date getLastMod() {
		return tLastMod.getJDate();
	}

	@Override
	public void setParent(PwGroup prt) {
		parent = (PwGroupV3) prt;
		level = parent.level + 1;

	}

	@Override
	public void initNewGroup(String nm, PwGroupId newId) {
		super.initNewGroup(nm, newId);

		Date now = Calendar.getInstance().getTime();
		tCreation = new PwDate(now);
		tLastAccess = new PwDate(now);
		tLastMod = new PwDate(now);
		tExpire = new PwDate(PwGroupV3.NEVER_EXPIRE);

	}

	public void populateBlankFields(PwDatabaseV3 db) {
		if (icon == null) {
			icon = db.iconFactory.getIcon(1);
		}

		if (name == null) {
			name = "";
		}

		if (tCreation == null) {
			tCreation = PwEntryV3.DEFAULT_PWDATE;
		}

		if (tLastMod == null) {
			tLastMod = PwEntryV3.DEFAULT_PWDATE;
		}

		if (tLastAccess == null) {
			tLastAccess = PwEntryV3.DEFAULT_PWDATE;
		}

		if (tExpire == null) {
			tExpire = PwEntryV3.DEFAULT_PWDATE;
		}
	}

	@Override
	public void setLastAccessTime(Date date) {
		tLastAccess = new PwDate(date);
	}

	@Override
	public void setLastModificationTime(Date date) {
		tLastMod = new PwDate(date);
	}

}

public class PwGroupV4 extends PwGroup implements ITimeLogger {

	//public static final int FOLDER_ICON = 48;
	public static final boolean DEFAULT_SEARCHING_ENABLED = true;

	public PwGroupV4 parent = null;
	public UUID uuid = PwDatabaseV4.UUID_ZERO;
	public String notes = "";
	public PwIconCustom customIcon = PwIconCustom.ZERO;
	public boolean isExpanded = true;
	public String defaultAutoTypeSequence = "";
	public Boolean enableAutoType = null;
	public Boolean enableSearching = null;
	public UUID lastTopVisibleEntry = PwDatabaseV4.UUID_ZERO;
	private Date parentGroupLastMod = PwDatabaseV4.DEFAULT_NOW;
	private Date creation = PwDatabaseV4.DEFAULT_NOW;
	private Date lastMod = PwDatabaseV4.DEFAULT_NOW;
	private Date lastAccess = PwDatabaseV4.DEFAULT_NOW;
	private Date expireDate = PwDatabaseV4.DEFAULT_NOW;
	private boolean expires = false;
	private long usageCount = 0;
	public Map<String, String> customData = new HashMap<String, String>();

	public PwGroupV4() {

	}

	public PwGroupV4(boolean createUUID, boolean setTimes, String name, PwIconStandard icon) {
		if (createUUID) {
			uuid = UUID.randomUUID();
		}

		if (setTimes) {
			creation = lastMod = lastAccess = new Date();
		}

		this.name = name;
		this.icon = icon;
	}

	public void AddGroup(PwGroupV4 subGroup, boolean takeOwnership) {
		AddGroup(subGroup, takeOwnership, false);
	}

	public void AddGroup(PwGroupV4 subGroup, boolean takeOwnership, boolean updateLocationChanged) {
		if ( subGroup == null ) throw new RuntimeException("subGroup");

		childGroups.add(subGroup);

		if ( takeOwnership ) subGroup.parent = this;

		if ( updateLocationChanged ) subGroup.parentGroupLastMod = new Date(System.currentTimeMillis());

	}

	public void AddEntry(PwEntryV4 pe, boolean takeOwnership) {
		AddEntry(pe, takeOwnership, false);
	}

	public void AddEntry(PwEntryV4 pe, boolean takeOwnership, boolean updateLocationChanged) {
		assert(pe != null);

		childEntries.add(pe);

		if ( takeOwnership ) pe.parent = this;

		if ( updateLocationChanged ) pe.setLocationChanged(new Date(System.currentTimeMillis()));
	}

	@Override
	public PwGroup getParent() {
		return parent;
	}

	public void buildChildGroupsRecursive(List<PwGroup> list) {
		list.add(this);

		for ( int i = 0; i < childGroups.size(); i++) {
			PwGroupV4 child = (PwGroupV4) childGroups.get(i);
			child.buildChildGroupsRecursive(list);

		}
	}

	public void buildChildEntriesRecursive(List<PwEntry> list) {
		for ( int i = 0; i < childEntries.size(); i++ ) {
			list.add(childEntries.get(i));
		}

		for ( int i = 0; i < childGroups.size(); i++ ) {
			PwGroupV4 child = (PwGroupV4) childGroups.get(i);
			child.buildChildEntriesRecursive(list);
		}

	}

	@Override
	public PwGroupId getId() {
		return new PwGroupIdV4(uuid);
	}

	@Override
	public void setId(PwGroupId id) {
		PwGroupIdV4 id4 = (PwGroupIdV4) id;
		uuid = id4.getId();
	}

	@Override
	public String getName() {
		return name;
	}

	@Override
	public Date getLastMod() {
		return parentGroupLastMod;
	}

	public Date getCreationTime() {
		return creation;
	}

	public Date getExpiryTime() {
		return expireDate;
	}

	public Date getLastAccessTime() {
		return lastAccess;
	}

	public Date getLastModificationTime() {
		return lastMod;
	}

	public Date getLocationChanged() {
		return parentGroupLastMod;
	}

	public long getUsageCount() {
		return usageCount;
	}

	public void setCreationTime(Date date) {
		creation = date;

	}

	public void setExpiryTime(Date date) {
		expireDate = date;
	}

	@Override
	public void setLastAccessTime(Date date) {
		lastAccess = date;
	}

	@Override
	public void setLastModificationTime(Date date) {
		lastMod = date;
	}

	public void setLocationChanged(Date date) {
		parentGroupLastMod = date;
	}

	public void setUsageCount(long count) {
		usageCount = count;
	}

	public boolean expires() {
		return expires;
	}

	public void setExpires(boolean exp) {
		expires = exp;
	}

	@Override
	public void setParent(PwGroup prt) {
		parent = (PwGroupV4) prt;

	}

	@Override
	public PwIcon getIcon() {
		if (customIcon == null || customIcon.uuid.equals(PwDatabaseV4.UUID_ZERO)) {
			return super.getIcon();
		} else {
			return customIcon;
		}
	}

	@Override
	public void initNewGroup(String nm, PwGroupId newId) {
		super.initNewGroup(nm, newId);

		lastAccess = lastMod = creation = parentGroupLastMod = new Date();
	}

	public boolean isSearchEnabled() {
		PwGroupV4 group = this;
		while (group != null) {
			Boolean search = group.enableSearching;
			if (search != null) {
				return search;
			}

			group = group.parent;
		}

		// If we get to the root group and its null, default to true
		return true;
	}

}

public abstract class PwIcon {

	public boolean isMetaStreamIcon() {
		return false;
	}

	public void writeBytes() {
	}

}

public class PwIconCustom extends PwIcon {
	public static final PwIconCustom ZERO = new PwIconCustom(PwDatabaseV4.UUID_ZERO, new byte[0]);

	public final UUID uuid;
	public byte[] imageData;

	public PwIconCustom(UUID u, byte[] data) {
		uuid = u;
		imageData = data;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((uuid == null) ? 0 : uuid.hashCode());
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		PwIconCustom other = (PwIconCustom) obj;
		if (uuid == null) {
			if (other.uuid != null)
				return false;
		} else if (!uuid.equals(other.uuid))
			return false;
		return true;
	}
}

public class PwIconFactory {
	/** customIconMap
	 *  Cache for icon drawable. 
	 *  Keys: Integer, Values: PwIconStandard
	 */
	private ReferenceMap cache = new ReferenceMap(AbstractReferenceMap.HARD, AbstractReferenceMap.WEAK);

	/** standardIconMap
	 *  Cache for icon drawable. 
	 *  Keys: UUID, Values: PwIconCustom
	 */
	private ReferenceMap customCache = new ReferenceMap(AbstractReferenceMap.HARD, AbstractReferenceMap.WEAK);

	public PwIconStandard getIcon(int iconId) {
		PwIconStandard icon = (PwIconStandard) cache.get(iconId);

		if (icon == null) {
			if (iconId == 1) {
				icon = PwIconStandard.FIRST;
			}
			else {
				icon = new PwIconStandard(iconId);
			}
			cache.put(iconId, icon);
		}

		return icon;
	}

	public PwIconCustom getIcon(UUID iconUuid) {
		PwIconCustom icon = (PwIconCustom) customCache.get(iconUuid);

		if (icon == null) {
			icon = new PwIconCustom(iconUuid, null);
			customCache.put(iconUuid, icon);
		}

		return icon;
	}

	public PwIconCustom getIcon(UUID iconUuid, byte[] data) {
		PwIconCustom icon = (PwIconCustom) customCache.get(iconUuid);

		if (icon == null) {
			icon = new PwIconCustom(iconUuid, data);
			customCache.put(iconUuid, icon);
		} else {
			icon.imageData = data;
		}

		return icon;
	}

	public void setIconData(UUID iconUuid, byte[] data) {
		getIcon(iconUuid, data);
	}

	public void put(PwIconCustom icon) {
		customCache.put(icon.uuid, icon);
	}

}

public class PwIconStandard extends PwIcon {
	public final int iconId;

	public static PwIconStandard FIRST = new PwIconStandard(1);

	public static final int TRASH_BIN = 43;
	public static final int FOLDER = 48;

	public PwIconStandard(int iconId) {
		this.iconId = iconId;
	}

	@Override
	public boolean isMetaStreamIcon() {
		return iconId == 0;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + iconId;
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		PwIconStandard other = (PwIconStandard) obj;
		if (iconId != other.iconId)
			return false;
		return true;
	}
}

public class SearchParameters implements Cloneable {
	public static final SearchParameters DEFAULT = new SearchParameters();

	public String searchString;

	public boolean regularExpression = false;
	public boolean searchInTitles = true;
	public boolean searchInUserNames = true;
	public boolean searchInPasswords = false;
	public boolean searchInUrls = true;
	public boolean searchInGroupNames = false;
	public boolean searchInNotes = true;
	public boolean ignoreCase = true;
	public boolean ignoreExpired = false;
	public boolean respectEntrySearchingDisabled = true;
	public boolean excludeExpired = false;

	@Override
	public Object clone() {
		try {
			return super.clone();
		} catch (CloneNotSupportedException e) {
			return null;
		}
	}

	public void setupNone() {
		searchInTitles = false;
		searchInUserNames = false;
		searchInPasswords = false;
		searchInUrls = false;
		searchInGroupNames = false;
		searchInNotes = false;
	}
}

public class SearchParametersFactory {
	public static SearchParameters getNone(PwDatabase db) {
		SearchParameters sp = getInstance(db);
		sp.setupNone();

		return sp;
	}

	public static SearchParameters getInstance(PwDatabase db) {
		if (db instanceof PwDatabase) {
			return new SearchParametersV4();
		}
		else {
			return new SearchParameters();
		}
	}

	public static SearchParameters getInstance(PwGroup group) {
		if (group instanceof PwGroupV4) {
			return new SearchParametersV4();
		}
		else {
			return new SearchParameters();
		}
	}
}

