	public boolean searchInTags = true;

	@Override
	public Object clone() {
		return super.clone();
	}

	@Override
	public void setupNone() {
		super.setupNone();
		searchInOther = false;
		searchInUUIDs = false;
		searchInTags = false;
	}
}

public class AddEntry extends RunnableOnFinish {
	protected Database mDb;
	private PwEntry mEntry;
	private Context ctx;

	public static AddEntry getInstance(Context ctx, Database db, PwEntry entry, OnFinish finish) {
		return new AddEntry(ctx, db, entry, finish);
	}

	protected AddEntry(Context ctx, Database db, PwEntry entry, OnFinish finish) {
		super(finish);

		mDb = db;
		mEntry = entry;
		this.ctx = ctx;

		mFinish = new AfterAdd(mFinish);
	}

	@Override
	public void run() {
		mDb.pm.addEntryTo(mEntry, mEntry.getParent());

		// Commit to disk
		SaveDB save = new SaveDB(ctx, mDb, mFinish);
		save.run();
	}

	private class AfterAdd extends OnFinish {

		public AfterAdd(OnFinish finish) {
			super(finish);
		}

		@Override
		public void run() {
			PwDatabase pm = mDb.pm;
			if ( mSuccess ) {

				PwGroup parent = mEntry.getParent();

				// Mark parent group dirty
				mDb.dirty.add(parent);

			} else {
				pm.removeEntryFrom(mEntry, mEntry.getParent());
			}

			super.run();
		}
	}


}

public class AddGroup extends RunnableOnFinish {
	protected Database mDb;
	private String mName;
	private int mIconID;
	private PwGroup mGroup;
	private PwGroup mParent;
	private Context ctx;
	protected boolean mDontSave;


	public static AddGroup getInstance(Context ctx, Database db, String name, int iconid, PwGroup parent, OnFinish finish, boolean dontSave) {
		return new AddGroup(ctx, db, name, iconid, parent, finish, dontSave);
	}


	private AddGroup(Context ctx, Database db, String name, int iconid, PwGroup parent, OnFinish finish, boolean dontSave) {
		super(finish);

		mDb = db;
		mName = name;
		mIconID = iconid;
		mParent = parent;
		mDontSave = dontSave;
		this.ctx = ctx;

		mFinish = new AfterAdd(mFinish);
	}

	@Override
	public void run() {
		PwDatabase pm = mDb.pm;

		// Generate new group
		mGroup = pm.createGroup();
		mGroup.initNewGroup(mName, pm.newGroupId());
		mGroup.icon = mDb.pm.iconFactory.getIcon(mIconID);
		pm.addGroupTo(mGroup, mParent);

		//mParent.sortGroupsByName();

		// Commit to disk
		SaveDB save = new SaveDB(ctx, mDb, mFinish, mDontSave);
		save.run();
	}

	private class AfterAdd extends OnFinish {

		public AfterAdd(OnFinish finish) {
			super(finish);
		}

		@Override
		public void run() {
			PwDatabase pm = mDb.pm;
			if ( mSuccess ) {
				// Mark parent group dirty
				mDb.dirty.add(mParent);
			} else {
				pm.removeGroupFrom(mGroup, mParent);
			}

			super.run();
		}

	}


}

public class CreateDB extends RunnableOnFinish {

	private final int DEFAULT_ENCRYPTION_ROUNDS = 300;

	private String mFilename;
	private boolean mDontSave;
	private Context ctx;

	public CreateDB(Context ctx, String filename, OnFinish finish, boolean dontSave) {
		super(finish);

		mFilename = filename;
		mDontSave = dontSave;
		this.ctx = ctx;
	}

	@Override
	public void run() {
		// Create new database record
		Database db = new Database();
		App.setDB(db);

		PwDatabase pm = PwDatabase.getNewDBInstance(mFilename);
		pm.initNew(mFilename);

		// Set Database state
		db.pm = pm;
		Uri.Builder b = new Uri.Builder();
		db.mUri = UriUtil.parseDefaultFile(mFilename);
		db.setLoaded();
		App.clearShutdown();

		// Commit changes
		SaveDB save = new SaveDB(ctx, db, mFinish, mDontSave);
		mFinish = null;
		save.run();
	}
}

public class DeleteEntry extends RunnableOnFinish {

	private Database mDb;
	private PwEntry mEntry;
	private boolean mDontSave;
	private Context ctx;

	public DeleteEntry(Context ctx, Database db, PwEntry entry, OnFinish finish) {
		this(ctx, db, entry, finish, false);
	}

	public DeleteEntry(Context ctx, Database db, PwEntry entry, OnFinish finish, boolean dontSave) {
		super(finish);

		mDb = db;
		mEntry = entry;
		mDontSave = dontSave;
		this.ctx = ctx;

	}

	@Override
	public void run() {
		PwDatabase pm = mDb.pm;
		PwGroup parent = mEntry.getParent();

		// Remove Entry from parent
		boolean recycle = pm.canRecycle(mEntry);
		if (recycle) {
			pm.recycle(mEntry);
		}
		else {
			pm.deleteEntry(mEntry);
		}

		// Save
		mFinish = new AfterDelete(mFinish, parent, mEntry, recycle);

		// Commit database
		SaveDB save = new SaveDB(ctx, mDb, mFinish, mDontSave);
		save.run();


	}

	private class AfterDelete extends OnFinish {

		private PwGroup mParent;
		private PwEntry mEntry;
		private boolean recycled;

		public AfterDelete(OnFinish finish, PwGroup parent, PwEntry entry, boolean r) {
			super(finish);

			mParent = parent;
			mEntry = entry;
			recycled = r;
		}

		@Override
		public void run() {
			PwDatabase pm = mDb.pm;
			if ( mSuccess ) {
				// Mark parent dirty
				if ( mParent != null ) {
					mDb.dirty.add(mParent);
				}

				if (recycled) {
					PwGroup recycleBin = pm.getRecycleBin();
					mDb.dirty.add(recycleBin);
					mDb.dirty.add(mDb.pm.rootGroup);
				}
			} else {
				if (recycled) {
					pm.undoRecycle(mEntry, mParent);
				}
				else {
					pm.undoDeleteEntry(mEntry, mParent);
				}
			}

			super.run();

		}

	}

}

public class DeleteGroup extends RunnableOnFinish {

	private Database mDb;
	private PwGroup mGroup;
	private GroupBaseActivity mAct;
	private boolean mDontSave;

	public DeleteGroup(Database db, PwGroup group, GroupBaseActivity act, OnFinish finish) {
		super(finish);
		setMembers(db, group, act, false);
	}

	public DeleteGroup(Database db, PwGroup group, GroupBaseActivity act, OnFinish finish, boolean dontSave) {
		super(finish);
		setMembers(db, group, act, dontSave);
	}


	public DeleteGroup(Database db, PwGroup group, OnFinish finish, boolean dontSave) {
		super(finish);
		setMembers(db, group, null, dontSave);
	}

	private void setMembers(Database db, PwGroup group, GroupBaseActivity act, boolean dontSave) {
		mDb = db;
		mGroup = group;
		mAct = act;
		mDontSave = dontSave;

		mFinish = new AfterDelete(mFinish);
	}



	@Override
	public void run() {

		// Remove child entries
		List<PwEntry> childEnt = new ArrayList<PwEntry>(mGroup.childEntries);
		for ( int i = 0; i < childEnt.size(); i++ ) {
			DeleteEntry task = new DeleteEntry(mAct, mDb, childEnt.get(i), null, true);
			task.run();
		}

		// Remove child groups
		List<PwGroup> childGrp = new ArrayList<PwGroup>(mGroup.childGroups);
		for ( int i = 0; i < childGrp.size(); i++ ) {
			DeleteGroup task = new DeleteGroup(mDb, childGrp.get(i), mAct, null, true);
			task.run();
		}


		// Remove from parent
		PwGroup parent = mGroup.getParent();
		if ( parent != null ) {
			parent.childGroups.remove(mGroup);
		}

		// Remove from PwDatabaseV3
		mDb.pm.getGroups().remove(mGroup);

		// Save
		SaveDB save = new SaveDB(mAct, mDb, mFinish, mDontSave);
		save.run();

	}

	private class AfterDelete extends OnFinish {
		public AfterDelete(OnFinish finish) {
			super(finish);
		}

		public void run() {
			if ( mSuccess ) {
				// Remove from group global
				mDb.pm.groups.remove(mGroup.getId());

				// Remove group from the dirty global (if it is present), not a big deal if this fails
				mDb.dirty.remove(mGroup);

				// Mark parent dirty
				PwGroup parent = mGroup.getParent();
				if ( parent != null ) {
					mDb.dirty.add(parent);
				}
				mDb.dirty.add(mDb.pm.rootGroup);
			} else {
				// Let's not bother recovering from a failure to save a deleted group.  It is too much work.
				App.setShutdown();
			}

			super.run();

		}

	}
}

public class FileOnFinish extends OnFinish {
	private Uri mFilename = null;
	protected FileOnFinish mOnFinish;

	public FileOnFinish(FileOnFinish finish) {
		super(finish);

		mOnFinish = finish;
	}

	public void setFilename(Uri filename) {
		mFilename = filename;
	}

	public Uri getFilename() {
		return mFilename;
	}

}

public class LoadDB extends RunnableOnFinish {
    private Uri mUri;
    private String mPass;
    private Uri mKey;
    private Database mDb;
    private Context mCtx;
    private boolean mRememberKeyfile;

    public LoadDB(Database db, Context ctx, Uri uri, String pass, Uri key, OnFinish finish) {
        super(finish);

        mDb = db;
        mCtx = ctx;
        mUri = uri;
        mPass = pass;
        mKey = key;

        SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(ctx);
        mRememberKeyfile = prefs.getBoolean(ctx.getString(R.string.keyfile_key), ctx.getResources().getBoolean(R.bool.keyfile_default));
    }

    @Override
    public void run() {
        try {
            mDb.LoadData(mCtx, mUri, mPass, mKey, mStatus);

            saveFileData(mUri, mKey);

        } catch (ArcFourException e) {
            finish(false, mCtx.getString(R.string.error_arc4));
            return;
        } catch (InvalidPasswordException e) {
            finish(false, mCtx.getString(R.string.InvalidPassword));
            return;
        } catch (ContentFileNotFoundException e) {
            finish(false, mCtx.getString(R.string.file_not_found_content));
            return;
        } catch (FileNotFoundException e) {
            finish(false, mCtx.getString(R.string.FileNotFound));
            return;
        } catch (IOException e) {
            finish(false, e.getMessage());
            return;
        } catch (KeyFileEmptyException e) {
            finish(false, mCtx.getString(R.string.keyfile_is_empty));
            return;
        } catch (InvalidAlgorithmException e) {
            finish(false, mCtx.getString(R.string.invalid_algorithm));
            return;
        } catch (InvalidKeyFileException e) {
            finish(false, mCtx.getString(R.string.keyfile_does_not_exist));
            return;
        } catch (InvalidDBSignatureException e) {
            finish(false, mCtx.getString(R.string.invalid_db_sig));
            return;
        } catch (InvalidDBVersionException e) {
            finish(false, mCtx.getString(R.string.unsupported_db_version));
            return;
        } catch (InvalidDBException e) {
            finish(false, mCtx.getString(R.string.error_invalid_db));
            return;
        } catch (OutOfMemoryError e) {
            finish(false, mCtx.getString(R.string.error_out_of_memory));
            return;
        }

        finish(true);
    }

    private void saveFileData(Uri uri, Uri key) {
        if ( ! mRememberKeyfile ) {
            key = null;
        }

        App.getFileHistory().createFile(uri, key);
    }



}

public class OnFinish implements Runnable {
	protected boolean mSuccess;
	protected String mMessage;

	protected OnFinish mOnFinish;
	protected Handler mHandler;

	public OnFinish() {
	}

	public OnFinish(Handler handler) {
		mOnFinish = null;
		mHandler = handler;
	}

	public OnFinish(OnFinish finish, Handler handler) {
		mOnFinish = finish;
		mHandler = handler;
	}

	public OnFinish(OnFinish finish) {
		mOnFinish = finish;
		mHandler = null;
	}

	public void setResult(boolean success, String message) {
		mSuccess = success;
		mMessage = message;
	}

	public void setResult(boolean success) {
		mSuccess = success;
	}

	public void run() {
		if ( mOnFinish != null ) {
			// Pass on result on call finish
			mOnFinish.setResult(mSuccess, mMessage);

			if ( mHandler != null ) {
				mHandler.post(mOnFinish);
			} else {
				mOnFinish.run();
			}
		}
	}

	protected void displayMessage(Context ctx) {
		if ( ctx != null && mMessage != null && mMessage.length() > 0 ) {
			Toast.makeText(ctx, mMessage, Toast.LENGTH_LONG).show();
		}
	}

}

public abstract class RunnableOnFinish implements Runnable {

	public OnFinish mFinish;
	public UpdateStatus mStatus;

	public RunnableOnFinish(OnFinish finish) {
		mFinish = finish;
	}

	protected void finish(boolean result, String message) {
		if ( mFinish != null ) {
			mFinish.setResult(result, message);
			mFinish.run();
		}
	}

	protected void finish(boolean result) {
		if ( mFinish != null ) {
			mFinish.setResult(result);
			mFinish.run();
		}
	}

	public void setStatus(UpdateStatus status) {
		mStatus = status;
	}

	abstract public void run();
}

public class SaveDB extends RunnableOnFinish {
	private Database mDb;
	private boolean mDontSave;
	private Context mCtx;

	public SaveDB(Context ctx, Database db, OnFinish finish, boolean dontSave) {
		super(finish);

		mDb = db;
		mDontSave = dontSave;
		mCtx = ctx;
	}

	public SaveDB(Context ctx, Database db, OnFinish finish) {
		super(finish);

		mDb = db;
		mDontSave = false;
		mCtx = ctx;
	}

	@Override
	public void run() {

		if ( ! mDontSave ) {
			try {
				mDb.SaveData(mCtx);
			} catch (IOException e) {
				finish(false, e.getMessage());
				return;
			} catch (PwDbOutputException e) {
				// TODO: Restore
				throw new RuntimeException(e);
				/*
				finish(false, e.getMessage());
				return;
				*/
			}
		}

		finish(true);
	}

}

public class SetPassword extends RunnableOnFinish {

	private String mPassword;
	private Uri mKeyfile;
	private Database mDb;
	private boolean mDontSave;
	private Context ctx;

	public SetPassword(Context ctx, Database db, String password, Uri keyfile, OnFinish finish) {
		this(ctx, db, password, keyfile, finish, false);

	}

	public SetPassword(Context ctx, Database db, String password, Uri keyfile, OnFinish finish, boolean dontSave) {
		super(finish);

		mDb = db;
		mPassword = password;
		mKeyfile = keyfile;
		mDontSave = dontSave;
		this.ctx = ctx;
	}

	public boolean validatePassword(Context ctx, DialogInterface.OnClickListener onclick) {
		if (!mDb.pm.validatePasswordEncoding(mPassword)) {
			PasswordEncodingDialogHelper dialog = new PasswordEncodingDialogHelper();
			dialog.show(ctx, onclick, true);
			return false;
		}

		return true;
	}

	@Override
	public void run() {
		PwDatabase pm = mDb.pm;

		byte[] backupKey = new byte[pm.masterKey.length];
		System.arraycopy(pm.masterKey, 0, backupKey, 0, backupKey.length);

		// Set key
		try {
			InputStream is = UriUtil.getUriInputStream(ctx, mKeyfile);
			pm.setMasterKey(mPassword, is);
		} catch (InvalidKeyFileException e) {
			erase(backupKey);
			finish(false, e.getMessage());
			return;
		} catch (IOException e) {
			erase(backupKey);
			finish(false, e.getMessage());
			return;
		}

		// Save Database
		mFinish = new AfterSave(backupKey, mFinish);
		SaveDB save = new SaveDB(ctx, mDb, mFinish, mDontSave);
		save.run();
	}

	private class AfterSave extends OnFinish {
		private byte[] mBackup;

		public AfterSave(byte[] backup, OnFinish finish) {
			super(finish);

			mBackup = backup;
		}

		@Override
		public void run() {
			if ( ! mSuccess ) {
				// Erase the current master key
				erase(mDb.pm.masterKey);
				mDb.pm.masterKey = mBackup;
			}

			super.run();
		}

	}

	/** Overwrite the array as soon as we don't need it to avoid keeping the extra data in memory
	 * @param array
	 */
	private void erase(byte[] array) {
		if ( array == null ) return;

		for ( int i = 0; i < array.length; i++ ) {
			array[i] = 0;
		}
	}

}

public class UpdateEntry extends RunnableOnFinish {
	private Database mDb;
	private PwEntry mOldE;
	private PwEntry mNewE;
	private Context ctx;

	public UpdateEntry(Context ctx, Database db, PwEntry oldE, PwEntry newE, OnFinish finish) {
		super(finish);

		mDb = db;
		mOldE = oldE;
		mNewE = newE;
		this.ctx = ctx;

		// Keep backup of original values in case save fails
		PwEntry backup;
		backup = (PwEntry) mOldE.clone();

		mFinish = new AfterUpdate(backup, finish);
	}

	@Override
	public void run() {
		// Update entry with new values
		mOldE.assign(mNewE);
		mOldE.touch(true, true);


		// Commit to disk
		SaveDB save = new SaveDB(ctx, mDb, mFinish);
		save.run();
	}

	private class AfterUpdate extends OnFinish {
		private PwEntry mBackup;

		public AfterUpdate(PwEntry backup, OnFinish finish) {
			super(finish);

			mBackup = backup;
		}

		@Override
		public void run() {
			if ( mSuccess ) {
				// Mark group dirty if title or icon changes
				if ( ! mBackup.getTitle().equals(mNewE.getTitle()) || ! mBackup.getIcon().equals(mNewE.getIcon()) ) {
					PwGroup parent = mBackup.getParent();
					if ( parent != null ) {
						// Resort entries
						parent.sortEntriesByName();

						// Mark parent group dirty
						mDb.dirty.add(parent);

					}
				}
			} else {
				// If we fail to save, back out changes to global structure
				mOldE.assign(mBackup);
			}

			super.run();
		}

	}


}

