public abstract class EntrySearchStringIterator implements Iterator<String> {

	public static EntrySearchStringIterator getInstance(PwEntry e) {
		if (e instanceof PwEntryV3) {
			return new EntrySearchStringIteratorV3((PwEntryV3) e);
		} else if (e instanceof PwEntryV4) {
			return new EntrySearchStringIteratorV4((PwEntryV4) e);
		} else {
			throw new RuntimeException("This should not be possible");
		}
	}

	public static EntrySearchStringIterator getInstance(PwEntry e, SearchParameters sp) {
		if (e instanceof PwEntryV3) {
			return new EntrySearchStringIteratorV3((PwEntryV3) e, sp);
		} else if (e instanceof PwEntryV4) {
			return new EntrySearchStringIteratorV4((PwEntryV4) e, (SearchParametersV4) sp);
		} else {
			throw new RuntimeException("This should not be possible");
		}
	}

	@Override
	public abstract boolean hasNext();

	@Override
	public abstract String next();

	@Override
	public void remove() {
		throw new UnsupportedOperationException("This iterator cannot be used to remove strings.");

	}


}

public class EntrySearchStringIteratorV3 extends EntrySearchStringIterator {

	private PwEntryV3 entry; 
	private SearchParameters sp;

	public EntrySearchStringIteratorV3(PwEntryV3 entry) {
		this.entry = entry;
		this.sp = SearchParameters.DEFAULT;
	}

	public EntrySearchStringIteratorV3(PwEntryV3 entry, SearchParameters sp) {
		this.entry = entry;
		this.sp = sp;
	}

	private static final int title = 0;
	private static final int url = 1;
	private static final int username = 2;
	private static final int comment = 3;
	private static final int maxEntries = 4;

	private int current = 0;

	@Override
	public boolean hasNext() {
		return current < maxEntries;
	}

	@Override
	public String next() {
		// Past the end of the list
		if (current == maxEntries) {
			throw new NoSuchElementException("Past final string");
		}

        useSearchParameters();

		String str = getCurrentString();
		current++;
		return str;
	}

	private void useSearchParameters() {

		if (sp == null) { return; }

		boolean found = false;

		while (!found) {
            switch (current) {
            case title:
                found = sp.searchInTitles;
                break;

            case url:
            	found = sp.searchInUrls;
                break;

            case username:
                found = sp.searchInUserNames;    
                break;

            case comment:
            	found = sp.searchInNotes;
                break;

            default:
            	found = true;
            }

            if (!found) { current++; }   
		}
	}

	private String getCurrentString() {
		switch (current) {
		case title:
			return entry.getTitle();

		case url:
			return entry.getUrl();

		case username:
			return entry.getUsername();

		case comment:
			return entry.getNotes();

		default:
			return "";
		}
	}

}

public class EntrySearchStringIteratorV4 extends EntrySearchStringIterator {

	private String current;
	private Iterator<Entry<String, ProtectedString>> setIterator;
	private SearchParametersV4 sp;

	public EntrySearchStringIteratorV4(PwEntryV4 entry) {
		this.sp = SearchParametersV4.DEFAULT;
		setIterator = entry.strings.entrySet().iterator();
		advance();

	}

	public EntrySearchStringIteratorV4(PwEntryV4 entry, SearchParametersV4 sp) {
		this.sp = sp;
		setIterator = entry.strings.entrySet().iterator();
		advance();
	}

	@Override
	public boolean hasNext() {
		return current != null;
	}

	@Override
	public String next() {
		if (current == null) {
			throw new NoSuchElementException("Past the end of the list.");
		}

		String next = current;
		advance();
		return next;
	}

	private void advance() {
		while (setIterator.hasNext()) {
			Entry<String, ProtectedString> entry = setIterator.next();

			String key = entry.getKey();

			if (searchInField(key)) {
				current = entry.getValue().toString();
				return;
			}

		}

		current = null;
	}

	private boolean searchInField(String key) {
		if (key.equals(PwEntryV4.STR_TITLE)) {
			return sp.searchInTitles;
		} else if (key.equals(PwEntryV4.STR_USERNAME)) {
			return sp.searchInUserNames;
		} else if (key.equals(PwEntryV4.STR_PASSWORD)) {
			return sp.searchInPasswords;
		} else if (key.equals(PwEntryV4.STR_URL)) {
			return sp.searchInUrls;
		} else if (key.equals(PwEntryV4.STR_NOTES)) {
			return sp.searchInNotes;
		} else {
			return sp.searchInOther;
		}
	}

}

public abstract class Importer {

	public static final boolean DEBUG = true;

	public abstract PwDatabase openDatabase( InputStream inStream, String password, InputStream keyInputStream)
		throws IOException, InvalidDBException;

	public abstract PwDatabase openDatabase( InputStream inStream, String password, InputStream keyInputStream, UpdateStatus status, long roundsFix)
		throws IOException, InvalidDBException;


}

public class ImporterFactory {
	public static Importer createImporter(InputStream is, File streamDir) throws InvalidDBSignatureException, IOException
	{
		return createImporter(is, streamDir, false);
	}

	public static Importer createImporter(InputStream is, File streamDir, boolean debug) throws InvalidDBSignatureException, IOException
	{
		int sig1 = LEDataInputStream.readInt(is);
		int sig2 = LEDataInputStream.readInt(is);

		if ( PwDbHeaderV3.matchesHeader(sig1, sig2) ) {
			if (debug) {
				return new ImporterV3Debug();
			}

			return new ImporterV3();
		} else if ( PwDbHeaderV4.matchesHeader(sig1, sig2) ) {
			return new ImporterV4(streamDir);
		}

		throw new InvalidDBSignatureException();

	}
}

public class ImporterV3 extends Importer {

	public ImporterV3() {
		super();
	}

	protected PwDatabaseV3 createDB() {
		return new PwDatabaseV3();
	}

	/**
	 * Load a v3 database file, return contents in a new PwDatabaseV3.
	 * 
	 * @param inStream  Existing file to load.
	 * @param password Pass phrase for infile.
	 * @return new PwDatabaseV3 container.
	 * 
	 * @throws IOException on any file error.
	 * @throws InvalidKeyFileException 
	 * @throws InvalidPasswordException 
	 * @throws InvalidPasswordException on a decryption error, or possible internal bug.
	 * @throws InvalidDBSignatureException 
	 * @throws InvalidDBVersionException 
	 * @throws IllegalBlockSizeException on a decryption error, or possible internal bug.
	 * @throws BadPaddingException on a decryption error, or possible internal bug.
	 * @throws NoSuchAlgorithmException on a decryption error, or possible internal bug.
	 * @throws NoSuchPaddingException on a decryption error, or possible internal bug.
	 * @throws InvalidAlgorithmParameterException if error decrypting main file body. 
	 * @throws ShortBufferException if error decrypting main file body.
	 */
	public PwDatabaseV3 openDatabase( InputStream inStream, String password, InputStream kfIs)
	throws IOException, InvalidDBException
	{
		return openDatabase(inStream, password, kfIs, new UpdateStatus(), 0);
	}

	public PwDatabaseV3 openDatabase( InputStream inStream, String password, InputStream kfIs, UpdateStatus status, long roundsFix)
	throws IOException, InvalidDBException
	{
		PwDatabaseV3        newManager;


		// Load entire file, most of it's encrypted.
		int fileSize = inStream.available();
		byte[] filebuf = new byte[fileSize + 16]; // Pad with a blocksize (Twofish uses 128 bits), since Android 4.3 tries to write more to the buffer
		inStream.read(filebuf, 0, fileSize);
		inStream.close();

		// Parse header (unencrypted)
		if( fileSize < PwDbHeaderV3.BUF_SIZE )
			throw new IOException( "File too short for header" );
		PwDbHeaderV3 hdr = new PwDbHeaderV3();
		hdr.loadFromFile(filebuf, 0 );

		if( (hdr.signature1 != PwDbHeader.PWM_DBSIG_1) || (hdr.signature2 != PwDbHeaderV3.DBSIG_2) ) {
			throw new InvalidDBSignatureException();
		}

		if( !hdr.matchesVersion() ) {
			throw new InvalidDBVersionException();
		}

		status.updateMessage(R.string.creating_db_key);
		newManager = createDB();
		newManager.setMasterKey(password, kfIs);

		// Select algorithm
		if( (hdr.flags & PwDbHeaderV3.FLAG_RIJNDAEL) != 0 ) {
			newManager.algorithm = PwEncryptionAlgorithm.Rjindal;
		} else if( (hdr.flags & PwDbHeaderV3.FLAG_TWOFISH) != 0 ) {
			newManager.algorithm = PwEncryptionAlgorithm.Twofish;
		} else {
			throw new InvalidAlgorithmException();
		}

		// Copy for testing
		newManager.copyHeader(hdr);

		newManager.numKeyEncRounds = hdr.numKeyEncRounds;

		newManager.name = "KeePass Password Manager";

		// Generate transformedMasterKey from masterKey
		newManager.makeFinalKey(hdr.masterSeed, hdr.transformSeed, newManager.numKeyEncRounds);

		status.updateMessage(R.string.decrypting_db);
		// Initialize Rijndael algorithm
		Cipher cipher;
		try {
			if ( newManager.algorithm == PwEncryptionAlgorithm.Rjindal ) {
				cipher = CipherFactory.getInstance("AES/CBC/PKCS5Padding");
			} else if ( newManager.algorithm == PwEncryptionAlgorithm.Twofish ) {
				cipher = CipherFactory.getInstance("Twofish/CBC/PKCS7PADDING");
			} else {
				throw new IOException( "Encryption algorithm is not supported" );
			}

		} catch (NoSuchAlgorithmException e1) {
			throw new IOException("No such algorithm");
		} catch (NoSuchPaddingException e1) {
			throw new IOException("No such pdading");
		}

		try {
			cipher.init( Cipher.DECRYPT_MODE, new SecretKeySpec( newManager.finalKey, "AES" ), new IvParameterSpec( hdr.encryptionIV ) );
		} catch (InvalidKeyException e1) {
			throw new IOException("Invalid key");
		} catch (InvalidAlgorithmParameterException e1) {
			throw new IOException("Invalid algorithm parameter.");
		}

		// Decrypt! The first bytes aren't encrypted (that's the header)
		int encryptedPartSize;
		try {
			encryptedPartSize = cipher.doFinal(filebuf, PwDbHeaderV3.BUF_SIZE, fileSize - PwDbHeaderV3.BUF_SIZE, filebuf, PwDbHeaderV3.BUF_SIZE );
		} catch (ShortBufferException e1) {
			throw new IOException("Buffer too short");
		} catch (IllegalBlockSizeException e1) {
			throw new IOException("Invalid block size");
		} catch (BadPaddingException e1) {
			throw new InvalidPasswordException();
		}

		// Copy decrypted data for testing
		newManager.copyEncrypted(filebuf, PwDbHeaderV3.BUF_SIZE, encryptedPartSize);

		MessageDigest md = null;
		try {
			md = MessageDigest.getInstance("SHA-256");
		} catch (NoSuchAlgorithmException e) {
			throw new IOException("No SHA-256 algorithm");
		}
		NullOutputStream nos = new NullOutputStream();
		DigestOutputStream dos = new DigestOutputStream(nos, md);
		dos.write(filebuf, PwDbHeaderV3.BUF_SIZE, encryptedPartSize);
		dos.close();
		byte[] hash = md.digest();

		if( ! Arrays.equals(hash, hdr.contentsHash) ) {

			Log.w("KeePassDroid","Database file did not decrypt correctly. (checksum code is broken)");
			throw new InvalidPasswordException();
		}

		// Import all groups

		int pos = PwDbHeaderV3.BUF_SIZE;
		PwGroupV3 newGrp = new PwGroupV3();
		for( int i = 0; i < hdr.numGroups; ) {
			int fieldType = LEDataInputStream.readUShort( filebuf, pos );
			pos += 2;
			int fieldSize = LEDataInputStream.readInt( filebuf, pos );
			pos += 4;

			if( fieldType == 0xFFFF ) {

				// End-Group record.  Save group and count it.
				newGrp.populateBlankFields(newManager);
				newManager.groups.add(newGrp);
				newGrp = new PwGroupV3();
				i++;
			}
			else {
				readGroupField(newManager, newGrp, fieldType, filebuf, pos);
			}
			pos += fieldSize;
		}

		// Import all entries
		PwEntryV3 newEnt = new PwEntryV3();
		for( int i = 0; i < hdr.numEntries; ) {
			int fieldType = LEDataInputStream.readUShort( filebuf, pos );
			int fieldSize = LEDataInputStream.readInt( filebuf, pos + 2 );

			if( fieldType == 0xFFFF ) {
				// End-Group record.  Save group and count it.
				newEnt.populateBlankFields(newManager);
				newManager.entries.add(newEnt);
				newEnt = new PwEntryV3();
				i++;
			}
			else {
				readEntryField(newManager, newEnt, filebuf, pos);
			}
			pos += 2 + 4 + fieldSize;
		}

		newManager.constructTree(null);

		return newManager;
	}

	/**
	 * KeePass's custom pad style.
	 * 
	 * @param data buffer to pad.
	 * @return addtional bytes to append to data[] to make
	 *    a properly padded array.
	 */
	public static byte[] makePad( byte[] data ) {
		//custom pad method

		// append 0x80 plus zeros to a multiple of 4 bytes
		int thisblk = 32 - data.length % 32;  // bytes needed to finish blk
		int nextblk = 0;                      // 32 if we need another block
		// need 9 bytes; add new block if no room
		if( thisblk < 9 ) {
			nextblk = 32;
		}

		// all bytes are zeroed for free
		byte[] pad = new byte[ thisblk + nextblk ];
		pad[0] = (byte)0x80;

		// write length*8 to end of final block
		int ix = thisblk + nextblk - 8;
		LEDataOutputStream.writeInt( data.length>>29, pad, ix );
		bsw32( pad, ix );
		ix += 4;
		LEDataOutputStream.writeInt( data.length<<3, pad, ix );
		bsw32( pad, ix );

		return pad;
	}

	public static void bsw32( byte[] ary, int offset ) {
		byte t = ary[offset];
		ary[offset] = ary[offset+3];
		ary[offset+3] = t;
		t = ary[offset+1];
		ary[offset+1] = ary[offset+2];
		ary[offset+2] = t;
	}


	/**
	 * Parse and save one record from binary file.
	 * @param buf
	 * @param offset
	 * @return If >0, 
	 * @throws UnsupportedEncodingException 
	 */
	void readGroupField(PwDatabaseV3 db, PwGroupV3 grp, int fieldType, byte[] buf, int offset) throws UnsupportedEncodingException {
		switch( fieldType ) {
		case 0x0000 :
			// Ignore field
			break;
		case 0x0001 :
			grp.groupId = LEDataInputStream.readInt(buf, offset);
			break;
		case 0x0002 :
			grp.name = Types.readCString(buf, offset);
			break;
		case 0x0003 :
			grp.tCreation = new PwDate(buf, offset);
			break;
		case 0x0004 :
			grp.tLastMod = new PwDate(buf, offset);
			break;
		case 0x0005 :
			grp.tLastAccess = new PwDate(buf, offset);
			break;
		case 0x0006 :
			grp.tExpire = new PwDate(buf, offset);
			break;
		case 0x0007 :
			grp.icon = db.iconFactory.getIcon(LEDataInputStream.readInt(buf, offset));
			break;
		case 0x0008 :
			grp.level = LEDataInputStream.readUShort(buf, offset);
			break;
		case 0x0009 :
			grp.flags = LEDataInputStream.readInt(buf, offset);
			break;
		}
	}



	void readEntryField(PwDatabaseV3 db, PwEntryV3 ent, byte[] buf, int offset)
	throws UnsupportedEncodingException
	{
		int fieldType = LEDataInputStream.readUShort(buf, offset);
		offset += 2;
		int fieldSize = LEDataInputStream.readInt(buf, offset);
		offset += 4;

		switch( fieldType ) {
		case 0x0000 :
			// Ignore field
			break;
		case 0x0001 :
			ent.setUUID(Types.bytestoUUID(buf, offset));
			break;
		case 0x0002 :
			ent.groupId = LEDataInputStream.readInt(buf, offset);
			break;
		case 0x0003 :
			int iconId = LEDataInputStream.readInt(buf, offset);

			// Clean up after bug that set icon ids to -1
			if (iconId == -1) {
				iconId = 0;
			}

			ent.icon = db.iconFactory.getIcon(iconId);
			break;
		case 0x0004 :
			ent.title = Types.readCString(buf, offset); 
			break;
		case 0x0005 :
			ent.url = Types.readCString(buf, offset);
			break;
		case 0x0006 :
			ent.username = Types.readCString(buf, offset);
			break;
		case 0x0007 :
			ent.setPassword(buf, offset, Types.strlen(buf, offset));
			break;
		case 0x0008 :
			ent.additional = Types.readCString(buf, offset);
			break;
		case 0x0009 :
			ent.tCreation = new PwDate(buf, offset);
			break;
		case 0x000A :
			ent.tLastMod = new PwDate(buf, offset);
			break;
		case 0x000B :
			ent.tLastAccess = new PwDate(buf, offset);
			break;
		case 0x000C :
			ent.tExpire = new PwDate(buf, offset);
			break;
		case 0x000D :
			ent.binaryDesc = Types.readCString(buf, offset);
			break;
		case 0x000E :
			ent.setBinaryData(buf, offset, fieldSize);
			break;
		}
	}
}

public class ImporterV3Debug extends ImporterV3 {

	@Override
	protected PwDatabaseV3Debug createDB() {
		return new PwDatabaseV3Debug();
	}

	@Override
	public PwDatabaseV3Debug openDatabase(InputStream inStream, String password,
			InputStream keyInputStream, UpdateStatus status, long roundsFix) throws IOException,
			InvalidDBException {
		return (PwDatabaseV3Debug) super.openDatabase(inStream, password, keyInputStream, status,
				roundsFix);
	}


}

public class ImporterV4 extends Importer {

	private StreamCipher randomStream;
	private PwDatabaseV4 db;

    private byte[] hashOfHeader = null;
	private byte[] pbHeader = null;
	private long version;
	private int binNum = 0;
	Calendar utcCal;
    private File streamDir;

	public ImporterV4(File streamDir) {
		this.utcCal = Calendar.getInstance(TimeZone.getTimeZone("UTC"));
        this.streamDir = streamDir;
	}

	protected PwDatabaseV4 createDB() {
		return new PwDatabaseV4();
	}

	@Override
	public PwDatabaseV4 openDatabase(InputStream inStream, String password,
			InputStream keyInputStream) throws IOException, InvalidDBException {

		return openDatabase(inStream, password, keyInputStream, new UpdateStatus(), 0);
	}

	@Override
    public PwDatabaseV4 openDatabase(InputStream inStream, String password,
            InputStream keyInputStream, UpdateStatus status, long roundsFix) throws IOException,
            InvalidDBException {
		db = createDB();

		PwDbHeaderV4 header = new PwDbHeaderV4(db);
        db.binPool.clear();

		PwDbHeaderV4.HeaderAndHash hh = header.loadFromFile(inStream);
        version = header.version;

		hashOfHeader = hh.hash;
		pbHeader = hh.header;

		db.setMasterKey(password, keyInputStream);
		db.makeFinalKey(header.masterSeed, db.kdfParameters, roundsFix);

		CipherEngine engine;
		Cipher cipher;
		try {
			engine = CipherFactory.getInstance(db.dataCipher);
			db.dataEngine = engine;
			cipher = engine.getCipher(Cipher.DECRYPT_MODE, db.finalKey, header.encryptionIV);
		} catch (NoSuchAlgorithmException e) {
			throw new IOException("Invalid algorithm.");
		} catch (NoSuchPaddingException e) {
			throw new IOException("Invalid algorithm.");
		} catch (InvalidKeyException e) {
			throw new IOException("Invalid algorithm.");
		} catch (InvalidAlgorithmParameterException e) {
			throw new IOException("Invalid algorithm.");
		}

		InputStream isPlain;
		if (version < PwDbHeaderV4.FILE_VERSION_32_4) {

			InputStream decrypted = AttachCipherStream(inStream, cipher);
			LEDataInputStream dataDecrypted = new LEDataInputStream(decrypted);
			byte[] storedStartBytes = null;
			try {
				storedStartBytes = dataDecrypted.readBytes(32);
				if (storedStartBytes == null || storedStartBytes.length != 32) {
					throw new InvalidPasswordException();
				}
			} catch (IOException e) {
				throw new InvalidPasswordException();
			}

			if (!Arrays.equals(storedStartBytes, header.streamStartBytes)) {
				throw new InvalidPasswordException();
			}

			isPlain = new HashedBlockInputStream(dataDecrypted);
		}
		else { // KDBX 4
			LEDataInputStream isData = new LEDataInputStream(inStream);
			byte[] storedHash = isData.readBytes(32);
			if (!Arrays.equals(storedHash,hashOfHeader)) {
				throw new InvalidDBException();
			}

			byte[] hmacKey = db.hmacKey;
			byte[] headerHmac = PwDbHeaderV4.computeHeaderHmac(pbHeader, hmacKey);
			byte[] storedHmac = isData.readBytes(32);
			if (storedHmac == null || storedHmac.length != 32) {
				throw new InvalidDBException();
			}
			// Mac doesn't match
			if (! Arrays.equals(headerHmac, storedHmac)) {
				throw new InvalidDBException();
			}

			HmacBlockInputStream hmIs = new HmacBlockInputStream(isData, true, hmacKey);

			isPlain = AttachCipherStream(hmIs, cipher);
		}

		InputStream isXml;
		if ( db.compressionAlgorithm == PwCompressionAlgorithm.Gzip ) {
			isXml = new GZIPInputStream(isPlain);
		} else {
			isXml = isPlain;
		}

		if (version >= header.FILE_VERSION_32_4) {
			LoadInnerHeader(isXml, header);
		}

		if ( header.innerRandomStreamKey == null ) {
			assert(false);
			throw new IOException("Invalid stream key.");
		}

		randomStream = PwStreamCipherFactory.getInstance(header.innerRandomStream, header.innerRandomStreamKey);

		if ( randomStream == null ) {
			throw new ArcFourException();
		}

		ReadXmlStreamed(isXml);

		return db;


	}

	private InputStream AttachCipherStream(InputStream is, Cipher cipher) {
		return new BetterCipherInputStream(is, cipher, 50 * 1024);
	}

	private void LoadInnerHeader(InputStream is, PwDbHeaderV4 header) throws IOException {
		LEDataInputStream lis = new LEDataInputStream(is);

		while(true) {
			if (!ReadInnerHeader(lis, header)) break;
		}

	}

	private boolean ReadInnerHeader(LEDataInputStream lis, PwDbHeaderV4 header) throws IOException {
		byte fieldId = (byte)lis.read();

		int size = lis.readInt();
		if (size < 0) throw new IOException("Corrupted file");

		byte[] data = new byte[0];
		if (size > 0) {
			if (fieldId != PwDbHeaderV4.PwDbInnerHeaderV4Fields.Binary)
			data = lis.readBytes(size);
		}

		boolean result = true;
		switch(fieldId) {
			case PwDbHeaderV4.PwDbInnerHeaderV4Fields.EndOfHeader:
				result = false;
				break;
			case PwDbHeaderV4.PwDbInnerHeaderV4Fields.InnerRandomStreamID:
			    header.setRandomStreamID(data);
				break;
			case PwDbHeaderV4.PwDbInnerHeaderV4Fields.InnerRandomstreamKey:
			    header.innerRandomStreamKey = data;
				break;
			case PwDbHeaderV4.PwDbInnerHeaderV4Fields.Binary:
				byte flag = lis.readBytes(1)[0];
				boolean protectedFlag = (flag & PwDbHeaderV4.KdbxBinaryFlags.Protected) !=
						PwDbHeaderV4.KdbxBinaryFlags.None;
				// Read the serialized binary
				int binaryKey = db.binPool.findUnusedKey();
				File file = new File(streamDir, String.valueOf(binaryKey));
				ProtectedBinary protectedBinary = new ProtectedBinary(protectedFlag, file, size -1);
				final OutputStream outputStream = protectedBinary.getOutputStream();
				Util.copyStream(lis, outputStream, size -1);
                outputStream.close();

				db.binPool.poolAdd(protectedBinary);
				break;
			default:
				assert(false);
				break;
		}

		return result;
	}

	private enum KdbContext {
        Null,
        KeePassFile,
        Meta,
        Root,
        MemoryProtection,
        CustomIcons,
        CustomIcon,
        CustomData,
        CustomDataItem,
        RootDeletedObjects,
        DeletedObject,
        Group,
        GroupTimes,
		GroupCustomData,
		GroupCustomDataItem,
        Entry,
        EntryTimes,
        EntryString,
        EntryBinary,
        EntryAutoType,
        EntryAutoTypeItem,
        EntryHistory,
		EntryCustomData,
		EntryCustomDataItem,
        Binaries
	}

    private static final long DEFAULT_HISTORY_DAYS = 365;

	private boolean readNextNode = true;
	private Stack<PwGroupV4> ctxGroups = new Stack<PwGroupV4>();
	private PwGroupV4 ctxGroup = null;
	private PwEntryV4 ctxEntry = null;
	private String ctxStringName = null;
	private ProtectedString ctxStringValue = null;
	private String ctxBinaryName = null;
	private ProtectedBinary ctxBinaryValue = null;
	private String ctxATName = null;
	private String ctxATSeq = null;
	private boolean entryInHistory = false;
	private PwEntryV4 ctxHistoryBase = null;
	private PwDeletedObject ctxDeletedObject = null;
	private UUID customIconID = PwDatabaseV4.UUID_ZERO;
	private byte[] customIconData;
	private String customDataKey = null;
	private String customDataValue = null;
	private String groupCustomDataKey = null;
	private String groupCustomDataValue = null;
	private String entryCustomDataKey = null;
	private String entryCustomDataValue = null;

	private void ReadXmlStreamed(InputStream readerStream) throws IOException, InvalidDBException {

			try {
				ReadDocumentStreamed(CreatePullParser(readerStream));
			} catch (XmlPullParserException e) {
				e.printStackTrace();
				throw new IOException(e.getLocalizedMessage());
			}
	}

	private static XmlPullParser CreatePullParser(InputStream readerStream) throws XmlPullParserException {
		XmlPullParserFactory xppf = XmlPullParserFactory.newInstance();
		xppf.setNamespaceAware(false);

		XmlPullParser xpp = xppf.newPullParser();
		xpp.setInput(readerStream, null);

		return xpp;
	}

	private void ReadDocumentStreamed(XmlPullParser xpp) throws XmlPullParserException, IOException, InvalidDBException {

		ctxGroups.clear();

		KdbContext ctx = KdbContext.Null;

		readNextNode = true;

		while (true) {
			if ( readNextNode ) {
				if( xpp.next() == XmlPullParser.END_DOCUMENT ) break;
			} else {
				readNextNode = true;
			}

			switch ( xpp.getEventType() ) {
			case XmlPullParser.START_TAG:
				ctx = ReadXmlElement(ctx, xpp);
				break;

			case XmlPullParser.END_TAG:
				ctx = EndXmlElement(ctx, xpp);
				break;

			default:
				assert(false);
				break;

			}

		}

		// Error checks
		if ( ctx != KdbContext.Null ) throw new IOException("Malformed");
		if ( ctxGroups.size() != 0 ) throw new IOException("Malformed");
	}


	private KdbContext ReadXmlElement(KdbContext ctx, XmlPullParser xpp) throws XmlPullParserException, IOException, InvalidDBException {
		String name = xpp.getName();
		switch (ctx) {
		case Null:
			if ( name.equalsIgnoreCase(ElemDocNode) ) {
				return SwitchContext(ctx, KdbContext.KeePassFile, xpp);
			} else ReadUnknown(xpp);
			break;

		case KeePassFile:
			if ( name.equalsIgnoreCase(ElemMeta) ) {
				return SwitchContext(ctx, KdbContext.Meta, xpp);
			} else if ( name.equalsIgnoreCase(ElemRoot) ) {
				return SwitchContext(ctx, KdbContext.Root, xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case Meta:
			if ( name.equalsIgnoreCase(ElemGenerator) ) {
				ReadString(xpp); // Ignore
			} else if ( name.equalsIgnoreCase(ElemHeaderHash) ) {
				String encodedHash = ReadString(xpp);
				if (!EmptyUtils.isNullOrEmpty(encodedHash) && (hashOfHeader != null)) {
					byte[] hash = Base64.decode(encodedHash, Base64.NO_WRAP);
					if (!Arrays.equals(hash, hashOfHeader)) {
						throw new InvalidDBException();
					}
				}
			} else if (name.equalsIgnoreCase(ElemSettingsChanged)) {
				db.settingsChanged = ReadTime(xpp);
			} else if ( name.equalsIgnoreCase(ElemDbName) ) {
				db.name = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemDbNameChanged) ) {
				db.nameChanged = ReadTime(xpp);
			} else if ( name.equalsIgnoreCase(ElemDbDesc) ) {
				db.description = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemDbDescChanged) ) {
				db.descriptionChanged = ReadTime(xpp);
			} else if ( name.equalsIgnoreCase(ElemDbDefaultUser) ) {
				db.defaultUserName = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemDbDefaultUserChanged) ) {
				db.defaultUserNameChanged = ReadTime(xpp);
			} else if ( name.equalsIgnoreCase(ElemDbColor)) {
				// TODO: Add support to interpret the color if we want to allow changing the database color
				db.color = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemDbMntncHistoryDays) ) {
				db.maintenanceHistoryDays = ReadUInt(xpp, DEFAULT_HISTORY_DAYS);
			} else if ( name.equalsIgnoreCase(ElemDbKeyChanged) ) {
				db.keyLastChanged = ReadTime(xpp);
			} else if ( name.equalsIgnoreCase(ElemDbKeyChangeRec) ) {
				db.keyChangeRecDays = ReadLong(xpp, -1);
			} else if ( name.equalsIgnoreCase(ElemDbKeyChangeForce) ) {
				db.keyChangeForceDays = ReadLong(xpp, -1);
			} else if ( name.equalsIgnoreCase(ElemDbKeyChangeForceOnce) ) {
				db.keyChangeForceOnce = ReadBool(xpp, false);
			} else if ( name.equalsIgnoreCase(ElemMemoryProt) ) {
				return SwitchContext(ctx, KdbContext.MemoryProtection, xpp);
			} else if ( name.equalsIgnoreCase(ElemCustomIcons) ) {
				return SwitchContext(ctx, KdbContext.CustomIcons, xpp);
			} else if ( name.equalsIgnoreCase(ElemRecycleBinEnabled) ) {
				db.recycleBinEnabled = ReadBool(xpp, true);
			} else if ( name.equalsIgnoreCase(ElemRecycleBinUuid) ) {
				db.recycleBinUUID = ReadUuid(xpp);
			} else if ( name.equalsIgnoreCase(ElemRecycleBinChanged) ) {
				db.recycleBinChanged = ReadTime(xpp);
			} else if ( name.equalsIgnoreCase(ElemEntryTemplatesGroup) ) {
				db.entryTemplatesGroup = ReadUuid(xpp);
			} else if ( name.equalsIgnoreCase(ElemEntryTemplatesGroupChanged) ) {
				db.entryTemplatesGroupChanged = ReadTime(xpp);
			} else if ( name.equalsIgnoreCase(ElemHistoryMaxItems) ) {
				db.historyMaxItems = ReadInt(xpp, -1);
			} else if ( name.equalsIgnoreCase(ElemHistoryMaxSize) ) {
				db.historyMaxSize = ReadLong(xpp, -1);
			} else if ( name.equalsIgnoreCase(ElemEntryTemplatesGroupChanged) ) {
				db.entryTemplatesGroupChanged = ReadTime(xpp);
			} else if ( name.equalsIgnoreCase(ElemLastSelectedGroup) ) {
				db.lastSelectedGroup = ReadUuid(xpp);
			} else if ( name.equalsIgnoreCase(ElemLastTopVisibleGroup) ) {
				db.lastTopVisibleGroup = ReadUuid(xpp);
			} else if ( name.equalsIgnoreCase(ElemBinaries) ) {
				return SwitchContext(ctx, KdbContext.Binaries, xpp);
			} else if ( name.equalsIgnoreCase(ElemCustomData) ) {
				return SwitchContext(ctx, KdbContext.CustomData, xpp);
			}
			break;

		case MemoryProtection:
			if ( name.equalsIgnoreCase(ElemProtTitle) ) {
				db.memoryProtection.protectTitle = ReadBool(xpp, false);
			} else if ( name.equalsIgnoreCase(ElemProtUserName) ) {
				db.memoryProtection.protectUserName = ReadBool(xpp, false);
			} else if ( name.equalsIgnoreCase(ElemProtPassword) ) {
				db.memoryProtection.protectPassword = ReadBool(xpp, false);
			} else if ( name.equalsIgnoreCase(ElemProtURL) ) {
				db.memoryProtection.protectUrl = ReadBool(xpp, false);
			} else if ( name.equalsIgnoreCase(ElemProtNotes) ) {
				db.memoryProtection.protectNotes = ReadBool(xpp, false);
			} else if ( name.equalsIgnoreCase(ElemProtAutoHide) ) {
				db.memoryProtection.autoEnableVisualHiding = ReadBool(xpp, false);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case CustomIcons:
			if ( name.equalsIgnoreCase(ElemCustomIconItem) ) {
				return SwitchContext(ctx, KdbContext.CustomIcon, xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case CustomIcon:
			if ( name.equalsIgnoreCase(ElemCustomIconItemID) ) {
				customIconID = ReadUuid(xpp);
			} else if ( name.equalsIgnoreCase(ElemCustomIconItemData) ) {
				String strData = ReadString(xpp);
				if ( strData != null && strData.length() > 0 ) {
					customIconData = Base64.decode(strData, Base64.NO_WRAP);
				} else {
					assert(false);
				}
			} else {
				ReadUnknown(xpp);
			}
			break;

		case Binaries:
			if ( name.equalsIgnoreCase(ElemBinary) ) {
				String key = xpp.getAttributeValue(null, AttrId);
				if ( key != null ) {
					ProtectedBinary pbData = ReadProtectedBinary(xpp);
					int id = Integer.parseInt(key);
					db.binPool.put(id, pbData);
				} else {
					ReadUnknown(xpp);
				}
			} else {
				ReadUnknown(xpp);
			}

			break;

		case CustomData:
			if ( name.equalsIgnoreCase(ElemStringDictExItem) ) {
				return SwitchContext(ctx, KdbContext.CustomDataItem, xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case CustomDataItem:
			if ( name.equalsIgnoreCase(ElemKey) ) {
				customDataKey = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemValue) ) {
				customDataValue = ReadString(xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case Root:
			if ( name.equalsIgnoreCase(ElemGroup) ) {
				assert(ctxGroups.size() == 0);
				if ( ctxGroups.size() != 0 ) throw new IOException("Group list should be empty.");

				db.rootGroup = new PwGroupV4();
				ctxGroups.push((PwGroupV4)db.rootGroup);
				ctxGroup = ctxGroups.peek();

				return SwitchContext(ctx, KdbContext.Group, xpp);
			} else if ( name.equalsIgnoreCase(ElemDeletedObjects) ) {
				return SwitchContext(ctx, KdbContext.RootDeletedObjects, xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case Group:
			if ( name.equalsIgnoreCase(ElemUuid) ) {
				ctxGroup.uuid = ReadUuid(xpp);
			} else if ( name.equalsIgnoreCase(ElemName) ) {
				ctxGroup.name = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemNotes) ) {
				ctxGroup.notes = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemIcon) ) {
				ctxGroup.icon = db.iconFactory.getIcon((int)ReadUInt(xpp, 0));
			} else if ( name.equalsIgnoreCase(ElemCustomIconID) ) {
				ctxGroup.customIcon = db.iconFactory.getIcon(ReadUuid(xpp));
			} else if ( name.equalsIgnoreCase(ElemTimes) ) {
				return SwitchContext(ctx, KdbContext.GroupTimes, xpp);
			} else if ( name.equalsIgnoreCase(ElemIsExpanded) ) {
				ctxGroup.isExpanded = ReadBool(xpp, true);
			} else if ( name.equalsIgnoreCase(ElemGroupDefaultAutoTypeSeq) ) {
				ctxGroup.defaultAutoTypeSequence = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemEnableAutoType) ) {
				ctxGroup.enableAutoType = StringToBoolean(ReadString(xpp));
			} else if ( name.equalsIgnoreCase(ElemEnableSearching) ) {
				ctxGroup.enableSearching = StringToBoolean(ReadString(xpp));
			} else if ( name.equalsIgnoreCase(ElemLastTopVisibleEntry) ) {
				ctxGroup.lastTopVisibleEntry = ReadUuid(xpp);
			} else if ( name.equalsIgnoreCase(ElemCustomData) ) {
                return SwitchContext(ctx, KdbContext.GroupCustomData, xpp);
			} else if ( name.equalsIgnoreCase(ElemGroup) ) {
				ctxGroup = new PwGroupV4();
				ctxGroups.peek().AddGroup(ctxGroup, true);
				ctxGroups.push(ctxGroup);

				return SwitchContext(ctx, KdbContext.Group, xpp);
			} else if ( name.equalsIgnoreCase(ElemEntry) ) {
				ctxEntry = new PwEntryV4();
				ctxGroup.AddEntry(ctxEntry, true);

				entryInHistory = false;
				return SwitchContext(ctx, KdbContext.Entry, xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;
        case GroupCustomData:
        	if (name.equalsIgnoreCase(ElemStringDictExItem)) {
				return SwitchContext(ctx, KdbContext.GroupCustomDataItem, xpp);
			} else {
				ReadUnknown(xpp);
			}
            break;
        case GroupCustomDataItem:
        	if (name.equalsIgnoreCase(ElemKey)) {
				groupCustomDataKey = ReadString(xpp);
			} else if (name.equalsIgnoreCase(ElemValue)) {
				groupCustomDataValue = ReadString(xpp);
            } else {
                ReadUnknown(xpp);
            }
            break;


		case Entry:
			if ( name.equalsIgnoreCase(ElemUuid) ) {
				ctxEntry.setUUID(ReadUuid(xpp));
			} else if ( name.equalsIgnoreCase(ElemIcon) ) {
				ctxEntry.icon = db.iconFactory.getIcon((int)ReadUInt(xpp, 0));
			} else if ( name.equalsIgnoreCase(ElemCustomIconID) ) {
				ctxEntry.customIcon = db.iconFactory.getIcon(ReadUuid(xpp));
			} else if ( name.equalsIgnoreCase(ElemFgColor) ) {
				ctxEntry.foregroundColor = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemBgColor) ) {
				ctxEntry.backgroupColor = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemOverrideUrl) ) {
				ctxEntry.overrideURL = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemTags) ) {
				ctxEntry.tags = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemTimes) ) {
				return SwitchContext(ctx, KdbContext.EntryTimes, xpp);
			} else if ( name.equalsIgnoreCase(ElemString) ) {
				return SwitchContext(ctx, KdbContext.EntryString, xpp);
			} else if ( name.equalsIgnoreCase(ElemBinary) ) {
				return SwitchContext(ctx, KdbContext.EntryBinary, xpp);
			} else if ( name.equalsIgnoreCase(ElemAutoType) ) {
				return SwitchContext(ctx, KdbContext.EntryAutoType, xpp);
			} else if ( name.equalsIgnoreCase(ElemCustomData)) {
				return SwitchContext(ctx, KdbContext.EntryCustomData, xpp);
			} else if ( name.equalsIgnoreCase(ElemHistory) ) {
				assert(!entryInHistory);

				if ( ! entryInHistory ) {
					ctxHistoryBase = ctxEntry;
					return SwitchContext(ctx, KdbContext.EntryHistory, xpp);
				} else {
					ReadUnknown(xpp);
				}
			} else {
				ReadUnknown(xpp);
			}
			break;
        case EntryCustomData:
            if (name.equalsIgnoreCase(ElemStringDictExItem)) {
                return SwitchContext(ctx, KdbContext.EntryCustomDataItem, xpp);
            } else {
                ReadUnknown(xpp);
            }
            break;
        case EntryCustomDataItem:
            if (name.equalsIgnoreCase(ElemKey)) {
                entryCustomDataKey = ReadString(xpp);
            } else if (name.equalsIgnoreCase(ElemValue)) {
                entryCustomDataValue = ReadString(xpp);
            } else {
                ReadUnknown(xpp);
            }
            break;

		case GroupTimes:
		case EntryTimes:
			ITimeLogger tl;
			if ( ctx == KdbContext.GroupTimes ) {
				tl = ctxGroup;
			} else {
				tl = ctxEntry;
			}

			if ( name.equalsIgnoreCase(ElemLastModTime) ) {
				tl.setLastModificationTime(ReadTime(xpp));
			} else if ( name.equalsIgnoreCase(ElemCreationTime) ) {
				tl.setCreationTime(ReadTime(xpp));
			} else if ( name.equalsIgnoreCase(ElemLastAccessTime) ) {
				tl.setLastAccessTime(ReadTime(xpp));
			} else if ( name.equalsIgnoreCase(ElemExpiryTime) ) {
				tl.setExpiryTime(ReadTime(xpp));
			} else if ( name.equalsIgnoreCase(ElemExpires) ) {
				tl.setExpires(ReadBool(xpp, false));
			} else if ( name.equalsIgnoreCase(ElemUsageCount) ) {
				tl.setUsageCount(ReadULong(xpp, 0));
			} else if ( name.equalsIgnoreCase(ElemLocationChanged) ) {
				tl.setLocationChanged(ReadTime(xpp));
			} else {
				ReadUnknown(xpp);
			}
			break;

		case EntryString:
			if ( name.equalsIgnoreCase(ElemKey) ) {
				ctxStringName = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemValue) ) {
				ctxStringValue = ReadProtectedString(xpp); 
			} else {
				ReadUnknown(xpp);
			}
			break;

		case EntryBinary:
			if ( name.equalsIgnoreCase(ElemKey) ) {
				ctxBinaryName = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemValue) ) {
				ctxBinaryValue = ReadProtectedBinary(xpp);
			}
			break;

		case EntryAutoType:
			if ( name.equalsIgnoreCase(ElemAutoTypeEnabled) ) {
				ctxEntry.autoType.enabled = ReadBool(xpp, true);
			} else if ( name.equalsIgnoreCase(ElemAutoTypeObfuscation) ) {
				ctxEntry.autoType.obfuscationOptions = ReadUInt(xpp, 0);
			} else if ( name.equalsIgnoreCase(ElemAutoTypeDefaultSeq) ) {
				ctxEntry.autoType.defaultSequence = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemAutoTypeItem) ) {
				return SwitchContext(ctx, KdbContext.EntryAutoTypeItem, xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case EntryAutoTypeItem:
			if ( name.equalsIgnoreCase(ElemWindow) ) {
				ctxATName = ReadString(xpp);
			} else if ( name.equalsIgnoreCase(ElemKeystrokeSequence) ) {
				ctxATSeq = ReadString(xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case EntryHistory:
			if ( name.equalsIgnoreCase(ElemEntry) ) {
				ctxEntry = new PwEntryV4();
				ctxHistoryBase.history.add(ctxEntry);

				entryInHistory = true;
				return SwitchContext(ctx, KdbContext.Entry, xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case RootDeletedObjects:
			if ( name.equalsIgnoreCase(ElemDeletedObject) ) {
				ctxDeletedObject = new PwDeletedObject();
				db.deletedObjects.add(ctxDeletedObject);

				return SwitchContext(ctx, KdbContext.DeletedObject, xpp);
			} else {
				ReadUnknown(xpp);
			}
			break;

		case DeletedObject:
			if ( name.equalsIgnoreCase(ElemUuid) ) {
				ctxDeletedObject.uuid = ReadUuid(xpp);
			} else if ( name.equalsIgnoreCase(ElemDeletionTime) ) {
				ctxDeletedObject.setDeletionTime(ReadTime(xpp));
			} else {
				ReadUnknown(xpp);
			}
			break;

		default:
			ReadUnknown(xpp);
			break;
		}

		return ctx;
	}

	private KdbContext EndXmlElement(KdbContext ctx, XmlPullParser xpp) throws XmlPullParserException {
		assert(xpp.getEventType() == XmlPullParser.END_TAG);

		String name = xpp.getName();
		if ( ctx == KdbContext.KeePassFile && name.equalsIgnoreCase(ElemDocNode) ) {
			return KdbContext.Null;
		} else if ( ctx == KdbContext.Meta && name.equalsIgnoreCase(ElemMeta) ) {
			return KdbContext.KeePassFile;
		} else if ( ctx == KdbContext.Root && name.equalsIgnoreCase(ElemRoot) ) {
			return KdbContext.KeePassFile;
		} else if ( ctx == KdbContext.MemoryProtection && name.equalsIgnoreCase(ElemMemoryProt) ) {
			return KdbContext.Meta;
		} else if ( ctx == KdbContext.CustomIcons && name.equalsIgnoreCase(ElemCustomIcons) ) {
			return KdbContext.Meta;
		} else if ( ctx == KdbContext.CustomIcon && name.equalsIgnoreCase(ElemCustomIconItem) ) {
			if ( ! customIconID.equals(PwDatabaseV4.UUID_ZERO) ) {
				PwIconCustom icon = new PwIconCustom(customIconID, customIconData);
				db.customIcons.add(icon);
				db.iconFactory.put(icon);
			} else assert(false);

			customIconID = PwDatabaseV4.UUID_ZERO;
			customIconData = null;

			return KdbContext.CustomIcons;
		} else if ( ctx == KdbContext.Binaries && name.equalsIgnoreCase(ElemBinaries) ) {
			return KdbContext.Meta;
		} else if ( ctx == KdbContext.CustomData && name.equalsIgnoreCase(ElemCustomData) ) {
			return KdbContext.Meta;
		} else if ( ctx == KdbContext.CustomDataItem && name.equalsIgnoreCase(ElemStringDictExItem) ) {
			if ( customDataKey != null && customDataValue != null) {
				db.customData.put(customDataKey, customDataValue);
			} else assert(false);

			customDataKey = null;
			customDataValue = null;

			return KdbContext.CustomData;
		} else if ( ctx == KdbContext.Group && name.equalsIgnoreCase(ElemGroup) ) {
			if ( ctxGroup.uuid == null || ctxGroup.uuid.equals(PwDatabaseV4.UUID_ZERO) ) {
				ctxGroup.uuid = UUID.randomUUID();
			}

			ctxGroups.pop();

			if ( ctxGroups.size() == 0 ) {
				ctxGroup = null;
				return KdbContext.Root;
			} else {
				ctxGroup = ctxGroups.peek();
				return KdbContext.Group;
			}
		} else if ( ctx == KdbContext.GroupTimes && name.equalsIgnoreCase(ElemTimes) ) {
			return KdbContext.Group;
		} else if ( ctx == KdbContext.GroupCustomData && name.equalsIgnoreCase(ElemCustomData) ) {
			return KdbContext.Group;
		} else if ( ctx == KdbContext.GroupCustomDataItem && name.equalsIgnoreCase(ElemStringDictExItem)) {
			if (groupCustomDataKey != null && groupCustomDataValue != null) {
				ctxGroup.customData.put(groupCustomDataKey, groupCustomDataKey);
			} else {
				assert(false);
			}

			groupCustomDataKey = null;
			groupCustomDataValue = null;

			return KdbContext.GroupCustomData;

		} else if ( ctx == KdbContext.Entry && name.equalsIgnoreCase(ElemEntry) ) {
			if ( ctxEntry.uuid == null || ctxEntry.uuid.equals(PwDatabaseV4.UUID_ZERO) ) {
				ctxEntry.uuid = UUID.randomUUID();
			}

			if ( entryInHistory ) {
				ctxEntry = ctxHistoryBase;
				return KdbContext.EntryHistory;
			}

			return KdbContext.Group;
		} else if ( ctx == KdbContext.EntryTimes && name.equalsIgnoreCase(ElemTimes) ) {
			return KdbContext.Entry;
		} else if ( ctx == KdbContext.EntryString && name.equalsIgnoreCase(ElemString) ) {
			ctxEntry.strings.put(ctxStringName, ctxStringValue);
			ctxStringName = null;
			ctxStringValue = null;

			return KdbContext.Entry;
		} else if ( ctx == KdbContext.EntryBinary && name.equalsIgnoreCase(ElemBinary) ) {
			ctxEntry.binaries.put(ctxBinaryName, ctxBinaryValue);
			ctxBinaryName = null;
			ctxBinaryValue = null;

			return KdbContext.Entry;
		} else if ( ctx == KdbContext.EntryAutoType && name.equalsIgnoreCase(ElemAutoType) ) {
			return KdbContext.Entry;
		} else if ( ctx == KdbContext.EntryAutoTypeItem && name.equalsIgnoreCase(ElemAutoTypeItem) ) {
			ctxEntry.autoType.put(ctxATName, ctxATSeq);
			ctxATName = null;
			ctxATSeq = null;

			return KdbContext.EntryAutoType;
		} else if ( ctx == KdbContext.EntryCustomData && name.equalsIgnoreCase(ElemCustomData)) {
			return KdbContext.Entry;
		} else if ( ctx == KdbContext.EntryCustomDataItem && name.equalsIgnoreCase(ElemStringDictExItem)) {
			if (entryCustomDataKey != null && entryCustomDataValue != null) {
				ctxEntry.customData.put(entryCustomDataKey, entryCustomDataValue);
			} else {
				assert(false);
			}

			entryCustomDataKey = null;
			entryCustomDataValue = null;

			return KdbContext.EntryCustomData;
		} else if ( ctx == KdbContext.EntryHistory && name.equalsIgnoreCase(ElemHistory) ) {
			entryInHistory = false;
			return KdbContext.Entry;
		} else if ( ctx == KdbContext.RootDeletedObjects && name.equalsIgnoreCase(ElemDeletedObjects) ) {
			return KdbContext.Root;
		} else if ( ctx == KdbContext.DeletedObject && name.equalsIgnoreCase(ElemDeletedObject) ) {
			ctxDeletedObject = null;
			return KdbContext.RootDeletedObjects;
		} else {
			assert(false);

			String contextName = "";
			if (ctx != null) {
				contextName = ctx.name();
			}
			throw new RuntimeException("Invalid end element: Context " +  contextName + "End element: " + name);
		}
	}

	private Date ReadTime(XmlPullParser xpp) throws IOException, XmlPullParserException {
		String sDate = ReadString(xpp);
		Date utcDate = null;

		if (version >= PwDbHeaderV4.FILE_VERSION_32_4) {
			byte[] buf = Base64.decode(sDate, Base64.NO_WRAP);
			if (buf.length != 8) {
				byte[] buf8 = new byte[8];
				System.arraycopy(buf, 0, buf8, 0, Math.min(buf.length, 8));
				buf = buf8;
			}

			long seconds = LEDataInputStream.readLong(buf, 0);
			utcDate = DateUtil.convertKDBX4Time(seconds);

		} else {

			try {
				utcDate = PwDatabaseV4XML.dateFormatter.get().parse(sDate);
			} catch (ParseException e) {
				// Catch with null test below
			}

			if (utcDate == null) {
				utcDate = new Date(0L);
			}
		}

		return utcDate;

	}

	private void ReadUnknown(XmlPullParser xpp) throws XmlPullParserException, IOException {
		assert(false);

		if ( xpp.isEmptyElementTag() ) return;

		String unknownName = xpp.getName();
		ProcessNode(xpp);

		while (xpp.next() != XmlPullParser.END_DOCUMENT ) {
			if ( xpp.getEventType() == XmlPullParser.END_TAG ) break;
			if ( xpp.getEventType() == XmlPullParser.START_TAG ) continue;

			ReadUnknown(xpp);
		}

		assert(xpp.getName().equals(unknownName));

	}

	private boolean ReadBool(XmlPullParser xpp, boolean bDefault) throws IOException, XmlPullParserException {
		String str = ReadString(xpp);

		if ( str.equalsIgnoreCase("true") ) {
			return true;
		} else if ( str.equalsIgnoreCase("false") ) {
			return false;
		} else {
			return bDefault;
		}
	}

	private UUID ReadUuid(XmlPullParser xpp) throws IOException, XmlPullParserException {
		String encoded = ReadString(xpp);

		if (encoded == null || encoded.length() == 0 ) {
			return PwDatabaseV4.UUID_ZERO;
		}

		byte[] buf = Base64.decode(encoded, Base64.NO_WRAP);

		return Types.bytestoUUID(buf);
	}

	private int ReadInt(XmlPullParser xpp, int def) throws IOException, XmlPullParserException {
		String str = ReadString(xpp);

		int u;
		try {
			u = Integer.parseInt(str);
		} catch( NumberFormatException e) {
			u = def;
		}

		return u;
	}

	private static final long MAX_UINT = 4294967296L; // 2^32
	private long ReadUInt(XmlPullParser xpp, long uDefault) throws IOException, XmlPullParserException {
		long u;

		u = ReadULong(xpp, uDefault);
		if ( u < 0 || u > MAX_UINT ) {
			throw new NumberFormatException("Outside of the uint size");
		}

		return u;

	}

	private long ReadLong(XmlPullParser xpp, long def) throws IOException, XmlPullParserException {
		String str = ReadString(xpp);

		long u;
		try {
			u = Long.parseLong(str);
		} catch( NumberFormatException e) {
			u = def;
		}

		return u;
	}

	private long ReadULong(XmlPullParser xpp, long uDefault) throws IOException, XmlPullParserException {
		long u = ReadLong(xpp, uDefault);

		if ( u < 0 ) {
			u = uDefault;
		}

		return u;

	}

	private ProtectedString ReadProtectedString(XmlPullParser xpp) throws XmlPullParserException, IOException {
		byte[] buf = ProcessNode(xpp);

		if ( buf != null) {
			try {
				return new ProtectedString(true, new String(buf, "UTF-8"));
			} catch (UnsupportedEncodingException e) {
				e.printStackTrace();
				throw new IOException(e.getLocalizedMessage());
			} 
		}

		return new ProtectedString(false, ReadString(xpp));
	}

	private ProtectedBinary ReadProtectedBinary(XmlPullParser xpp) throws XmlPullParserException, IOException {
		String ref = xpp.getAttributeValue(null, AttrRef);
		if (ref != null) {
			xpp.next(); // Consume end tag

			int id = Integer.parseInt(ref);
			return db.binPool.get(id);
		} 

		boolean compressed = false;
		String comp = xpp.getAttributeValue(null, AttrCompressed);
		if (comp != null) {
			compressed = comp.equalsIgnoreCase(ValTrue);
		}

		byte[] buf = ProcessNode(xpp);

		if ( buf != null ) return new ProtectedBinary(true, buf);

		String base64 = ReadString(xpp);
		if ( base64.length() == 0 ) return ProtectedBinary.EMPTY;

		byte[] data = Base64.decode(base64, Base64.NO_WRAP);

		if (compressed) {
			data = MemUtil.decompress(data);
		}

		return new ProtectedBinary(false, data);
	}

	private String ReadString(XmlPullParser xpp) throws IOException, XmlPullParserException {
		byte[] buf = ProcessNode(xpp);

		if ( buf != null ) {
			try {
				return new String(buf, "UTF-8");
			} catch (UnsupportedEncodingException e) {
				throw new IOException(e.getLocalizedMessage());
			}
		}

		//readNextNode = false;
		return xpp.nextText();

	}

	private String ReadStringRaw(XmlPullParser xpp) throws XmlPullParserException, IOException {

		//readNextNode = false;
		return xpp.nextText();
	}

	private byte[] ProcessNode(XmlPullParser xpp) throws XmlPullParserException, IOException {
		assert(xpp.getEventType() == XmlPullParser.START_TAG);

		byte[] buf = null;

		if ( xpp.getAttributeCount() > 0 ) {
			String protect = xpp.getAttributeValue(null, AttrProtected);
			if ( protect != null && protect.equalsIgnoreCase(ValTrue) ) {
				String encrypted = ReadStringRaw(xpp);

				if ( encrypted.length() > 0 ) {
					buf = Base64.decode(encrypted, Base64.NO_WRAP);
					byte[] plainText = new byte[buf.length];

					randomStream.processBytes(buf, 0, buf.length, plainText, 0);

					return plainText;
				} else {
					buf = new byte[0];
				}
			}
		}

		return buf;
	}

	private KdbContext SwitchContext(KdbContext ctxCurrent, KdbContext ctxNew,
			XmlPullParser xpp) throws XmlPullParserException, IOException {

		if ( xpp.isEmptyElementTag() ) {
			xpp.next();  // Consume the end tag
			return ctxCurrent;
		}
		return ctxNew;
	}


	private Boolean StringToBoolean(String str) {
		if ( str == null || str.length() == 0 ) {
			return null;
		}

		String trimmed = str.trim();
		if ( trimmed.equalsIgnoreCase("true") ) {
			return true;
		} else if ( trimmed.equalsIgnoreCase("false") ) {
			return false;
		}

		return null;

	}
}

public class ImporterV4Debug extends ImporterV4 {

	public ImporterV4Debug(File streamDir) {
		super(streamDir);
	}

	@Override
	protected PwDatabaseV4Debug createDB() {
		return new PwDatabaseV4Debug();
	}

	@Override
	public PwDatabaseV4Debug openDatabase(InputStream inStream, String password,
			InputStream keyInputFile, UpdateStatus status, long roundsFix) throws IOException,
			InvalidDBException {
		return (PwDatabaseV4Debug) super.openDatabase(inStream, password, keyInputFile, status,
				roundsFix);
	}

}

public class PwDbHeaderOutput {
	protected byte[] hashOfHeader = null;

	public byte[] getHashOfHeader() { return hashOfHeader; }

}

public class PwDbHeaderOutputV3 {
	private PwDbHeaderV3 mHeader;
	private OutputStream mOS;

	public PwDbHeaderOutputV3(PwDbHeaderV3 header, OutputStream os) {
		mHeader = header;
		mOS = os;
	}

	public void outputStart() throws IOException {
		mOS.write(LEDataOutputStream.writeIntBuf(mHeader.signature1));
		mOS.write(LEDataOutputStream.writeIntBuf(mHeader.signature2));
		mOS.write(LEDataOutputStream.writeIntBuf(mHeader.flags));
		mOS.write(LEDataOutputStream.writeIntBuf(mHeader.version));
		mOS.write(mHeader.masterSeed);
		mOS.write(mHeader.encryptionIV);
		mOS.write(LEDataOutputStream.writeIntBuf(mHeader.numGroups));
		mOS.write(LEDataOutputStream.writeIntBuf(mHeader.numEntries));
	}

	public void outputContentHash() throws IOException {
		mOS.write(mHeader.contentsHash);
	}

	public void outputEnd() throws IOException {
		mOS.write(mHeader.transformSeed);
		mOS.write(LEDataOutputStream.writeIntBuf(mHeader.numKeyEncRounds));
	}

	public void output() throws IOException {
		outputStart();
		outputContentHash();
		outputEnd();
	}

	public void close() throws IOException {
		mOS.close();
	}
}

public class PwDbHeaderOutputV4 extends PwDbHeaderOutput {
	private PwDbHeaderV4 header;
	private LEDataOutputStream los;
    private MacOutputStream mos;
	private DigestOutputStream dos;
	private PwDatabaseV4 db;
    public byte[] headerHmac;

	private static byte[] EndHeaderValue = {'\r', '\n', '\r', '\n'};

	public PwDbHeaderOutputV4(PwDatabaseV4 d, PwDbHeaderV4 h, OutputStream os) throws PwDbOutputException {
		db = d;
		header = h;

		MessageDigest md = null;
		try {
			md = MessageDigest.getInstance("SHA-256");
		} catch (NoSuchAlgorithmException e) {
			throw new PwDbOutputException("SHA-256 not implemented here.");
		}

		try {
			d.makeFinalKey(header.masterSeed, d.kdfParameters);
		} catch (IOException e) {
		    throw new PwDbOutputException(e);
		}

		Mac hmac;
		try {
			hmac = Mac.getInstance("HmacSHA256");
			SecretKeySpec signingKey = new SecretKeySpec(HmacBlockStream.GetHmacKey64(db.hmacKey, Types.ULONG_MAX_VALUE), "HmacSHA256");
			hmac.init(signingKey);
		} catch (NoSuchAlgorithmException e) {
            throw new PwDbOutputException(e);
		} catch (InvalidKeyException e) {
			throw new PwDbOutputException(e);
		}

		dos = new DigestOutputStream(os, md);
		mos = new MacOutputStream(dos, hmac);
		los = new LEDataOutputStream(mos);
	}

	public void output() throws IOException {

		los.writeUInt(PwDbHeader.PWM_DBSIG_1);
		los.writeUInt(PwDbHeaderV4.DBSIG_2);
        los.writeUInt(header.version);


		writeHeaderField(PwDbHeaderV4Fields.CipherID, Types.UUIDtoBytes(db.dataCipher));
		writeHeaderField(PwDbHeaderV4Fields.CompressionFlags, LEDataOutputStream.writeIntBuf(db.compressionAlgorithm.id));
		writeHeaderField(PwDbHeaderV4Fields.MasterSeed, header.masterSeed);

		if (header.version < PwDbHeaderV4.FILE_VERSION_32_4) {
			writeHeaderField(PwDbHeaderV4Fields.TransformSeed, header.getTransformSeed());
			writeHeaderField(PwDbHeaderV4Fields.TransformRounds, LEDataOutputStream.writeLongBuf(db.numKeyEncRounds));
		} else {
            writeHeaderField(PwDbHeaderV4Fields.KdfParameters, KdfParameters.serialize(db.kdfParameters));
		}

		if (header.encryptionIV.length > 0) {
			writeHeaderField(PwDbHeaderV4Fields.EncryptionIV, header.encryptionIV);
		}

		if (header.version < PwDbHeaderV4.FILE_VERSION_32_4) {
			writeHeaderField(PwDbHeaderV4Fields.InnerRandomstreamKey, header.innerRandomStreamKey);
			writeHeaderField(PwDbHeaderV4Fields.StreamStartBytes, header.streamStartBytes);
			writeHeaderField(PwDbHeaderV4Fields.InnerRandomStreamID, LEDataOutputStream.writeIntBuf(header.innerRandomStream.id));
		}

		if (db.publicCustomData.size() > 0) {
			ByteArrayOutputStream bos = new ByteArrayOutputStream();
			LEDataOutputStream los = new LEDataOutputStream(bos);
			VariantDictionary.serialize(db.publicCustomData, los);
			writeHeaderField(PwDbHeaderV4Fields.PublicCustomData, bos.toByteArray());
		}

		writeHeaderField(PwDbHeaderV4Fields.EndOfHeader, EndHeaderValue);

		los.flush();
		hashOfHeader = dos.getMessageDigest().digest();
		headerHmac = mos.getMac();
	}

	private void writeHeaderField(byte fieldId, byte[] pbData) throws IOException {
		// Write the field id
		los.write(fieldId);

		if (pbData != null) {
			writeHeaderFieldSize(pbData.length);
			los.write(pbData);
		} else {
		    writeHeaderFieldSize(0);
		}
	}

	private void writeHeaderFieldSize(int size) throws IOException {
		if (header.version < PwDbHeaderV4.FILE_VERSION_32_4) {
			los.writeUShort(size);
		} else {
			los.writeInt(size);
		}

	}
}

public class PwDbInnerHeaderOutputV4 {
    private PwDatabaseV4 db;
    private PwDbHeaderV4 header;
    private LEDataOutputStream los;

    public PwDbInnerHeaderOutputV4(PwDatabaseV4 db, PwDbHeaderV4 header, OutputStream os) {
        this.db = db;
        this.header = header;

        this.los = new LEDataOutputStream(os);
    }

    public void output() throws IOException {
        los.write(PwDbInnerHeaderV4Fields.InnerRandomStreamID);
        los.writeInt(4);
        los.writeInt(header.innerRandomStream.id);

        int streamKeySize = header.innerRandomStreamKey.length;
        los.write(PwDbInnerHeaderV4Fields.InnerRandomstreamKey);
        los.writeInt(streamKeySize);
        los.write(header.innerRandomStreamKey);

        for (ProtectedBinary bin : db.binPool.binaries()) {
            byte flag = KdbxBinaryFlags.None;
            if (bin.isProtected()) {
                flag |= KdbxBinaryFlags.Protected;
            }

            los.write(PwDbInnerHeaderV4Fields.Binary);
            los.writeInt((int) bin.length() + 1);
            los.write(flag);

            InputStream inputStream = bin.getData();
            int binLength = bin.length();
            Util.copyStream(inputStream, los);

        }

        los.write(PwDbInnerHeaderV4Fields.EndOfHeader);
        los.writeInt(0);
    }

}

public abstract class PwDbOutput {

	protected OutputStream mOS;

	public static PwDbOutput getInstance(PwDatabase pm, OutputStream os) {
		if ( pm instanceof PwDatabaseV3 ) {
			return new PwDbV3Output((PwDatabaseV3)pm, os);
		} else if ( pm instanceof PwDatabaseV4 ) {
			return new PwDbV4Output((PwDatabaseV4)pm, os);
		}

		return null;
	}

	protected PwDbOutput(OutputStream os) {
		mOS = os;
	}

	protected SecureRandom setIVs(PwDbHeader header) throws PwDbOutputException  {
		SecureRandom random;
		try {
			random = SecureRandom.getInstance("SHA1PRNG");
		} catch (NoSuchAlgorithmException e) {
			throw new PwDbOutputException("Does not support secure random number generation.");
		}
		random.nextBytes(header.encryptionIV);
		random.nextBytes(header.masterSeed);

		return random;
	}

	public abstract void output() throws PwDbOutputException;

	public abstract PwDbHeader outputHeader(OutputStream os) throws PwDbOutputException;

}

public class PwDbV3Output extends PwDbOutput {
	private PwDatabaseV3 mPM;
	private byte[] headerHashBlock;

	public PwDbV3Output(PwDatabaseV3 pm, OutputStream os) {
		super(os);

		mPM = pm;

	}

	public byte[] getFinalKey(PwDbHeader header) throws PwDbOutputException {
		try {
			PwDbHeaderV3 h3 = (PwDbHeaderV3) header;
			mPM.makeFinalKey(h3.masterSeed, h3.transformSeed, mPM.numKeyEncRounds);
			return mPM.finalKey;
		} catch (IOException e) {
			throw new PwDbOutputException("Key creation failed: " + e.getMessage());
		}
	}

	@Override
	public void output() throws PwDbOutputException {
		prepForOutput();

		PwDbHeader header = outputHeader(mOS);

		byte[] finalKey = getFinalKey(header);

		Cipher cipher;
		try {
			if (mPM.algorithm == PwEncryptionAlgorithm.Rjindal) {
				cipher = CipherFactory.getInstance("AES/CBC/PKCS5Padding");
			} else if (mPM.algorithm == PwEncryptionAlgorithm.Twofish){
				cipher = CipherFactory.getInstance("Twofish/CBC/PKCS7PADDING");
			} else {
				throw new Exception();
			}
		} catch (Exception e) {
			throw new PwDbOutputException("Algorithm not supported.");
		}

		try {
			cipher.init( Cipher.ENCRYPT_MODE, new SecretKeySpec(finalKey, "AES" ), new IvParameterSpec(header.encryptionIV) );
			CipherOutputStream cos = new CipherOutputStream(mOS, cipher);
			BufferedOutputStream bos = new BufferedOutputStream(cos);
			outputPlanGroupAndEntries(bos);
			bos.flush();
			bos.close();

		} catch (InvalidKeyException e) {
			throw new PwDbOutputException("Invalid key");
		} catch (InvalidAlgorithmParameterException e) {
			throw new PwDbOutputException("Invalid algorithm parameter.");
		} catch (IOException e) {
			throw new PwDbOutputException("Failed to output final encrypted part.");
		}
	}

	private void prepForOutput() {
		// Before we output the header, we should sort our list of groups and remove any orphaned nodes that are no longer part of the group hierarchy
		sortGroupsForOutput();
	}

	@Override
	protected SecureRandom setIVs(PwDbHeader header) throws PwDbOutputException {
		SecureRandom random = super.setIVs(header);

		PwDbHeaderV3 h3 = (PwDbHeaderV3) header;
		random.nextBytes(h3.transformSeed);

		return random;
	}

	public PwDbHeaderV3 outputHeader(OutputStream os) throws PwDbOutputException {
		// Build header
		PwDbHeaderV3 header = new PwDbHeaderV3();
		header.signature1 = PwDbHeader.PWM_DBSIG_1;
		header.signature2 = PwDbHeaderV3.DBSIG_2;
		header.flags = PwDbHeaderV3.FLAG_SHA2;

		if ( mPM.getEncAlgorithm() == PwEncryptionAlgorithm.Rjindal ) {
			header.flags |= PwDbHeaderV3.FLAG_RIJNDAEL;
		} else if ( mPM.getEncAlgorithm() == PwEncryptionAlgorithm.Twofish ) {
			header.flags |= PwDbHeaderV3.FLAG_TWOFISH;
		} else {
			throw new PwDbOutputException("Unsupported algorithm.");
		}

		header.version = PwDbHeaderV3.DBVER_DW;
		header.numGroups = mPM.getGroups().size();
		header.numEntries = mPM.entries.size();
		header.numKeyEncRounds = mPM.getNumKeyEncRecords();

		setIVs(header);

		// Content checksum
		MessageDigest md = null;
		try {
			md = MessageDigest.getInstance("SHA-256");
		} catch (NoSuchAlgorithmException e) {
			throw new PwDbOutputException("SHA-256 not implemented here.");
		}

		// Header checksum
		MessageDigest headerDigest;
		try {
			headerDigest = MessageDigest.getInstance("SHA-256");
		} catch (NoSuchAlgorithmException e) {
			throw new PwDbOutputException("SHA-256 not implemented here.");
		}
		NullOutputStream nos;
		nos = new NullOutputStream();
		DigestOutputStream headerDos = new DigestOutputStream(nos, headerDigest);

		// Output header for the purpose of calculating the header checksum
		PwDbHeaderOutputV3 pho = new PwDbHeaderOutputV3(header, headerDos);
		try {
			pho.outputStart();
			pho.outputEnd();
			headerDos.flush();
		} catch (IOException e) {
			throw new PwDbOutputException(e);
		}
		byte[] headerHash = headerDigest.digest();
		headerHashBlock = getHeaderHashBuffer(headerHash);

		// Output database for the purpose of calculating the content checksum
		nos = new NullOutputStream();
		DigestOutputStream dos = new DigestOutputStream(nos, md);
		BufferedOutputStream bos = new BufferedOutputStream(dos);
		try {
			outputPlanGroupAndEntries(bos);
			bos.flush();
			bos.close();
		} catch (IOException e) {
			throw new PwDbOutputException("Failed to generate checksum.");
		}

		header.contentsHash = md.digest();

		// Output header for real output, containing content hash
		pho = new PwDbHeaderOutputV3(header, os);
		try {
			pho.outputStart();
			dos.on(false);
			pho.outputContentHash();
			dos.on(true);
			pho.outputEnd();
			dos.flush();
		} catch (IOException e) {
			throw new PwDbOutputException(e);
		}

		return header;
	}

	public void outputPlanGroupAndEntries(OutputStream os) throws PwDbOutputException  {
		LEDataOutputStream los = new LEDataOutputStream(os);

		if (useHeaderHash() && headerHashBlock != null) {
		    try {
			    los.writeUShort(0x0000);
			    los.writeInt(headerHashBlock.length);
			    los.write(headerHashBlock);
		    } catch (IOException e) {
			    throw new PwDbOutputException("Failed to output header hash: " + e.getMessage());
		    }
		}

		// Groups
		List<PwGroup> groups = mPM.getGroups();
		for ( int i = 0; i < groups.size(); i++ ) {
			PwGroupV3 pg = (PwGroupV3) groups.get(i);
			PwGroupOutputV3 pgo = new PwGroupOutputV3(pg, os);
			try {
				pgo.output();
			} catch (IOException e) {
				throw new PwDbOutputException("Failed to output a group: " + e.getMessage());
			}
		}

		// Entries
		for (int i = 0; i < mPM.entries.size(); i++ ) {
			PwEntryV3 pe = (PwEntryV3) mPM.entries.get(i);
			PwEntryOutputV3 peo = new PwEntryOutputV3(pe, os);
			try {
				peo.output();
			} catch (IOException e) {
				throw new PwDbOutputException("Failed to output an entry.");
			}
		}
	}

	private void sortGroupsForOutput() {
		List<PwGroup> groupList = new ArrayList<PwGroup>();

		// Rebuild list according to coalation sorting order removing any orphaned groups
		List<PwGroup> roots = mPM.getGrpRoots();
		for ( int i = 0; i < roots.size(); i++ ) {
			sortGroup((PwGroupV3) roots.get(i), groupList);
		}

		mPM.setGroups(groupList);
	}

	private void sortGroup(PwGroupV3 group, List<PwGroup> groupList) {
		// Add current group
		groupList.add(group);

		// Recurse over children
		for ( int i = 0; i < group.childGroups.size(); i++ ) {
			sortGroup((PwGroupV3) group.childGroups.get(i), groupList);
		}
	}

	private byte[] getHeaderHashBuffer(byte[] headerDigest) {
		ByteArrayOutputStream baos = new ByteArrayOutputStream();
		try {
			writeExtData(headerDigest, baos);
			return baos.toByteArray();
		} catch (IOException e) {
			return null;
		}
	}

	private void writeExtData(byte[] headerDigest, OutputStream os) throws IOException {
		LEDataOutputStream los = new LEDataOutputStream(os);

	    writeExtDataField(los, 0x0001, headerDigest, headerDigest.length);
	    byte[] headerRandom = new byte[32];
	    SecureRandom rand = new SecureRandom();
	    rand.nextBytes(headerRandom);
	    writeExtDataField(los, 0x0002, headerRandom, headerRandom.length);
	    writeExtDataField(los, 0xFFFF, null, 0);

	}

	private void writeExtDataField(LEDataOutputStream los, int fieldType, byte[] data, int fieldSize) throws IOException {
		los.writeUShort(fieldType);
		los.writeInt(fieldSize);
		if (data != null) {
		    los.write(data);
		}

	}

	protected boolean useHeaderHash() {
		return true;
	}
}

public class PwDbV3OutputDebug extends PwDbV3Output {
	PwDatabaseV3Debug debugDb;
	private boolean noHeaderHash;

	public PwDbV3OutputDebug(PwDatabaseV3 pm, OutputStream os) {
		this(pm, os, false);
	}

	public PwDbV3OutputDebug(PwDatabaseV3 pm, OutputStream os, boolean noHeaderHash) {
		super(pm, os);
		debugDb = (PwDatabaseV3Debug) pm;
		this.noHeaderHash = noHeaderHash;
	}

	@Override
	protected SecureRandom setIVs(PwDbHeader h) throws PwDbOutputException {
		PwDbHeaderV3 header = (PwDbHeaderV3) h;


		// Reuse random values to test equivalence in debug mode
		PwDbHeaderV3 origHeader = debugDb.dbHeader;
		System.arraycopy(origHeader.encryptionIV, 0, header.encryptionIV, 0, origHeader.encryptionIV.length);
		System.arraycopy(origHeader.masterSeed, 0, header.masterSeed, 0, origHeader.masterSeed.length);
		System.arraycopy(origHeader.transformSeed, 0, header.transformSeed, 0, origHeader.transformSeed.length);

		return null;
	}

	@Override
	protected boolean useHeaderHash() {
		return !noHeaderHash;
	}

}

public class PwDbV4Output extends PwDbOutput {

	PwDatabaseV4 mPM;
	private StreamCipher randomStream;
	private XmlSerializer xml;
	private PwDbHeaderV4 header;
	private byte[] hashOfHeader;
	private byte[] headerHmac;
    private CipherEngine engine = null;

	protected PwDbV4Output(PwDatabaseV4 pm, OutputStream os) {
		super(os);

		mPM = pm;
	}

	@Override
	public void output() throws PwDbOutputException {

        try {
			try {
				engine = CipherFactory.getInstance(mPM.dataCipher);
			} catch (NoSuchAlgorithmException e) {
				throw new PwDbOutputException("No such cipher", e);
			}

			header = (PwDbHeaderV4) outputHeader(mOS);

			OutputStream osPlain;
			if (header.version < PwDbHeaderV4.FILE_VERSION_32_4) {
				CipherOutputStream cos = attachStreamEncryptor(header, mOS);
				cos.write(header.streamStartBytes);

				HashedBlockOutputStream hashed = new HashedBlockOutputStream(cos);
				osPlain = hashed;
			} else {
				mOS.write(hashOfHeader);
				mOS.write(headerHmac);

				HmacBlockOutputStream hbos = new HmacBlockOutputStream(mOS, mPM.hmacKey);
				osPlain = attachStreamEncryptor(header, hbos);
			}

			OutputStream osXml;
			try {


				if (mPM.compressionAlgorithm == PwCompressionAlgorithm.Gzip) {
					osXml = new GZIPOutputStream(osPlain);
				} else {
					osXml = osPlain;
				}

				if (header.version >= PwDbHeaderV4.FILE_VERSION_32_4) {
					PwDbInnerHeaderOutputV4 ihOut =  new PwDbInnerHeaderOutputV4((PwDatabaseV4)mPM, header, osXml);
                    ihOut.output();
				}


				outputDatabase(osXml);
				osXml.close();
			} catch (IllegalArgumentException e) {
				throw new PwDbOutputException(e);
			} catch (IllegalStateException e) {
				throw new PwDbOutputException(e);
			}
		} catch (IOException e) {
			throw new PwDbOutputException(e);
		}
	}

	private class GroupWriter extends GroupHandler<PwGroup> {
		private Stack<PwGroupV4> groupStack;

		public GroupWriter(Stack<PwGroupV4> gs) {
			groupStack = gs;
		}

		@Override
		public boolean operate(PwGroup g) {
			PwGroupV4 group = (PwGroupV4) g;
			assert(group != null);

			while(true) {
				try {
					if (group.parent == groupStack.peek()) {
						groupStack.push(group);
						startGroup(group);
						break;
					} else {
						groupStack.pop();
						if (groupStack.size() <= 0) return false;
						endGroup();
					}
				} catch (IOException e) {
					throw new RuntimeException(e);
				}
			}

			return true;
		}
	}

	private class EntryWriter extends EntryHandler<PwEntry> {

		@Override
		public boolean operate(PwEntry e) {
			PwEntryV4 entry = (PwEntryV4) e;
			assert(entry != null);

			try {
				writeEntry(entry, false);
			} catch (IOException ex) {
				throw new RuntimeException(ex);
			}

			return true;
		}

	}

	private void outputDatabase(OutputStream os) throws IllegalArgumentException, IllegalStateException, IOException {

		xml = Xml.newSerializer();

		xml.setOutput(os, "UTF-8");
		xml.startDocument("UTF-8", true);

		xml.startTag(null, ElemDocNode);

		writeMeta();

		PwGroupV4 root = (PwGroupV4) mPM.rootGroup;
		xml.startTag(null, ElemRoot);
		startGroup(root);
		Stack<PwGroupV4> groupStack = new Stack<PwGroupV4>();
		groupStack.push(root);

		if (!root.preOrderTraverseTree(new GroupWriter(groupStack), new EntryWriter())) throw new RuntimeException("Writing groups failed");

		while (groupStack.size() > 1) {
			xml.endTag(null, ElemGroup);
			groupStack.pop();
		}

		endGroup();

		writeList(ElemDeletedObjects, mPM.deletedObjects);

		xml.endTag(null, ElemRoot);

		xml.endTag(null, ElemDocNode);
		xml.endDocument();

	}

	private void writeMeta() throws IllegalArgumentException, IllegalStateException, IOException {
		xml.startTag(null, ElemMeta);

		writeObject(ElemGenerator, mPM.localizedAppName);

		if (hashOfHeader != null) {
			writeObject(ElemHeaderHash, Base64.encodeToString(hashOfHeader, Base64.NO_WRAP));
		}

		writeObject(ElemDbName, mPM.name, true);
		writeObject(ElemDbNameChanged, mPM.nameChanged);
		writeObject(ElemDbDesc, mPM.description, true);
		writeObject(ElemDbDescChanged, mPM.descriptionChanged);
		writeObject(ElemDbDefaultUser, mPM.defaultUserName, true);
		writeObject(ElemDbDefaultUserChanged, mPM.defaultUserNameChanged);
		writeObject(ElemDbMntncHistoryDays, mPM.maintenanceHistoryDays);
		writeObject(ElemDbColor, mPM.color);
		writeObject(ElemDbKeyChanged, mPM.keyLastChanged);
		writeObject(ElemDbKeyChangeRec, mPM.keyChangeRecDays);
		writeObject(ElemDbKeyChangeForce, mPM.keyChangeForceDays);


		writeList(ElemMemoryProt, mPM.memoryProtection);

		writeCustomIconList();

		writeObject(ElemRecycleBinEnabled, mPM.recycleBinEnabled);
		writeObject(ElemRecycleBinUuid, mPM.recycleBinUUID);
		writeObject(ElemRecycleBinChanged, mPM.recycleBinChanged);
		writeObject(ElemEntryTemplatesGroup, mPM.entryTemplatesGroup);
		writeObject(ElemEntryTemplatesGroupChanged, mPM.entryTemplatesGroupChanged);
		writeObject(ElemHistoryMaxItems, mPM.historyMaxItems);
		writeObject(ElemHistoryMaxSize, mPM.historyMaxSize);
		writeObject(ElemLastSelectedGroup, mPM.lastSelectedGroup);
		writeObject(ElemLastTopVisibleGroup, mPM.lastTopVisibleGroup);

		if (header.version < PwDbHeaderV4.FILE_VERSION_32_4) {
			writeBinPool();
		}
		writeList(ElemCustomData, mPM.customData);

		xml.endTag(null, ElemMeta);

	}

	private CipherOutputStream attachStreamEncryptor(PwDbHeaderV4 header, OutputStream os) throws PwDbOutputException {
		Cipher cipher;
		try {
			//mPM.makeFinalKey(header.masterSeed, mPM.kdfParameters);

			cipher = engine.getCipher(Cipher.ENCRYPT_MODE, mPM.finalKey, header.encryptionIV);
		} catch (Exception e) {
			throw new PwDbOutputException("Invalid algorithm.", e);
		}

		CipherOutputStream cos = new CipherOutputStream(os, cipher);

		return cos;
	}

	@Override
	protected SecureRandom setIVs(PwDbHeader header) throws PwDbOutputException {
		SecureRandom random = super.setIVs(header);

		PwDbHeaderV4 h = (PwDbHeaderV4) header;
		random.nextBytes(h.masterSeed);

		int ivLength = engine.ivLength();
		if (ivLength != h.encryptionIV.length) {
			h.encryptionIV = new byte[ivLength];
		}
		random.nextBytes(h.encryptionIV);

		UUID kdfUUID = mPM.kdfParameters.kdfUUID;
		KdfEngine kdf = KdfFactory.get(kdfUUID);
		kdf.randomize(mPM.kdfParameters);

		if (h.version < PwDbHeaderV4.FILE_VERSION_32_4) {
			h.innerRandomStream = CrsAlgorithm.Salsa20;
            h.innerRandomStreamKey = new byte[32];
		} else {
			h.innerRandomStream = CrsAlgorithm.ChaCha20;
			h.innerRandomStreamKey = new byte[64];
		}
		random.nextBytes(h.innerRandomStreamKey);

		randomStream = PwStreamCipherFactory.getInstance(h.innerRandomStream, h.innerRandomStreamKey);
		if (randomStream == null) {
			throw new PwDbOutputException("Invalid random cipher");
		}

		if ( h.version < PwDbHeaderV4.FILE_VERSION_32_4) {
			random.nextBytes(h.streamStartBytes);
		}

		return random;
	}

	@Override
	public PwDbHeader outputHeader(OutputStream os) throws PwDbOutputException {
		PwDbHeaderV4 header = new PwDbHeaderV4(mPM);
		setIVs(header);

		PwDbHeaderOutputV4 pho = new PwDbHeaderOutputV4(mPM, header, os);
		try {
			pho.output();
		} catch (IOException e) {
			throw new PwDbOutputException("Failed to output the header.", e);
		}

		hashOfHeader = pho.getHashOfHeader();
		headerHmac = pho.headerHmac;

		return header;
	}

	private void startGroup(PwGroupV4 group) throws IllegalArgumentException, IllegalStateException, IOException {
		xml.startTag(null, ElemGroup);
		writeObject(ElemUuid, group.uuid);
		writeObject(ElemName, group.name);
		writeObject(ElemNotes, group.notes);
		writeObject(ElemIcon, group.icon.iconId);

		if (!group.customIcon.equals(PwIconCustom.ZERO)) {
			writeObject(ElemCustomIconID, group.customIcon.uuid);
		}

		writeList(ElemTimes, group);
		writeObject(ElemIsExpanded, group.isExpanded);
		writeObject(ElemGroupDefaultAutoTypeSeq, group.defaultAutoTypeSequence);
		writeObject(ElemEnableAutoType, group.enableAutoType);
		writeObject(ElemEnableSearching, group.enableSearching);
		writeObject(ElemLastTopVisibleEntry, group.lastTopVisibleEntry);

	}

	private void endGroup() throws IllegalArgumentException, IllegalStateException, IOException {
		xml.endTag(null, ElemGroup);
	}

	private void writeEntry(PwEntryV4 entry, boolean isHistory) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(entry != null);

		xml.startTag(null, ElemEntry);

		writeObject(ElemUuid, entry.uuid);
		writeObject(ElemIcon, entry.icon.iconId);

		if (!entry.customIcon.equals(PwIconCustom.ZERO)) {
			writeObject(ElemCustomIconID, entry.customIcon.uuid);
		}

		writeObject(ElemFgColor, entry.foregroundColor);
		writeObject(ElemBgColor, entry.backgroupColor);
		writeObject(ElemOverrideUrl, entry.overrideURL);
		writeObject(ElemTags, entry.tags);

		writeList(ElemTimes, entry);

		writeList(entry.strings, true);
		writeList(entry.binaries);
		writeList(ElemAutoType, entry.autoType);

		if (!isHistory) {
			writeList(ElemHistory, entry.history, true);
		} else {
			assert(entry.history.size() == 0);
		}

		xml.endTag(null, ElemEntry);
	}


	private void writeObject(String key, ProtectedBinary value, boolean allowRef) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(key != null && value != null);

		xml.startTag(null, ElemBinary);
		xml.startTag(null, ElemKey);
		xml.text(safeXmlString(key));
		xml.endTag(null, ElemKey);

		xml.startTag(null, ElemValue);
		String strRef = null;
		if (allowRef) {
			int ref = mPM.binPool.poolFind(value);
			strRef = Integer.toString(ref);
		}

		if (strRef != null) {
			xml.attribute(null, AttrRef, strRef);
		}
		else {
			subWriteValue(value);
		}
		xml.endTag(null, ElemValue);

		xml.endTag(null, ElemBinary);
	}

	private void subWriteValue(ProtectedBinary value) throws IllegalArgumentException, IllegalStateException, IOException {

		int valLength = (int) value.length();
		if (valLength > 0) {
			byte[] buffer = new byte[valLength];
			value.getData().read(buffer, 0, valLength);

			if (value.isProtected()) {
				xml.attribute(null, AttrProtected, ValTrue);

				byte[] encoded = new byte[valLength];
				randomStream.processBytes(buffer, 0, valLength, encoded, 0);
				xml.text(Base64.encodeToString(encoded, Base64.NO_WRAP));

			} else {
				if (mPM.compressionAlgorithm == PwCompressionAlgorithm.Gzip) {
					xml.attribute(null, AttrCompressed, ValTrue);
					byte[] compressData = MemUtil.compress(buffer);
					xml.text(Base64.encodeToString(compressData, Base64.NO_WRAP));
				} else {
					xml.text(Base64.encodeToString(buffer, Base64.NO_WRAP));
				}

			}
		}
	}

	private void writeObject(String name, String value, boolean filterXmlChars) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(name != null && value != null);

		xml.startTag(null, name);

		if (filterXmlChars) {
			value = safeXmlString(value);
		}

		xml.text(value);
		xml.endTag(null, name);
	}

	private void writeObject(String name, String value) throws IllegalArgumentException, IllegalStateException, IOException {
		writeObject(name, value, false);
	}

	private void writeObject(String name, Date value) throws IllegalArgumentException, IllegalStateException, IOException {
		if (header.version < PwDbHeaderV4.FILE_VERSION_32_4) {
			writeObject(name, PwDatabaseV4XML.dateFormatter.get().format(value));
		} else {
			DateTime dt = new DateTime(value);
			long seconds = DateUtil.convertDateToKDBX4Time(dt);
			byte[] buf = LEDataOutputStream.writeLongBuf(seconds);
			String b64 = Base64.encodeToString(buf, Base64.NO_WRAP);
			writeObject(name, b64);
		}

	}

	private void writeObject(String name, long value) throws IllegalArgumentException, IllegalStateException, IOException {
		writeObject(name, String.valueOf(value));
	}

	private void writeObject(String name, Boolean value) throws IllegalArgumentException, IllegalStateException, IOException {
		String text;
		if (value == null) {
			text = "null";
		}
		else if (value) {
			text = ValTrue;
		}
		else {
			text = ValFalse;
		}

		writeObject(name, text);
	}

	private void writeObject(String name, UUID uuid) throws IllegalArgumentException, IllegalStateException, IOException {
		byte[] data = Types.UUIDtoBytes(uuid);
		writeObject(name, Base64.encodeToString(data, Base64.NO_WRAP));
	}

	private void writeObject(String name, String keyName, String keyValue, String valueName, String valueValue) throws IllegalArgumentException, IllegalStateException, IOException {
		xml.startTag(null, name);

		xml.startTag(null, keyName);
		xml.text(safeXmlString(keyValue));
		xml.endTag(null, keyName);

		xml.startTag(null, valueName);
		xml.text(safeXmlString(valueValue));
		xml.endTag(null, valueName);

		xml.endTag(null, name);
	}

	private void writeList(String name, AutoType autoType) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(name != null && autoType != null);

		xml.startTag(null, name);

		writeObject(ElemAutoTypeEnabled, autoType.enabled);
		writeObject(ElemAutoTypeObfuscation, autoType.obfuscationOptions);

		if (autoType.defaultSequence.length() > 0) {
			writeObject(ElemAutoTypeDefaultSeq, autoType.defaultSequence, true);
		}

		for (Entry<String, String> pair : autoType.entrySet()) {
			writeObject(ElemAutoTypeItem, ElemWindow, pair.getKey(), ElemKeystrokeSequence, pair.getValue());
		}

		xml.endTag(null, name);

	}

	private void writeList(Map<String, ProtectedString> strings, boolean isEntryString) throws IllegalArgumentException, IllegalStateException, IOException {
		assert (strings != null);

		for (Entry<String, ProtectedString> pair : strings.entrySet()) {
			writeObject(pair.getKey(), pair.getValue(), isEntryString);

		}

	}

	private void writeObject(String key, ProtectedString value, boolean isEntryString) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(key !=null && value != null);

		xml.startTag(null, ElemString);
		xml.startTag(null, ElemKey);
		xml.text(safeXmlString(key));
		xml.endTag(null, ElemKey);

		xml.startTag(null, ElemValue);
		boolean protect = value.isProtected();
		if (isEntryString) {
			if (key.equals(PwDefsV4.TITLE_FIELD)) {
				protect = mPM.memoryProtection.protectTitle;
			}
			else if (key.equals(PwDefsV4.USERNAME_FIELD)) {
				protect = mPM.memoryProtection.protectUserName;
			}
			else if (key.equals(PwDefsV4.PASSWORD_FIELD)) {
				protect = mPM.memoryProtection.protectPassword;
			}
			else if (key.equals(PwDefsV4.URL_FIELD)) {
				protect = mPM.memoryProtection.protectUrl;
			}
			else if (key.equals(PwDefsV4.NOTES_FIELD)) {
				protect = mPM.memoryProtection.protectNotes;
			}
		}

		if (protect) {
			xml.attribute(null, AttrProtected, ValTrue);

			byte[] data = value.toString().getBytes("UTF-8");
			int valLength = data.length;

			if (valLength > 0) {
				byte[] encoded = new byte[valLength];
				randomStream.processBytes(data, 0, valLength, encoded, 0);
				xml.text(Base64.encodeToString(encoded, Base64.NO_WRAP));
			}
		}
		else {
			xml.text(safeXmlString(value.toString()));
		}

		xml.endTag(null, ElemValue);
		xml.endTag(null, ElemString);

	}

	private void writeObject(String name, PwDeletedObject value) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(name != null && value != null);

		xml.startTag(null, name);

		writeObject(ElemUuid, value.uuid);
		writeObject(ElemDeletionTime, value.getDeletionTime());

		xml.endTag(null, name);
	}

	private void writeList(Map<String, ProtectedBinary> binaries) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(binaries != null);

		for (Entry<String, ProtectedBinary> pair : binaries.entrySet()) {
			writeObject(pair.getKey(), pair.getValue(), true);
		}
	}


	private void writeList(String name, List<PwDeletedObject> value) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(name != null && value != null);

		xml.startTag(null, name);

		for (PwDeletedObject pdo : value) {
			writeObject(ElemDeletedObject, pdo);
		}

		xml.endTag(null, name);

	}

	private void writeList(String name, MemoryProtectionConfig value) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(name != null && value != null);

		xml.startTag(null, name);

		writeObject(ElemProtTitle, value.protectTitle);
		writeObject(ElemProtUserName, value.protectUserName);
		writeObject(ElemProtPassword, value.protectPassword);
		writeObject(ElemProtURL, value.protectUrl);
		writeObject(ElemProtNotes, value.protectNotes);

		xml.endTag(null, name);

	}

	private void writeList(String name, Map<String, String> customData) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(name != null && customData != null);

		xml.startTag(null, name);

		for (Entry<String, String> pair : customData.entrySet()) {
			writeObject(ElemStringDictExItem, ElemKey, pair.getKey(), ElemValue, pair.getValue());

		}

		xml.endTag(null, name);

	}

	private void writeList(String name, ITimeLogger it) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(name != null && it != null);

		xml.startTag(null, name);

		writeObject(ElemLastModTime, it.getLastModificationTime());
		writeObject(ElemCreationTime, it.getCreationTime());
		writeObject(ElemLastAccessTime, it.getLastAccessTime());
		writeObject(ElemExpiryTime, it.getExpiryTime());
		writeObject(ElemExpires, it.expires());
		writeObject(ElemUsageCount, it.getUsageCount());
		writeObject(ElemLocationChanged, it.getLocationChanged());

		xml.endTag(null, name);
	}

	private void writeList(String name, List<PwEntryV4> value, boolean isHistory) throws IllegalArgumentException, IllegalStateException, IOException {
		assert(name != null && value != null);

		xml.startTag(null, name);

		for (PwEntryV4 entry : value) {
			writeEntry(entry, isHistory);
		}

		xml.endTag(null, name);

	}

	private void writeCustomIconList() throws IllegalArgumentException, IllegalStateException, IOException {
		List<PwIconCustom> customIcons = mPM.customIcons;
		if (customIcons.size() == 0) return;

		xml.startTag(null, ElemCustomIcons);

		for (PwIconCustom icon : customIcons) {
			xml.startTag(null, ElemCustomIconItem);

			writeObject(ElemCustomIconItemID, icon.uuid);
			writeObject(ElemCustomIconItemData, Base64.encodeToString(icon.imageData, Base64.NO_WRAP));

			xml.endTag(null, ElemCustomIconItem);
		}

		xml.endTag(null, ElemCustomIcons);
	}

	private void writeBinPool() throws IllegalArgumentException, IllegalStateException, IOException {
		xml.startTag(null, ElemBinaries);

		for (Entry<Integer, ProtectedBinary> pair : mPM.binPool.entrySet()) {
			xml.startTag(null, ElemBinary);
			xml.attribute(null, AttrId, Integer.toString(pair.getKey()));

			subWriteValue(pair.getValue());

			xml.endTag(null, ElemBinary);

		}

		xml.endTag(null, ElemBinaries);

	}

	private String safeXmlString(String text) {
		if (EmptyUtils.isNullOrEmpty(text)) {
			return text;
		}

		StringBuilder sb = new StringBuilder();

		char ch;
		for (int i = 0; i < text.length(); i++) {
			ch = text.charAt(i);

			if(((ch >= 0x20) && (ch <= 0xD7FF)) ||              
			        (ch == 0x9) || (ch == 0xA) || (ch == 0xD) ||
			        ((ch >= 0xE000) && (ch <= 0xFFFD))) {

				sb.append(ch);
			}

		}

		return sb.toString();
	}

}

public class PwEntryOutputV3 {
	// Constants
	public static final byte[] UUID_FIELD_TYPE =     LEDataOutputStream.writeUShortBuf(1);
	public static final byte[] GROUPID_FIELD_TYPE =  LEDataOutputStream.writeUShortBuf(2);
	public static final byte[] IMAGEID_FIELD_TYPE =  LEDataOutputStream.writeUShortBuf(3);
	public static final byte[] TITLE_FIELD_TYPE =    LEDataOutputStream.writeUShortBuf(4);
	public static final byte[] URL_FIELD_TYPE =      LEDataOutputStream.writeUShortBuf(5);
	public static final byte[] USERNAME_FIELD_TYPE =  LEDataOutputStream.writeUShortBuf(6);
	public static final byte[] PASSWORD_FIELD_TYPE = LEDataOutputStream.writeUShortBuf(7);
	public static final byte[] ADDITIONAL_FIELD_TYPE =   LEDataOutputStream.writeUShortBuf(8);
	public static final byte[] CREATE_FIELD_TYPE =   LEDataOutputStream.writeUShortBuf(9);
	public static final byte[] MOD_FIELD_TYPE =   LEDataOutputStream.writeUShortBuf(10);
	public static final byte[] ACCESS_FIELD_TYPE =   LEDataOutputStream.writeUShortBuf(11);
	public static final byte[] EXPIRE_FIELD_TYPE =   LEDataOutputStream.writeUShortBuf(12);
	public static final byte[] BINARY_DESC_FIELD_TYPE =   LEDataOutputStream.writeUShortBuf(13);
	public static final byte[] BINARY_DATA_FIELD_TYPE =   LEDataOutputStream.writeUShortBuf(14);
	public static final byte[] END_FIELD_TYPE =     LEDataOutputStream.writeUShortBuf(0xFFFF);
	public static final byte[] LONG_FOUR = LEDataOutputStream.writeIntBuf(4);
	public static final byte[] UUID_FIELD_SIZE =    LEDataOutputStream.writeIntBuf(16);
	public static final byte[] DATE_FIELD_SIZE =    LEDataOutputStream.writeIntBuf(5);
	public static final byte[] IMAGEID_FIELD_SIZE = LONG_FOUR;
	public static final byte[] LEVEL_FIELD_SIZE =   LONG_FOUR;
	public static final byte[] FLAGS_FIELD_SIZE =   LONG_FOUR;
	public static final byte[] ZERO_FIELD_SIZE =    LEDataOutputStream.writeIntBuf(0);
	public static final byte[] ZERO_FIVE       =   {0x00, 0x00, 0x00, 0x00, 0x00};
	public static final byte[] TEST = {0x33, 0x33, 0x33, 0x33};

	private OutputStream mOS;
	private PwEntryV3 mPE;
	private long outputBytes = 0;

	/** Output the PwGroupV3 to the stream
	 * @param pe
	 * @param os
	 */
	public PwEntryOutputV3(PwEntryV3 pe, OutputStream os) {
		mPE = pe;
		mOS = os;
	}

	//NOTE: Need be to careful about using ints.  The actual type written to file is a unsigned int
	public void output() throws IOException {

		outputBytes += 134;  // Length of fixed size fields

		// UUID
		mOS.write(UUID_FIELD_TYPE);
		mOS.write(UUID_FIELD_SIZE);
		mOS.write(Types.UUIDtoBytes(mPE.getUUID()));

		// Group ID
		mOS.write(GROUPID_FIELD_TYPE);
		mOS.write(LONG_FOUR);
		mOS.write(LEDataOutputStream.writeIntBuf(mPE.groupId));

		// Image ID
		mOS.write(IMAGEID_FIELD_TYPE);
		mOS.write(LONG_FOUR);
		mOS.write(LEDataOutputStream.writeIntBuf(mPE.icon.iconId));

		// Title
		//byte[] title = mPE.title.getBytes("UTF-8");
		mOS.write(TITLE_FIELD_TYPE);
		int titleLen = Types.writeCString(mPE.title, mOS);
		outputBytes += titleLen;

		// URL
		mOS.write(URL_FIELD_TYPE);
		int urlLen = Types.writeCString(mPE.url, mOS);
		outputBytes += urlLen;

		// Username
		mOS.write(USERNAME_FIELD_TYPE);
		int userLen = Types.writeCString(mPE.username, mOS);
		outputBytes += userLen;

		// Password
		byte[] password = mPE.getPasswordBytes();
		mOS.write(PASSWORD_FIELD_TYPE);
		mOS.write(LEDataOutputStream.writeIntBuf(password.length+1));
		mOS.write(password);
		mOS.write(0);
		outputBytes += password.length + 1;

		// Additional
		mOS.write(ADDITIONAL_FIELD_TYPE);
		int addlLen = Types.writeCString(mPE.additional, mOS);
		outputBytes += addlLen;

		// Create date
		writeDate(CREATE_FIELD_TYPE, mPE.tCreation.getCDate());

		// Modification date
		writeDate(MOD_FIELD_TYPE, mPE.tLastMod.getCDate());

		// Access date
		writeDate(ACCESS_FIELD_TYPE, mPE.tLastAccess.getCDate());

		// Expiration date
		writeDate(EXPIRE_FIELD_TYPE, mPE.tExpire.getCDate());

		// Binary desc
		mOS.write(BINARY_DESC_FIELD_TYPE);
		int descLen = Types.writeCString(mPE.binaryDesc, mOS);
		outputBytes += descLen;

		// Binary data
		int dataLen = writeByteArray(mPE.getBinaryData());
		outputBytes += dataLen;

		// End
		mOS.write(END_FIELD_TYPE);
		mOS.write(ZERO_FIELD_SIZE);
	}

	private int writeByteArray(byte[] data) throws IOException {
		int dataLen;
		if ( data != null ) {
			dataLen = data.length;
		} else {
			dataLen = 0;
		}
		mOS.write(BINARY_DATA_FIELD_TYPE);
		mOS.write(LEDataOutputStream.writeIntBuf(dataLen));
		if ( data != null ) {
			mOS.write(data);
		}

		return dataLen;

	}

	private void writeDate(byte[] type, byte[] date) throws IOException {
		mOS.write(type);
		mOS.write(DATE_FIELD_SIZE);
		if ( date != null ) {
			mOS.write(date);
		} else {
			mOS.write(ZERO_FIVE);
		}
	}

	/** Returns the number of bytes written by the stream
	 * @return Number of bytes written
	 */
	public long getLength() {
		return outputBytes;
	}
}

public class PwGroupOutputV3 {
	// Constants
	public static final byte[] GROUPID_FIELD_TYPE = LEDataOutputStream.writeUShortBuf(1);
	public static final byte[] NAME_FIELD_TYPE =    LEDataOutputStream.writeUShortBuf(2);
	public static final byte[] CREATE_FIELD_TYPE =  LEDataOutputStream.writeUShortBuf(3);
	public static final byte[] MOD_FIELD_TYPE =     LEDataOutputStream.writeUShortBuf(4);
	public static final byte[] ACCESS_FIELD_TYPE =  LEDataOutputStream.writeUShortBuf(5);
	public static final byte[] EXPIRE_FIELD_TYPE =  LEDataOutputStream.writeUShortBuf(6);
	public static final byte[] IMAGEID_FIELD_TYPE = LEDataOutputStream.writeUShortBuf(7);
	public static final byte[] LEVEL_FIELD_TYPE =   LEDataOutputStream.writeUShortBuf(8);
	public static final byte[] FLAGS_FIELD_TYPE =   LEDataOutputStream.writeUShortBuf(9);
	public static final byte[] END_FIELD_TYPE =     LEDataOutputStream.writeUShortBuf(0xFFFF);
	public static final byte[] LONG_FOUR =          LEDataOutputStream.writeIntBuf(4);
	public static final byte[] GROUPID_FIELD_SIZE = LONG_FOUR;
	public static final byte[] DATE_FIELD_SIZE =    LEDataOutputStream.writeIntBuf(5);
	public static final byte[] IMAGEID_FIELD_SIZE = LONG_FOUR;
	public static final byte[] LEVEL_FIELD_SIZE =   LEDataOutputStream.writeIntBuf(2);
	public static final byte[] FLAGS_FIELD_SIZE =   LONG_FOUR;
	public static final byte[] ZERO_FIELD_SIZE =    LEDataOutputStream.writeIntBuf(0);

	private OutputStream mOS;
	private PwGroupV3 mPG;

	/** Output the PwGroupV3 to the stream
	 * @param pg
	 * @param os
	 */
	public PwGroupOutputV3(PwGroupV3 pg, OutputStream os) {
		mPG = pg;
		mOS = os;
	}

	public void output() throws IOException {
		//NOTE: Need be to careful about using ints.  The actual type written to file is a unsigned int, but most values can't be greater than 2^31, so it probably doesn't matter.

		// Group ID
		mOS.write(GROUPID_FIELD_TYPE);
		mOS.write(GROUPID_FIELD_SIZE);
		mOS.write(LEDataOutputStream.writeIntBuf(mPG.groupId));

		// Name
		mOS.write(NAME_FIELD_TYPE);
		Types.writeCString(mPG.name, mOS);

		// Create date
		mOS.write(CREATE_FIELD_TYPE);
		mOS.write(DATE_FIELD_SIZE);
		mOS.write(mPG.tCreation.getCDate());

		// Modification date
		mOS.write(MOD_FIELD_TYPE);
		mOS.write(DATE_FIELD_SIZE);
		mOS.write(mPG.tLastMod.getCDate());

		// Access date
		mOS.write(ACCESS_FIELD_TYPE);
		mOS.write(DATE_FIELD_SIZE);
		mOS.write(mPG.tLastAccess.getCDate());

		// Expiration date
		mOS.write(EXPIRE_FIELD_TYPE);
		mOS.write(DATE_FIELD_SIZE);
		mOS.write(mPG.tExpire.getCDate());

		// Image ID
		mOS.write(IMAGEID_FIELD_TYPE);
		mOS.write(IMAGEID_FIELD_SIZE);
		mOS.write(LEDataOutputStream.writeIntBuf(mPG.icon.iconId));

		// Level
		mOS.write(LEVEL_FIELD_TYPE);
		mOS.write(LEVEL_FIELD_SIZE);
		mOS.write(LEDataOutputStream.writeUShortBuf(mPG.level));

		// Flags
		mOS.write(FLAGS_FIELD_TYPE);
		mOS.write(FLAGS_FIELD_SIZE);
		mOS.write(LEDataOutputStream.writeIntBuf(mPG.flags));

		// End
		mOS.write(END_FIELD_TYPE);
		mOS.write(ZERO_FIELD_SIZE);
	}

}

public class ProtectedBinary {

	private static final String TAG = ProtectedBinary.class.getName();
	public final static ProtectedBinary EMPTY = new ProtectedBinary();

	private byte[] data;
	private boolean protect;
	private File dataFile;
	private int size;
	private static final SecureRandom secureRandom = new SecureRandom();
	private FileParams fileParams;

	private class FileParams {

		private File dataFile;
		public CipherOutputStream cos;
		public SecretKeySpec keySpec;
		public IvParameterSpec ivSpec;


		public Cipher initCipher(int mode) {
		    Cipher cipher;
			try {
				cipher = CipherFactory.getInstance("AES/CBC/PKCS5Padding");
				cipher.init(mode, keySpec, ivSpec);
			} catch (NoSuchAlgorithmException e) {
				throw new IllegalStateException(e);
			} catch (NoSuchPaddingException e) {
				throw new IllegalStateException(e);
			} catch (InvalidKeyException e) {
				throw new IllegalStateException(e);
			} catch (InvalidAlgorithmParameterException e) {
				throw new IllegalStateException(e);
			}

			return cipher;
		}

		public void setupEnc(File file) {

			byte[] iv = new byte[16];
			byte[] key = new byte[32];
			secureRandom.nextBytes(key);
			secureRandom.nextBytes(iv);

			keySpec = new SecretKeySpec(key, "AES");
			ivSpec = new IvParameterSpec((iv));

			Cipher cipherOut = initCipher(Cipher.ENCRYPT_MODE);
			FileOutputStream fos;
			try {
				fos = new FileOutputStream(file);
			} catch (FileNotFoundException e) {
				throw new IllegalStateException(e);
			}

			cos = new CipherOutputStream(fos, cipherOut);
		}

		public FileParams(File dataFile) {
			this.dataFile = dataFile;
			setupEnc(dataFile);
		}
	}

	public boolean isProtected() {
		return protect;
	}

	public int length() {
		if (data != null)
			return data.length;
		if (dataFile != null)
			return size;
		return 0;
	}

	private ProtectedBinary() {
		this.protect = false;
		this.data = null;
		this.dataFile = null;
		this.size = 0;
	}

	public ProtectedBinary(boolean enableProtection, byte[] data) {
		this.protect = enableProtection;
		this.data = data;
		this.dataFile = null;
		if (data != null)
		    this.size = data.length;
		else
		    this.size = 0;
	}

	public ProtectedBinary(boolean enableProtection, File dataFile, int size) {
		this.protect = enableProtection;
		this.data = null;
		this.dataFile = dataFile;
		this.size = size;

		fileParams = new FileParams(dataFile);
	}

	public OutputStream getOutputStream() {
		assert(fileParams != null);
	    return fileParams.cos;
	}

	public InputStream getData() throws IOException {
		if (data != null)
			return new ByteArrayInputStream(data);
		else if (dataFile != null)
			return new CipherInputStream(new FileInputStream(dataFile), fileParams.initCipher(Cipher.DECRYPT_MODE));
		else
			return null;
	}

	public void clear() {
		data = null;
		if (dataFile != null && !dataFile.delete())
			Log.e(TAG, "Unable to delete temp file " + dataFile.getAbsolutePath());
	}

	public boolean equals(ProtectedBinary o) {
        return (this == o) || ((o != null)
                && (getClass() == o.getClass())
                && (protect == o.protect)
                && (size == o.size)
                && (Arrays.equals(data, o.data))
                && (dataFile != null)
                && (dataFile.equals(o.dataFile)));
    }
}

public class ProtectedString {

	private String string;
	private boolean protect;

	public boolean isProtected() {
		return protect;
	}

	public int length() {
		if (string == null) {
			return 0;
		}

		return string.length();
	}

	public ProtectedString() {
		this(false, "");

	}

	public ProtectedString(boolean enableProtection, String string) {
		protect = enableProtection;
		this.string = string;

	}

	public String toString() {
		return string;
	}

}

public class PasswordEncodingDialogHelper {
	private AlertDialog dialog;

	public void show(Context ctx, DialogInterface.OnClickListener onclick) {
		show(ctx, onclick, false);
	}

	public void show(Context ctx, DialogInterface.OnClickListener onclick, boolean showCancel) {
	    AlertDialog.Builder builder = new AlertDialog.Builder(ctx);
	    builder.setMessage(R.string.warning_password_encoding).setTitle(R.string.warning);
	    builder.setPositiveButton(android.R.string.ok, onclick);


	    if (showCancel) {
	    	builder.setNegativeButton(android.R.string.cancel, new DialogInterface.OnClickListener() {

				@Override
				public void onClick(DialogInterface dialog, int which) {
					dialog.cancel();
				}
			});

	    }

	    dialog = builder.create();

	    dialog.show();
	}

}

public class ReadOnlyDialog extends WarningDialog {

	public ReadOnlyDialog(Context context) {
		super(context, R.string.show_read_only_warning);

		warning = context.getString(R.string.read_only_warning);

		if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.KITKAT) {
			warning = warning.concat("\n\n").concat(context.getString(R.string.read_only_kitkat_warning));
		}
	}
}

public class WarningDialog extends AlertDialog {

	protected String warning;
	private int showKey;

	public WarningDialog(Context context, int dontShowKey) {
		super(context);

		this.showKey = dontShowKey;
	}

	public WarningDialog(Context context, int warningKey, int dontShowKey) {
		this(context, dontShowKey);

		warning = context.getString(warningKey);
	}

	@Override
	protected void onCreate(Bundle savedInstanceState) {
		Context ctx = getContext();
		setMessage(warning);

		setButton(AlertDialog.BUTTON1, ctx.getText(android.R.string.ok), new DialogInterface.OnClickListener() {

			@Override
			public void onClick(DialogInterface dialog, int which) {
				dismiss();
			}
		});

		setButton(AlertDialog.BUTTON2, ctx.getText(R.string.beta_dontask), new DialogInterface.OnClickListener() {

			@Override
			public void onClick(DialogInterface dialog, int which) {
				Context ctx = getContext();
				SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(ctx);
				SharedPreferences.Editor edit = prefs.edit();
				edit.putBoolean(ctx.getString(showKey), false);
				edit.commit();

				dismiss();
			}
		});

		super.onCreate(savedInstanceState);
	}

}

public class BrowserDialog extends Dialog {

	public BrowserDialog(Context context) {
		super(context);
	}

	@Override
	protected void onCreate(Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);
		setContentView(R.layout.browser_install);
		setTitle(R.string.file_browser);

		Button cancel = (Button) findViewById(R.id.cancel);
		cancel.setOnClickListener(new View.OnClickListener() {

			public void onClick(View v) {
				BrowserDialog.this.cancel();
			}
		});

		Button market = (Button) findViewById(R.id.install_market);
		market.setOnClickListener(new View.OnClickListener() {

			public void onClick(View v) {
				Util.gotoUrl(getContext(), R.string.oi_filemanager_market);
				BrowserDialog.this.cancel();
			}
		});
		if (!isMarketInstalled()) {
			market.setVisibility(View.GONE);
		}

		Button web = (Button) findViewById(R.id.install_web);
		web.setOnClickListener(new View.OnClickListener() {

			public void onClick(View v) {
				Util.gotoUrl(getContext(), R.string.oi_filemanager_web);
				BrowserDialog.this.cancel();
			}
		});
	}

	private boolean isMarketInstalled() {
		PackageManager pm = getContext().getPackageManager();

		try {
			pm.getPackageInfo("com.android.vending", 0);
		} catch (NameNotFoundException e) {
			return false;
		}

		return true;

	}

}

public class FileDbHelper {

	public static final String LAST_FILENAME = "lastFile";
	public static final String LAST_KEYFILE = "lastKey";

	public static final String DATABASE_NAME = "keepassdroid";
	private static final String FILE_TABLE = "files";
	private static final int DATABASE_VERSION = 1;

	public static final int MAX_FILES = 5;

	public static final String KEY_FILE_ID = "_id";
	public static final String KEY_FILE_FILENAME = "fileName";
	public static final String KEY_FILE_KEYFILE = "keyFile";
	public static final String KEY_FILE_UPDATED = "updated";

	private static final String DATABASE_CREATE = 
		"create table " + FILE_TABLE + " ( " + KEY_FILE_ID + " integer primary key autoincrement, " 
			+ KEY_FILE_FILENAME + " text not null, " + KEY_FILE_KEYFILE + " text, "
			+ KEY_FILE_UPDATED + " integer not null);";

	private final Context mCtx;
	private DatabaseHelper mDbHelper;
	private SQLiteDatabase mDb;

	private static class DatabaseHelper extends SQLiteOpenHelper {
		private final Context mCtx;

		DatabaseHelper(Context ctx) {
			super(ctx, DATABASE_NAME, null, DATABASE_VERSION);
			mCtx = ctx;
		}

		@Override
		public void onCreate(SQLiteDatabase db) {
			db.execSQL(DATABASE_CREATE);

			// Migrate preference to database if it is set.
			SharedPreferences settings = mCtx.getSharedPreferences("PasswordActivity", Context.MODE_PRIVATE); 
			String lastFile = settings.getString(LAST_FILENAME, "");
			String lastKey = settings.getString(LAST_KEYFILE,"");

			if ( lastFile.length() > 0 ) {
				ContentValues vals = new ContentValues();
				vals.put(KEY_FILE_FILENAME, lastFile);
				vals.put(KEY_FILE_UPDATED, System.currentTimeMillis());

				if ( lastKey.length() > 0 ) {
					vals.put(KEY_FILE_KEYFILE, lastKey);
				}

				db.insert(FILE_TABLE, null, vals);

				// Clear old preferences
				deletePrefs(settings);

			}
		}

		@Override
		public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
			// Only one database version so far
		}

		private void deletePrefs(SharedPreferences prefs) {
			// We won't worry too much if this fails
			try {
				SharedPreferences.Editor editor = prefs.edit();
				editor.remove(LAST_FILENAME);
				editor.remove(LAST_KEYFILE);
				editor.apply();
			} catch (Exception e) {
				assert(true);
			}
		}
	}

	public FileDbHelper(Context ctx) {
		mCtx = ctx;
	}

	public FileDbHelper open() throws SQLException {
		mDbHelper = new DatabaseHelper(mCtx);
		mDb = mDbHelper.getWritableDatabase();
		return this;
	}

	public boolean isOpen() {
		return mDb.isOpen();
	}

	public void close() {
		mDb.close();
	}

	public long createFile(String fileName, String keyFile) {

		// Check to see if this filename is already used
		Cursor cursor;
		try {
			cursor = mDb.query(true, FILE_TABLE, new String[] {KEY_FILE_ID}, 
					KEY_FILE_FILENAME + "=?", new String[] {fileName}, null, null, null, null);
		} catch (Exception e ) {
			assert(true);
			return -1;
		}

		long result;
		// If there is an existing entry update it with the new key file
		if ( cursor.getCount() > 0 ) {
			cursor.moveToFirst();
			long id = cursor.getLong(cursor.getColumnIndexOrThrow(KEY_FILE_ID));

			ContentValues vals = new ContentValues();
			vals.put(KEY_FILE_KEYFILE, keyFile);
			vals.put(KEY_FILE_UPDATED, System.currentTimeMillis());

			result = mDb.update(FILE_TABLE, vals, KEY_FILE_ID + " = " + id, null);

		// Otherwise add the new entry
		} else {
			ContentValues vals = new ContentValues();
			vals.put(KEY_FILE_FILENAME, fileName);
			vals.put(KEY_FILE_KEYFILE, keyFile);
			vals.put(KEY_FILE_UPDATED, System.currentTimeMillis());

			result = mDb.insert(FILE_TABLE, null, vals);

		}
		// Delete all but the last five records
		try {
			deleteAllBut(MAX_FILES);
		} catch (Exception e) {
			e.printStackTrace();
			assert(true);
		}

		cursor.close();

		return result;

	}

	private void deleteAllBut(int limit) {
		Cursor cursor = mDb.query(FILE_TABLE, new String[] {KEY_FILE_UPDATED}, null, null, null, null, KEY_FILE_UPDATED);

		if ( cursor.getCount() > limit ) {
			cursor.moveToFirst();
			long time = cursor.getLong(cursor.getColumnIndexOrThrow(KEY_FILE_UPDATED));

			mDb.execSQL("DELETE FROM " + FILE_TABLE + " WHERE " + KEY_FILE_UPDATED + "<" + time + ";");
		}

		cursor.close();

	}

	public void deleteAllKeys() {
		ContentValues vals = new ContentValues();
		vals.put(KEY_FILE_KEYFILE, "");

		mDb.update(FILE_TABLE, vals, null, null);
	}

	public void deleteFile(String filename) {
		mDb.delete(FILE_TABLE, KEY_FILE_FILENAME + " = ?", new String[] {filename});
	}


	public Cursor fetchAllFiles() {
		Cursor ret;
		ret = mDb.query(FILE_TABLE, new String[] {KEY_FILE_ID, KEY_FILE_FILENAME, KEY_FILE_KEYFILE }, null, null, null, null, KEY_FILE_UPDATED + " DESC", Integer.toString(MAX_FILES));
		return ret;
	}

	public Cursor fetchFile(long fileId) throws SQLException {
		Cursor cursor = mDb.query(true, FILE_TABLE, new String[] {KEY_FILE_FILENAME, KEY_FILE_KEYFILE}, 
				KEY_FILE_ID + "=" + fileId, null, null, null, null, null);

		if ( cursor != null ) {
			cursor.moveToFirst();
		}

		return cursor;

	}

	public String getFileByName(String name) {
		Cursor cursor = mDb.query(true, FILE_TABLE, new String[] {KEY_FILE_KEYFILE}, 
				KEY_FILE_FILENAME + "= ?", new String[] {name}, null, null, null, null);

		if ( cursor == null ) {
			return "";
		}

		String filename;

		if ( cursor.moveToFirst() ) {
			filename = cursor.getString(0);
		} else {
			// Cursor is empty
			filename = "";
		}
		cursor.close();
		return filename;
	}

	public boolean hasRecentFiles() {
		Cursor cursor = fetchAllFiles();

		boolean hasRecent = cursor.getCount() > 0;
		cursor.close();

		return hasRecent; 
	}

    /**
     * Deletes a database including its journal file and other auxiliary files
     * that may have been created by the database engine.
     *
     * @param file The database file path.
     * @return True if the database was successfully deleted.
     */
    public static boolean deleteDatabase(Context ctx) {
    	File file = ctx.getDatabasePath(DATABASE_NAME);
        if (file == null) {
            throw new IllegalArgumentException("file must not be null");
        }

        boolean deleted = false;
        deleted |= file.delete();
        deleted |= new File(file.getPath() + "-journal").delete();
        deleted |= new File(file.getPath() + "-shm").delete();
        deleted |= new File(file.getPath() + "-wal").delete();

        File dir = file.getParentFile();
        if (dir != null) {
            final String prefix = file.getName() + "-mj";
            final FileFilter filter = new FileFilter() {
                @Override
                public boolean accept(File candidate) {
                    return candidate.getName().startsWith(prefix);
                }
            };
            for (File masterJournal : dir.listFiles(filter)) {
                deleted |= masterJournal.delete();
            }
        }
        return deleted;
    }
}

public class FileSelectActivity extends AppCompatActivity {

    private ListView mList;
    private ListAdapter mAdapter;

    private static final int CMENU_CLEAR = Menu.FIRST;

    public static final int FILE_BROWSE = 1;
    public static final int GET_CONTENT = 2;
    public static final int OPEN_DOC = 3;
    private static final int PERMISSION_REQUEST_ID = 4;

    private RecentFileHistory fileHistory;

    private boolean recentMode = false;


    private void createFile(String filename) {
        // Try to create the file
        File file = new File(filename);
        try {
            if (file.exists()) {
                Toast.makeText(FileSelectActivity.this,
                        R.string.error_database_exists,
                        Toast.LENGTH_LONG).show();
                return;
            }
            File parent = file.getParentFile();

            if ( parent == null || (parent.exists() && ! parent.isDirectory()) ) {
                Toast.makeText(FileSelectActivity.this,
                        R.string.error_invalid_path,
                        Toast.LENGTH_LONG).show();
                return;
            }

            if ( ! parent.exists() ) {
                // Create parent dircetory
                if ( ! parent.mkdirs() ) {
                    Toast.makeText(FileSelectActivity.this,
                            R.string.error_could_not_create_parent,
                            Toast.LENGTH_LONG).show();
                    return;

                }
            }

            file.createNewFile();
        } catch (IOException e) {
            Toast.makeText(
                    FileSelectActivity.this,
                    getText(R.string.error_file_not_create) + " "
                            + e.getLocalizedMessage(),
                    Toast.LENGTH_LONG).show();
            return;
        }

        // Prep an object to collect a password once the database has
        // been created
        CollectPassword password = new CollectPassword(
                new LaunchGroupActivity(filename));

        // Create the new database
        CreateDB create = new CreateDB(FileSelectActivity.this, filename, password, true);
        ProgressTask createTask = new ProgressTask(
                FileSelectActivity.this, create,
                R.string.progress_create);
        createTask.run();


    }

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        fileHistory = App.getFileHistory();

        if (fileHistory.hasRecentFiles()) {
            recentMode = true;
            setContentView(R.layout.file_selection);
        } else {
            setContentView(R.layout.file_selection_no_recent);
        }

        Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);

        mList = (ListView)findViewById(R.id.file_list);

        mList.setOnItemClickListener(
                new AdapterView.OnItemClickListener() {
                    public void onItemClick(AdapterView<?> parent, View v, int position, long id)
                    {
                        onListItemClick((ListView)parent, v, position, id);
                    }
                }
        );

        // Open button
        Button openButton = (Button) findViewById(R.id.open);
        openButton.setOnClickListener(new View.OnClickListener() {

            public void onClick(View v) {
                String fileName = Util.getEditText(FileSelectActivity.this,
                        R.id.file_filename);

                try {
                    PasswordActivity.Launch(FileSelectActivity.this, fileName);
                }
                catch (ContentFileNotFoundException e) {
                    Toast.makeText(FileSelectActivity.this,
                            R.string.file_not_found_content, Toast.LENGTH_LONG).show();
                }
                catch (FileNotFoundException e) {
                    Toast.makeText(FileSelectActivity.this,
                            R.string.FileNotFound, Toast.LENGTH_LONG).show();
                }

            }
        });

        // Create button
        Button createButton = (Button) findViewById(R.id.create);
        createButton.setOnClickListener(new View.OnClickListener() {

            public void onClick(View v) {
                String filename = Util.getEditText(FileSelectActivity.this,
                        R.id.file_filename);

                // Make sure file name exists
                if (filename.length() == 0) {
                    Toast
                            .makeText(FileSelectActivity.this,
                                    R.string.error_filename_required,
                                    Toast.LENGTH_LONG).show();
                    return;
                }

                if (PermissionUtil.checkAndRequest(FileSelectActivity.this, PERMISSION_REQUEST_ID)) {
                    createFile(filename);
                }
            }

        });

        ImageButton browseButton = (ImageButton) findViewById(R.id.browse_button);
        browseButton.setOnClickListener(new View.OnClickListener() {

            public void onClick(View v) {
                if (StorageAF.useStorageFramework(FileSelectActivity.this)) {
                    Intent i = new Intent(StorageAF.ACTION_OPEN_DOCUMENT);
                    i.addCategory(Intent.CATEGORY_OPENABLE);
                    i.setType("*/*");
                    i.setFlags(Intent.FLAG_GRANT_READ_URI_PERMISSION|Intent.FLAG_GRANT_WRITE_URI_PERMISSION|Intent.FLAG_GRANT_PERSISTABLE_URI_PERMISSION);
                    startActivityForResult(i, OPEN_DOC);
                }
                else {
                    Intent i;
                    i = new Intent(Intent.ACTION_GET_CONTENT);
                    i.addCategory(Intent.CATEGORY_OPENABLE);
                    i.setType("*/*");

                    try {
                        startActivityForResult(i, GET_CONTENT);
                    } catch (ActivityNotFoundException e) {
                        lookForOpenIntentsFilePicker();
                    } catch (SecurityException e) {
                        lookForOpenIntentsFilePicker();
                    }
                }
            }

            private void lookForOpenIntentsFilePicker() {

                if (Interaction.isIntentAvailable(FileSelectActivity.this, Intents.OPEN_INTENTS_FILE_BROWSE)) {
                    Intent i = new Intent(Intents.OPEN_INTENTS_FILE_BROWSE);
                    i.setData(Uri.parse("file://" + Util.getEditText(FileSelectActivity.this, R.id.file_filename)));
                    try {
                        startActivityForResult(i, FILE_BROWSE);
                    } catch (ActivityNotFoundException e) {
                        showBrowserDialog();
                    }

                } else {
                    showBrowserDialog();
                }
            }

            private void showBrowserDialog() {
                BrowserDialog diag = new BrowserDialog(FileSelectActivity.this);
                diag.show();
            }
        });

        fillData();

        registerForContextMenu(mList);

        // Load default database
        SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(this);
        String fileName = prefs.getString(PasswordActivity.KEY_DEFAULT_FILENAME, "");

        if (fileName.length() > 0) {
            Uri dbUri = UriUtil.parseDefaultFile(fileName);
            String scheme = dbUri.getScheme();

            if (!EmptyUtils.isNullOrEmpty(scheme) && scheme.equalsIgnoreCase("file")) {
                String path = dbUri.getPath();
                File db = new File(path);

                if (db.exists()) {
                    try {
                        PasswordActivity.Launch(FileSelectActivity.this, path);
                    } catch (Exception e) {
                        // Ignore exception
                    }
                }
            }
            else {
                try {
                    PasswordActivity.Launch(FileSelectActivity.this, dbUri.toString());
                } catch (Exception e) {
                    // Ignore exception
                }
            }
        }
    }

    public void onRequestPermissionsResult (int requestCode,
                                            String[] permissions,
                                            int[] grantResults) {

        if (requestCode == PERMISSION_REQUEST_ID &&
                grantResults[0] == PackageManager.PERMISSION_GRANTED &&
                grantResults[1] == PackageManager.PERMISSION_GRANTED){

            String filename = Util.getEditText(FileSelectActivity.this,
                    R.id.file_filename);
            createFile(filename);
        } else {
            Toast.makeText(this, R.string.no_external_permissions, Toast.LENGTH_LONG).show();
        }
    }

    private class LaunchGroupActivity extends FileOnFinish {
        private Uri mUri;

        public LaunchGroupActivity(String filename) {
            super(null);

            mUri = UriUtil.parseDefaultFile(filename);
        }

        @Override
        public void run() {
            if (mSuccess) {
                // Add to recent files
                fileHistory.createFile(mUri, getFilename());

                GroupActivity.Launch(FileSelectActivity.this);
            }
        }
    }

    private class CollectPassword extends FileOnFinish {

        public CollectPassword(FileOnFinish finish) {
            super(finish);
        }

        @Override
        public void run() {
            SetPasswordDialog password = new SetPasswordDialog(FileSelectActivity.this, mOnFinish);
            password.show();
        }

    }

    private void fillData() {
        // Set the initial value of the filename
        EditText filename = (EditText) findViewById(R.id.file_filename);
        filename.setText(Environment.getExternalStorageDirectory().getAbsolutePath() + getString(R.string.default_file_path));

        mAdapter = new ArrayAdapter<String>(this, R.layout.file_row, R.id.file_filename, fileHistory.getDbList());
        mList.setAdapter(mAdapter);
    }

    protected void onListItemClick(ListView l, View v, int position, long id) {

        new AsyncTask<Integer, Void, Void>() {
            String fileName;
            String keyFile;
            protected Void doInBackground(Integer... args) {
                int position = args[0];
                fileName = fileHistory.getDatabaseAt(position);
                keyFile = fileHistory.getKeyfileAt(position);
                return null;
            }

            protected void onPostExecute(Void v) {
                try {
                    PasswordActivity.Launch(FileSelectActivity.this, fileName, keyFile);
                }
                catch (ContentFileNotFoundException e) {
                    Toast.makeText(FileSelectActivity.this, R.string.file_not_found_content, Toast.LENGTH_LONG)
                            .show();
                }
                catch (FileNotFoundException e) {
                    Toast.makeText(FileSelectActivity.this, R.string.FileNotFound, Toast.LENGTH_LONG)
                            .show();
                }
            }
        }.execute(position);
    }

    @Override
    protected void onActivityResult(int requestCode, int resultCode, Intent data) {
        super.onActivityResult(requestCode, resultCode, data);

        fillData();

        String filename = null;
        if (requestCode == FILE_BROWSE && resultCode == RESULT_OK) {
            filename = data.getDataString();
            if (filename != null) {
                if (filename.startsWith("file://")) {
                    filename = filename.substring(7);
                }

                filename = URLDecoder.decode(filename);
            }

        }
        else if ((requestCode == GET_CONTENT || requestCode == OPEN_DOC) && resultCode == RESULT_OK) {
            if (data != null) {
                Uri uri = data.getData();
                if (uri != null) {
                    if (StorageAF.useStorageFramework(this)) {
                        try {
                            // try to persist read and write permissions
                            ContentResolver resolver = getContentResolver();
                            ContentResolverCompat.takePersistableUriPermission(resolver, uri, Intent.FLAG_GRANT_READ_URI_PERMISSION);
                            ContentResolverCompat.takePersistableUriPermission(resolver, uri, Intent.FLAG_GRANT_WRITE_URI_PERMISSION);
                        } catch (Exception e) {
                            // nop
                        }
                    }
                    if (requestCode == GET_CONTENT) {
                        uri = UriUtil.translate(this, uri);
                    }
                    filename = uri.toString();
                }
            }
        }

        if (filename != null) {
            EditText fn = (EditText) findViewById(R.id.file_filename);
            fn.setText(filename);
        }
    }

    @Override
    protected void onResume() {
        super.onResume();

        // Check to see if we need to change modes
        if ( fileHistory.hasRecentFiles() != recentMode ) {
            // Restart the activity
            Intent intent = getIntent();
            startActivity(intent);
            finish();
        }

        FileNameView fnv = (FileNameView) findViewById(R.id.file_select);
        fnv.updateExternalStorageWarning();
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        super.onCreateOptionsMenu(menu);

        MenuInflater inflater = getMenuInflater();
        inflater.inflate(R.menu.fileselect, menu);

        return true;
    }

    private boolean launchUrl(int resId) {
        try {
            Util.gotoUrl(this, resId);
        } catch (ActivityNotFoundException e) {
            Toast.makeText(this, R.string.error_failed_to_launch_link, Toast.LENGTH_LONG).show();
            return false;
        }

        return true;
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        switch (item.getItemId()) {
        case R.id.menu_patreon:
            return launchUrl(R.string.patreon_url);

        case R.id.menu_donate:
            return launchUrl(R.string.donate_url);

        case R.id.menu_about:
            AboutDialog dialog = new AboutDialog(this);
            dialog.show();
            return true;

        case R.id.menu_app_settings:
            AppSettingsActivity.Launch(this);
            return true;
        }

        return super.onOptionsItemSelected(item);
    }

    @Override
    public void onCreateContextMenu(ContextMenu menu, View v,
            ContextMenuInfo menuInfo) {
        super.onCreateContextMenu(menu, v, menuInfo);

        menu.add(0, CMENU_CLEAR, 0, R.string.remove_from_filelist);
    }

    @Override
    public boolean onContextItemSelected(MenuItem item) {
        super.onContextItemSelected(item);

        if ( item.getItemId() == CMENU_CLEAR ) {
            AdapterContextMenuInfo acmi = (AdapterContextMenuInfo) item.getMenuInfo();

            TextView tv = (TextView) acmi.targetView;
            String filename = tv.getText().toString();
            new AsyncTask<String, Void, Void>() {
                protected java.lang.Void doInBackground(String... args) {
                    String filename = args[0];
                    fileHistory.deleteFile(Uri.parse(args[0]));
                    return null;
                }

                protected void onPostExecute(Void v) {
                    refreshList();
                }
            }.execute(filename);
            return true;
        }

        return false;
    }

    private void refreshList() {
        ((BaseAdapter) mAdapter).notifyDataSetChanged();
    }

}

public class RecentFileHistory {

    private static String DB_KEY = "recent_databases";
    private static String KEYFILE_KEY = "recent_keyfiles";

    private List<String> databases = new ArrayList<String>();
    private List<String> keyfiles = new ArrayList<String>();
    private Context ctx;
    private SharedPreferences prefs;
    private OnSharedPreferenceChangeListener listner;
    private boolean enabled;
    private boolean init = false;

    public RecentFileHistory(Context c) {
        ctx = c.getApplicationContext();

        prefs = PreferenceManager.getDefaultSharedPreferences(c);
        enabled = prefs.getBoolean(ctx.getString(R.string.recentfile_key), ctx.getResources().getBoolean(R.bool.recentfile_default));
        listner = new OnSharedPreferenceChangeListener() {

            @Override
            public void onSharedPreferenceChanged(SharedPreferences sharedPreferences,
                    String key) {
                if (key.equals(ctx.getString(R.string.recentfile_key))) {
                    enabled = sharedPreferences.getBoolean(ctx.getString(R.string.recentfile_key), ctx.getResources().getBoolean(R.bool.recentfile_default));
                }
            }
        };
        prefs.registerOnSharedPreferenceChangeListener(listner);
    }

    private synchronized void init() {
        if (!init) {
            if (!upgradeFromSQL()) {
                loadPrefs();
            }

            init = true;
        }
    }

    private boolean upgradeFromSQL() {

        try {
            // Check for a database to upgrade from
            if (!sqlDatabaseExists()) {
                return false;
            }

            databases.clear();
            keyfiles.clear();

            FileDbHelper helper = new FileDbHelper(ctx);
            helper.open();
            Cursor cursor = helper.fetchAllFiles();

            int dbIndex = cursor.getColumnIndex(FileDbHelper.KEY_FILE_FILENAME);
            int keyIndex = cursor.getColumnIndex(FileDbHelper.KEY_FILE_KEYFILE);

            if(cursor.moveToFirst()) {
                while (cursor.moveToNext()) {
                    String filename = cursor.getString(dbIndex);
                    String keyfile = cursor.getString(keyIndex);

                    databases.add(filename);
                    keyfiles.add(keyfile);
                }
            }

            savePrefs();

            cursor.close();
            helper.close();

        } catch (Exception e) {
            // If upgrading fails, we'll just give up on it.
        }

        try {
            FileDbHelper.deleteDatabase(ctx);
        } catch (Exception e) {
            // If we fail to delete it, just move on
        }

        return true;
    }

    private boolean sqlDatabaseExists() {
        File db = ctx.getDatabasePath(FileDbHelper.DATABASE_NAME);
        return db.exists();
    }

    public void createFile(Uri uri, Uri keyUri) {
        if (!enabled || uri == null) return;

        init();

        // Remove any existing instance of the same filename
        deleteFile(uri, false);

        databases.add(0, uri.toString());

        String key = (keyUri == null) ? "" : keyUri.toString();
        keyfiles.add(0, key);

        trimLists();
        savePrefs();
    }

    public boolean hasRecentFiles() {
        if (!enabled) return false;

        init();

        return databases.size() > 0;
    }

    public String getDatabaseAt(int i) {
        init();
        if (i < databases.size()) {
            return databases.get(i);
        } else {
            return "";
        }
    }

    public String getKeyfileAt(int i) {
        init();
        if (i < keyfiles.size()) {
            return keyfiles.get(i);
        } else {
            return "";
        }
    }

    private void loadPrefs() {
        loadList(databases, DB_KEY);
        loadList(keyfiles, KEYFILE_KEY);
    }

    private void savePrefs() {
        saveList(DB_KEY, databases);
        saveList(KEYFILE_KEY, keyfiles);
    }

    private void loadList(List<String> list, String keyprefix) {
        SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(ctx);
        int size = prefs.getInt(keyprefix, 0);

        list.clear();
        for (int i = 0; i < size; i++) {
            list.add(prefs.getString(keyprefix + "_" + i, ""));
        }
    }

    private void saveList(String keyprefix, List<String> list) {
        SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(ctx);
        SharedPreferences.Editor edit = prefs.edit();
        int size = list.size();
        edit.putInt(keyprefix, size);

        for (int i = 0; i < size; i++) {
            edit.putString(keyprefix + "_" + i, list.get(i));
        }
        edit.apply();;
    }

    public void deleteFile(Uri uri) {
        deleteFile(uri, true);
    }

    public void deleteFile(Uri uri, boolean save) {
        init();

        String uriName = uri.toString();
        String fileName = uri.getPath();

        for (int i = 0; i < databases.size(); i++) {
            String entry = databases.get(i);
            if (uriName.equals(entry) || fileName.equals(entry)) {
                databases.remove(i);
                keyfiles.remove(i);
                break;
            }
        }

        if (save) {
            savePrefs();
        }
    }

    public List<String> getDbList() {
        init();

        return databases;
    }

    public Uri getFileByName(Uri database) {
        if (!enabled) return null;

        init();

        int size = databases.size();
        for (int i = 0; i < size; i++) {
            if (UriUtil.equalsDefaultfile(database,databases.get(i))) {
                return UriUtil.parseDefaultFile(keyfiles.get(i));
            }
        }

        return null;
    }

    public void deleteAll() {
        init();

        databases.clear();
        keyfiles.clear();

        savePrefs();
    }

    public void deleteAllKeys() {
        init();

        keyfiles.clear();

        int size = databases.size();
        for (int i = 0; i < size; i++) {
            keyfiles.add("");
        }

        savePrefs();
    }

    private void trimLists() {
        int size = databases.size();
        for (int i = FileDbHelper.MAX_FILES; i < size; i++) {
            if (i < databases.size()) {
                databases.remove(i);
            }

            if (i < keyfiles.size()) {
                keyfiles.remove(i);
            }
        }
    }
}

public class PasswordFragment extends Fragment implements BiometricHelper.BiometricCallback {
    private static final int FILE_BROWSE = 256;
    public static final int GET_CONTENT = 257;
    private static final int OPEN_DOC = 258;

    private static final String KEY_PASSWORD = "password";
    private static final String KEY_LAUNCH_IMMEDIATELY = "launchImmediately";
    private static final String VIEW_INTENT = "android.intent.action.VIEW";

    private static final int PERMISSION_REQUEST_ID = 1;

    private Uri mDbUri = null;
    private Uri mKeyUri = null;
    private boolean mRememberKeyfile;
    SharedPreferences prefs;
    SharedPreferences prefsNoBackup;

    private Uri storedKeyUri = null;
    private String storedPassword = null;
    private int mode;
    private static final String PREF_KEY_VALUE_PREFIX = "valueFor_"; // key is a combination of db file name and this prefix
    private static final String PREF_KEY_IV_PREFIX = "ivFor_"; // key is a combination of db file name and this prefix
    private View mView;

    private CheckBox biometricCheck;
    private EditText passwordView;
    private Button biometricOpen;
    private Button biometricClear;
    private View divider3;
    private Button confirmButton;
    private boolean biometricsAvailable = false;
    private BiometricPrompt biometricSavePrompt;
    private BiometricPrompt biometricOpenPrompt;
    private BiometricPrompt.PromptInfo savePrompt;
    private BiometricPrompt.PromptInfo loadPrompt;
    private BiometricHelper biometricHelper;

    private Activity mActivity;

    private boolean afterOnCreateBeforeEndOfOnResume = false;

    @Override
    public void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        setHasOptionsMenu(true);
        setRetainInstance(true);
        afterOnCreateBeforeEndOfOnResume = true;
    }

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        super.onCreateView(inflater, container, savedInstanceState);

        mView = inflater.inflate(R.layout.password, container, false);
        return mView;
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        Context context = getContext();

        prefs = PreferenceManager.getDefaultSharedPreferences(mActivity);
        prefsNoBackup = mActivity.getSharedPreferences("nobackup", Context.MODE_PRIVATE);

        mRememberKeyfile = prefs.getBoolean(getString(R.string.keyfile_key), getResources().getBoolean(R.bool.keyfile_default));
        confirmButton = (Button) view.findViewById(R.id.pass_ok);
        passwordView = (EditText) view.findViewById(R.id.password);
        biometricOpen = (Button) view.findViewById(R.id.open_biometric);
        biometricClear = (Button) view.findViewById(R.id.clear_biometric);
        divider3 = view.findViewById(R.id.divider3);
        biometricCheck = (CheckBox) view.findViewById(R.id.save_password);

        biometricOpen.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                biometricLogin();
            }
        });

        biometricClear.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                clearStoredCredentials();
            }
        });
    }

    private void biometricLogin() {
        if (!initDecryptData()) {
            return;
        }

        biometricCheck.setChecked(false);

        Cipher cipher = biometricHelper.getCipher();
        biometricOpenPrompt.authenticate(loadPrompt, new BiometricPrompt.CryptoObject(cipher));
    }

    @Override
    public void onCreateOptionsMenu(@NonNull Menu menu, @NonNull MenuInflater inflater) {
        super.onCreateOptionsMenu(menu, inflater);

        MenuInflater inflate = mActivity.getMenuInflater();
        inflate.inflate(R.menu.password, menu);
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        Context context = getContext();
        switch (item.getItemId()) {
            case R.id.menu_about:
                AboutDialog dialog = new AboutDialog(context);
                dialog.show();
                return true;

            case R.id.menu_app_settings:
                AppSettingsActivity.Launch(context);
                return true;
        }

        return super.onContextItemSelected(item);
    }

    @Override
    public void onAttach(@NonNull Context context) {
        super.onAttach(context);

        if (context instanceof Activity) {
            mActivity = (Activity) context;
        }
    }

    @Override
    public void onDetach() {
        super.onDetach();

        mActivity = null;
    }

    @Override
    public void onDestroy() {
        super.onDestroy();

        mActivity = null;
    }

    private void setFingerPrintVisibilty() {
        if (biometricsAvailable) {
            biometricCheck.setVisibility(View.VISIBLE);
        } else {
            biometricCheck.setVisibility(View.GONE);
        }

        biometricOpenUpdateVisibility();
    }

    private void biometricOpenUpdateVisibility() {
        int visibility;
        boolean autoOpen = false;
        BiometricManager biometricManager = BiometricManager.from(mActivity);
        int auth = biometricManager.canAuthenticate();
        if (biometricsAvailable && auth != BiometricManager.BIOMETRIC_ERROR_NONE_ENROLLED) {
            String encryptedValue = prefsNoBackup.getString(getPreferenceKeyValue(), null);
            String ivSpecValue = prefsNoBackup.getString(getPreferenceKeyIvSpec(), null);

            boolean hasStoredKey = encryptedValue != null && ivSpecValue != null;
            if (hasStoredKey) {
                // Check key value
                visibility = View.VISIBLE;
                autoOpen = prefs.getBoolean(getString(R.string.biometric_autoscan_key),
                        getResources().getBoolean(R.bool.biometric_autoscan))
                        && afterOnCreateBeforeEndOfOnResume;
            } else {
                visibility = View.GONE;
            }

        } else {
            visibility = View.GONE;
        }

        biometricOpen.setVisibility(visibility);
        biometricClear.setVisibility(visibility);
        divider3.setVisibility(visibility);

        if (autoOpen) {
            biometricLogin();
        }
    }

    private void initBiometrics() {
        final Context context = getContext();
        biometricsAvailable = true;

        biometricHelper = new BiometricHelper(context, this);

        Executor executor = ContextCompat.getMainExecutor(context);

        BiometricPrompt.AuthenticationCallback saveCallback =
                new BiometricPrompt.AuthenticationCallback() {
                    @Override
                    public void onAuthenticationFailed() {
                        super.onAuthenticationFailed();
                        Toast.makeText(context, R.string.biometric_auth_failed_store, Toast.LENGTH_LONG).show();
                        GroupActivity.Launch(mActivity);
                    }

                    @Override
                    public void onAuthenticationSucceeded(@NonNull BiometricPrompt.AuthenticationResult result) {
                        super.onAuthenticationSucceeded(result);

                        // newly store the entered password in encrypted way
                        final String password = passwordView.getText().toString();
                        biometricHelper.encryptData(password);
                        GroupActivity.Launch(mActivity);

                    }

                    @Override
                    public void onAuthenticationError(int errorCode, @NonNull CharSequence errString) {
                        super.onAuthenticationError(errorCode, errString);
                        if (!canceledBiometricAuth(errorCode)) {
                            Toast.makeText(context, R.string.biometric_auth_error, Toast.LENGTH_LONG).show();
                        }
                        GroupActivity.Launch(mActivity);
                    }
                };

        BiometricPrompt.AuthenticationCallback openCallback =
                new BiometricPrompt.AuthenticationCallback() {
                    @Override
                    public void onAuthenticationFailed() {
                        super.onAuthenticationFailed();
                        Toast.makeText(context, R.string.biometric_auth_failed, Toast.LENGTH_LONG).show();
                    }

                    @Override
                    public void onAuthenticationSucceeded(@NonNull BiometricPrompt.AuthenticationResult result) {
                        super.onAuthenticationSucceeded(result);

                        // retrieve the encrypted value from preferences
                        final String encryptedValue = prefsNoBackup.getString(getPreferenceKeyValue(), null);
                        if (encryptedValue != null) {
                            biometricHelper.decryptData(encryptedValue);
                        }
                    }

                    @Override
                    public void onAuthenticationError(int errorCode, @NonNull CharSequence errString) {
                        super.onAuthenticationError(errorCode, errString);

                        if (!canceledBiometricAuth(errorCode)) {
                            Toast.makeText(context, R.string.biometric_auth_error, Toast.LENGTH_LONG).show();
                        }
                    }
                };

        biometricSavePrompt = new BiometricPrompt(this, executor, saveCallback);
        BiometricPrompt.PromptInfo.Builder saveBuilder = new BiometricPrompt.PromptInfo.Builder();
        savePrompt = saveBuilder.setDescription(getString(R.string.biometric_auth_to_store))
                .setConfirmationRequired(false)
                .setTitle(getString(R.string.biometric_save_password))
                .setNegativeButtonText(getString(android.R.string.cancel))
                .build();

        biometricOpenPrompt = new BiometricPrompt(this, executor, openCallback);
        BiometricPrompt.PromptInfo.Builder openBuilder = new BiometricPrompt.PromptInfo.Builder();
        loadPrompt = openBuilder.setDescription(getString(R.string.biometric_auth_to_open))
                .setConfirmationRequired(false)
                .setTitle(getString(R.string.biometric_open_db))
                .setNegativeButtonText(getString(android.R.string.cancel))
                .build();


        setFingerPrintVisibilty();
    }

    private boolean canceledBiometricAuth(int errorCode) {
        switch (errorCode) {
            case BiometricPrompt.ERROR_CANCELED:
            case BiometricPrompt.ERROR_USER_CANCELED:
            case BiometricPrompt.ERROR_NEGATIVE_BUTTON:
                return true;
            default:
                return false;
        }
    }


    private boolean initDecryptData() {
        final String ivSpecValue = prefsNoBackup.getString(getPreferenceKeyIvSpec(), null);

        return biometricHelper.initDecryptData(ivSpecValue);
    }


    @Override
    public void onActivityCreated(@Nullable Bundle savedInstanceState) {
        super.onActivityCreated(savedInstanceState);

        Intent i = mActivity.getIntent();
        InitTask task = new InitTask();
        task.onPostExecute(task.doInBackground(i));
    }

    @Override
    public void onActivityResult(int requestCode, int resultCode, @Nullable Intent data) {
        super.onActivityResult(requestCode, resultCode, data);

        Activity activity = mActivity;

        switch (requestCode) {

            case KeePass.EXIT_NORMAL:
                setEditText(R.id.password, "");
                App.getDB().clear(activity.getApplicationContext());
                break;

            case KeePass.EXIT_LOCK:
                activity.setResult(KeePass.EXIT_LOCK);
                setEditText(R.id.password, "");
                activity.finish();
                App.getDB().clear(activity.getApplicationContext());
                break;
            case FILE_BROWSE:
                if (resultCode == Activity.RESULT_OK) {
                    String filename = data.getDataString();
                    if (filename != null) {
                        EditText fn = (EditText) mView.findViewById(R.id.pass_keyfile);
                        fn.setText(filename);
                        mKeyUri = UriUtil.parseDefaultFile(filename);
                    }
                }
                break;
            case GET_CONTENT:
            case OPEN_DOC:
                if (resultCode == Activity.RESULT_OK) {
                    if (data != null) {
                        Uri uri = data.getData();
                        if (uri != null) {
                            if (requestCode == GET_CONTENT) {
                                uri = UriUtil.translate(activity, uri);
                            }
                            String path = uri.toString();
                            if (path != null) {
                                EditText fn = (EditText) mView.findViewById(R.id.pass_keyfile);
                                fn.setText(path);

                            }
                            mKeyUri = uri;
                        }
                    }
                }
                break;
        }

    }

    @Override
    public void onResume() {
        super.onResume();

        // If the application was shutdown make sure to clear the password field, if it
        // was saved in the instance state
        if (App.isShutdown()) {
            TextView password = (TextView) mView.findViewById(R.id.password);
            password.setText("");
        }

        // Clear the shutdown flag
        App.clearShutdown();

        BiometricManager biometricManager = BiometricManager.from(mActivity);
        int auth = biometricManager.canAuthenticate();
        if (auth == BiometricManager.BIOMETRIC_SUCCESS){
            initBiometrics();
        } else {
            biometricsAvailable = false;
            setFingerPrintVisibilty();
        }

        afterOnCreateBeforeEndOfOnResume = false;
    }

    @Override
    public void onRequestPermissionsResult(int requestCode, @NonNull String[] permissions, @NonNull int[] grantResults) {
        super.onRequestPermissionsResult(requestCode, permissions, grantResults);

        if (requestCode == PERMISSION_REQUEST_ID &&
                grantResults[0] == PackageManager.PERMISSION_GRANTED &&
                grantResults[1] == PackageManager.PERMISSION_GRANTED){

            loadDatabaseWithPermission();
        } else {
            errorMessage(R.string.no_external_permissions);
        }
    }

    private String getPreferenceKeyValue() {
        // makes it possible to store passwords uniqly per database
        return PREF_KEY_VALUE_PREFIX + (mDbUri != null ? mDbUri.getPath() : "");
    }

    private String getPreferenceKeyIvSpec() {
        return PREF_KEY_IV_PREFIX + (mDbUri != null ? mDbUri.getPath() : "");
    }

    // Moved this to the foreground TOOD: Move this to a more typical pattern
    private class InitTask {

        String password = "";
        boolean launch_immediately = false;

        public Integer doInBackground(Intent... args) {
            Intent i = args[0];
            String action = i.getAction();
            ;
            if (action != null && action.equals(VIEW_INTENT)) {
                Uri incoming = i.getData();
                mDbUri = incoming;

                mKeyUri = ClipDataCompat.getUriFromIntent(i, PasswordActivity.KEY_KEYFILE);

                if (incoming == null) {
                    return R.string.error_can_not_handle_uri;
                } else if (incoming.getScheme().equals("file")) {
                    String fileName = incoming.getPath();

                    if (fileName.length() == 0) {
                        // No file name
                        return R.string.FileNotFound;
                    }

                    File dbFile = new File(fileName);
                    if (!dbFile.exists()) {
                        // File does not exist
                        return R.string.FileNotFound;
                    }

                    if (mKeyUri == null) {
                        mKeyUri = getKeyFile(mDbUri);
                    }
                } else if (incoming.getScheme().equals("content")) {
                    if (mKeyUri == null) {
                        mKeyUri = getKeyFile(mDbUri);
                    }
                } else {
                    return R.string.error_can_not_handle_uri;
                }
                password = i.getStringExtra(KEY_PASSWORD);
                launch_immediately = i.getBooleanExtra(KEY_LAUNCH_IMMEDIATELY, false);

            } else {
                mDbUri = UriUtil.parseDefaultFile(i.getStringExtra(PasswordActivity.KEY_FILENAME));
                mKeyUri = UriUtil.parseDefaultFile(i.getStringExtra(PasswordActivity.KEY_KEYFILE));
                password = i.getStringExtra(KEY_PASSWORD);
                launch_immediately = i.getBooleanExtra(KEY_LAUNCH_IMMEDIATELY, false);

                if (mKeyUri == null || mKeyUri.toString().length() == 0) {
                    mKeyUri = getKeyFile(mDbUri);
                }
            }

            biometricOpenUpdateVisibility();

            return null;
        }

        public void onPostExecute(Integer result) {
            if (result != null) {
                Toast.makeText(mActivity, result, Toast.LENGTH_LONG).show();
                mActivity.finish();
                return;
            }

            populateView();

            confirmButton.setOnClickListener(new OkClickHandler());

            CheckBox checkBox = (CheckBox) mView.findViewById(R.id.show_password);
            // Show or hide password
            checkBox.setOnCheckedChangeListener(new CompoundButton.OnCheckedChangeListener() {

                public void onCheckedChanged(
                        CompoundButton buttonView,
                        boolean isChecked) {
                    TextView password = (TextView) mView.findViewById(R.id.password);

                    if (isChecked) {
                        password.setInputType(InputType.TYPE_CLASS_TEXT | InputType.TYPE_TEXT_VARIATION_VISIBLE_PASSWORD);
                    } else {
                        password.setInputType(InputType.TYPE_CLASS_TEXT | InputType.TYPE_TEXT_VARIATION_PASSWORD);
                    }
                }

            });

            if (password != null) {
                TextView tv_password = (TextView) mView.findViewById(R.id.password);
                tv_password.setText(password);
            }

            CheckBox defaultCheck = (CheckBox) mView.findViewById(R.id.default_database);
            defaultCheck.setOnCheckedChangeListener(new DefaultCheckChange());

            ImageButton browse = (ImageButton) mView.findViewById(R.id.browse_button);
            browse.setOnClickListener(new View.OnClickListener() {

                public void onClick(View v) {
                    if (StorageAF.useStorageFramework(mActivity)) {
                        Intent i = new Intent(StorageAF.ACTION_OPEN_DOCUMENT);
                        i.addCategory(Intent.CATEGORY_OPENABLE);
                        i.setType("*/*");
                        startActivityForResult(i, OPEN_DOC);
                    } else {
                        Intent i = new Intent(Intent.ACTION_GET_CONTENT);
                        i.addCategory(Intent.CATEGORY_OPENABLE);
                        i.setType("*/*");

                        try {
                            startActivityForResult(i, GET_CONTENT);
                        } catch (ActivityNotFoundException e) {
                            lookForOpenIntentsFilePicker();
                        }
                    }
                }

                private void lookForOpenIntentsFilePicker() {
                    if (Interaction.isIntentAvailable(mActivity, Intents.OPEN_INTENTS_FILE_BROWSE)) {
                        Intent i = new Intent(Intents.OPEN_INTENTS_FILE_BROWSE);

                        // Get file path parent if possible
                        try {
                            if (mDbUri != null && mDbUri.toString().length() > 0) {
                                if (mDbUri.getScheme().equals("file")) {
                                    File keyfile = new File(mDbUri.getPath());
                                    File parent = keyfile.getParentFile();
                                    if (parent != null) {
                                        i.setData(Uri.parse("file://" + parent.getAbsolutePath()));
                                    }
                                }
                            }
                        } catch (Exception e) {
                            // Ignore
                        }

                        try {
                            startActivityForResult(i, FILE_BROWSE);
                        } catch (ActivityNotFoundException e) {
                            showBrowserDialog();
                        }
                    } else {
                        showBrowserDialog();
                    }
                }

                private void showBrowserDialog() {
                    BrowserDialog diag = new BrowserDialog(mActivity);
                    diag.show();
                }
            });

            retrieveSettings();

            if (launch_immediately) {
                loadDatabase(password, mKeyUri);
            }
        }
    }

    private class DefaultCheckChange implements CompoundButton.OnCheckedChangeListener {

        @Override
        public void onCheckedChanged(
                CompoundButton buttonView,
                boolean isChecked) {

            String newDefaultFileName;

            if (isChecked) {
                newDefaultFileName = mDbUri.toString();
            } else {
                newDefaultFileName = "";
            }

            SharedPreferences.Editor editor = prefs.edit();
            editor.putString(PasswordActivity.KEY_DEFAULT_FILENAME, newDefaultFileName);
            editor.apply();

            BackupManager backupManager = new BackupManager(getContext());
            backupManager.dataChanged();

        }

    }

    private void retrieveSettings() {
        String defaultFilename = prefs.getString(PasswordActivity.KEY_DEFAULT_FILENAME, "");
        if (!EmptyUtils.isNullOrEmpty(mDbUri.getPath()) && UriUtil.equalsDefaultfile(mDbUri, defaultFilename)) {
            CheckBox checkbox = (CheckBox) mView.findViewById(R.id.default_database);
            checkbox.setChecked(true);
        }
    }

    private Uri getKeyFile(Uri dbUri) {
        if (mRememberKeyfile) {

            return App.getFileHistory().getFileByName(dbUri);
        } else {
            return null;
        }
    }

    private void populateView() {
        String db = (mDbUri == null) ? "" : mDbUri.toString();
        setEditText(R.id.filename, db);

        String key = (mKeyUri == null) ? "" : mKeyUri.toString();
        setEditText(R.id.pass_keyfile, key);
    }

    private void errorMessage(int resId) {
        Toast.makeText(mActivity, resId, Toast.LENGTH_LONG).show();
    }

    private void setEditText(
            int resId,
            String str) {

        TextView te = (TextView) mView.findViewById(resId);
        assert (te == null);

        if (te != null) {
            te.setText(str);
        }
    }
    private void loadDatabase(
            String pass,
            Uri keyfile) {
        if (pass.length() == 0 && (keyfile == null || keyfile.toString().length() == 0)) {
            errorMessage(R.string.error_nopass);
            return;
        }

        storedPassword = pass;
        storedKeyUri = keyfile;

        if (checkFilePermissions(mDbUri, keyfile)) {
            loadDatabaseWithPermission();
        }
    }

    private void loadDatabaseWithPermission() {
        String pass = storedPassword;
        storedPassword = null;
        Uri keyfile = storedKeyUri;
        storedKeyUri = null;
        Activity activity = mActivity;


        // Clear before we load
        Database db = App.getDB();
        db.clear(activity.getApplicationContext());

        // Clear the shutdown flag
        App.clearShutdown();

        Handler handler = new Handler();
        LoadDB task = new LoadDB(db, activity, mDbUri, pass, keyfile, new PasswordFragment.AfterLoad(handler, db));
        ProgressTask pt = new ProgressTask(activity, task, R.string.loading_database);
        pt.run();
    }

    private String getEditText(int resId) {
        return Util.getEditText(mActivity, resId);
    }
    private final class AfterLoad extends OnFinish {

        private Database db;

        public AfterLoad(
                Handler handler,
                Database db) {
            super(handler);

            this.db = db;
        }

        @Override
        public void run() {
            if (db.passwordEncodingError) {
                PasswordEncodingDialogHelper dialog = new PasswordEncodingDialogHelper();
                dialog.show(mActivity, new DialogInterface.OnClickListener() {

                    @Override
                    public void onClick(
                            DialogInterface dialog,
                            int which) {
                        GroupActivity.Launch(mActivity);
                    }

                });
            } else if (mSuccess) {
                passwordView.setText("");
                if (biometricCheck.isChecked()) {
                    if (!biometricHelper.initEncryptData()) {
                        return;
                    }
                    Cipher cipher = biometricHelper.getCipher();

                    biometricSavePrompt.authenticate(savePrompt, new BiometricPrompt.CryptoObject(cipher));

                }
                else {
                    // Check to see if the fragement detached before this finished
                    if (mActivity != null) {
                        GroupActivity.Launch(mActivity);
                    }
                }
            } else {
                displayMessage(mActivity);
            }
        }
    }

    private class OkClickHandler implements View.OnClickListener {

        public void onClick(View view) {
            String pass = getEditText(R.id.password);
            String key = getEditText(R.id.pass_keyfile);

            loadDatabase(pass, key);
        }
    }

    private void loadDatabase(
            String pass,
            String keyfile) {

        loadDatabase(pass, UriUtil.parseDefaultFile(keyfile));
    }

    private boolean hasFileUri(Uri uri) {
        try {
            if (uri == null) { return false; }

            return uri.getScheme().equalsIgnoreCase("file");
        } catch (Exception e) {
            return false;
        }

    }

    private void clearStoredCredentials() {
        prefsNoBackup.edit()
                .remove(getPreferenceKeyValue())
                .remove(getPreferenceKeyIvSpec())
                .commit();
        setFingerPrintVisibilty();
    }

    @Override
    public void handleEncryptedResult(String value, String ivSpec) {
        prefsNoBackup.edit()
                .putString(getPreferenceKeyValue(), value)
                .putString(getPreferenceKeyIvSpec(), ivSpec)
                .commit();
        // and remove visual input to reset UI
        Toast.makeText(getContext(), R.string.encrypted_value_stored, Toast.LENGTH_SHORT).show();

    }

    @Override
    public void handleDecryptedResult(String value) {
        // on decrypt enter it for the purchase/login action
        passwordView.setText(value);
        confirmButton.performClick();
    }

    @Override
    public void onInvalidKeyException() {
        Toast.makeText(getContext(), R.string.fingerprint_invalid_key, Toast.LENGTH_SHORT).show();
    }

    @Override
    public void onException() {
        onException(true);
    }

    @Override
    public void onException(boolean showWarningMessage) {
        if (showWarningMessage) {
            onException(R.string.biometric_error);
        }
    }

    @Override
    public void onException(CharSequence message) {
        Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
    }

    @Override
    public void onException(int resId) {
        Toast.makeText(getContext(), resId, Toast.LENGTH_SHORT).show();
    }

    @Override
    public void onKeyInvalidated() {
        clearStoredCredentials();
        Toast.makeText(getContext(), R.string.biometric_invalidated, Toast.LENGTH_LONG).show();
    }

    private boolean checkFilePermissions(Uri db, Uri keyfile) {
        boolean hasFileUri = hasFileUri(db) ||
                hasFileUri(keyfile);

        if (!hasFileUri) return true;

        return PermissionUtil.checkAndRequest(this.mActivity, PERMISSION_REQUEST_ID);
    }
}

public class DrawableFactory {
	private static Drawable blank = null;
	private static int blankWidth = -1;
	private static int blankHeight = -1;

	/** customIconMap
	 *  Cache for icon drawable. 
	 *  Keys: UUID, Values: Drawables
	 */
	private ReferenceMap customIconMap = new ReferenceMap(AbstractReferenceMap.HARD, AbstractReferenceMap.WEAK);

	/** standardIconMap
	 *  Cache for icon drawable. 
	 *  Keys: Integer, Values: Drawables
	 */
	private ReferenceMap standardIconMap = new ReferenceMap(AbstractReferenceMap.HARD, AbstractReferenceMap.WEAK);

	public void assignDrawableTo(ImageView iv, Resources res, PwIcon icon) {
		Drawable draw = getIconDrawable(res, icon);
		iv.setImageDrawable(draw);
	}

	private Drawable getIconDrawable(Resources res, PwIcon icon) {
		if (icon instanceof PwIconStandard) {
			return getIconDrawable(res, (PwIconStandard) icon);
		} else {
			return getIconDrawable(res, (PwIconCustom) icon);
		}
	}

	private static void initBlank(Resources res) {
		if (blank==null) {
			blank = res.getDrawable(R.drawable.ic99_blank);
			blankWidth = blank.getIntrinsicWidth();
			blankHeight = blank.getIntrinsicHeight();
		}
	}

	public Drawable getIconDrawable(Resources res, PwIconStandard icon) {
		int resId = Icons.iconToResId(icon.iconId);

		Drawable draw = (Drawable) standardIconMap.get(resId);
		if (draw == null) {
			draw = res.getDrawable(resId);
			standardIconMap.put(resId, draw);
		}

		return draw;
	}

	public Drawable getIconDrawable(Resources res, PwIconCustom icon) {
		initBlank(res);
		if (icon == null) {
			return blank;
		}

		Drawable draw = (Drawable) customIconMap.get(icon.uuid);

		if (draw == null) {
			if (icon.imageData == null) {
				return blank;
			}

			Bitmap bitmap = BitmapFactory.decodeByteArray(icon.imageData, 0, icon.imageData.length);

			// Could not understand custom icon
			if (bitmap == null) {
				return blank;
			}

			bitmap = resize(bitmap);

			draw = new BitmapDrawable(res, bitmap);
			customIconMap.put(icon.uuid, draw);
		}

		return draw;
	}

	/** Resize the custom icon to match the built in icons
	 * @param bitmap
	 * @return
	 */
	private Bitmap resize(Bitmap bitmap) {
		int width = bitmap.getWidth();
		int height = bitmap.getHeight();

		if (width == blankWidth && height == blankHeight) {
			return bitmap;
		}

		return Bitmap.createScaledBitmap(bitmap, blankWidth, blankHeight, true);
	}

	public void clear() {
		standardIconMap.clear();
		customIconMap.clear();
	}

}

public class Icons {
	private static SparseIntArray icons = null;

	private static void buildList() {
		if (icons == null) {
			icons = new SparseIntArray();

			Class<com.android.keepass.R.drawable> c = com.android.keepass.R.drawable.class;

			Field[] fields = c.getFields();

			for (int i = 0; i < fields.length; i++) {
				String fieldName = fields[i].getName();
				if (fieldName.matches("ic\\d{2}.*")) {
					String sNum = fieldName.substring(2, 4);
					int num = Integer.parseInt(sNum);
					if (num > 69) {
						continue;
					}

					int resId;
					try {
						resId = fields[i].getInt(null);
					} catch (Exception e) {
						continue;
					}

					icons.put(num, resId);
				}
			}
		}	
	}

	public static int iconToResId(int iconId) {
		buildList();

		return icons.get(iconId, R.drawable.ic99_blank);
	}

	public static int count() {
		buildList();

		return icons.size();
	}

}

public class Intents {
	public static final String TIMEOUT = "com.keepassdroid.timeout";

	public static final String COPY_USERNAME = "com.keepassdroid.copy_username";
	public static final String COPY_PASSWORD = "com.keepassdroid.copy_password";

	public static final String OPEN_INTENTS_FILE_BROWSE = "org.openintents.action.PICK_FILE";
}

public class PasswordGenerator {
	private static final String UPPERCASE_CHARS	= "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
	private static final String LOWERCASE_CHARS 	= "abcdefghijklmnopqrstuvwxyz";
	private static final String DIGIT_CHARS 	= "0123456789";
	private static final String MINUS_CHAR	 	= "-";
	private static final String UNDERLINE_CHAR 	= "_";
	private static final String SPACE_CHAR 		= " ";
	private static final String SPECIAL_CHARS 	= "!\"#$%&'*+,./:;=?@\\^`";
	private static final String BRACKET_CHARS 	= "[]{}()<>";


	private Context cxt;

	public PasswordGenerator(Context cxt) {
		this.cxt = cxt;
	}

	public String generatePassword(int length, boolean upperCase, boolean lowerCase, boolean digits, boolean minus, boolean underline, boolean space, boolean specials, boolean brackets) throws IllegalArgumentException{
		// Desired password length is 0 or less
		if (length <= 0) {
			throw new IllegalArgumentException(cxt.getString(R.string.error_wrong_length));
		}

		// No option has been checked
		if (!upperCase && !lowerCase && !digits && !minus && !underline && !space && !specials && !brackets) {
			throw new IllegalArgumentException(cxt.getString(R.string.error_pass_gen_type));
		}

		String characterSet = getCharacterSet(upperCase, lowerCase, digits, minus, underline, space, specials, brackets);

		int size = characterSet.length();

		StringBuffer buffer = new StringBuffer();

		SecureRandom random = new SecureRandom(); // use more secure variant of Random!
		if (size > 0) {
			for (int i = 0; i < length; i++) {
				char c = characterSet.charAt((char) random.nextInt(size));
				buffer.append(c);
			}
		}
		return buffer.toString();
	}

	public String getCharacterSet(boolean upperCase, boolean lowerCase, boolean digits, boolean minus, boolean underline, boolean space, boolean specials, boolean brackets) {
		StringBuffer charSet = new StringBuffer();

		if (upperCase) {
			charSet.append(UPPERCASE_CHARS);
		}

		if (lowerCase) {
			charSet.append(LOWERCASE_CHARS);
		}

		if (digits) {
			charSet.append(DIGIT_CHARS);
		}

		if (minus) {
			charSet.append(MINUS_CHAR);
		}

		if (underline) {
			charSet.append(UNDERLINE_CHAR);
		}

		if (space) {
			charSet.append(SPACE_CHAR);
		}

		if (specials) {
			charSet.append(SPECIAL_CHARS);
		}

		if (brackets) {
			charSet.append(BRACKET_CHARS);
		}

		return charSet.toString();
	}
}

public class SearchDbHelper {
	private final Context mCtx;

	public SearchDbHelper(Context ctx) {
		mCtx = ctx;
	}

	private boolean omitBackup() {
		SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(mCtx);
		return prefs.getBoolean(mCtx.getString(R.string.omitbackup_key), mCtx.getResources().getBoolean(R.bool.omitbackup_default));

	}

	public PwGroup search(Database db, String qStr) {
		PwDatabase pm = db.pm;

		PwGroup group;
		if ( pm instanceof PwDatabaseV3 ) {
			group = new PwGroupV3();
		} else if ( pm instanceof PwDatabaseV4 ) {
			group = new PwGroupV4();
		} else {
			Log.d("SearchDbHelper", "Tried to search with unknown db");
			return null;
		}
		group.name = mCtx.getString(R.string.search_results);
		group.childEntries = new ArrayList<PwEntry>();

		// Search all entries
		Locale loc = Locale.getDefault();
		qStr = qStr.toLowerCase(loc);
		boolean isOmitBackup = omitBackup();

		Queue<PwGroup> worklist = new LinkedList<PwGroup>();
		if (pm.rootGroup != null) {
			worklist.add(pm.rootGroup);
		}

		while (worklist.size() != 0) {
			PwGroup top = worklist.remove();

			if (pm.isGroupSearchable(top, isOmitBackup)) {
				for (PwEntry entry : top.childEntries) {
					processEntries(entry, group.childEntries, qStr, loc);
				}

				for (PwGroup childGroup : top.childGroups) {
					if (childGroup != null) {
						worklist.add(childGroup);
					}
				}
			}
		}

		return group;
	}

	public void processEntries(PwEntry entry, List<PwEntry> results, String qStr, Locale loc) {
		// Search all strings in the entry
		Iterator<String> iter = entry.stringIterator();
		while (iter.hasNext()) {
			String str = iter.next();
			if (str != null && str.length() != 0) {
				String lower = str.toLowerCase(loc);
				if (lower.contains(qStr)) {
					results.add(entry);
					break;
				}
			}
		}
	}

}

public class SearchResults extends GroupBaseActivity {

	private Database mDb;
	//private String mQuery;

	@Override
	protected void onCreate(Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);

		if ( isFinishing() ) {
			return;
		}

		setResult(KeePass.EXIT_NORMAL);

		mDb = App.getDB();

		// Likely the app has been killed exit the activity 
		if ( ! mDb.Loaded() ) {
			finish();
		}

		performSearch(getSearchStr(getIntent()));

	}

	private void performSearch(String query) {
		query(query.trim());
	}

	private void query(String query) {
		mGroup = mDb.Search(query);

		if ( mGroup == null || mGroup.childEntries.size() < 1 ) {
			setContentView(new GroupEmptyView(this));
		} else {
			setContentView(new GroupViewOnlyView(this));
		}
		Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);
		setSupportActionBar(toolbar);

		setGroupTitle();

		setListAdapter(new PwGroupListAdapter(this, mGroup));
	}

	/*
	@Override
	protected void onNewIntent(Intent intent) {
		super.onNewIntent(intent);

		mQuery = getSearchStr(intent);
		performSearch();
		//mGroup = processSearchIntent(intent);
		//assert(mGroup != null);
	}
	*/

	private String getSearchStr(Intent queryIntent) {
        // get and process search query here
        final String queryAction = queryIntent.getAction();
        if ( Intent.ACTION_SEARCH.equals(queryAction) ) {
        	return queryIntent.getStringExtra(SearchManager.QUERY);
        }

        return "";

	}

}

