public class AppSettingsFragement extends PreferenceFragmentCompat {
	public static boolean KEYFILE_DEFAULT = false;

	private BackupManager backupManager;

	public static void Launch(Context ctx) {
		Intent i = new Intent(ctx, AppSettingsFragement.class);

		ctx.startActivity(i);
	}

	@Override
	public void onCreate(@Nullable Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);

		setRetainInstance(true);
	}

	@Override
	public void onCreatePreferences(Bundle savedInstanceState, String rootKey) {
		addPreferencesFromResource(R.xml.preferences_app);

		Preference keyFile = findPreference(getString(R.string.keyfile_key));
		keyFile.setOnPreferenceChangeListener(new Preference.OnPreferenceChangeListener() {

			public boolean onPreferenceChange(Preference preference, Object newValue) {
				Boolean value = (Boolean) newValue;

				if ( ! value.booleanValue() ) {
					App.getFileHistory().deleteAllKeys();
				}

				return true;
			}
		});

		Preference recentHistory = findPreference(getString(R.string.recentfile_key));
		recentHistory.setOnPreferenceChangeListener(new Preference.OnPreferenceChangeListener() {

			public boolean onPreferenceChange(Preference preference, Object newValue) {
				Boolean value = (Boolean) newValue;

				if (value == null) {
					value = true;
				}

				if (!value) {
					App.getFileHistory().deleteAll();
				}

				return true;
			}
		});

		backupManager = new BackupManager(getContext());
	}

	@Override
	public void onStop() {
		backupManager.dataChanged();

		super.onStop();
	}

}

public class DBSettingsFragement extends PreferenceFragmentCompat {
	public static boolean KEYFILE_DEFAULT = false;

	private BackupManager backupManager;

	public static void Launch(Context ctx) {
		Intent i = new Intent(ctx, DBSettingsFragement.class);

		ctx.startActivity(i);
	}

	@Override
	public void onCreate(@Nullable Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);

		setRetainInstance(true);
	}

	private static final String ROUNDS_PREFERENCE_TAG = "ROUNDS";
	@Override
	public void onDisplayPreferenceDialog(Preference preference) {
		if (getFragmentManager().findFragmentByTag(ROUNDS_PREFERENCE_TAG) != null) {
			return;
		}

		String key = preference.getKey();
		if ((preference instanceof EditTextPreference) && key.equals(getString(R.string.rounds_key)) ) {
			final DialogFragment f = RoundsPreferenceFragment.newInstance(preference.getKey());
			f.setTargetFragment(this, 0);
			f.show(getFragmentManager(), ROUNDS_PREFERENCE_TAG);
		} else {
			super.onDisplayPreferenceDialog(preference);
		}
	}


	@Override
	public void onCreatePreferences(Bundle savedInstanceState, String rootKey) {
		addPreferencesFromResource(R.xml.preferences_db);

		Database db = App.getDB();
		if ( db.Loaded() && db.pm.appSettingsEnabled() ) {
			Preference rounds = findPreference(getString(R.string.rounds_key));
			rounds.setOnPreferenceChangeListener(new Preference.OnPreferenceChangeListener() {

				public boolean onPreferenceChange(Preference preference, Object newValue) {
					setRounds(App.getDB(), preference);
					return true;
				}
			});

			setRounds(db, rounds);

			Preference algorithm = findPreference(getString(R.string.algorithm_key));
			setAlgorithm(db, algorithm);
		}

		backupManager = new BackupManager(getContext());
	}

	@Override
	public void onStop() {
		backupManager.dataChanged();

		super.onStop();
	}

	private void setRounds(Database db, Preference rounds) {
		rounds.setSummary(Long.toString(db.pm.getNumRounds()));
	}

	private void setAlgorithm(Database db, Preference algorithm) {
		int resId;
		if ( db.pm.getEncAlgorithm() == PwEncryptionAlgorithm.Rjindal ) {
			resId = R.string.rijndael;
		} else  {
			resId = R.string.twofish;
		}

		algorithm.setSummary(resId);
	}



}

public class LongEditTextPreference extends EditTextPreference {
    public LongEditTextPreference(Context context, AttributeSet attrs, int defStyleAttr, int defStyleRes) {
        super(context, attrs, defStyleAttr, defStyleRes);
    }

    public LongEditTextPreference(Context context, AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
    }

    public LongEditTextPreference(Context context, AttributeSet attrs) {
        super(context, attrs);
    }

    public LongEditTextPreference(Context context) {
        super(context);
    }

    @Override
    protected String getPersistedString(String defaultReturnValue) {
        return String.valueOf(getPersistedLong(-1));
    }

    @Override
    protected boolean persistString(String value) {
        try {
            return persistLong(Long.valueOf(value));
        } catch (NumberFormatException e) {
            Toast.makeText(getContext(), R.string.error_rounds_not_number, Toast.LENGTH_LONG).show();
        }

        return false;
    }
}

