public class RoundsPreferenceFragment extends EditTextPreferenceDialogFragmentCompat {

	private EditText mEditText;
	private PwDatabase mPM;

	@Override
	protected void onBindDialogView(View view) {
		super.onBindDialogView(view);

		mEditText = view.findViewById(android.R.id.edit);
		mEditText.setInputType(InputType.TYPE_CLASS_NUMBER|InputType.TYPE_NUMBER_VARIATION_NORMAL);

		Database db = App.getDB();
		mPM = db.pm;
		mEditText.setText(Long.toString(db.pm.getNumRounds()));

	}


	public static RoundsPreferenceFragment newInstance(String key) {
		final RoundsPreferenceFragment
				fragment = new RoundsPreferenceFragment();
		final Bundle b = new Bundle(1);
		b.putString(ARG_KEY, key);
		fragment.setArguments(b);
		return fragment;
	}

	@Override
	public void onDialogClosed(boolean positiveResult) {
	    EditTextPreference pref = (EditTextPreference) getPreference();

		if ( positiveResult ) {
			int rounds;

			try {
				String strRounds = mEditText.getText().toString();
				rounds = Integer.parseInt(strRounds);
			} catch (NumberFormatException e) {
				Toast.makeText(getContext(), R.string.error_rounds_not_number, Toast.LENGTH_LONG).show();
				return;
			}

			if ( rounds < 1 ) {
				rounds = 1;
			}


			long oldRounds = mPM.getNumRounds();
			try {
				mPM.setNumRounds(rounds);
			} catch (NumberFormatException e) {
				Toast.makeText(getContext(), R.string.error_rounds_too_large, Toast.LENGTH_LONG).show();
				mPM.setNumRounds(Integer.MAX_VALUE);
			}

			Handler handler = new Handler();
			SaveDB save = new SaveDB(getContext(), App.getDB(), new AfterSave(getContext(), handler, oldRounds));
			ProgressTask pt = new ProgressTask(getActivity(), save, R.string.saving_database);
			pt.run();

		}

	}

	private class AfterSave extends OnFinish {
		private long mOldRounds;
		private Context mCtx;

		public AfterSave(Context ctx, Handler handler, long oldRounds) {
			super(handler);

			mCtx = ctx;
			mOldRounds = oldRounds;
		}

		@Override
		public void run() {
			if ( mSuccess ) {
				Preference preference = getPreference();
				Preference.OnPreferenceChangeListener listner = preference.getOnPreferenceChangeListener();
				if ( listner != null ) {
					listner.onPreferenceChange(preference, null);
				}
			} else {
				displayMessage(mCtx);
				mPM.setNumRounds(mOldRounds);
			}

			super.run();
		}

	}

}

public class BetterCipherInputStream extends FilterInputStream {

    private final Cipher cipher;
    private static final int I_DEFAULT_BUFFER_SIZE = 8 * 1024;
    private final byte[] i_buffer;
    private int index; // index of the bytes to return from o_buffer
    private byte[] o_buffer;
    private boolean finished;

    /**
     * Creates a new {@code BetterCipherInputStream} instance for an {@code
     * InputStream} and a cipher.
     *
     * @param is
     *            the input stream to read data from.
     * @param c
     *            the cipher to process the data with.
     */
    public BetterCipherInputStream(InputStream is, Cipher c) {
    	this(is, c, I_DEFAULT_BUFFER_SIZE);
    }

    /**
     * Creates a new {@code BetterCipherInputStream} instance for an {@code
     * InputStream} and a cipher.
     *
     * @param is
     *            the input stream to read data from.
     * @param c
     *            the cipher to process the data with.
     * @param bufferSize
     *            size to buffer output from the cipher
     */
    public BetterCipherInputStream(InputStream is, Cipher c, int bufferSize) {
        super(is);
        this.cipher = c;
        i_buffer = new byte[bufferSize];
    }

    /**
     * Creates a new {@code BetterCipherInputStream} instance for an {@code
     * InputStream} without a cipher.
     * <p>
     * A {@code NullCipher} is created and used to process the data.
     *
     * @param is
     *            the input stream to read data from.
     */
    protected BetterCipherInputStream(InputStream is) {
        this(is, new NullCipher());
    }

    /**
     * Reads the next byte from this cipher input stream.
     *
     * @return the next byte, or {@code -1} if the end of the stream is reached.
     * @throws IOException
     *             if an error occurs.
     */
    @Override
    public int read() throws IOException {
        if (finished) {
            return ((o_buffer == null) || (index == o_buffer.length))
                            ? -1
                            : o_buffer[index++] & 0xFF;
        }
        if ((o_buffer != null) && (index < o_buffer.length)) {
            return o_buffer[index++] & 0xFF;
        }
        index = 0;
        o_buffer = null;
        int num_read;
        while (o_buffer == null) {
            if ((num_read = in.read(i_buffer)) == -1) {
                try {
                    o_buffer = cipher.doFinal();
                } catch (Exception e) {
                    throw new IOException(e.getMessage());
                }
                finished = true;
                break;
            }
            o_buffer = cipher.update(i_buffer, 0, num_read);
        }
        return read();
    }

    /**
     * Reads the next {@code b.length} bytes from this input stream into buffer
     * {@code b}.
     *
     * @param b
     *            the buffer to be filled with data.
     * @return the number of bytes filled into buffer {@code b}, or {@code -1}
     *         if the end of the stream is reached.
     * @throws IOException
     *             if an error occurs.
     */
    @Override
    public int read(byte[] b) throws IOException {
        return read(b, 0, b.length);
    }

    /**
     * Reads the next {@code len} bytes from this input stream into buffer
     * {@code b} starting at offset {@code off}.
     * <p>
     * if {@code b} is {@code null}, the next {@code len} bytes are read and
     * discarded.
     *
     * @param b
     *            the buffer to be filled with data.
     * @param off
     *            the offset to start in the buffer.
     * @param len
     *            the maximum number of bytes to read.
     * @return the number of bytes filled into buffer {@code b}, or {@code -1}
     *         of the of the stream is reached.
     * @throws IOException
     *             if an error occurs.
     * @throws NullPointerException
     *             if the underlying input stream is {@code null}.
     */
    @Override
    public int read(byte[] b, int off, int len) throws IOException {
        if (in == null) {
            throw new NullPointerException("Underlying input stream is null");
        }

        int read_b;
        int i;
        for (i=0; i<len; i++) {
            if ((read_b = read()) == -1) {
                return (i == 0) ? -1 : i;
            }
            if (b != null) {
                b[off+i] = (byte) read_b;
            }
        }
        return i;
    }

    /**
     * Skips up to n bytes from this input stream.
     * <p>
     * The number of bytes skipped depends on the result of a call to
     * {@link BetterCipherInputStream#available() available}. The smaller of n and the
     * result are the number of bytes being skipped.
     *
     * @param n
     *            the number of bytes that should be skipped.
     * @return the number of bytes actually skipped.
     * @throws IOException
     *             if an error occurs
     */
    @Override
    public long skip(long n) throws IOException {
        long i = 0;
        int available = available();
        if (available < n) {
            n = available;
        }
        while ((i < n) && (read() != -1)) {
            i++;
        }
        return i;
    }

    /**
     * Returns the number of bytes available without blocking.
     *
     * @return the number of bytes available, currently zero.
     * @throws IOException
     *             if an error occurs
     */
    @Override
    public int available() throws IOException {
        return 0;
    }

    /**
     * Closes this {@code BetterCipherInputStream}, also closes the underlying input
     * stream and call {@code doFinal} on the cipher object.
     *
     * @throws IOException
     *             if an error occurs.
     */
    @Override
    public void close() throws IOException {
        in.close();
        try {
            cipher.doFinal();
        } catch (GeneralSecurityException ignore) {
            //do like RI does
        }

    }

    /**
     * Returns whether this input stream supports {@code mark} and
     * {@code reset}, which it does not.
     *
     * @return false, since this input stream does not support {@code mark} and
     *         {@code reset}.
     */
    @Override
    public boolean markSupported() {
        return false;
    }
}

public class CopyInputStream extends InputStream {
	private InputStream is;
	private OutputStream os;

	public CopyInputStream(InputStream is, OutputStream os) {
		this.is = is;
		this.os = os;
	}

	@Override
	public int available() throws IOException {
		return is.available();
	}

	@Override
	public void close() throws IOException {
		is.close();
		os.close();
	}

	@Override
	public void mark(int readlimit) {
		is.mark(readlimit);
	}

	@Override
	public boolean markSupported() {
		return is.markSupported();
	}

	@Override
	public int read() throws IOException {
		int data = is.read();

		if (data != -1) {
			os.write(data);
		}

		return data;
	}

	@Override
	public int read(byte[] b, int offset, int length) throws IOException {
		int len = is.read(b, offset, length);

		if (len != -1) {
			os.write(b, offset, len);
		}

		return len;
	}

	@Override
	public int read(byte[] b) throws IOException {
		int len = is.read(b);

		if (len != -1) {
			os.write(b, 0, len);
		}

		return len;
	}

	@Override
	public synchronized void reset() throws IOException {
		is.reset();
	}

	@Override
	public long skip(long byteCount) throws IOException {
		return is.skip(byteCount);
	}

}

public class CountInputStream extends InputStream {
	InputStream is;
	long bytes = 0;

	public CountInputStream(InputStream is) {
		this.is = is;
	}

	@Override
	public int available() throws IOException {
		return is.available();
	}

	@Override
	public void close() throws IOException {
		is.close();
	}

	@Override
	public void mark(int readlimit) {
		is.mark(readlimit);
	}

	@Override
	public boolean markSupported() {
		return is.markSupported();
	}

	@Override
	public int read() throws IOException {
		bytes++;
		return is.read();
	}

	@Override
	public int read(byte[] buffer, int offset, int length) throws IOException {
		bytes += length;
		return is.read(buffer, offset, length);
	}

	@Override
	public int read(byte[] buffer) throws IOException {
		bytes += buffer.length;
		return is.read(buffer);
	}

	@Override
	public synchronized void reset() throws IOException {
		is.reset();
	}

	@Override
	public long skip(long byteCount) throws IOException {
		bytes += byteCount;
		return is.skip(byteCount);
	}

}

public class CountOutputStream extends OutputStream {
	OutputStream os;
	long bytes = 0;

	public CountOutputStream(OutputStream os) {
		this.os = os;
	}


	@Override
	public void close() throws IOException {
		os.close();
	}

	@Override
	public void flush() throws IOException {
		os.flush();
	}


	@Override
	public void write(byte[] buffer, int offset, int count) throws IOException {
		bytes += count;
		os.write(buffer, offset, count);
	}

	@Override
	public void write(byte[] buffer) throws IOException {
		bytes += buffer.length;
		os.write(buffer);
	}

	@Override
	public void write(int oneByte) throws IOException {
		bytes++;
		os.write(oneByte);
	}
}

public class HashedBlockInputStream extends InputStream {

	private final static int HASH_SIZE = 32;

	private LEDataInputStream baseStream;
	private int bufferPos = 0;
	private byte[] buffer = new byte[0];
	private long bufferIndex = 0;
	private boolean atEnd = false;


	@Override
	public int read(byte[] b) throws IOException {
		return read(b, 0, b.length);
	}

	public HashedBlockInputStream(InputStream is) {
		baseStream = new LEDataInputStream(is);
	}

	@Override
	public int read(byte[] b, int offset, int length) throws IOException {
		if ( atEnd ) return -1;

		int remaining = length;

		while ( remaining > 0 ) {
			if ( bufferPos == buffer.length ) {
				// Get more from the source into the buffer
				if ( ! ReadHashedBlock() ) {
					return length - remaining;
				}

			}

			// Copy from buffer out
			int copyLen = Math.min(buffer.length - bufferPos, remaining);

			System.arraycopy(buffer, bufferPos, b, offset, copyLen);

			offset += copyLen;
			bufferPos += copyLen;

			remaining -= copyLen;
		}

		return length;
	}

	/**
	 * @return false, when the end of the source stream is reached 
	 * @throws IOException 
	 */
	private boolean ReadHashedBlock() throws IOException {
		if ( atEnd ) return false;

		bufferPos = 0;

		long index = baseStream.readUInt();
		if ( index != bufferIndex ) {
			throw new IOException("Invalid data format");
		}
		bufferIndex++;

		byte[] storedHash = baseStream.readBytes(32);
		if ( storedHash == null || storedHash.length != HASH_SIZE) {
			throw new IOException("Invalid data format");
		}

		int bufferSize = LEDataInputStream.readInt(baseStream);
		if ( bufferSize < 0 ) {
			throw new IOException("Invalid data format");
		}

		if ( bufferSize == 0 ) {
			for (int hash = 0; hash < HASH_SIZE; hash++) {
				if ( storedHash[hash] != 0 ) {
					throw new IOException("Invalid data format");
				}
			}

			atEnd = true;
			buffer = new byte[0];
			return false;
		}

		buffer = baseStream.readBytes(bufferSize);
		if ( buffer == null || buffer.length != bufferSize ) {
			throw new IOException("Invalid data format");
		}

		MessageDigest md = null;
		try {
			md = MessageDigest.getInstance("SHA-256");
		} catch (NoSuchAlgorithmException e) {
			throw new IOException("SHA-256 not implemented here.");
		}

		byte[] computedHash = md.digest(buffer);
		if ( computedHash == null || computedHash.length != HASH_SIZE ) {
			throw new IOException("Hash wrong size");
		}

		if ( ! Arrays.equals(storedHash, computedHash) ) {
			throw new IOException("Hashes didn't match.");
		}

		return true;
	}

	@Override
	public long skip(long n) throws IOException {
		return 0;
	}

	@Override
	public int read() throws IOException {
		if ( atEnd ) return -1;

		if ( bufferPos == buffer.length ) {
			if ( ! ReadHashedBlock() ) return -1;
		}

		int output = Types.readUByte(buffer, bufferPos);
		bufferPos++;

		return output;
	}

	@Override
	public void close() throws IOException {
		baseStream.close();
	}

}

public class HashedBlockOutputStream extends OutputStream {

	private final static int DEFAULT_BUFFER_SIZE = 1024 * 1024; 

	private LEDataOutputStream baseStream;
	private int bufferPos = 0;
	private byte[] buffer;
	private long bufferIndex = 0;

	public HashedBlockOutputStream(OutputStream os) {
		init(os, DEFAULT_BUFFER_SIZE);
	}

	public HashedBlockOutputStream(OutputStream os, int bufferSize) {
		if ( bufferSize <= 0 ) {
			bufferSize = DEFAULT_BUFFER_SIZE;
		}

		init(os, bufferSize);
	}

	private void init(OutputStream os, int bufferSize) {
		baseStream = new LEDataOutputStream(os);
		buffer = new byte[bufferSize];

	}

	@Override
	public void write(int oneByte) throws IOException {
		byte[] buf = new byte[1];
		buf[0] = (byte)oneByte;
		write(buf, 0, 1);
	}

	@Override
	public void close() throws IOException {
		if ( bufferPos != 0 ) {
			// Write remaining buffered amount
			WriteHashedBlock();
		}

		// Write terminating block
		WriteHashedBlock();

		flush();
		baseStream.close();
	}

	@Override
	public void flush() throws IOException {
		baseStream.flush();
	}

	@Override
	public void write(byte[] b, int offset, int count) throws IOException {
		while ( count > 0 ) {
			if ( bufferPos == buffer.length ) {
				WriteHashedBlock();
			}

			int copyLen = Math.min(buffer.length - bufferPos, count);

			System.arraycopy(b, offset, buffer, bufferPos, copyLen);

			offset += copyLen;
			bufferPos += copyLen;

			count -= copyLen;
		}
	}

	private void WriteHashedBlock() throws IOException {
		baseStream.writeUInt(bufferIndex);
		bufferIndex++;

		if ( bufferPos > 0 ) {
			MessageDigest md = null;
			try {
				md = MessageDigest.getInstance("SHA-256");
			} catch (NoSuchAlgorithmException e) {
				throw new IOException("SHA-256 not implemented here.");
			}

			byte[] hash;
			md.update(buffer, 0, bufferPos);
			hash = md.digest();
			/*
			if ( bufferPos == buffer.length) {
				hash = md.digest(buffer);
			} else {
				byte[] b = new byte[bufferPos];
				System.arraycopy(buffer, 0, b, 0, bufferPos);
				hash = md.digest(b);
			}
			*/

			baseStream.write(hash);

		} else {
			// Write 32-bits of zeros
			baseStream.writeLong(0L);
			baseStream.writeLong(0L);
			baseStream.writeLong(0L);
			baseStream.writeLong(0L);
		}

		baseStream.writeInt(bufferPos);

		if ( bufferPos > 0 ) {
			baseStream.write(buffer, 0, bufferPos);
		}

		bufferPos = 0;

	}

	@Override
	public void write(byte[] buffer) throws IOException {
		write(buffer, 0, buffer.length);
	}

}

public class HmacBlockInputStream extends InputStream {
    private LEDataInputStream baseStream;
    private boolean verify;
    private byte[] key;
    private byte[] buffer;
    private int bufferPos = 0;
    private long blockIndex = 0;
    private boolean endOfStream = false;

    public HmacBlockInputStream(InputStream baseStream, boolean verify, byte[] key) {
        super();

        this.baseStream = new LEDataInputStream(baseStream);
        this.verify = verify;
        this.key = key;
        buffer = new byte[0];
    }

    @Override
    public int read() throws IOException {
        if (endOfStream) return -1;

        if (bufferPos == buffer.length) {
            if (!readSafeBlock()) return -1;
        }

        int output = Types.readUByte(buffer, bufferPos);
        bufferPos++;

        return output;
    }

    @Override
    public int read(byte[] outBuffer, int byteOffset, int byteCount) throws IOException {
        int remaining = byteCount;
        while (remaining > 0) {
            if (bufferPos == buffer.length) {
                if (!readSafeBlock()) {
                    int read = byteCount - remaining;
                    if (read <= 0) {
                        return -1;
                    } else {
                        return byteCount - remaining;
                    }
                }
            }

            int copy = Math.min(buffer.length - bufferPos, remaining);
            assert(copy > 0);

            System.arraycopy(buffer, bufferPos, outBuffer, byteOffset, copy);
            byteOffset += copy;
            bufferPos += copy;

            remaining -= copy;
        }

        return byteCount;
    }

    @Override
    public int read(byte[] outBuffer) throws IOException {
        return read(outBuffer, 0, outBuffer.length);
    }

    private boolean readSafeBlock() throws IOException {
        if (endOfStream) return false;

        byte[] storedHmac = baseStream.readBytes(32);
        if (storedHmac == null || storedHmac.length != 32) {
            throw new IOException("File corrupted");
        }

        byte[] pbBlockIndex = LEDataOutputStream.writeLongBuf(blockIndex);
        byte[] pbBlockSize = baseStream.readBytes(4);
        if (pbBlockSize == null || pbBlockSize.length != 4) {
            throw new IOException("File corrupted");
        }
        int blockSize = LEDataInputStream.readInt(pbBlockSize, 0);
        bufferPos = 0;

        buffer = baseStream.readBytes(blockSize);

        if (verify) {
            byte[] cmpHmac;
            byte[] blockKey = HmacBlockStream.GetHmacKey64(key, blockIndex);
            Mac hmac;
            try {
                hmac = Mac.getInstance("HmacSHA256");
                SecretKeySpec signingKey = new SecretKeySpec(blockKey, "HmacSHA256");
                hmac.init(signingKey);
            } catch (NoSuchAlgorithmException e) {
                throw new IOException("Invalid Hmac");
            } catch (InvalidKeyException e) {
                throw new IOException("Invalid Hmac");
            }

            hmac.update(pbBlockIndex);
            hmac.update(pbBlockSize);

            if (buffer.length > 0) {
                hmac.update(buffer);
            }

            cmpHmac = hmac.doFinal();
            Arrays.fill(blockKey, (byte)0);

            if (!Arrays.equals(cmpHmac, storedHmac)) {
                throw new IOException("Invalid Hmac");
            }

        }

        blockIndex++;

        if (blockSize == 0) {
            endOfStream = true;
            return false;
        }

        return true;
    }

    @Override
    public boolean markSupported() {
        return false;
    }

    @Override
    public void close() throws IOException {
        baseStream.close();
    }

    @Override
    public long skip(long byteCount) throws IOException {
        return 0;
    }

    @Override
    public int available() throws IOException {
        return buffer.length - bufferPos;
    }
}

public class HmacBlockOutputStream extends OutputStream {
    private static final int DEFAULT_BUFFER_SIZE = 1024 * 1024;
    private LEDataOutputStream baseStream;
    private byte[] key;

    private byte[] buffer = new byte[DEFAULT_BUFFER_SIZE];
    private int bufferPos = 0;
    private long blockIndex = 0;

    public HmacBlockOutputStream(OutputStream os, byte[] key) {
        this.baseStream = new LEDataOutputStream(os);
        this.key = key;
    }

    @Override
    public void close() throws IOException {
        if (bufferPos == 0) {
            WriteSafeBlock();
        } else {
            WriteSafeBlock();
            WriteSafeBlock();
        }

        baseStream.flush();;
        baseStream.close();
    }

    @Override
    public void flush() throws IOException {
        baseStream.flush();
    }

    @Override
    public void write(byte[] outBuffer) throws IOException {
        write(outBuffer, 0, outBuffer.length);
    }

    @Override
    public void write(byte[] outBuffer, int offset, int count) throws IOException {
        while (count > 0) {
            if (bufferPos == buffer.length) {
                WriteSafeBlock();
            }

            int copy = Math.min(buffer.length - bufferPos, count);
            assert(copy > 0);

            System.arraycopy(outBuffer, offset, buffer, bufferPos, copy);
            offset += copy;
            bufferPos += copy;

            count -= copy;
        }
    }

    @Override
    public void write(int oneByte) throws IOException {
        byte[] outByte = new byte[1];
        write(outByte, 0, 1);
    }

    private void WriteSafeBlock() throws IOException {
        byte[] bufBlockIndex = LEDataOutputStream.writeLongBuf(blockIndex);
        byte[] blockSizeBuf = LEDataOutputStream.writeIntBuf(bufferPos);

        byte[] blockHmac;
        byte[] blockKey = HmacBlockStream.GetHmacKey64(key, blockIndex);

        Mac hmac;
        try {
            hmac = Mac.getInstance("HmacSHA256");
            SecretKeySpec signingKey = new SecretKeySpec(blockKey, "HmacSHA256");
            hmac.init(signingKey);
        } catch (NoSuchAlgorithmException e) {
            throw new IOException("Invalid Hmac");
        } catch (InvalidKeyException e) {
            throw new IOException("Invalid HMAC");
        }

        hmac.update(bufBlockIndex);
        hmac.update(blockSizeBuf);

        if (bufferPos > 0) {
            hmac.update(buffer, 0, bufferPos);
        }

        blockHmac = hmac.doFinal();

        baseStream.write(blockHmac);
        baseStream.write(blockSizeBuf);

        if (bufferPos > 0) {
            baseStream.write(buffer, 0, bufferPos);
        }

        blockIndex++;
        bufferPos = 0;
    }
}

public class HmacBlockStream {
    public static byte[] GetHmacKey64(byte[] key, long blockIndex) {
        MessageDigest hash;
        try {
            hash = MessageDigest.getInstance("SHA-512");
        } catch (NoSuchAlgorithmException e) {
            throw new RuntimeException(e);
        }

        NullOutputStream nos = new NullOutputStream();
        DigestOutputStream dos = new DigestOutputStream(nos, hash);
        LEDataOutputStream leos = new LEDataOutputStream(dos);

        try {
            leos.writeLong(blockIndex);
            leos.write(key);
            leos.close();
        } catch (IOException e) {
            throw new RuntimeException(e);
        }

        byte[] hashKey = hash.digest();
        assert(hashKey.length == 64);

        return hashKey;
    }

}

public class LEDataInputStream extends InputStream {

	public static final long INT_TO_LONG_MASK = 0xffffffffL;

	private InputStream baseStream;

	public LEDataInputStream(InputStream in) {
		baseStream = in;
	}

	/** Read a 32-bit value and return it as a long, so that it can
	 *  be interpreted as an unsigned integer.
	 * @return
	 * @throws IOException
	 */
	public long readUInt() throws IOException {
		return readUInt(baseStream);
	}

	public int readInt() throws IOException {
		return readInt(baseStream);
	}

	public long readLong() throws IOException {
		byte[] buf = readBytes(8);

		return readLong(buf, 0);
	}

	@Override
	public int available() throws IOException {
		return baseStream.available();
	}

	@Override
	public void close() throws IOException {
		baseStream.close();
	}

	@Override
	public void mark(int readlimit) {
		baseStream.mark(readlimit);
	}

	@Override
	public boolean markSupported() {
		return baseStream.markSupported();
	}

	@Override
	public int read() throws IOException {
		return baseStream.read();
	}

	@Override
	public int read(byte[] b, int offset, int length) throws IOException {
		return baseStream.read(b, offset, length);
	}

	@Override
	public int read(byte[] b) throws IOException {
		// TODO Auto-generated method stub
		return super.read(b);
	}

	@Override
	public synchronized void reset() throws IOException {
		baseStream.reset();
	}

	@Override
	public long skip(long n) throws IOException {
		return baseStream.skip(n);
	}

	public static byte[] readBytes(InputStream is, int length) throws IOException {
		byte[] buf = new byte[length];

		int count = 0;
		while ( count < length ) {
			int read = is.read(buf, count, length - count);

			// Reached end
			if ( read == -1 ) {
				// Stop early
				byte[] early = new byte[count];
				System.arraycopy(buf, 0, early, 0, count);
				return early;
			}

			count += read;
		}

		return buf;
	}

	public byte[] readBytes(int length) throws IOException {
		return readBytes(baseStream, length);
	}

	public static int readUShort(InputStream is) throws IOException {
		  byte[] buf = readBytes(is, 2);

		  return readUShort(buf, 0);
	  }

	public int readUShort() throws IOException {
		return readUShort(baseStream);
	}

	/**
	   * Read an unsigned 16-bit value.
	   * 
	   * @param buf
	   * @param offset
	   * @return
	   */
	  public static int readUShort( byte[] buf, int offset ) {
	    return (buf[offset + 0] & 0xFF) + ((buf[offset + 1] & 0xFF) << 8);
	  }

	public static long readLong( byte buf[], int offset ) {
		return ((long)buf[offset + 0] & 0xFF) + (((long)buf[offset + 1] & 0xFF) << 8) 
		+ (((long)buf[offset + 2] & 0xFF) << 16) + (((long)buf[offset + 3] & 0xFF) << 24) 
		+ (((long)buf[offset + 4] & 0xFF) << 32) + (((long)buf[offset + 5] & 0xFF) << 40) 
		+ (((long)buf[offset + 6] & 0xFF) << 48) + (((long)buf[offset + 7] & 0xFF) << 56);
	}

	public static long readUInt( byte buf[], int offset ) {
		  return (readInt(buf, offset) & INT_TO_LONG_MASK);
	  }

	public static int readInt(InputStream is) throws IOException {
		  byte[] buf = readBytes(is, 4);

		  return readInt(buf, 0);
	  }

	public static long readUInt(InputStream is) throws IOException {
		  return (readInt(is) & INT_TO_LONG_MASK);
	  }

	/**
	   * Read a 32-bit value.
	   * 
	   * @param buf
	   * @param offset
	   * @return
	   */
	  public static int readInt( byte buf[], int offset ) {
	    return (buf[offset + 0] & 0xFF) + ((buf[offset + 1] & 0xFF) << 8) + ((buf[offset + 2] & 0xFF) << 16)
	           + ((buf[offset + 3] & 0xFF) << 24);
	  }

	  public UUID readUUID() throws IOException {
		  byte[] buf = readBytes(16);

		  return Types.bytestoUUID(buf);
	  }

}

public class LEDataOutputStream extends OutputStream {

	private OutputStream baseStream; 

	public LEDataOutputStream(OutputStream out) {
		baseStream = out;
	}

	public void writeUInt(long uint) throws IOException {
		baseStream.write(LEDataOutputStream.writeIntBuf((int) uint));
	}

	@Override
	public void close() throws IOException {
		baseStream.close();
	}

	@Override
	public void flush() throws IOException {
		baseStream.flush();
	}

	@Override
	public void write(byte[] buffer, int offset, int count) throws IOException {
		baseStream.write(buffer, offset, count);
	}

	@Override
	public void write(byte[] buffer) throws IOException {
		baseStream.write(buffer);
	}

	@Override
	public void write(int oneByte) throws IOException {
		baseStream.write(oneByte);
	}

	public void writeLong(long val) throws IOException {
		byte[] buf = new byte[8];

		writeLong(val, buf, 0);
		baseStream.write(buf);
	}

	public void writeInt(int val) throws IOException {
		byte[] buf = new byte[4];
		writeInt(val, buf, 0);

		baseStream.write(buf);
	}

	public void writeUShort(int val) throws IOException {
		byte[] buf = new byte[2];
		writeUShort(val, buf, 0);
		baseStream.write(buf);
	}

	public static byte[] writeIntBuf(int val) {
		  byte[] buf = new byte[4];
		  writeInt(val, buf, 0);

		  return buf;
	  }

	public static byte[] writeUShortBuf(int val) {
		  byte[] buf = new byte[2];

		  writeUShort(val, buf, 0);

		  return buf;
	  }

	/** Write an unsigned 16-bit value
	   * 
	   * @param val
	   * @param buf
	   * @param offset
	   */
	  public static void writeUShort(int val, byte[] buf, int offset) {
		  buf[offset + 0] = (byte)(val & 0x00FF);
		  buf[offset + 1] = (byte)((val & 0xFF00) >> 8);
	  }

	/**
	   * Write a 32-bit value.
	   * 
	   * @param val
	   * @param buf
	   * @param offset
	   */
	  public static void writeInt( int val, byte[] buf, int offset ) {
	    buf[offset + 0] = (byte)(val & 0xFF);
	    buf[offset + 1] = (byte)((val >>> 8) & 0xFF);
	    buf[offset + 2] = (byte)((val >>> 16) & 0xFF);
	    buf[offset + 3] = (byte)((val >>> 24) & 0xFF);
	  }

	  public static byte[] writeLongBuf(long val) {
		  byte[] buf = new byte[8];
		  writeLong(val, buf, 0);
		  return buf;
	  }

	public static void writeLong( long val, byte[] buf, int offset ) {
		buf[offset + 0] = (byte)(val & 0xFF);
		buf[offset + 1] = (byte)((val >>> 8) & 0xFF);
		buf[offset + 2] = (byte)((val >>> 16) & 0xFF);
		buf[offset + 3] = (byte)((val >>> 24) & 0xFF);
		buf[offset + 4] = (byte)((val >>> 32) & 0xFF);
		buf[offset + 5] = (byte)((val >>> 40) & 0xFF);
		buf[offset + 6] = (byte)((val >>> 48) & 0xFF);
		buf[offset + 7] = (byte)((val >>> 56) & 0xFF);
	}

}

public class MacOutputStream extends OutputStream {
    private Mac mac;
    private OutputStream os;

    public MacOutputStream(OutputStream os, Mac mac) {
        this.mac = mac;
        this.os = os;
    }

    @Override
    public void flush() throws IOException {
        os.flush();
    }

    @Override
    public void close() throws IOException {
        os.close();
    }

    @Override
    public void write(int oneByte) throws IOException {
        mac.update((byte) oneByte);
        os.write(oneByte);
    }

    @Override
    public void write(byte[] buffer, int offset, int count) throws IOException {
        mac.update(buffer, offset, count);
        os.write(buffer, offset, count);
    }

    @Override
    public void write(byte[] buffer) throws IOException {
        mac.update(buffer, 0, buffer.length);
        os.write(buffer);
    }

    public byte[] getMac() {
        return mac.doFinal();
    }
}

