	}

}

public class RandomFileOutputStream extends OutputStream {

	RandomAccessFile mFile;

	RandomFileOutputStream(RandomAccessFile file) {
		mFile = file;
	}

	@Override
	public void close() throws IOException {
		super.close();

		mFile.close();		
	}

	@Override
	public void write(byte[] buffer, int offset, int count) throws IOException {
		super.write(buffer, offset, count);

		mFile.write(buffer, offset, count);
	}

	@Override
	public void write(byte[] buffer) throws IOException {
		super.write(buffer);

		mFile.write(buffer);
	}

	@Override
	public void write(int oneByte) throws IOException {
		mFile.write(oneByte);
	}

	public void seek(long pos) throws IOException {
		mFile.seek(pos);
	}

}

public class TimeoutHelper {

	private static final long DEFAULT_TIMEOUT = 5 * 60 * 1000;  // 5 minutes

	public static void pause(Activity act) {
		// Record timeout time in case timeout service is killed
		long time = System.currentTimeMillis();

		SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(act);
		SharedPreferences.Editor edit = prefs.edit();
		edit.putLong(act.getString(R.string.timeout_key), time);

		edit.apply();

		if ( App.getDB().Loaded() ) {
	        Timeout.start(act);
		}

	}

	public static long getTimeoutLength(Context ctx) {
		SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(ctx);
		String sTimeout = prefs.getString(ctx.getString(R.string.app_timeout_key), ctx.getString(R.string.clipboard_timeout_default));
		long timeout;
		try {
			timeout = Long.parseLong(sTimeout);
		} catch (NumberFormatException e) {
			timeout = DEFAULT_TIMEOUT;
		}

		return timeout;
	}

	public static void resume(Activity act) {
		if ( App.getDB().Loaded() ) {
	        Timeout.cancel(act);
		}


		// Check whether the timeout has expired
		long cur_time = System.currentTimeMillis();

		SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(act);
		long timeout_start = prefs.getLong(act.getString(R.string.timeout_key), -1);
		// The timeout never started
		if (timeout_start == -1) {
			return;
		}


		long timeout = getTimeoutLength(act);
		// We are set to never timeout
		if (timeout == -1) {
			return;
		}

		long diff = cur_time - timeout_start;
		if (diff >= timeout) {
			// We have timed out
			App.setShutdown();
		}
	}

	public static void checkShutdown(Activity act) {
		if ( App.isShutdown() && App.getDB().Loaded() ) {
			act.setResult(KeePass.EXIT_LOCK);
			act.finish();
		}
	}
}

public class Timeout {
	private static final int REQUEST_ID = 0;
	private static final long DEFAULT_TIMEOUT = 5 * 60 * 1000;  // 5 minutes
	private static String TAG = "KeePass Timeout";

	private static PendingIntent buildIntent(Context ctx) {
		Intent intent = new Intent(Intents.TIMEOUT);
		PendingIntent sender = PendingIntent.getBroadcast(ctx, REQUEST_ID, intent, PendingIntent.FLAG_CANCEL_CURRENT);

		return sender;
	}

	public static void start(Context ctx) {


		SharedPreferences prefs = PreferenceManager.getDefaultSharedPreferences(ctx);
		String sTimeout = prefs.getString(ctx.getString(R.string.app_timeout_key), ctx.getString(R.string.clipboard_timeout_default));

		long timeout;
		try {
			timeout = Long.parseLong(sTimeout);
		} catch (NumberFormatException e) {
			timeout = DEFAULT_TIMEOUT;
		}

		if ( timeout == -1 ) {
			// No timeout don't start timeout service
			return;
		}

		long triggerTime = System.currentTimeMillis() + timeout;
		AlarmManager am = (AlarmManager) ctx.getSystemService(Context.ALARM_SERVICE);

		Log.d(TAG, "Timeout start");
		am.set(AlarmManager.RTC, triggerTime, buildIntent(ctx));
	}

	public static void cancel(Context ctx) {
		AlarmManager am = (AlarmManager) ctx.getSystemService(Context.ALARM_SERVICE);

		Log.d(TAG, "Timeout cancel");
		am.cancel(buildIntent(ctx));
	}

}

public class DateUtil {
    private static final DateTime dotNetEpoch = new DateTime(1, 1, 1, 0, 0, 0, DateTimeZone.UTC);
    private static final DateTime javaEpoch = new DateTime(1970, 1, 1, 0, 0, 0, DateTimeZone.UTC);

    private static final long epochOffset;

    static {
        Date dotNet = dotNetEpoch.toDate();
        Date java = javaEpoch.toDate();

        epochOffset = (javaEpoch.getMillis() - dotNetEpoch.getMillis()) / 1000L;
    }

    public static Date convertKDBX4Time(long seconds) {

        DateTime dt = dotNetEpoch.plus(seconds * 1000L);

        // Switch corrupted dates to a more recent date that won't cause issues on the client
        if (dt.isBefore(javaEpoch)) {
            return javaEpoch.toDate();
        }

        return dt.toDate();
    }

    public static long convertDateToKDBX4Time(DateTime dt) {
        try {
            Seconds secs = Seconds.secondsBetween(javaEpoch, dt);
            return secs.getSeconds() + epochOffset;
        } catch (ArithmeticException e) {
            // secondsBetween overflowed an int
            Date javaDt = dt.toDate();
            long seconds = javaDt.getTime() / 1000L;
            return seconds + epochOffset;
        }
    }
}

public class EmptyUtils {
	public static boolean isNullOrEmpty(String str) {
		return (str == null) || (str.length() == 0);
	}

	public static boolean isNullOrEmpty(byte[] buf) {
		return (buf == null) || (buf.length == 0);
	}

	public static boolean isNullOrEmpty(PwDate date) {
		return (date == null) || date.equals(PwEntryV3.DEFAULT_PWDATE);
	}

	public static boolean isNullOrEmpty(Uri uri) {
		return (uri==null) || (uri.toString().length() == 0);
	}
}

public class Interaction {
	/**
	 * Indicates whether the specified action can be used as an intent. This
	 * method queries the package manager for installed packages that can
	 * respond to an intent with the specified action. If no suitable package is
	 * found, this method returns false.
	 *
	 * @param context The application's environment.
	 * @param action The Intent action to check for availability.
	 *
	 * @return True if an Intent with the specified action can be sent and
	 *         responded to, false otherwise.
	 */
	public static boolean isIntentAvailable(Context context, String action) {
	    final PackageManager packageManager = context.getPackageManager();
	    final Intent intent = new Intent(action);
	    List<ResolveInfo> list =
	            packageManager.queryIntentActivities(intent,
	                    PackageManager.MATCH_DEFAULT_ONLY);
	    return list.size() > 0;
	}
}

public class MemUtil {
	public static byte[] decompress(byte[] input) throws IOException {
		ByteArrayInputStream bais = new ByteArrayInputStream(input);
		GZIPInputStream gzis = new GZIPInputStream(bais);

		ByteArrayOutputStream baos = new ByteArrayOutputStream();
		Util.copyStream(gzis, baos);

		return baos.toByteArray();
	}

	public static byte[] compress(byte[] input) throws IOException {		
		ByteArrayInputStream bais = new ByteArrayInputStream(input);

		ByteArrayOutputStream baos = new ByteArrayOutputStream();
		GZIPOutputStream gzos = new GZIPOutputStream(baos);
		Util.copyStream(bais, gzos);
		gzos.close();

		return baos.toByteArray();
	}

}

public class NotificationUtil {
    public static final String COPY_CHANNEL_ID = "copy";
    public static final String COPY_CHANNEL_NAME = "Copy username and password";

    public static void createChannels(Context ctx) {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {

            NotificationChannel channel = new NotificationChannel(COPY_CHANNEL_ID,
                    COPY_CHANNEL_NAME, NotificationManager.IMPORTANCE_LOW);

            channel.enableLights(false);
            channel.enableVibration(false);
            channel.setLockscreenVisibility(Notification.VISIBILITY_PRIVATE);

            NotificationManager manager =
                    (NotificationManager) ctx.getSystemService(Context.NOTIFICATION_SERVICE);
            if (manager == null) return;

            manager.createNotificationChannel(channel);
        }
    }
}

public class PermissionUtil {

    private static final String[] READ_WRITE_PERMISSIONS =
            {Manifest.permission.READ_EXTERNAL_STORAGE, Manifest.permission.WRITE_EXTERNAL_STORAGE};

    public static boolean checkAndRequest(Activity act, int reqId) {

        boolean hasRead = ContextCompat.checkSelfPermission(act,
                Manifest.permission.READ_EXTERNAL_STORAGE) == PackageManager.PERMISSION_GRANTED;
        boolean hasWrite = ContextCompat.checkSelfPermission(act,
                Manifest.permission.WRITE_EXTERNAL_STORAGE) == PackageManager.PERMISSION_GRANTED;

        if (!hasRead || !hasWrite) {
            ActivityCompat.requestPermissions(act, READ_WRITE_PERMISSIONS,
                    reqId);

            return false;
        }

        return true;
    }
}

public class SprContextV4 implements Cloneable {
	public PwDatabaseV4 db;
	public PwEntryV4 entry;
	public Map<String, String> refsCache = new HashMap<String, String>();

	public SprContextV4(PwDatabaseV4 db, PwEntryV4 entry) {
		this.db = db;
		this.entry = entry;
	}

	@Override
	protected Object clone() {
		try {
			return super.clone();
		} catch (CloneNotSupportedException e) {
			return null;
		}
	}
}

public class SprEngine {

	private static SprEngineV4 sprV4 = new SprEngineV4();
	private static SprEngine spr = new SprEngine();

	public static SprEngine getInstance(PwDatabase db) {
		if (db instanceof PwDatabaseV4) {
            return sprV4;
		} 
		else {
            return spr;
		}
	}

	public String compile(String text, PwEntry entry, PwDatabase database) {
		return text;
	}

}

public class SprEngineV4 extends SprEngine {
	private final int MAX_RECURSION_DEPTH = 12;
	private final String STR_REF_START = "{REF:";
	private final String STR_REF_END = "}";

	public class TargetResult {
		public PwEntryV4 entry;
		public char wanted;

		public TargetResult(PwEntryV4 entry, char wanted) {
			this.entry = entry;
			this.wanted = wanted;
		}
	}

	@Override
	public String compile(String text, PwEntry entry, PwDatabase database) {
		SprContextV4 ctx = new SprContextV4((PwDatabaseV4)database, (PwEntryV4)entry);

		return compileInternal(text, ctx, 0);
	}

	private String compileInternal(String text, SprContextV4 ctx, int recursionLevel) {
		if (text == null) { return ""; }
		if (ctx == null) { return ""; }
		if (recursionLevel >= MAX_RECURSION_DEPTH) { return ""; }

		return fillRefPlaceholders(text, ctx, recursionLevel);
	}

	private String fillRefPlaceholders(String text, SprContextV4 ctx, int recursionLevel) {

		if (ctx.db == null) { return text; }

		int offset = 0;
		for (int i = 0; i < 20; ++i) {
			text = fillRefsUsingCache(text, ctx);

			int start = StrUtil.indexOfIgnoreCase(text, STR_REF_START, offset, Locale.ENGLISH);
			if (start < 0) { break; }
			int end = StrUtil.indexOfIgnoreCase(text, STR_REF_END, start + 1, Locale.ENGLISH);
			if (end <= start) { break; }

			String fullRef = text.substring(start, end + 1);
			TargetResult result = findRefTarget(fullRef, ctx);

			if (result != null) {
                PwEntryV4 found = result.entry;
                char wanted = result.wanted;

                if (found != null) {
                	String data;
                	switch (wanted) {
                	case 'T':
                		data = found.getTitle();
                		break;
                	case 'U':
                		data = found.getUsername();
                		break;
                	case 'A':
                		data = found.getUrl();
                		break;
                	case 'P':
                		data = found.getPassword();
                		break;
                	case 'N':
                		data = found.getNotes();
                		break;
                	case 'I':
                		data = found.getUUID().toString();
                		break;
                	default:
                		offset = start + 1;
                		continue;
                	}

                	SprContextV4 subCtx = (SprContextV4) ctx.clone();
                	subCtx.entry = found;

                	String innerContent = compileInternal(data, subCtx, recursionLevel + 1);
                	addRefsToCache(fullRef, innerContent, ctx);
                	text = fillRefsUsingCache(text, ctx);
                } else {
                	offset = start + 1;
                	continue;
                }
			}

		}

		return text;
	}

	public TargetResult findRefTarget(String fullRef, SprContextV4 ctx) {
		if (fullRef == null) { return null; }

		fullRef = fullRef.toUpperCase(Locale.ENGLISH);
		if (!fullRef.startsWith(STR_REF_START) || !fullRef.endsWith(STR_REF_END)) { 
			return null;
		}

		String ref = fullRef.substring(STR_REF_START.length(), fullRef.length() - STR_REF_END.length());
		if (ref.length() <= 4) { return null; }
		if (ref.charAt(1) != '@') { return null; }
		if (ref.charAt(3) != ':') { return null; }

		char scan = Character.MIN_VALUE;
		char wanted = Character.MIN_VALUE;

		scan = Character.toUpperCase(ref.charAt(2));
		wanted = Character.toUpperCase(ref.charAt(0));

		SearchParametersV4 sp = new SearchParametersV4();
		sp.setupNone();

		sp.searchString = ref.substring(4);
		if (scan == 'T') { sp.searchInTitles = true; }
		else if (scan == 'U') { sp.searchInUserNames = true; }
		else if (scan == 'A') { sp.searchInUrls = true; }
		else if (scan == 'P') { sp.searchInPasswords = true; }
		else if (scan == 'N') { sp.searchInNotes = true; }
		else if (scan == 'I') { sp.searchInUUIDs = true; }
		else if (scan == 'O') { sp.searchInOther = true; }
		else { return null; }

		List<PwEntry> list = new ArrayList<PwEntry>();
		ctx.db.rootGroup.searchEntries(sp, list);

		if (list.size() > 0) { 
			return new TargetResult((PwEntryV4)list.get(0), wanted); 
        }

		return null;
	}

	private void addRefsToCache(String ref, String value, SprContextV4 ctx) {
		if (ref == null) { return; }
		if (value == null) { return; }
		if (ctx == null) { return; }

		if (!ctx.refsCache.containsKey(ref)) {
			ctx.refsCache.put(ref, value);
		}
	}

	private String fillRefsUsingCache(String text, SprContextV4 ctx) {
		for (Entry<String, String> entry : ctx.refsCache.entrySet()) {
			text = StrUtil.replaceAllIgnoresCase(text, entry.getKey(), entry.getValue(), Locale.ENGLISH);
		}

		return text;

	}

}

public class StrUtil {
	public static List<String> splitSearchTerms(String search) {
		List<String> list = new ArrayList<String>();
		if (search == null) { return list; }

		StringBuilder sb = new StringBuilder();
		boolean quoted = false;

		for (int i = 0; i < search.length(); i++) {
			char ch = search.charAt(i);

			if ( ((ch == ' ') || (ch == '\t') || (ch == '\r') || (ch == '\n'))
					&& !quoted) {

				int len = sb.length();
				if (len > 0) {
					list.add(sb.toString());
					sb.delete(0, len);
				}
				else if (ch == '\"') { 
					quoted = !quoted;
				}
				else {
					sb.append(ch);
				}
			}
		}

		if (sb.length() > 0) {
			list.add(sb.toString());
		}

		return list;
	}

	public static int indexOfIgnoreCase(String text, String search, int start, Locale locale) {
		if (text == null || search == null) return -1;

		return text.toLowerCase(locale).indexOf(search.toLowerCase(locale), start);
	}

	public static int indexOfIgnoreCase(String text, String search, Locale locale) {
		return indexOfIgnoreCase(text, search, 0, locale);
	}

	public static String replaceAllIgnoresCase(String text, String find, String newText, Locale locale) {
		if (text == null || find == null || newText == null) { return text; }

		int pos = 0;
		while (pos < text.length()) {
			pos = indexOfIgnoreCase(text, find, pos, locale);
			if (pos < 0) { break; }

			String before = text.substring(0, pos);
			String after = text.substring(pos + find.length());

			text = before.concat(newText).concat(after);
			pos += newText.length();
		}

		return text;
	}

}

public class Types {

  public static long ULONG_MAX_VALUE = -1;

	/** Read an unsigned byte */
  public static int readUByte( byte[] buf, int offset ) {
    return ((int)buf[offset] & 0xFF);
  }

  /** Write an unsigned byte
   * 
   * @param val
   * @param buf
   * @param offset
   */
  public static void writeUByte(int val, byte[] buf, int offset) {
	  buf[offset] = (byte)(val & 0xFF);
  }

  public static byte writeUByte(int val) {
	  byte[] buf = new byte[1];

	  writeUByte(val, buf, 0);

	  return buf[0];
  }

  /**
   * Return len of null-terminated string (i.e. distance to null)
   * within a byte buffer.
   * 
   * @param buf
   * @param offset
   * @return
   */
  public static int strlen( byte[] buf, int offset ) {
    int len = 0;
    while( buf[offset + len] != 0 )
      len++;
    return len;
  }



  /**
   * Copy a sequence of bytes into a new array.
   * 
   * @param b - source array
   * @param offset - first byte
   * @param len - number of bytes
   * @return new byte[len]
   */
  public static byte[] extract( byte[] b, int offset, int len ) {
    byte[] b2 = new byte[len];
    System.arraycopy( b, offset, b2, 0, len );
    return b2;
  }


  private static final byte[] CRLFbuf = { 0x0D, 0x0A };
  private static final String CRLF = new String(CRLFbuf);
  private static final String SEP = System.getProperty("line.separator");
  private static final boolean REPLACE = ! SEP.equals(CRLF);

  public static String readCString(byte[] buf, int offset) throws UnsupportedEncodingException {
	  String jstring = new String(buf, offset, strlen(buf, offset), "UTF-8");

	  if ( REPLACE ) {
		  jstring = jstring.replace(CRLF, SEP);
	  }

	  return jstring;
  }

  public static int writeCString(String str, OutputStream os) throws IOException {
	  if ( str == null ) {
		  // Write out a null character
		  os.write(LEDataOutputStream.writeIntBuf(1));
		  os.write(0x00);
		  return 0;
	  }

	  if ( REPLACE ) {
		  str = str.replace(SEP, CRLF);
	  }

	  byte[] initial = str.getBytes("UTF-8");

	  int length = initial.length+1;
	  os.write(LEDataOutputStream.writeIntBuf(length));
	  os.write(initial);
	  os.write(0x00);

	  return length;
  }

  public static UUID bytestoUUID(byte[] buf) {
	  return bytestoUUID(buf, 0);
  }

  public static UUID bytestoUUID(byte[] buf, int offset) {
	  long lsb = 0;
	  for (int i = 15; i >= 8; i--) {
		  lsb = (lsb << 8) | (buf[i + offset] & 0xff);
	  }

	  long msb = 0;
	  for (int i = 7; i >= 0; i--) {
		  msb = (msb << 8) | (buf[i + offset] & 0xff);
	  }

	  return new UUID(msb, lsb);

  }

  public static byte[] UUIDtoBytes(UUID uuid) {
	  byte[] buf = new byte[16];

	  LEDataOutputStream.writeLong(uuid.getMostSignificantBits(), buf, 0);
	  LEDataOutputStream.writeLong(uuid.getLeastSignificantBits(), buf, 8);

	  return buf;
  }

}

