public class UriUtil {
    public static Uri parseDefaultFile(String text) {
        if (EmptyUtils.isNullOrEmpty(text)) {
            return null;
        }

        Uri uri = Uri.parse(text);
        if (EmptyUtils.isNullOrEmpty(uri.getScheme())) {
            uri = uri.buildUpon().scheme("file").authority("").build();
        }

        return uri;
    }
    public static Uri parseDefaultFile(Uri uri) {
        if (EmptyUtils.isNullOrEmpty(uri.getScheme())) {
            uri = uri.buildUpon().scheme("file").authority("").build();
        }

        return uri;
    }

    public static boolean equalsDefaultfile(Uri left, String right) {
        if (left == null || right == null) {
            return false;
        }

        left = parseDefaultFile(left);
        Uri uriRight = parseDefaultFile(right);

        return left.equals(uriRight);
    }

    public static InputStream getUriInputStream(Context ctx, Uri uri) throws FileNotFoundException {
        if (uri == null) return null;

        String scheme = uri.getScheme();
        if (EmptyUtils.isNullOrEmpty(scheme) || scheme.equals("file")) {
            return new FileInputStream(uri.getPath());
        }
        else if (scheme.equals("content")) {
            return ctx.getContentResolver().openInputStream(uri);
        }
        else {
            return null;
        }
    }

    /**
     * Many android apps respond with non-writeable content URIs that correspond to files.
     * This will attempt to translate the content URIs to file URIs when possible/appropriate
     * @param uri
     * @return
     */
    public static Uri translate(Context ctx, Uri uri) {
        // StorageAF provides nice URIs
        if (StorageAF.useStorageFramework(ctx) || hasWritableContentUri(uri)) { return uri; }

        String scheme = uri.getScheme();
        if (EmptyUtils.isNullOrEmpty(scheme)) { return uri; }

        String filepath = null;

        try {
            // Use content resolver to try and find the file
            if (scheme.equalsIgnoreCase("content")) {
                Cursor cursor = ctx.getContentResolver().query(uri, new String[]{android.provider.MediaStore.Images.ImageColumns.DATA}, null, null, null);
                cursor.moveToFirst();

                if (cursor != null) {
                    filepath = cursor.getString(0);
                    cursor.close();


                    if (!isValidFilePath(filepath)) {
                        filepath = null;
                    }
                }
            }

            // Try using the URI path as a straight file
            if (EmptyUtils.isNullOrEmpty(filepath)) {
                filepath = uri.getEncodedPath();
                if (!isValidFilePath(filepath)) {
                    filepath = null;
                }
            }
        }
        // Fall back to URI if this fails.
        catch (Exception e) {
            filepath = null;
        }

        // Update the file to a file URI
        if (!EmptyUtils.isNullOrEmpty(filepath)) {
            Uri.Builder b = new Uri.Builder();
            uri = b.scheme("file").authority("").path(filepath).build();
        }

        return uri;
    }

    private static boolean isValidFilePath(String filepath) {
        if (EmptyUtils.isNullOrEmpty(filepath)) { return false; }

        File file = new File(filepath);
        return file.exists() && file.canRead();
    }

    /**
     * Whitelist for known content providers that support writing
     * @param uri
     * @return
     */
    private static boolean hasWritableContentUri(Uri uri) {
        String scheme = uri.getScheme();

        if (EmptyUtils.isNullOrEmpty(scheme)) { return false; }

        if (!scheme.equalsIgnoreCase("content")) { return false; }

        switch (uri.getAuthority()) {
            case "com.google.android.apps.docs.storage":
                return true;
        }

        return false;
    }

}

public class Util {
	public static String getClipboard(Context context) {
		ClipboardManager clipboard = (ClipboardManager) context.getSystemService(Context.CLIPBOARD_SERVICE);
		CharSequence csText = clipboard.getText();
		if (csText == null) {
			return "";
		}

		return csText.toString();
	}

	public static void copyToClipboard(Context context, String text) throws SamsungClipboardException {
		ClipboardManager clipboard = (ClipboardManager) context.getSystemService(Context.CLIPBOARD_SERVICE);

		try {
			clipboard.setText(text);
		} catch (NullPointerException e) {
			throw new SamsungClipboardException(e);
		}
	}

	public static void gotoUrl(Context context, String url) throws ActivityNotFoundException {
		if ( url != null && url.length() > 0 ) {
			Uri uri = Uri.parse(url);
			context.startActivity(new Intent(Intent.ACTION_VIEW, uri));
		}
	}

	public static void gotoUrl(Context context, int resId) throws ActivityNotFoundException {
		gotoUrl(context, context.getString(resId));
	}

	public static String getEditText(Activity act, int resId) {
		TextView te =  (TextView) act.findViewById(resId);

		if (te != null) {
			return te.getText().toString();
		} else {
			return "";
		}
	}

	public static void setEditText(Activity act, int resId, String str) {
		TextView te =  (TextView) act.findViewById(resId);

		if (te != null) {
			te.setText(str);
		}
	}

	private static final int MAX_BUF_SIZE = 1024;
	public static void copyStream(InputStream in, OutputStream out) throws IOException {
		byte[] buf = new byte[MAX_BUF_SIZE];
		int read;
		while ((read = in.read(buf)) != -1) {
			out.write(buf, 0, read);
		}
	}

	public static int copyStream(InputStream in, OutputStream out, int maxBytes) throws IOException {
	    if (maxBytes <= 0) return 0;

		int bufSize = Math.min(maxBytes, MAX_BUF_SIZE);
		byte[] buf = new byte[bufSize];
		int origMax = maxBytes;

		int read;
		do {
			assert(maxBytes > 0);
			if (maxBytes >= buf.length) {
				read = in.read(buf);
			} else {
				read = in.read(buf, 0, maxBytes);
			}
			if (read == -1) { break; }

			out.write(buf, 0 , read);
			maxBytes -= read;

		} while (maxBytes > 0);

		// return total amonut read
		return origMax - maxBytes;
	}



}

public class UuidUtil {
	public static String toHexString(UUID uuid) {
		if (uuid == null) { return null; }

		byte[] buf = Types.UUIDtoBytes(uuid);
		if (buf == null) { return null; }

		int len = buf.length;
		if (len == 0) { return ""; }

		StringBuilder sb = new StringBuilder();

		short bt;
		char high, low;
		for (int i = 0; i < len; i++) {
			bt = (short)(buf[i] & 0xFF);
			high = (char)(bt >>> 4);


			low = (char)(bt & 0x0F);

			char h,l;
			h = byteToChar(high);
			l = byteToChar(low);

			sb.append(byteToChar(high));
			sb.append(byteToChar(low));
		}

		return sb.toString();
	}

	// Use short to represent unsigned byte
	private static char byteToChar(char bt) {
        if (bt >= 10) {
            return (char)('A' + bt - 10);
        }
        else {
            return (char)('0' + bt);
        }
	}
}

